[9549] = {"Schwarzschlammartefakte",nil, {"Bringt 3 kunstlose Murlocgötzen und 6 kunstlose Murlocmesser zu Klopper Knacksklug, der sich in einem angespülten Schildkrötenpanzer versteckt hält."}},
[9550] = {"Wohin führt die Karte?",nil, {"Macht den auf der Karte beschriebenen Pavillon ausfindig und sucht, auf was der Verfasser der Karte hinweisen wollte."}},
[9551] = {"Ruf der Luft",nil, {"Sprecht mit Weissager Nobundo in der Kristallhalle der Exodar auf der Azurmythosinsel."}},
[9552] = {"Ruf der Luft",nil, {"Sprecht mit Velaada beim Wildwindgipfel auf der Azurmythosinsel."}},
