[10485] = {"Kriegsherr des Blutenden Auges",nil, {"Bringt Kriegsherr Morkhs zerschmetterte Rüstung zu Korporal Eisengrat im Expeditionsposten."}},
[10486] = {"Die nahende Wildnis",nil, {"Gor'drek möchte, dass Ihr 12 Messerschwingenblutvergießer tötet. Kehrt zu ihm in der Donnerfeste im Schergrat zurück, wenn Ihr Eure Aufgabe erledigt habt."}},
[10487] = {"Drachenstaub",nil, {"Gor'drek möchte, dass Ihr 4 Einheiten Siechdrachenstaub sammelt. Kehrt danach zu ihm in der Donnerfeste im Schergrat zurück."}},
[10488] = {"Die Eigenen schützen",nil, {"Wendet Gor'dreks Salbe an 5 Terrorwölfen der Donnerfürsten an, um sie stärker zu machen und die Verteidigung des Zackengrats zu unterstützen. Kehrt danach zu Gor'drek in der Donnerfeste im Schergrat zurück."}},
[10489] = {"Einen uralten Baum fällen",nil, {"Besorgt Starkast Tiefenwurz' Stumpf und bringt ihn zu Tor'chunk Zweiklaue in der Donnerfeste im Schergrat."}},
[10490] = {"Ruf des Wassers",nil, {"Sprecht mit Weissager Nobundo in der Kristallhalle der Exodar auf der Azurmythosinsel."}},
[10491] = {"Ruf der Luft",nil, {"Sprecht mit Weissager Nobundo in der Kristallhalle der Exodar auf der Azurmythosinsel."}},
[10492] = {"Ein aufrichtiges Angebot",nil, {"Sammelt 15 Blutproben aus Winterquell und bringt sie zusammen mit 20 Gold und einem Paar Bindungen der Elemente zu Deliana in Eisenschmiede."}},
[10493] = {"Ein aufrichtiges Angebot",nil, {"Sammelt 15 Giftproben aus Silithus und bringt sie zusammen mit 20 Gold und einem Paar Armschienen des Lichts zu Mokvar in Orgrimmar zurück."}},
[10494] = {"Die angemessene Entlohnung",nil, {"Bringt Deliana in Eisenschmiede eine Kordel der Elemente und ein Paar Stulpen der Elemente."}},
[10495] = {"Die angemessene Entlohnung",nil, {"Bringt Mokvar in Orgrimmar einen Gürtel des Lichts und ein Paar Stulpen des Lichts."}},
[10496] = {"Anthions Abschiedsworte",nil, {"Kehrt mit einem Paar Stiefel der Elemente, einem Kilt der Elemente und einem Paar Schulterstücke der Elemente zu Deliana in Eisenschmiede zurück."}},
[10497] = {"Anthions Abschiedsworte",nil, {"Kehrt mit einem Paar Stiefel des Lichts, ein Paar Beinplatten des Lichts und einer Schiftung des Lichts zu Mokvar in Orgrimmar zurück."}},
[10498] = {"Das Beste gibt's zum Schluss",nil, {"Gebt Deliana Eure Helmkappe der Elemente und Eure Weste der Elemente."}},
[10499] = {"Das Beste gibt's zum Schluss",nil, {"Gebt Mokvar Euren Helm des Lichts und Eure Brustplatte des Lichts."}},
[10500] = {"Die Horde braucht Euch!",nil, {"Sprecht mit Kriegsherr Gorchuk im Tal der Geister in Orgrimmar."}},
[10501] = {"Die Allianz braucht Euch!",nil, {"Sprecht mit Feldmarschall Schneefall im Militärviertel von Eisenschmiede."}},
[10502] = {"Die Oger der Blutschläger",nil, {"Kommandant Himmelsschatten möchte, dass Ihr 30 Oger der Blutschläger im Außenposten der Blutschläger vernichtet. Kehrt zu ihm in Sylvanaar im Schergrat zurück, wenn Ihr die grauenvolle Aufgabe erledigt habt."}},
[10503] = {"Die Speerspießerbedrohung",nil, {"Tor'chunk Zweiklaue möchte, dass Ihr 30 Oger der Speerspießer und 10 Raptoren der Speerspießer in der Wehr der Speerspießer und im Grund der Speerspießer vernichtet. Kehrt zu ihm in der Donnerfeste im Schergrat zurück, wenn Ihr Eure Aufgabe erfüllt habt."}},
[10504] = {"Die Oger der Speerspießer",nil, {"Kommandant Himmelsschatten bittet Euch, 30 Oger der Speerspießer in der Wehr der Speerspießer und dem Grund der Speerspießer zu töten. Kehrt danach zu ihm in Sylvanaar im Schergrat zurück."}},
[10505] = {"Die Oger der Blutschläger",nil, {"Tor'chunk Zweiklaue bittet Euch, 30 Oger der Blutschläger beim Außenposten der Blutschläger zu töten. Kehrt zu ihm in der Donnerfeste im Schergrat zurück, wenn Ihr erfolgreich wart."}},
[10506] = {"Eine düstere Lage",nil, {"Rina Mondquell möchte, dass Ihr ihr Schwächungspulver auf 5 Terrorwölfe der Blutschläger im Außenposten der Blutschläger in der Blutschlägerklamm streut. Kehrt danach zu ihr in Sylvanaar im Schergrat zurück."}},
[10507] = {"Der Wendepunkt",nil, {"Benutzt Socrethars Teleportationsstein beim Invasionspunkt: Oberanführer im Norden der Konstruktionsbasis: Vergessenheit, um Eure Gruppe zu Socrethars Sitz zu bringen. Wenn Ihr dort seid, benutzt Voren'thals Präsenz, um Socrethar zu besiegen.nnDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10508] = {"Ein Geschenk für Voren'thal",nil, {"Spionagemeister Thalodien in Area 52 möchte, dass Ihr die erste Hälfte von Socrethars Stein von Konstruktionsmeister Morug in der Konstruktionsbasis: Vergessenheit und die zweite Hälfte des Steins von Konstruktionsmeister Silroth in der Konstruktionsbasis: Gehenna besorgt.nnDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10509] = {"Für Ruhm bestimmt",nil, {"Bringt Socrethars Teleportationsstein zu Voren'thal dem Seher auf der Sehertreppe in Shattrath.nnDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10510] = {"In die Draenethystmine",nil, {"Bronwyn Starkhammer bittet Euch, 5 Kristalle der Draenethystmine zu sammeln. Kehrt danach zu ihr in Sylvanaar im Schergrat zurück."}},
[10511] = {"Merkwürdiges Gebräu",nil, {"Borgrim Starkhammer möchte, dass Ihr 11 Einheiten von Blutschlägers Hauweggebräu besorgt. Wenn Ihr fertig seid, kehrt zu ihm in Sylvanaar im Schergrat zurück."}},
[10512] = {"Die Speerspießer abfüllen",nil, {"Nehmt Blutschlägers Hauweggebräu und versucht 5 Oger der Speerspießer damit betrunken zu machen. Meldet Euch bei Borgrim in Sylvanaar im Schergrat zurück, wenn Ihr erfolgreich wart."}},
[10513] = {"Oronok Herzeleid",nil, {"Sucht Oronok Herzeleid auf dem zerschlagenen Vorsprung nördlich der Zisterne der Echsennarbe."}},
[10514] = {"Ich war schon vieles...",nil, {"Oronok Herzeleid bei Oronoks Hof im Schattenmondtal möchte, dass Ihr 10 Schattenmondknollen aus den zerschlagenen Weiten besorgt.nnBringt Oronoks Eberpfeife zu ihm zurück, wenn Ihr fertig seid."}},
[10515] = {"Eine Lektion gelernt",nil, {"Oronok Herzeleid bei Oronoks Hof im Schattenmondtal möchte, dass Ihr 10 Eier eines gefräßigen Schinders in den zerschlagenen Weiten zerstört."}},
[10516] = {"Die Gefangenschaft der Verteidigerin",nil, {"Holt Verteidigerin Vuuleens Klinge und Verteidigerin Vuuleens Schild zurück. Kehrt dann zu Verteidigerin Vuuleen in der Wehr der Speerspießer im Schergrat zurück."}},
[10517] = {"Eure Zeit ist gekommen, Gorr'Dim...",nil, {"Verteidigerin Vuuleen bittet Euch, Gorr'Dim, den Anführer der Oger bei der Wehr der Speerspießer, zu töten. Wenn Ihr fertig seid, kehrt zu Ihr in der Wehr der Speerspießer im Schergrat zurück."}},
[10518] = {"Das Banner aufstellen",nil, {"Benutzt das Banner der Speerspießer auf dem Nordschlägerturm, um Gurn Grubnosh anzulocken. Bringt das Banner und Gurn Grubnoshs Helm zu Kommandant Himmelsschatten in Sylvanaar im Schergrat."}},
[10519] = {"Die Litanei der Verdammnis - Wahrheit und Geschichte",nil, {"Oronok Herzeleid bei Oronoks Hof im Schattenmondtal möchte, dass Ihr Euch seine Geschichte anhört. Sprecht mit Oronok, um ihm zusagen, dass er mit der Geschichte beginnen kann."}},
[10520] = {"Erzdruide Hirschhaupt helfen",nil, {"Sprecht mit Erzdruide Fandral Hirschhaupt in Darnassus."}},
[10521] = {"Grom'tor, Sohn von Oronok",nil, {"Sucht Grom'tor, Sohn von Oronok in der Echsennarbe im Schattenmondtal."}},
[10522] = {"Die Litanei der Verdammnis - Grom'tors Angriff",nil, {"Grom'tor, Sohn von Oronok, in der Echsennarbe im Schattenmondtal möchte, dass Ihr den ersten Teil der Litanei der Verdammnis besorgt."}},
[10523] = {"Die Litanei der Verdammnis - Erster Teil",nil, {"Bringt Grom'tors Schließkassette zu Oronok Herzeleid bei Oronoks Hof im Schattenmondtal."}},
[10524] = {"Artefakte der Donnerfürsten",nil, {"Beschafft die Trommel der Donnerfürsten, den Pfeil der Donnerfürsten und die Schrifttafel der Donnerfürsten. Bringt sie zusammen mit dem Artefakt der Donnerfürsten zu Rokgah Blutgriff in der Donnerfeste im Schergrat."}},
[10525] = {"Wegweisende Visionen",nil, {"Benutzt das feurige Seelenfragment, um Euren Geist in eine wegweisende Vision zu versetzen. Sprecht mit Rokgah Blutgriff in der Donnerfeste, nachdem Euch die Vision zum Aufenthaltsort des letzten Artefakts der Donnerfürsten geführt hat."}},
[10526] = {"Die Donnerlanze",nil, {"Rokgah Blutgriff möchte, dass Ihr die Donnerlanze zurückholt. Bringt sie zu ihr in der Donnerfeste im Schergrat zurück."}},
[10527] = {"Ar'tor, Sohn von Oronok",nil, {"Findet Ar'tor, Sohn von Oronok, bei der Stätte der Illidari im Schattenmondtal."}},
[10528] = {"Dämonische Kristallgefängnisse",nil, {"Sucht und tötet Schmerzensmeisterin Gabrissa in der Stätte der Illidari und kehrt mit dem kristallenen Schlüssel zum Leichnam von Ar'tor, Sohn von Oronok, zurück."}},
[10529] = {"Der Pfad des Jägers",nil, {"Sprecht mit Leutnant Dämmerflucht in der Zuflucht der Weltenwanderer."}},
[10530] = {"Der Pfad des Jägers",nil, {"Sprecht mit Leutnant Dämmerflucht in der Zuflucht der Weltenwanderer."}},
[10531] = {"Die Schlacht um das Arathibecken!",nil, {"Erobert die Mine, das Sägewerk, die Schmiede und die Farm und meldet Euch anschließend bei Feldmarschall Lichtmark in der Zuflucht."}},
[10532] = {"Zerschlagt die Versorgungslinien Arathors",nil, {"Entscheidet die Schlacht um das Arathibecken für Euch, erbeutet eine Arathivorratskiste und bringt sie Todespirscher Mortis in Hammerfall."}},
[10533] = {"Noch mehr Vorratskisten",nil, nil},
[10534] = {"Rückkehr nach Hause",nil, {"Caedmos in der Exodar wünscht Euch zu sehen."}},
[10535] = {"Die Ressourcen des Arathibeckens",nil, {"Gewinnt die Schlacht um das Arathibecken, raubt eine Arathivorratskiste und bringt sie Unteroffizier Maclear in der Zuflucht."}},
[10536] = {"Noch mehr Vorratskisten",nil, nil},
[10537] = {"Lohn'goron, Bogen des Herzeleid",nil, {"Der Geist von Ar'tor bei der Stätte der Illidari im Schattenmondtal möchte, dass Ihr Lohn'goron, den Bogen des Herzeleid, von den Dämonen in der Gegend beschafft."}},
[10538] = {"Kochendes Blut",nil, {"Benutzt den Verfluchten Kessel, um das Blut des Blutenden Auges in gekochtes Blut zu verwandeln. Bringt danach 12 Einheiten gekochtes Blut zu Apotheker Albreck im Rückenbrecherposten."}},
[10539] = {"Rückkehr nach Hause",nil, {"Caedmos in der Exodar wünscht Euch zu sehen."}},
[10540] = {"Die Litanei der Verdammnis - Ar'tors Angriff",nil, {"Der Geist von Ar'tor bei der Stätte der Illidari im Schattenmondtal möchte, dass Ihr den zweiten Teil der Litanei der Verdammnis von Veneratus dem Vielgesichtigen besorgt.nnKreaturen, die vom Geistjäger angegriffen werden oder Schaden erleiden, gewähren Euch keine Beute oder Erfahrung."}},
[10541] = {"Die Litanei der Verdammnis - Zweiter Teil",nil, {"Bringt Ar'tors Schließkassette zu Oronok Herzeleid bei Oronoks Hof im Schattenmondtal."}},
[10542] = {"Die ham meine Pfeife und mein Gesöff geklaut!",nil, {"Holt T'chalis Wasserpfeife und 10 Einheiten von Blutschlägers Hauweggebräu zurück. Bringt sie zu T'chali dem Hexendoktor in der Blutschlägerklamm im Schergrat."}},
[10543] = {"Grimnok und Korgaah, ich komm' Euch holen!",nil, {"T'chali der Hexendoktor hat Euch gebeten, seine Quäler, Grimnok Schlachttaufe und Korgaah, zu töten. Wenn Ihr fertig seid, kehrt zu ihm in der Blutschlägerklamm im Schergrat zurück."}},
[10544] = {"Verflucht seien Eure beiden Klans!",nil, {"Benutzt den heimtückischen mächtigen Fetisch, um 5 Gebäude in der Wehr der Speerspießer und 2 Gebäude im Außenposten der Blutschläger zu verfluchen. Kehrt zu T'chali dem Hexendoktor in der Blutschlägerklamm im Schergrat zurück, wenn Ihr fertig seid."}},
[10545] = {"Saufgelage der Speerspießer",nil, {"T'chali bittet Euch, Blutschlägers Hauweggebräu zu benutzen, um 5 Oger der Speerspießer betrunken zu machen. Wenn Ihr Erfolg hattet, kehrt zu T'chali dem Hexendoktor in der Blutschlägerklamm im Schergrat zurück."}},
[10546] = {"Borak, Sohn von Oronok",nil, {"Sucht Borak, Sohn von Oronok, in der Nähe der Stätte der Mondfinsternis im Schattenmondtal."}},
[10547] = {"Von Distelköpfen und Eiern...",nil, {"Borak, Sohn von Oronok, bei der Brücke nördlich der Stätte der Mondfinsternis möchte, dass Ihr ein verfaultes Arakkoaei besorgt und es zu Tobias dem Faulfraß in Shattrath, nordwestlich der Wälder von Terokkar, bringt."}},
[10548] = {"Die traurige Wahrheit",nil, {"Eralan in Tristessa möchte, dass Ihr den Schrumpfkopf des Archäologen aus einer der Truhen in Zeb'Tela oder Zeb'Nowa besorgt."}},
[10550] = {"Ein Bündel von Blutdisteln",nil, {"Bringt das Blutdistelbündel zurück zu Borak, Sohn von Oronok, bei der Brücke in der Nähe der Stätte der Mondfinsternis im Schattenmondtal."}},
[10551] = {"Loyalität gegenüber den Aldor",nil, {"Seid Ihr Euch Eurer Wahl sicher, <Name>? Die Aldor heißen Euch gerne als Verbündeten willkommen, aber die Seher werden Euch Eure Entscheidung nicht so leicht vergeben."}},
[10552] = {"Loyalität gegenüber den Sehern",nil, {"Seid Ihr Euch Eurer Wahl sicher, <Name>? Die Seher heißen Euch gerne als Verbündeten willkommen, aber die Aldor werden Euch Eure Entscheidung nicht so leicht vergeben."}},
[10553] = {"Voren'thal der Seher",nil, {"Sprecht mit Voren'thal dem Seher in der Bibliothek der Seher bei der Sehertreppe in Shattrath.nnDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10554] = {"Ishanah",nil, {"Sprecht mit Ishanah im Schrein des unendlichen Lichts auf der Aldorhöhe in Shattrath.nnDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10555] = {"Fauler Zauber",nil, {"Sammelt 18 ausgezupfte Federn der Lashh'an und lest den Folianten der Lashh'an in den Baumwipfeln des Lashhverstecks."}},
[10556] = {"Gekritzel",nil, {"Benutzt die Hand voll Federn im Zauberzirkel der Lashh'an und kehrt zu Daranelle in Sylvanaar zurück, bevor der Zauber nachlässt."}},
[10557] = {"Testflug: Das Zephyriumkapazitorium",nil, {"Sprecht mit Rally Zapschnapper und benutzt das Zephyriumkapazitorium. Kehrt danach zu Tally Zapschnapper in Toshleys Station zurück."}},
[10558] = {"Respektvoller Ruf in der Ehrenfeste",nil, {"Truppenkommandant Danath Trollbann in der Ehrenfeste auf der Höllenfeuerhalbinsel möchte, dass Ihr Euch einen respektvollen Ruf in der Ehrenfeste verschafft, indem Ihr die Höllenorcs in den Zerschmetterten Hallen der Höllenfeuerzitadelle tötet."}},
[10559] = {"Respektvoller Ruf in Thrallmar",nil, {"Nazgrel in Thrallmar auf der Höllenfeuerhalbinsel möchte, dass Ihr Euch einen respektvollen Ruf in Thrallmar verschafft, indem Ihr die Höllenorcs in den Zerschmetterten Hallen der Höllenfeuerzitadelle tötet."}},
[10560] = {"Respektvoller Ruf bei den Sha'tar",nil, {"A'dal in Shattrath möchte, dass Ihr Euch einen respektvollen Ruf bei den Sha'tar verschafft, indem Ihr Kael'thas Streitkräfte in der Festung der Stürme tötet."}},
[10561] = {"Respektvoller Ruf bei den Hütern der Zeit",nil, {"Andormu in den Höhlen der Zeit in Tanaris möchte, dass Ihr Euch einen respektvollen Ruf bei den Hütern der Zeit verdient, indem Ihr weiterhin die Ereignisse im Alten Hügelland und dem Schwarzen Morast in den Höhlen der Zeit verteidigt."}},
[10562] = {"Belagert!",nil, {"Schwadronskommandant Nuainn außerhalb der Wildhammerfeste möchte, dass Ihr 10 angreifende Höllenbestien tötet."}},
[10563] = {"Zur Feste der Legion",nil, {"Begebt Euch zur Feste der Legion und findet das Kommunikationsgerät der Legion. Benutzt die Verkleidung, während Ihr in der Nähe des Kommunikationsgeräts steht, um mehr über die Pläne der Legion herauszufinden. Kehrt danach zu Schwadronskommandant Nuainn in der Wildhammerfeste zurück."}},
[10564] = {"Jagt die Höllenbestien in die Luft!",nil, {"Zündet die Teufelsbombe, während Ihr auf der grünen Glyphe in der Feste der Legion steht. Meldet Euch bei Schwadronskommandant Nuainn in der Wildhammerfeste zurück, wenn Ihr Euren Auftrag erledigt habt."}},
[10565] = {"Die Steine der Vekh'nir",nil, {"Sammelt einen Kristall der Vekh'nir und benutzt ihn beim Zauberzirkel der Vekh'nir. Kehrt mit dem aufgeladenen Kristall der Vekh'nir zu Dertrok im Dorf der Mok'Nathal zurück."}},
[10566] = {"Probieren geht über Studieren",nil, {"Probiert jeden von Dertroks Zauberstäben an einem Ogersklaven der Arakkoa aus und kehrt danach zu Dertrok zurück. Ihr benötigt einen energieerfüllten Kristall der Vekh'nir für jeden Stab, den Ihr benutzen wollt."}},
[10567] = {"Die Herstellung des Anhängers",nil, {"Sammelt 6 Klauen der Ruuan'ok und benutzt sie bei dem Orakelkreis der Ruuan'ok, um einen Herold des Raben herbeizurufen. Tötet ihn und bringt den Anhänger des Herolds zu Timeon im Ruuanwald."}},
[10568] = {"Schrifttafeln von Baa'ri",nil, {"Anachoret Ceyla beim Altar der Sha'tar möchte, dass Ihr 12 Schrifttafeln von Baa'ri vom Boden und von den Arbeitern der Aschenzungen in den Ruinen von Baa'ri sammelt.nnDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10569] = {"Das Wrack der Sketh'lon",nil, {"Besorgt das Tagebuch des Kommandanten der Sketh'lon - Seite 1, das Tagebuch des Kommandanten der Sketh'lon - Seite 2 und das Tagebuch des Kommandanten der Sketh'lon - Seite 3 von den Arakkoa des Dunklen Rats beim Wrack der Sketh'lon und bringt die Seiten zu Greifenreiter Kieran."}},
[10570] = {"Einen Distelsüchtigen fangen",nil, {"Borak, Sohn von Oronok, bei der Brücke in der Nähe der Stätte der Mondfinsternis im Schattenmondtal möchte, dass Ihr Sturmgrimms Schriftwechsel beschafft."}},
[10571] = {"Oronu der Älteste",nil, {"Anachoret Ceyla beim Altar der Sha'tar möchte, dass Ihr die Befehle von Akama von Oronu dem Ältesten in den Ruinen von Baa'ri besorgt.nnDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10572] = {"Die Bombe bauen",nil, {"Besorgt Euch einen Teufelshäscherkraftkern und eine Teufelshäscherrüstungsplatte und bringt sie zu Schwadronskommandant Nuainn in der Wildhammerfeste."}},
[10573] = {"Die Todesschmiede",nil, {"Meldet Euch bei Sturmrufer Ewan Wildschwinge im Turm der Todesschmiede."}},
[10574] = {"Die Verderber der Aschenzungen",nil, {"Beschafft die vier Medaillonfragmente von Haalum, Eykenen, Lakaan und Uylaru und kehrt danach zu Anachoret Ceyla beim Altar der Sha'tar im Schattenmondtal zurück.nnDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10575] = {"Der Kerker des Wächters",nil, {"Anachoret Ceyla möchte, dass Ihr den Kerker des Wächters südlich der Ruinen von Baa'ri betretet und Sanoru befragt, um Akamas Aufenthaltsort herauszufinden.nnDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Seher."}},
[10576] = {"Unterwürfigkeit in Schattenmond",nil, {"Borak, Sohn von Oronok, bei der Brücke in der Nähe der Stätte der Mondfinsternis im Schattenmondtal möchte, dass Ihr 6 Teile der Rüstung der Mondfinsternis besorgt."}},
[10577] = {"Was Illidan will, soll Illidan bekommen...",nil, {"Borak, Sohn von Oronok, bei der Brücke in der Nähe der Stätte der Mondfinsternis im Schattenmondtal möchte, dass Ihr Großkommandant Ruusk in der Stätte der Mondfinsternis Illidans Nachricht überbringt."}},
[10578] = {"Die Litanei der Verdammnis - Boraks Angriff",nil, {"Borak, Sohn von Oronok, bei der Brücke in der Nähe der Stätte der Mondfinsternis im Schattenmondtal möchte, dass Ihr den dritten Teil der Litanei der Verdammnis von Ruul dem Verfinsterer besorgt.nnFür diesen Auftrag werdet Ihr eine Gruppe benötigen."}},
[10579] = {"Die Litanei der Verdammnis - Dritter Teil",nil, {"Bringt Boraks Schließkassette zu Oronok Herzeleid bei Oronoks Hof im Schattenmondtal."}},
[10580] = {"Wo sind diese verdammten Gnome hin?",nil, {"Kommandant Himmelschatten möchte, dass Ihr im südlichen Teil des Zackengrats im Schergrat nach Hinweisen auf den Verbleib von Toshley und seinen Gnomenfreunden sucht."}},
[10581] = {"Folget den Brotkrumen",nil, {"Auf eine verquere Art hat R-3D0 Euch gesagt, dass Ihr der Spur aus Ersatzteilen den Weg hinauf zum Messergrat folgen müsst, um die Gnome an einem Ort namens Toshleys Station zu finden.nnSprecht mit Toshley, wenn Ihr ihn findet."}},
[10582] = {"Diener des Schattenrats",nil, {"Sturmrufer Ewan Wildschwinge beim Turm der Todesschmiede möchte, dass Ihr 10 Wächter der Todesschmiede und 5 Beschwörer der Todesschmiede tötet."}},
[10583] = {"Flanis' Schicksal",nil, {"Sturmrufer Ewan Wildschwinge beim Turm der Todesschmiede möchte, dass Ihr die Todesschmiede nach Flanis Flinkschwinge absucht und Flanis' Beutel zurückholt."}},
[10584] = {"Ein paar Stromwandler besorgen",nil, {"Toshley möchte, dass Ihr den protovoltaischen Magnetsammler benutzt, um 5 Elektromentare einzufangen. Kehrt danach zu Toshleys Station im Schergrat zurück."}},
[10585] = {"Die Beschwörungskammer",nil, {"Besorgt Euch eine Elementarsteuerung von einem Schmied der Todesschmiede oder einem Tüftler der Todesschmiede. Benutzt sie, um das Ritual in der Beschwörungskammer zu unterbrechen. Meldet Euch danach bei Sturmrufer Ewan Wildschwinge im Turm der Todesschmiede zurück."}},
[10586] = {"Nieder mit dem Kriegshetzer!",nil, {"Besiegt Kriegshetzer Razuun, beschafft Razuuns Befehle und meldet Euch bei Schwadronskommandant Nuainn in der Wildhammerfeste."}},
[10587] = {"Ausbildungsgelände von Karabor",nil, {"Exarch Onaala beim Altar der Sha'tar möchte, dass Ihr 8 Gleven des Sonnenzorns von den Bittstellern der Dämonenjäger und den Initianden der Dämonenjäger in den Ruinen von Karabor, südöstlich des Altars der Sha'tar, beschafft.nnDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10588] = {"Die Litanei der Verdammnis",nil, {"Benutzt die Litanei der Verdammnis am Altar der Verdammnis um Cyrukh den Feuerlord zu rufen.nnVernichtet Cyrukh den Feuerlord und sprecht danach mit Erdheiler Torlok, der sich ebenfalls am Altar der Verdammnis befindet."}},
[10589] = {"Zugang erlangen",nil, {"Bringt eine Teleportersteuerung der Legion zu Schwadronskommandant Nuainn in der Wildhammerfeste. Bekanntlich tragen die Waffenschmiede der Mo'arg in der Feste der Legion diese Geräte bei sich."}},
[10590] = {"Beweist Euren Hass!",nil, {"Mehlar Dämmerklinge beim Bollwerk möchte, dass Ihr ihm 20 Geißelsteine des Dieners bringt."}},
[10592] = {"Die Weisheit der Bansheekönigin",nil, {"Bringt die Geißelsteinfragmente zu Fürstin Sylvanas Windläufer in Unterstadt."}},
[10593] = {"Das uralte Böse",nil, {"Lüftet die Geheimnisse des Versunkenen Tempels und befreit Atal'alarion. Tötet ihn und schneidet die Fäulnisranke aus seinem Fleisch. Kehrt danach zu Mehlar beim Bollwerk zurück."}},
[10594] = {"Die Klangfrequenz messen",nil, {"Schartwinkel der Metrognom möchte, dass Ihr die oszillierenden Schwingungsmesser so anordnet, dass Ihr, wenn Ihr in ihrer Mitte steht, von fünf sich überlappenden Schwingungsfeldern berührt werdet.nnKehrt danach zu Schartwinkel bei Toshleys Station im Schergrat zurück."}},
[10595] = {"Belagert!",nil, {"Blutwache Gulmok außerhalb von Schattenmond möchte, dass Ihr 10 angreifende Höllenbestien tötet."}},
[10596] = {"Zur Feste der Legion",nil, {"Begebt Euch zur Feste der Legion, findet ein Kommunikationsgerät der Legion und benutzt es, um mehr über die Pläne der Legion herauszufinden, während Ihr verkleidet seid. Kehrt danach zu Blutwache Gulmok in Schattenmond zurück."}},
[10597] = {"Die Bombe bauen",nil, {"Besorgt Euch einen Teufelshäscherkraftkern und eine Teufelshäscherrüstungsplatte und bringt sie zu Blutwache Gulmok in Schattenmond."}},
[10598] = {"Jagt die Höllenbestien in die Luft!",nil, {"Zündet die Teufelsbombe, während Ihr auf der grünen Glyphe in der Feste der Legion steht. Meldet Euch bei Blutwache Gulmok in Schattenmond zurück, wenn Ihr Euren Auftrag erledigt habt."}},
[10599] = {"Die Todesschmiede",nil, {"Meldet Euch bei Späher Zagran in der Schlackenwacht."}},
[10600] = {"Diener des Schattenrats",nil, {"Späher Zagran in der Schlackenwacht möchte, dass Ihr 10 Wächter der Todesschmiede und 5 Beschwörer der Todesschmiede tötet."}},
[10601] = {"Kagroshs Schicksal",nil, {"Späher Zagran in der Schlackenwacht möchte, dass Ihr die Todesschmiede nach Kagrosh absucht und Kagroshs Beutel zurückholt."}},
[10602] = {"Die Beschwörungskammer",nil, {"Besorgt Euch eine Elementarsteuerung von einem Schmied der Todesschmiede oder einem Tüftler der Todesschmiede. Benutzt sie, um das Ritual in der Beschwörungskammer zu unterbrechen. Meldet Euch danach bei Späher Zagran in der Schlackenwacht zurück."}},
[10603] = {"Nieder mit dem Kriegsrufer!",nil, {"Besiegt Kriegshetzer Razuun, beschafft Razuuns Befehle und meldet Euch bei Blutwache Gulmok in Schattenmond."}},
[10604] = {"Zugang erlangen",nil, {"Bringt eine Teleportersteuerung der Legion zu Blutwache Gulmok in Schattenmond. Bekanntlich tragen die Waffenschmiede der Mo'arg in der Feste der Legion diese Geräte bei sich."}},
[10605] = {"Carendin ruft",nil, {"Sprecht mit Carendin Halgar in Unterstadt."}},
[10606] = {"Die Kunst der Teufelshäscherwartung",nil, {"Tötet Doktor Gutrick und beschafft Euch den Schlüssel des Doktors. Benutzt den Schlüssel, um die Geldkassette des Doktors zu öffnen und 'Die Kunst der Teufelshäscherwartung' daraus zu entnehmen. Bringt das Buch zu Plexi beim Invasionspunkt: Katastrophe."}},
[10607] = {"Das Flüstern des Rabengottes",nil, {"Tötet die Arakkoa der Grishna, um für 2 Minuten den Zauber 'Rabensprache verstehen' zu erhalten. Hört Euch die Prophezeiungen aller vier Schreine an, solange der Zauber aktiv ist, und kehrt danach zurück."}},
[10608] = {"Kristallklar",nil, {"Schartwinkel der Metrognom möchte, dass Ihr 10 Kristallfelsklauen tötet. Kehrt danach zu ihm bei Toshleys Station im Schergrat zurück."}},
[10609] = {"Was war zuerst da, der Drache oder das Ei?",nil, {"Sammelt 3 Essenzen eines Protonetherdrachen, 3 Essenzen eines heranwachsenden Netherdrachen und 3 Essenzen eines ausgewachsenen Netherdrachen und bringt sie zu Fizit \"Dok\" Kuckuck bei Toshleys Station im Schergrat."}},
[10610] = {"Grundlagen des Sondierens",nil, nil},
[10611] = {"Die Kunst der Teufelshäscherwartung",nil, {"Tötet Doktor Maleficus und beschafft Euch den Schlüssel des Doktors. Benutzt den Schlüssel, um die Geldkassette des Doktors zu öffnen und 'Die Kunst der Teufelshäscherwartung' daraus zu entnehmen. Bringt das Buch zu Nakansi beim Invasionspunkt: Katastrophe."}},
[10612] = {"Teufelshäscherdrift",nil, {"Benutzt die Teufelshäscherfernsteuerung, um eine Schildwache der Teufelshäscher zu steuern. Zerstört 60 todesgeschmiedete Höllenbestien bevor Euer Teufelshäscher versagt. Meldet Euch danach bei Plexi am Invasionspunkt: Katastrophe."}},
[10613] = {"Teufelshäscherdrift",nil, {"Benutzt die Teufelshäscherfernsteuerung, um eine Schildwache der Teufelshäscher zu steuern. Zerstört 60 todesgeschmiedete Höllenbestien bevor Euer Teufelshäscher versagt. Meldet Euch danach bei Nakansi am Invasionspunkt: Katastrophe."}},
[10614] = {"Das Flüstern im Wind",nil, {"Sucht nach Leoroxx im Dorf der Mok'Nathal im Schergrat."}},
[10615] = {"Der Ruuanwald",nil, {"Sprecht mit Timeon im ewigen Hain im Ruuanwald des Schergrats."}},
[10617] = {"Seidenflügelkokons",nil, {"Taerek im Dorf der Mok'Nathal möchte, dass Ihr 8 Seidenflügelkokons von Seidenflügellarven besorgt.nnTötet die Larven schnell, da sie sich sonst in Seidenflügel verwandeln und ihre Kokons zerstören."}},
[10618] = {"Die weichsten Flügel",nil, {"Silmara im Dorf der Mok'Nathal möchte, dass Ihr 12 schillernde Flügel sammelt."}},
[10619] = {"Die Aschenzungen",nil, {"Verteidiger Aluumen möchte, dass Ihr zu den Ruinen von Baa'ri geht und 3 Antreiber der Aschenzungen, 4 Krieger der Aschenzungen und 6 Schamanen der Aschenzungen tötet.nnDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10620] = {"Gratwirbelbedrohung",nil, {"Taumel Dina möchte, dass Ihr 10 Gratwirbelpirscher tötet. Kehrt danach zu ihr in Toshleys Station im Schergrat zurück."}},
[10621] = {"Splitter des Illidaribanns",nil, {"Bringt den Splitter des Illidaribanns zu Ordinn Donnerfaust in der Wildhammerfeste."}},
[10622] = {"Ein Beweis der Treue",nil, {"Tötet Zandras im Kerker des Wächters im Schattenmondtal und kehrt danach zu Sanoru zurück."}},
[10623] = {"Splitter des Illidaribanns",nil, {"Bringt den Splitter des Illidaribanns zu Grokom Totauge in Schattenmond."}},
[10624] = {"Eine Spukgeschichte",nil, {"Chefapothekerin Hildagard in Schattenmond im Schattenmondtal möchte, dass Ihr ihr 15 Einheiten ewig brennende Asche bringt."}},
[10625] = {"Geisterbrille",nil, {"Chefapothekerin Hildagard in Schattenmond im Schattenmondtal möchte, dass Ihr 12 Zeloten des Schattenmondklans tötet."}},
[10626] = {"Beschafft die Waffen",nil, {"Ordinn Donnerfaust in der Wildhammerfeste möchte, dass Ihr ihm Makazradons Gleve und Morgrorons Gleve bringt."}},
[10627] = {"Beschafft die Waffen",nil, {"Grokom Totauge im Schattenmondtal möchte, dass Ihr ihm Makazradons Gleve und Morgrorons Gleve bringt."}},
[10628] = {"Akama",nil, {"Sprecht mit Akama in der verborgenen Kammer im Kerker des Wächters."}},
[10629] = {"Drecksarbeit",nil, {"Benutzt die Teufelshundepfeife, um einen teuflischen Wachhund zu rufen. Führt den teuflischen Wachhund Gassi und tötet ein paar Hölleneber. Durchsucht dann die Haufen des teuflischen Wachhunds nach den Schlüsseln. Wenn Ihr die Schlüssel gefunden habt, bringt sie zu Großknecht Razelcraz bei der Mine nordwestlich von Thrallmar."}},
[10630] = {"Unter Thrallmar",nil, {"Tötet Urga'zz und kehrt dann zu Großknecht Razelcraz in der Mine nordwestlich von Thrallmar zurück."}},
[10632] = {"Zähne zeigen",nil, {"Razak Eisenflanke möchte, dass Ihr 5 besonders scharfe Dolchrachenzähne sammelt. Kehrt danach zu ihm in Toshleys Station im Schergrat zurück."}},
[10633] = {"Teron Blutschatten - Geschichte und Mythos",nil, {"Chefapothekerin Hildagard in Schattenmond im Schattenmondtal möchte, dass Ihr den uralten Schattenmondgeist findet.nnWenn Ihr Eure Geisterbrille verlieren solltet, kann Euch Hildagard ein neues Paar herstellen."}},
[10634] = {"Hellsicht: Blutschattens Rüstung",nil, {"Der uralte Schattenmondgeist am Altar der Schatten im südöstlichen Schattenmondtal möchte, dass Ihr Blutschattens Rüstung besorgt."}},
[10635] = {"Hellsicht: Blutschattens Umhang",nil, {"Der uralte Schattenmondgeist am Altar der Schatten im südöstlichen Schattenmondtal möchte, dass Ihr Blutschattens Umhang besorgt."}},
[10636] = {"Hellsicht: Blutschattens Schlagstock",nil, {"Der uralte Schattenmondgeist am Altar der Schatten im südöstlichen Schattenmondtal möchte, dass Ihr Blutschattens Schlagstock besorgt."}},
[10637] = {"Eine notwendige Ablenkung",nil, {"Exarch Onaala möchte, dass Ihr zu den Ruinen von Karabor zurückkehrt und Azaloth auf seine Kerkermeister hetzt.nnDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10638] = {"NOT A QUEST",nil, {"Seek <NAME> in the <DISTRICT> of Silvermoon City."}},
[10639] = {"Ich bin Teron Blutschatten...",nil, {"Der uralte Schattenmondgeist hat Euch benutzt! Nun, da Ihr alle Gegenstände, die er wollte, gesammelt habt, hat er sich als Teron Blutschatten offenbart. Um von seinem Bann loszukommen, müsst Ihr seinen Willen erfüllen und Karsius den uralten Wächter töten.nnWenn Ihr erfolgreich wart, meldet Euch bei Chefapothekerin Hildagard in Schattenmond."}},
[10640] = {"Altruis",nil, {"Reist in den Westen Nagrands und sucht Altruis den Leider.nnDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10641] = {"Gegen die Legion",nil, {"Altruis der Leider möchte, dass Ihr das frisch gezapfte Blut einer Zornpriesterin der Konstruktionsbasis: Gehenna im Nethersturm besorgt. Schüttet es auf den Boden und tötet den Avatar von Sathal. Kehrt zu Altruis dem Leider zurück, wenn Ihr fertig seid."}},
[10642] = {"Spiritus ex machina",nil, {"Zorus der Richter in der Wildhammerfeste im Schattenmondtal möchte, dass Ihr ihm 15 Proben ewig brennende Asche bringt."}},
[10643] = {"Herolde des Schattenmondklans",nil, {"Zorus der Richter in der Wildhammerfeste im Schattenmondtal möchte, dass Ihr 12 Herolde des Schattenmondklans tötet."}},
[10644] = {"Teron Blutschatten - Geschichte und Mythos",nil, {"Zorus der Richter in der Wildhammerfeste im Schattenmondtal möchte, dass Ihr den uralten Schattenmondgeist findet.nnWenn Ihr Eure Geisterbrille verlieren solltet, kann Euch Zorus der Richter eine neue herstellen."}},
[10645] = {"Ich bin Teron Blutschatten...",nil, {"Der uralte Schattenmondgeist hat Euch benutzt! Nun, da Ihr alle Gegenstände, die er wollte, gesammelt habt, hat er sich als Teron Blutschatten offenbart. Um von seinem Bann loszukommen, müsst Ihr seinen Willen erfüllen und Karsius den uralten Wächter töten.nnWenn Ihr erfolgreich wart, meldet Euch bei Zorus dem Richter in der Wildhammerfeste."}},
[10646] = {"Illidans Schüler",nil, {"Hört Euch Altruis' Geschichte an."}},
[10647] = {"Gesucht: Uvuros, Geißel von Schattenmond",nil, {"Tötet Uvuros und beschafft Uvuros' feurige Mähne. Bringt sie zu Kriegsrufer Sardon Treuschnitt in Schattenmond im Schattenmondtal."}},
[10648] = {"Gesucht: Uvuros, Geißel von Schattenmond",nil, {"Tötet Uvuros und beschafft Uvuros' feurige Mähne. Bringt sie zu Kriegsrufer Bierschnut in der Wildhammerfeste im Schattenmondtal."}},
[10649] = {"Das Buch der teuflischen Namen",nil, {"Reist in das Schattenlabyrinth von Auchindoun und besorgt das Buch der teuflischen Namen von Schwarzherz dem Hetzer. Kehrt danach zu Altruis in Nagrand zurück."}},
[10650] = {"Rückkehr zu den Aldor",nil, {"Sprecht mit Exarch Onaala beim Altar der Sha'tar im Schattenmondtal.nnDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10651] = {"Varedis muss aufgehalten werden",nil, {"Exarch Onaala möchte, dass Ihr zu den Ruinen von Karabor geht und Alandien, Theras, Netharel und Varedis tötet. Benutzt das Buch der teuflischen Namen, während Varedis die Fähigkeit 'Metamorphose' einsetzt, um ihn zu schwächen. Kehrt danach mit dem Buch der teuflischen Namen zu Exarch Onaala zurück.nnDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10652] = {"Hinter feindlichen Linien",nil, {"Sprecht mit Veronia, wenn Ihr bereit seid, zur Manaschmiede Coruu aufzubrechen. Meldet Euch dort bei Caledis Morgenhell.nnDas Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10653] = {"Mal des Sargeras",nil, {"Bringt 10 Male des Sargeras zu Adyen dem Lichtwächter in Shattrath.nnDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10654] = {"Mehr Male des Sargeras",nil, {"Tragt Ihr noch mehr Insignien des Feindes bei Euch, <Name>? Die Brennende Legion ist auch weiterhin eine Bedrohung, und wir verfolgen sie überall, wo wir sie finden können."}},
[10655] = {"Einzelne Male des Sargeras",nil, {"Ich nehme Euch alle übrigen Male, die Ihr habt, ab. Keine Sorge, Eure Taten werden nicht unbemerkt bleiben."}},
[10656] = {"Siegel des Sonnenzorns",nil, {"Magistrix Fyalenn in Shattrath möchte, dass Ihr ihr 10 Siegel des Sonnenzorns bringt.nnDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10657] = {"Lasst sie abblitzen!",nil, {"Toshley möchte, dass Ihr die repolarisierte Magnetsphäre benutzt, um 25 Blitzschläge von Schuppenflügelschlangen abzufangen. Des Weiteren möchte er, dass Ihr 5 Blitzschlagdrüsen eines Schuppenflügels sammelt. Kehrt danach zu Toshley in Toshleys Station im Schergrat zurück."}},
[10658] = {"Mehr Siegel des Sonnenzorns",nil, {"Was habt Ihr in letzter Zeit für uns getan, <Name>? Der Krieg gegen Kael'thas kämpft sich nicht von alleine, verstanden?"}},
[10659] = {"Einzelne Siegel des Sonnenzorns",nil, {"Sogar der kleinste Beitrag zu unserer Sache wird bemerkt, <Name>. Unsere Feinde sind stark, aber wir werden siegen!"}},
