[10811] = {"Sucht Neltharaku auf",nil, {"Sucht Neltharaku, den Patron des Drachenschwarms der Netherschwingen auf."}},
[10812] = {"Die geheimnisvolle Maske",nil, {"Bringt die geheimnisvolle Maske zu Wildfürst Antelarion im Ruuanwald."}},
[10813] = {"Die Augen von Grillok",nil, {"Benutzt Zezzaks Splitter, um ein Auge von Grillok zu fangen und nähert Euch damit Zezzaks Kessel. Bringt den Splitter zu Zezzak zurück, wenn Ihr das Auge dem Kessel übergeben habt."}},
[10814] = {"Neltharakus Geschichte",nil, {"Sprecht mit Neltharaku und hört Euch seine Geschichte an."}},
[10815] = {"Val'zareqs Tagebuch: Vorboten des Krieges",nil, {"Findet Marcus Auralion, Hochgeneral der Sha'tar, bei der Purpurwacht."}},
[10816] = {"Rückforderung geheiligten Bodens",nil, {"Verteidiger Aluumen möchte, dass Ihr 8 Töter des Schattenmondklans, 8 Auserwählte des Schattenmondklans und 4 Dunkelwirker des Schattenmondklans tötet.nnDas Abschließen von Aufgaben für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10817] = {"Die große Vergeltung",nil, {"Varen der Fordernde möchte, dass Ihr 8 Töter des Schattenmondklans, 8 Auserwählte des Schattenmondklans und 4 Dunkelwirker des Schattenmondklans tötet.nnDas Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10818] = {"Baron Zobelmähne verlangt nach Euch",nil, {"Ihr habt eine Einladung erhalten, um in einer Angelegenheit von höchster Wichtigkeit mit Baron Zobelmähne zu sprechen. Sucht ihn beim Zirkel des Blutes im Schergrat auf."}},
[10819] = {"Die Gasmaske der Teufelsbrut",nil, {"Setzt die Gasmaske der Teufelsbrut auf und benutzt den Legionskommunikator im Konstruktionslager: Groll."}},
[10820] = {"Täuscht den Feind",nil, {"Tötet 4 Knechte der Schicksalsschmiede und 4 Ingenieure der Schicksalsschmiede."}},
[10821] = {"Ihr seid gefeuert!",nil, {"Tötet Schicksalsruferin und meldet Euch dann bei Wildfürst Antelarion im ewigen Hain."}},
[10822] = {"Einzelne Siegel des Sonnenzorns",nil, nil},
[10823] = {"Mehr Siegel des Sonnenzorns",nil, nil},
[10824] = {"Siegel des Sonnenzorns",nil, {"Kampfmagierin Vyara beim Sanktum der Sterne möchte, dass Ihr ihr 10 Siegel des Sonnenzorns bringt.nnDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10825] = {"Die kugellose Wahrheit",nil, {"Bringt die Kugel der Grishna zu Baumhüter Chawn im Ruuanwald im Schergrat."}},
[10826] = {"Male des Sargeras",nil, {"Bringt 10 Male des Sargeras zu Herold Saronen im Schattenmondtal.nnDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10827] = {"Mehr Male des Sargeras",nil, {"Tragt Ihr noch mehr Insignien des Feindes bei Euch, <Name>? Wir bekämpfen die Legion, bis das Licht siegt!"}},
[10828] = {"Einzelne Male des Sageras",nil, {"Ich nehme Euch alle übrigen Male, die Ihr habt, ab. Keine Sorge, Eure Taten werden nicht unbemerkt bleiben."}},
[10829] = {"Baumriese muss es wissen",nil, {"Baumhüter Chawn bittet Euch, Baumriese aufzusuchen und mit ihm zu sprechen. Ihr findet das Urtum im Rabenwald im Schergrat."}},
[10830] = {"Der Exorzismus der Bäume",nil, {"Sammelt 5 Kugeln von Grishnath und 5 Terrorfederkiele und stellt daraus Exorzismusfedern her. Benutzt die Federn, um 5 Koikoigeister von den Laubbärten des Rabenwaldes zu exorzieren und zu töten. Sobald Ihr die Aufgabe erledigt habt, kehrt zu Baumriese im Rabenwald im Schergrat zurück."}},
[10831] = {"Wie man ein Mondstoffschneider wird",nil, {"Benutzt das magieerfüllte Netherstoffstück während Ihr im Mondbrunnen der Zuflucht des Cenarius steht, um eine Urmondstoffprobe herzustellen. Bringt die fertige Urmondstoffprobe zu Nasmara Mondweise im unteren Viertel von Shattrath.nnACHTUNG! Ihr könnt nur eine Schneidereispezialisierung auswählen."}},
[10832] = {"Wie man ein Zauberfeuerschneider wird",nil, {"Bringt eine Probe einer Nethergespenstessenz zu Gidge Spruchwirker im unteren Viertel von Shattrath.nnACHTUNG! Ihr könnt nur eine Schneidereispezialisierung auswählen."}},
[10833] = {"Wie man ein Schattenzwirnschneider wird",nil, {"Benutzt den Kristall der tiefen Schatten in der Nähe des Altars der Schatten, um Eure Verbindung zu dem Altar zu stärken. Kehrt danach zu Andrion Dunkelweber im unteren Viertel in Shattrath zurück.nnACHTUNG! Ihr könnt nur eine Schneidereispezialisierung auswählen."}},
[10834] = {"Grillok 'Finsterauge'",nil, {"Tötet Grillok Finsterauge und bringt Grilloks Augenklappe zu Zezzak im Rückenbrecherposten."}},
[10835] = {"Apotheker Antonivich",nil, {"Bringt Albrecks Befund zu Apotheker Antonivich in Thrallmar."}},
[10836] = {"Unterwanderung der Festung des Drachenmals",nil, {"Neltharaku, der hoch über den Netherschwingenfeldern im Schattenmondtal seine Kreise zieht, möchte, dass Ihr 15 Orcs des Drachenmals tötet.nnSämtliche Orcs des Drachenmals in der Festung des Drachenmals kommen dafür in Frage."}},
[10837] = {"Zur Netherschwingenscherbe!",nil, {"Neltharaku, der hoch über den Netherschwingenfeldern im Schattenmondtal seine Kreise zieht, möchte, dass Ihr 12 Netherrankenkristalle auf der Netherschwingenscherbe sammelt."}},
[10838] = {"Der dämonische Seher",nil, {"Benutzt den dämonischen Seher im Höllenfeuerbecken, wartet, bis er sich eingestellt hat und führt dann eine Messung durch.nnBringt den dämonischen Seher und die Messungen des dämonischen Sehers zu Apotheker Antonivich in Thrallmar."}},
[10839] = {"Dunkelstein des Skithverstecks",nil, {"Benutzt die Rute der Läuterung bei Terokks Dunkelstein im Skithversteck und kehrt danach zu Kirrik bei der Flüchtlingskarawane in der Knochenwüste zurück."}},
[10840] = {"Das Grab des Lichts",nil, {"Hohepriester Orglum möchte, dass Ihr zum Grab des Lichts reist und 10 astrale Nethermanten und 10 astrale Brandschatzer tötet. Kehrt danach zu ihm bei der Flüchtlingskarawane zurück."}},
[10841] = {"Der rachsüchtige Herold",nil, {"Aktiviert das Gerät."}},
[10842] = {"Der rachsüchtige Herold",nil, {"Hohepriester Orglum möchte, dass Ihr in das Grab des Lichts reist und das Grabrelikt der Draenei in der Nähe des uralten Draeneialtars benutzt.nnVerteidigt den Grabwächter und vernichtet den rachsüchtigen Herold. Sprecht mit dem Draeneivorfahren beim uralten Draeneialtar, wenn das Ritual vollendet ist."}},
[10843] = {"Seit ich denken kann...",nil, {"Um Euch den Mok'Nathal gegenüber zu beweisen, hat Leoroxx Euch aufgetragen, Gnosh Brognat, den Anführer der Oger im Außenposten der Speerspießer zu töten. Kehrt zu Leoroxx im Dorf der Mok'Nathal im Schergrat zurück, wenn Ihr es geschafft habt, oder kehrt gar nicht mehr zurück."}},
[10844] = {"Konstruktionslager: Groll",nil, nil},
[10845] = {"Tötet die Brutmutter",nil, {"Leoroxx fordert Euch auf, Unheilschwinge zu töten. Kehrt zu ihm im Dorf der Mok'Nathal zurück, wenn Ihr es geschafft habt."}},
[10846] = {"Verständnis für die Mok'Nathal",nil, {"Um die Mok'Nathal zu verstehen, müsst Ihr Vekh, den Anführer der Arakkoa der Vekh'nir töten. Kehrt danach zu Leoroxx im Dorf der Mok'Nathal im Schergrat zurück."}},
[10847] = {"Die Augen von Skettis",nil, {"Bringt das Auge des Reskkverstecks und das Auge des Shienorverstecks zu Rilak dem Erlösten im unteren Viertel von Shattrath."}},
[10848] = {"Das untote Böse des Rhazeverstecks",nil, {"Tötet 4 Todeskrallengeister, 4 kreischende Geister und 2 Geistraben und kehrt danach zu Kirrik bei der Flüchtlingskarawane zurück."}},
[10849] = {"Sucht Kirrik",nil, {"Bringt Rilaks Schreiben zu Kirrik bei der Flüchtlingskarawane in der Knochenwüste."}},
[10850] = {"Nethergas in einem Teufelsfeuermotor",nil, {"Ihr findet die Öffnung, durch die der Teufelshäscher betankt wird."}},
[10851] = {"Die Totems meines Feindes",nil, {"Sammelt 5 Totems der Speerspießer für Geistrufer Dohgar im Dorf der Mok'Nathal im Schergrat."}},
[10852] = {"Vermisste Freunde",nil, {"Ethan möchte, dass Ihr 12 seiner vermissten Freunde im Skithversteck findet und sie zu ihm bei der Flüchtlingskarawane zurückbringt."}},
[10853] = {"Geisteranrufung",nil, {"Geistrufer Dohgar bittet Euch, das Totem der Geisteranrufung zu benutzen, um 8 Geister eines geringen Netherdrachen zu sammeln. Kehrt danach zu ihm im Dorf der Mok'Nathal im Schergrat zurück."}},
[10854] = {"Die Macht Neltharakus",nil, {"Neltharaku, der hoch über den Netherschwingenfeldern im Schattenmondtal seine Kreise zieht, möchte, dass Ihr 5 versklavte Drachen der Netherschwingen befreit."}},
[10855] = {"Teufelshäscher, nein Danke!",nil, {"Beschafft Euch 5 Einheiten kondensiertes Nethergas von den Robogenieuren der Gan'arg in der Konstruktionsbasis: Vergessenheit und füllt sie in einen deaktivierten Teufelshäscher. Kehrt danach zu Netherpirscher Nauthis zurück."}},
[10856] = {"Die beste Verteidigung",nil, {"Netherpirscher Nauthis möchte, dass Ihr 12 Zornschaffer in der Konstruktionsbasis: Gehenna nördlich der Sturmsäule vernichtet."}},
[10857] = {"Teleportiert das!",nil, {"Netherpirscher Nauthis möchte, dass Ihr den Stab der geistigen Beeinflussung bei den Mo'arg in der Konstruktionsbasis: Gehenna einsetzt und ihre Fähigkeit 'Teleporter sprengen' bei den drei Teleportern in der Konstruktionsbasis benutzt."}},
[10858] = {"Karynaku",nil, {"Sucht nach Karynaku in der Festung des Drachenmals."}},
[10859] = {"Sammelt die Kugeln",nil, {"Geistrufer Dohgar bittet Euch, das Kugelfängertotem zu benutzen, um 15 Lichtkugeln der Razaani zu sammeln. Kehrt danach zu ihm im Dorf der Mok'Nathal im Schergrat zurück."}},
[10860] = {"Leckerbissen der Mok'Nathal",nil, {"Matrone Varah hat Euch eine Liste mit Zutaten für die Zubereitung ihrer beiden Lieblingsgerichte gegeben. Darunter befinden sich 3 Raptorenrippchen und 3 Stück Schlangenfleisch. Bringt sie zu ihr im Dorf der Mok'Nathal im Schergrat."}},
[10861] = {"Präventivschlag gegen das Lithicversteck",nil, {"Zerbrecht die verfluchten Eier im Lithicversteck. Erlöst 3 Jungtiere und tötet 3 bösartige Jungtiere. Kehrt danach zu Kirrik bei der Flüchtlingskarawane zurück."}},
[10862] = {"Auslieferung an die Horde",nil, {"Rokag möchte, dass Ihr Rilak den Erlösten in Shattrath aufsucht und von ihm fordert, dass er und sein Volk sich der Horde ausliefern."}},
[10863] = {"Geheimnisse der Arakkoa",nil, {"Lady Dena Kennedy möchte, dass Ihr Rilak den Erlösten aufsucht und herausfindet, welche Geheimnisse der Arakkoa er mit Euch teilen wird."}},
[10864] = {"Eine Last auf der Seele",nil, {"Tötet 20 Orcs der Zerschmetterten Hand und kehrt danach zu Apotheker Antonivich in Thrallmar zurück."}},
[10865] = {"Informiert Leoroxx!",nil, {"Geistrufer Dohgar fleht Euch an, Leoroxx über die Taten der Astralen der Razaani zu informieren. Ihr findet ihn im Dorf der Mok'Nathal im Schergrat."}},
[10866] = {"Zuluhed der Geschlagene",nil, {"Tötet Zuluhed den Geschlagenen und beschafft Euch Zuluheds Schlüssel. Benutzt Zuluheds Schlüssel, um Zuluheds Fesseln zu öffnen und Karynaku zu befreien."}},
[10867] = {"Es kann nur eine Antwort geben",nil, {"Leoroxx fleht Euch an, die Seelensammlung zu beschaffen und sie zu Geistrufer Dohgar im Dorf der Mok'Nathal im Schergrat zu bringen."}},
[10868] = {"Der Kriegspfad der Arakkoa",nil, {"Beraterin Faila bittet Euch, 14 Krallaniere der Shienor und 6 Zauberhexer der Shienor zu töten. Kehrt danach zu ihr in der Steinbrecherfeste zurück."}},
[10869] = {"Die Herde ausdünnen",nil, {"Leutnant Kieshammer bittet Euch, 14 Krallaniere der Shienor und 6 Zauberhexer der Shienor zu töten. Kehrt danach zu ihm in Allerias Feste zurück."}},
[10870] = {"Verbündeter der Netherschwingen",nil, {"Lasst Euch von Karynaku zurück zu Mordenai in den Netherschwingenfeldern bringen."}},
[10871] = {"Verbündeter der Netherschwingen",nil, {"Lasst Euch von Karynaku zurück zu Mordenai in den Netherschwingenfeldern bringen."}},
[10872] = {"Zuluhed der Geschlagene",nil, {"Tötet Zuluhed den Geschlagenen und beschafft Euch Zuluheds Schlüssel. Benutzt Zuluheds Schlüssel, um Zuluheds Fesseln zu öffnen und Karynaku zu befreien."}},
[10873] = {"Bei Nacht verschleppt",nil, {"Späher Navrin möchte, dass Ihr zum Netherweberkamm reist und 6 eingesponnene Verteidiger der Sha'tar befreit. Kehrt danach zu Navrin im Außenposten der Sha'tari zurück."}},
[10874] = {"Signalfeuer des Shalasverstecks",nil, {"Kirrik bei der Flüchtlingskarawane möchte, dass Ihr die vier Signalfeuer des Shalasverstecks löscht."}},
[10875] = {"Bericht an Nazgrel",nil, {"Sprecht mit Nazgrel in Thrallmar."}},
[10876] = {"Am Fuße der Zitadelle",nil, {"Tötet Truppenkommandant Gorax, platziert die Herausforderung der Horde auf seinem Leichnam. Tötet danach die Hand von Kargath und bringt den Kopf der Hand von Kargath zu Nazgrel in Thrallmar."}},
[10877] = {"Das Schreckensrelikt",nil, {"Oakun möchte, dass Ihr nach Osten zur herrenlosen Karawane reist und das Schreckensrelikt besorgt. Kehrt zu Oakun zurück, wenn Eure Aufgabe erledigt ist."}},
[10878] = {"Bevor die Dunkelheit sich senkt",nil, {"Mekeda bei der Flüchtlingskarawane in den Wäldern von Terokkar möchte, dass Ihr 8 Scharmützler der Kabale, 4 Zauberwirker der Kabale und 2 Initianden der Kabale tötet."}},
[10879] = {"Offensive aus Skettis",nil, {"Haltet die Angreifer auf und sprecht mit Rilak."}},
[10880] = {"Befehle der Kabale",nil, {"Bringt die Befehle der Kabale zu Mekeda bei der Flüchtlingskarawane in den Wäldern von Terokkar."}},
[10881] = {"Das Schattengrab",nil, {"Geht in das Schattengrab westlich der Flüchtlingskarawane und beschafft Euch Arunens Tuch, K'alens Hammer und Atalors Schriftrolle. Kehrt danach zu Mekeda bei der Flüchtlingskarawane zurück."}},
[10882] = {"Herold der Verdammnis",nil, {"Ihr wurdet beauftragt, zur Arkatraz in der Festung der Stürme zu reisen und Herold Horizontiss zu töten. Kehrt danach zu A'dal auf der Terrasse des Lichts in Shattrath zurück."}},
[10883] = {"Der Schlüssel der Stürme",nil, {"Sprecht mit A'dal in Shattrath."}},
[10884] = {"Die Prüfung der Naaru: Erbarmen",nil, {"A'dal in Shattrath möchte, dass Ihr die unbenutzte Axt des Henkers aus den Zerschmetterten Hallen der Höllenfeuerzitadelle besorgt.nnDiese Aufgabe muss auf dem Schwierigkeitsgrad 'Heroisch' abgeschlossen werden."}},
[10885] = {"Die Prüfung der Naaru: Stärke",nil, {"A'dal in Shattrath möchte, dass Ihr Kalithreshs Dreizack und Murmurs Essenz besorgt.nnDiese Aufgabe muss auf dem Schwierigkeitsgrad 'Heroisch' abgeschlossen werden."}},
[10886] = {"Die Prüfung der Naaru: Zuverlässigkeit",nil, {"A'dal in Shattrath möchte, dass Ihr Millhaus Manasturm aus der Arkatraz in der Festung der Stürme rettet.nnDiese Aufgabe muss auf dem Schwierigkeitsgrad 'Heroisch' abgeschlossen werden."}},
[10887] = {"Flucht aus dem Grab",nil, {"Helft Akuno zur Flüchtlingskarawane in den Wäldern von Terokkar zu gelangen. Sprecht danach mit Mekeda."}},
[10888] = {"Die Prüfung der Naaru: Magtheridon",nil, {"A'dal in Shattrath möchte, dass Ihr Magtheridon vernichtet."}},
[10889] = {"Rückkehr nach Shattrath",nil, {"Reist nach Shattrath und Sprecht mit Rilak."}},
[10891] = {"Imperiale Plattenrüstung",nil, {"Sprecht Derotain Matschnipper in Gadgetzan."}},
[10892] = {"Imperiale Plattenrüstung",nil, {"Sprecht Derotain Matschnipper in Gadgetzan."}},
[10893] = {"Langschweif ist der springende Punkt",nil, {"Vernichtet Draaca Langschweif, die Anführerin der Wyrmkultisten im Tunnel des Wyrmschädels. Meldet Euch danach bei Behüter Mondschatten beim Tunnel des Wyrmschädels im Schergrat zurück."}},
[10894] = {"Behüter des Wyrmschädels",nil, {"Bringt die Einladung zum Treffen zu Behüter Mondschatten beim Eingang des Tunnels des Wyrmschädels im Schergrat."}},
[10895] = {"Zeth'Gor muss brennen!",nil, {"Benutzt das Rauchsignal unter jedem Wachturm in Zeth'Gor und kehrt danach zu Schwadronskommandant Dabir'ee beim Expeditionsposten zurück."}},
[10896] = {"Die verseuchten Beschützer",nil, {"Lakotae möchte, dass Ihr 25 Baummilben tötet und danach zu ihm bei der Flüchtlingskarawane zurückkehrt. Ihr findet die Milben in den Körpern der faulenden Waldwüter und befallenen Wurzelwandler."}},
[10897] = {"Meister der Tränke",nil, {"Lauranna Thar'well möchte, dass Ihr die Botanika der Festung der Stürme betretet und den Naturführer der Botanika von Hochbotaniker Freywinn besorgt. Zusätzlich möchte sie, dass Ihr ihr 5 erstklassige Heiltränke, 5 erstklassige Manatränke und 5 erhebliche Tränke des traumlosen Schlafs bringt.nnACHTUNG! Ihr könnt nur eine Alchimiespezialisierung wählen."}},
[10898] = {"Wolkenschwinge",nil, {"Begleitet Wolkenschwinge und meldet Euch dann bei Rilak dem Erlösten in Shattrath."}},
[10899] = {"Meister der Transmutation",nil, {"Zarevhi in der Sturmsäule im Nethersturm möchte, dass Ihr ihm 4 Einheiten Urmacht bringt.nnACHTUNG! Ihr könnt nur eine Alchimiespezialisierung wählen."}},
[10900] = {"Das Mal von Vashj",nil, {"Brennt es nieder, <Volk>! Brennt alles nieder! Sie haben nichts verdient. Habt Ihr mich verstanden? NICHTS! Sie müssen für ihre Taten bezahlen. Auch für ihre zukünftigen.nnIch würde Vashj selbst töten, wenn ich von diesen verdammten Fesseln frei wäre.nnOder wollt Ihr vielleicht Neptulons Arbeit erledigen? Ihr Tod ist uns beiden dienlich, Weichhaut. Werdet Ihr mir dabei helfen, den heiligen Knüppel herzustellen?"}},
[10901] = {"Der Knüppel von Kar'desh",nil, {"Nar'biss der Ketzer in den heroischen Sklavenunterkünften des Echsenkessels möchte, dass Ihr ihm das Erdensiegel und das Flammensiegel bringt."}},
[10902] = {"Meister der Elixiere",nil, {"Begebt Euch in den Schwarzen Morast in den Höhlen der Zeit und besorgt 10 Essenzen der Unendlichkeit von den Fürsten der Zeitenrisse und den Bewahrerinnen der Zeitenrisse. Bringt die Essenzen zusammen mit 5 Elixieren der erheblichen Verteidigung, 5 Elixieren des Meisters und 5 Elixieren der erheblichen Beweglichkeit zu Lorokeem im unteren Viertel von Shattrath.nnACHTUNG! Ihr könnt nur eine Spezialisierung in der Alchimie wählen!"}},
[10903] = {"Rückkehr zur Ehrenfeste",nil, {"Sprecht mit Gehilfe Klatu im Gasthaus der Ehrenfeste."}},
[10904] = {"Teufelsmunition",nil, {"Der Druide des ewigen Hains hat Euch aufgetragen, 5 Teufelskanonenkugeln zu sammeln. Sobald Ihr fertig seid, benutzt das Druidensignal, um den Druiden des ewigen Hains zur Schwelle des Todes im Schergrat zu rufen."}},
[10905] = {"Meister der Tränke",nil, {"Sprecht mit Lauranna Thar'well in der Zuflucht des Cenarius in den Zangarmarschen.nnACHTUNG! Ihr könnt nur eine Spezialisierung in der Alchimie erlernen."}},
[10906] = {"Meister der Elixiere",nil, {"Sprecht mit Lorokeem im unteren Viertel von Shattrath.nnACHTUNG! Ihr könnt nur eine Spezialisierung in der Alchimie erlernen."}},
[10907] = {"Meister der Transmutation",nil, {"Sprecht mit Zarevhi in der Sturmsäule im Nethersturm.nnACHTUNG! Ihr könnt nur eine Spezialisierung in der Alchimie erlernen."}},
[10908] = {"Sprecht mit Rilak dem Erlösten",nil, {"Kirrik der Erwachte möchte, dass Ihr mit Rilak dem Erlösten im unteren Viertel von Shattrath sprecht."}},
[10909] = {"Höllengeister",nil, {"Klatu in der Ehrenfeste möchte, dass Ihr das Relikt des Anachoreten beim Außenposten von Gor'gaz benutzt, die Orcs der Zerschmetterten Hand in der Nähe des Relikts tötet und dann 10 freigesetzte Höllengeister vernichtet, um Oberst Jules zu rächen."}},
[10910] = {"Die Schwelle des Todes",nil, {"Wildfürst Antelarion fleht Euch an, zur Schwelle des Todes zu reisen. Sobald Ihr dort angekommen seid, benutzt das Druidensignal, um einen Druiden des ewigen Hains zu rufen und mit ihm zu sprechen."}},
[10911] = {"Feuer frei!",nil, {"Benutzt die naturalisierte Munition, um die Kontrolle über die Teufelskanonen der Schwelle des Todes zu erlangen. Benutzt sie, um das südliche Warpportal und das nördliche Warpportal zu zerstören. Benutzt danach das Druidensignal, um einen Druiden des ewigen Hains zu rufen und ihm von Eurem Erfolg zu berichten."}},
[10912] = {"Der Hundehetzer",nil, {"Der Druide des ewigen Hains hat Euch aufgetragen, als letzte Aufgabe Baelmon den Hundehetzer zu töten. Kehrt danach zum ewigen Hain im Schergrat zurück und berichtet Wildfürst Antelarion von Eurem Erfolg."}},
[10913] = {"Ein unangemessenes Begräbnis",nil, {"Kommandant Ra'vaj beim Außenposten der Sha'tari in der Knochenwüste der Wälder von Terokkar möchte, dass Ihr 8 getötete Kämpfer der Auchenai und 8 getötete Verteidiger der Sha'tar verbrennt."}},
[10914] = {"Wir brauchen einen Helden!",nil, {"Kommandant Ra'vaj beim Außenposten der Sha'tari in der Knochenwüste der Wälder von Terokkar möchte, dass Ihr 12 Initianden der Auchenai und 5 Verdammnisverkünder der Auchenai tötet."}},
[10915] = {"Der gefallene Exarch",nil, {"Kommandant Ra'vaj beim Außenposten der Sha'tari in der Knochenwüste der Wälder von Terokkar möchte, dass Ihr den Inhalt des Sargs der Auchenai vernichtet."}},
[10916] = {"Nach Gebetsperlen graben",nil, {"Sucht die Gebetsperlen der Draenei und bringt sie zu Gehilfe Klatu in der Ehrenfeste."}},
[10917] = {"Die Pflicht eines Ausgestoßenen",nil, {"Bringt 30 Arakkoafedern zu Vekax im unteren Viertel von Shattrath."}},
[10918] = {"Mehr Federn",nil, nil},
[10919] = {"Fei-Feis Belohnung",nil, {"Fei-Fei schaut Euch erwartungsvoll an..."}},
[10920] = {"Für die Gefallenen",nil, {"Verteidiger Haylen möchte, dass Ihr 20 Schreckenswitwen tötet und danach zu ihm westlich des Netherweberkamms zurückkehrt."}},
[10921] = {"Terokkarantula",nil, {"Verteidiger Haylen möchte, dass Ihr Terokkarantula tötet und danach zu ihm westlich des Netherweberkamms zurückkehrt."}},
[10922] = {"In den Knochen wühlen",nil, {"Begleitet Letoll und seine Truppe und beschützt sie, während sie ihre Ausgrabung durchführen. Kehrt danach zu Zwergowitz beim Außenposten der Sha'tari zurück."}},
[10923] = {"Das Böse rückt näher",nil, {"Oakun möchte, dass Ihr das Schreckensrelikt zum gequälten Hügel bringt. Tötet dort die Todessprecher und Verdammnisverkünder der Auchenai, um 20 Verdammnisschädel zu sammeln. Sucht danach den Beschwörungskreis des gequälten Hügels auf und benutzt das Schreckensrelikt, um Teribus den Verfluchten zu beschwören und zu vernichten. Kehrt zu Oakun zurück, wenn Ihr Eure Aufgabe erfüllt habt."}},
[10924] = {"Verwichtelt und zugenäht!",nil, {"Bringt 8 Phiolen Sphärenhetzerblut zu Sab'aoth in Kosmozang."}},
[10925] = {"Das Böse rückt näher",nil, nil},
[10926] = {"Rückkehr zum Basislager der Sha'tari",nil, {"Verteidiger Haylen möchte, dass Ihr zum Basislager der Sha'tari zurückkehrt und mit Späher Navrin sprecht."}},
[10927] = {"Die Krabbler erschlagen",nil, {"Schildwache Mondwisper beim Tunnel zwischen den Zangarmarschen und dem Schergrat möchte, dass Ihr 6 Höhlenkrabbler tötet. Meldet Euch danach bei Kommandant Himmelsschatten in Sylvanaar."}},
[10928] = {"Die Krabbler erschlagen",nil, {"Grunzer Grahk beim Tunnel zwischen den Zangarmarschen und dem Schergrat möchte, dass Ihr 6 Höhlenkrabbler tötet. Meldet Euch danach bei Tor'chunk Zweiklaue in der Donnerfeste."}},
[10929] = {"Stampfen",nil, {"Zwergowitz beim Basislager der Sha'tari in der Knochenwüste möchte, dass Ihr ihm 8 Kadaver eines ausgewachsenen Knochenwühlers bringt."}},
[10930] = {"Der große Knochenwurm",nil, {"Zwergowitz beim Basislager der Sha'tari in der Knochenwüste möchte, dass Ihr ihm riesige Knochenwurmorgane bringt.nnBringt auch den Stampfer zu ihm zurück."}},
[10931] = {"Rückkehr zur Exodar",nil, {"Sprecht mit Caedmos in der Exodar."}},
[10932] = {"Rückkehr zur Exodar",nil, {"Sprecht mit Caedmos in der Exodar."}},
[10933] = {"Rückkehr nach Silbermond",nil, {"Sprecht mit Aldrae in Silbermond."}},
[10934] = {"Rückkehr nach Silbermond",nil, {"Sprecht mit Aldrae in Silbermond."}},
[10935] = {"Der Exorzismus des Oberst Jules",nil, {"Sprecht mit Anachoret Barada. Benutzt die Gebetsperlen, um bei dem Ritual zu helfen und sprecht mit Oberst Jules, wenn er gerettet wurde. Kehrt danach zu Gehilfe Klatu zurück."}},
[10936] = {"Trollbann sucht Euch",nil, {"Gehilfe Klatu hat Euch informiert, dass Truppenkommandant Danath Trollbann nach Euch sucht. Sprecht mit ihm in der Kaserne der Ehrenfeste auf der Höllenfeuerhalbinsel."}},
[10937] = {"Die Züchtigung des Drillmeisters",nil, {"Truppenkommandant Danath Trollbann befiehlt Euch, Drillmeister Zurok so schnell wie möglich zur Strecke zu bringen. Kehrt zu dem Truppenkommandanten in der Ehrenfeste auf der Höllenfeuerhalbinsel zurück, wenn der Drillmeister tot ist."}},
[10938] = {"Segenskartenset des Dunkelmond-Jahrmarkts",nil, {"Gebt das Segenskartenset auf dem Dunkelmond-Jahrmarkt ab, sobald dieser wieder in der Stadt ist."}},
[10939] = {"Sturmkartenset des Dunkelmond-Jahrmarkts",nil, {"Gebt das Sturmkartenset auf dem Dunkelmond-Jahrmarkt ab, sobald dieser wieder in der Stadt ist."}},
[10940] = {"Furienkartenset des Dunkelmond-Jahrmarkts",nil, {"Gebt das Furienkartenset auf dem Dunkelmond-Jahrmarkt ab, sobald dieser wieder in der Stadt ist."}},
[10941] = {"Deliriumkartenset des Dunkelmond-Jahrmarkts",nil, {"Gebt das Deliriumkartenset auf dem Dunkelmond-Jahrmarkt ab, sobald dieser wieder in der Stadt ist."}},
[10942] = {"Die Kinderwoche",nil, {"Benutzt die Pfeife des Blutelfenwaisenkinds, um Euren Schützling zu rufen, um den Ihr Euch während der Kinderwoche kümmern werdet."}},
[10943] = {"Die Kinderwoche",nil, {"Benutzt die Pfeife des Draeneiwaisenkinds um Euren Schützling zu rufen, um den Ihr Euch während der Kinderwoche kümmern werdet."}},
[10944] = {"Das gefährdete Geheimnis",nil, {"Reist zum Kerker des Wächters im Schattenmondtal und sprecht mit Akama."}},
[10945] = {"Hch'uu und das Pilzvolk",nil, {"Bringt Euer Waisenkind Salandria nach Sporeggar in den Zangarmarschen. Ruft sie zu Euch, falls sie nicht bei Euch ist, wenn Ihr ankommt. Sprecht danach mit Hch'uu."}},
[10946] = {"Die List der Aschenzungen",nil, {"Reist in die Festung der Stürme und tötet Al'ar, während Ihr die Gugel der Aschenzungen tragt. Kehrt nach Abschluss der Aufgabe zu Akama ins Schattenmondtal zurück."}},
[10947] = {"Ein Artefakt aus der Vergangenheit",nil, {"Reist zu den Höhlen der Zeit in Tanaris und verschafft Euch Zugang zur Schlacht um den Berg Hyjal. Habt Ihr dies geschafft, so bezwingt Furor Winterfrost und bringt das zeitverzerrte Phylakterium zu Akama im Schattenmondtal."}},
[10948] = {"Die Seelengeisel",nil, {"Reist nach Shattrath und erzählt A'dal von Akamas Bitte."}},
[10949] = {"Zutritt zum Schwarzen Tempel",nil, {"Reist zum Eingang des Schwarzen Tempels im Schattenmondtal und sprecht mit Xi'ri."}},
[10950] = {"Auchindoun und der Ring der Beobachtung",nil, {"Bringt Euer Waisenkind Dornaa zum Versammlungsstein beim Ring der Beobachtung in der Mitte von Auchindoun. Auchindoun liegt inmitten der Knochenwüste der Wälder von Terokkar. Ruft sie zu Euch, falls sie nicht bei Euch ist, wenn Ihr ankommt."}},
[10951] = {"Eine Reise zum Dunklen Portal",nil, {"Geht mit Eurem Waisenkind Salandria die Stufen des Schicksals hinauf und besucht das Dunkle Portal auf der Höllenfeuerhalbinsel. Ruft sie zu Euch, falls sie nicht bei Euch ist, wenn Ihr ankommt."}},
[10952] = {"Eine Reise zum Dunklen Portal",nil, {"Geht mit Eurem Waisenkind Dornaa die Stufen des Schicksals hinauf und besucht das Dunkle Portal auf der Höllenfeuerhalbinsel. Ruft sie zu Euch, falls sie nicht bei Euch ist, wenn Ihr ankommt."}},
[10953] = {"Besuch beim Thron der Elemente",nil, {"Bringt Euer Waisenkind Salandria zum Thron der Elemente in Nagrand. Ruft sie herbei, falls sie nicht bei Euch sein sollte, wenn Ihr dort ankommt. Sprecht dann mit Elementarist Sharvak."}},
[10954] = {"Jheel ist beim Aerissteg!",nil, {"Bringt Euer Waisenkind Dornaa zum Aerissteg in Nagrand. Ruft sie herbei, falls sie nicht bei Euch sein sollte, wenn Ihr dort ankommt. Sprecht dann mit Jheel."}},
[10955] = {"Morthis Flügelraunen",nil, {"Sucht nach Morthis Flügelraunen in der Zuflucht des Cenarius in den Zangarmarschen."}},
[10956] = {"Der Sitz der Naaru",nil, {"Bringt Euren Schützling Dornaa zu O'ros im unteren Teil des Sitzes der Naaru in der Exodar auf der Azurmythosinsel. Ruft sie herbei, falls sie nicht bei Euch sein sollte, wenn Ihr dort ankommt.nnVergesst nicht, dass Ihr in der Stadt Eure Karte verwenden könnt."}},
[10957] = {"Die Erlösung der Aschenzungen",nil, {"Helft Akama die Kontrolle über seine Seele wieder zu erlangen, indem Ihr Akamas Schemen im Schwarzen Tempel besiegt. Kehrt anschließend zu Seher Kanai zurück."}},
[10958] = {"Sucht die Aschenzungen auf",nil, {"Sucht Akamas Todeshörige im Schwarzen Tempel."}},
[10959] = {"Der Sturz des Verräters",nil, {"Seher Kanai möchte, dass Ihr Illidan im Schwarzen Tempel besiegt."}},
[10960] = {"Wenn ich groß bin...",nil, {"Bringt Euren Schützling Salandria zu den Paladinlehrern auf dem Platz der Weltenwanderer in Silbermond. Ruft sie zu Euch, falls sie nicht anwesend sein sollte, wenn Ihr ankommt. Sprecht danach mit Lady Liadrin, der Matriarchin der Blutritter.nnVergesst nicht, dass Ihr in der Stadt Eure Karte verwenden und die Wachen nach den Paladinlehrern fragen könnt."}},
[10961] = {"Der Zauber des Erwachens",nil, {"Bringt 10 Sumpfblüten, 10 Urflechten und 10 Traumwinden zu Morthis Flügelraunen in der Zuflucht des Cenarius."}},
[10962] = {"Zeit die Höhlen zu besuchen",nil, {"Bringt Euren Schützling Dornaa zu Zaladormu, dem Drachen auf der Anhöhe in den Höhlen der Zeit. Ruft sie zu Euch, falls sie nicht anwesend ist, wenn Ihr ankommt.nnErwerbt danach für sie einen Spielzeugdrachen bei Alurmi, dem Rüstmeister der Hüter der Zeit, in der Nähe des Endes des Eingangstunnels."}},
[10963] = {"Zeit die Höhlen zu besuchen",nil, {"Bringt Euren Schützling Salandria zu Zaladormu, dem Drachen auf der Anhöhe in den Höhlen der Zeit. Ruft sie zu Euch, falls sie nicht anwesend ist, wenn Ihr ankommt.nnErwerbt danach für sie einen Spielzeugdrachen bei Alurmi, dem Rüstmeister der Hüter der Zeit, in der Nähe des Endes des Eingangstunnels."}},
[10964] = {"Den Schläfer wecken",nil, {"Benutzt den Zauber des Erwachens um Clintar Traumwandler aus dem Smaragdgrünen Traum zu holen. Sprecht mit ihm, sobald er zu sich kommt."}},
[10965] = {"Mehr als ein Traum",nil, {"Beschützt Clintar Traumwandlers Geist während er die Relikte von Aviana sammelt. Sprecht danach mit Traumwächter Lurosa."}},
[10966] = {"Zurück zum Waisenhaus",nil, {"Kehrt zum Waisenhaus in Shattrath zurück und übergebt die Pfeife Eures Schützlings an Waisenmatrone Gnädig im Unteren Viertel."}},
[10967] = {"Zurück zum Waisenhaus",nil, {"Kehrt zum Waisenhaus in Shattrath zurück und übergebt die Pfeife Eures Schützlings an Waisenmatrone Gnädig im Unteren Viertel."}},
[10968] = {"Besucht den Scharfseher",nil, {"Bringt Euren Schützling Dornaa zu Scharfseher Nobundo in der Kristallhalle der Exodar auf der Azurmythosinsel. Ruft sie zu Euch, falls sie nicht anwesend sein sollte, wenn Ihr ankommt.nnVergesst nicht, dass Ihr in der Stadt Eure Karte verwenden könnt."}},
[10969] = {"Sucht Ameer!",nil, {"Sucht Kommandant Ameer beim Wachposten des Protektorats auf."}},
[10970] = {"Ein barmherziger Auftrag",nil, {"Beschafft Euch den sichergestellten Gefängnisschlüssel des Astraleums und bringt ihn zu Kommandant Ameer beim Wachposten des Protektorats im Nethersturm."}},
[10971] = {"Geheimnisse des Astraleums",nil, {"Kommandant Ameer beim Wachposten des Protektorats im Nethersturm möchte, dass Ihr ihm eine Identifizierungsmarke eines Gefangenen des Astraleums bringt."}},
[10972] = {"Katalog der Gefangenen des Astraleums",nil, {"Ich brauche mehr Identifikationsmarken, <Name>. Ich konnte bisher herausfinden, dass sie nicht nur die Gefangenen katalogisieren, sondern auch Aussagen über ihre Aufenthaltsorte machen!nnSobald wir diesen Code entschlüsselt haben, können wir genau feststellen, wo unsere Verbündeten festgehalten werden und sie mit minimalem Gewalteinsatz befreien."}},
[10973] = {"Tausend Welten",nil, {"Kommandant Ameer am Wachposten des Protektorats im Nethersturm möchte, dass Ihr ihm 5 Gefängnisschlüssel des Astraleums bringt."}},
[10974] = {"Stasiskammern von Bash'ir",nil, {"Benutzt die Schlüsseltafel des Astraleums – Alpha, um die Stasiskammer Alpha im Landeplatz von Bash'ir im Schergrat zu öffnen.nnBringt Beweisstücke aus Alpha zum Bild von Kommandant Ameer beim Landeplatz von Bash'ir."}},
[10975] = {"Die Reinigung der Kammern von Bash'ir",nil, {"Wir sitzen in der Sackgasse, <Name>. Die Daten, die Ihr gesammelt habt, haben uns genügend Informationen geliefert, um unsere Verbündeten aus den kleineren Gefängnissen des Nethersturms retten zu können. Wir müssen uns jedoch dringend um die Bedrohung kümmern, die von den Kammern hier im Ladeplatz von Bash'ir ausgeht.nnSchließt Euch dem Kampf gegen das Astraleum an! Bringt mir die Gefängnisschlüssel des Astraleums, damit ich Stasiskammerschlüssel für die Stasiskammern von Bash'ir für Euch herstellen kann."}},
[10976] = {"Das Abzeichen des Nexuskönigs",nil, {"Das Bild von Kommandant Ameer beim Landeplatz von Bash'ir im Schergrat möchte, dass Ihr ihm ein Abzeichen des Nexuskönigs bringt."}},
[10977] = {"Stasiskammern der Managruft",nil, {"Das Bild von Kommandant Ameer beim Landeplatz von Bash'ir im Schergrat möchte, dass Ihr den Stasiskammerschlüssel der Managruft verwendet, um die Stasiskammer in der Managruft Auchindouns zu öffnen.nnDiese Aufgabe kann nur auf dem Schwierigkeitsgrad 'Heroisch' abgeschlossen werden."}},
[10978] = {"Rückkehr zu Morthis Flügelraunen",nil, {"Bringt die Relikte von Aviana zu Morthis Flügelraunen in der Zuflucht des Cenarius in den Zangarmarschen."}},
[10979] = {"Zum Ewigen Hain",nil, {"Sprecht mit Arthorn Windweise im Ewigen Hain im Schergrat."}},
[10980] = {"Das Buch des Raben",nil, {"Benutzt das Auge eines Ätherrochen in Verbindung mit Eurem Stein des Sehers, um die Geister des Rabenkultes sehen zu können. Sucht beim Vortexgipfel nach dem Geist von Sai'kkal dem Ältesten und fragt ihn über das 'Buch des Raben' aus. Kehrt danach zu Arthorn Windweise zurück."}},
[10981] = {"Nexusprinz Shaffars persönliche Kammer",nil, nil},
[10982] = {"Das Auge des Haramad",nil, {"Ihr habt viel für das Protektorat und das Konsortium getan, <Name>. Daher ist es nur gerecht, dass einer unserer neuen Schlüssel an Euch geht. Mit diesem Schlüssel könnt Ihr Shaffars persönliche Stasiskammer jederzeit öffnen, ohne den Schlüssel dabei zu zerstören.nnnnEr ist von Dauer und wird auf ewig Euch gehören."}},
[10983] = {"Mog'dorg der Weise",nil, {"Besucht Mog'dorg den Weisen auf einem der Türme außerhalb des Zirkels des Blutes im Schergrat."}},
[10984] = {"Sprecht mit dem Oger",nil, {"Sprecht mit dem Oger Grok im unteren Viertel von Shattrath."}},
[10985] = {"Ein Ablenkungsmanöver für Akama",nil, {"Stellt sicher, dass Akama und Maiev den Schwarzen Tempel betreten, nachdem Xi'ris Streitkräfte ihr Ablenkungsmanöver durchgeführt haben."}},
[10986] = {"Blick zum Himmel",nil, {"Sprecht mit Behüterin Elaira bei der Zwielichthöhe in Nagrand."}},
[10987] = {"Der Spatz in der Hand",nil, {"Benutzt das Sperbernetz um einen wilden Sperber zu fangen und bringt ihn zu Behüterin Elaira auf der Zwielichthöhe."}},
[10988] = {"Die Rabensteine",nil, {"Sammelt 8 Rabensteine in Skettis und bringt sie zu Arthorn Windweise im Ewigen Hain im Schergrat."}},
[10989] = {"Mog'dorg der Weise",nil, {"Besucht Mog'dorg den Weisen auf der Spitze des Turms außerhalb des Zirkels des Blutes im Schergrat, bei dem Chort sich aufhält."}},
[10990] = {"Die Essenz des Adlers",nil, {"Besiegt den Wächter des Adlers und erlangt die Essenz des Adlers. Benutzt Arthorns Sperberpfeife, um Arthorns Sperber zu rufen, damit dieser die Kunde von Eurem Erfolg übermittelt.nnWenn Ihr die Pfeife verloren habt und Ersatz benötigt, sucht Arthorn Windweise im Ewigen Hain auf."}},
[10991] = {"Die Essenz des Falken",nil, {"Besiegt den Wächter des Falken und erlangt die Essenz des Falken. Benutzt Arthorns Sperberpfeife, um Arthorns Sperber zu rufen, damit dieser die Kunde von Eurem Erfolg übermittelt.nnWenn Ihr die Pfeife verloren habt und Ersatz benötigt, sucht Arthorn Windweise im Ewigen Hain auf."}},
[10992] = {"Die Essenz des Habichts",nil, {"Besiegt den Wächter des Habichts und bringt die Essenz des Habichts zu Arthorn Windweise im Ewigen Hain."}},
[10993] = {"Rückkehr zur Zuflucht des Cenarius",nil, {"Bringt Arthorns Päckchen zu Morthis Flügelraunen in der Zuflucht des Cenarius."}},
[10994] = {"Auf der Jagd nach dem Mondstein",nil, {"Bringt einen Mondstein des Südstroms zu Morthis Flügelraunen in der Zuflucht des Cenarius."}},
[10995] = {"Grulloc hat zwei Schädel",nil, {"Beschafft Grullocs Drachenschädel und bringt ihn zu Mog'dorg dem Weisen auf dem Turm beim Zirkel des Blutes im Schergrat."}},
[10996] = {"Maggocs Schatztruhe",nil, {"Beschafft Euch Maggocs Schatztruhe und bringt sie zu Mog'dorg dem Weisen auf dem Turm beim Zirkel des Blutes im Schergrat."}},
[10997] = {"Sogar ein Gronn hat Standards",nil, {"Besorgt Euch Slaags Standarte und bringt sie zu Mog'dorg dem Weisen auf dem Turm beim Zirkel des Blutes im Schergrat."}},
[10998] = {"In den übelsten Kreisen",nil, {"Ihr müsst Vim'gols üblen Zauberfolianten beschafften. Bringt ihn zu Mog'dorg dem Weisen auf dem Turm beim Zirkel des Blutes im Schergrat."}},
[11000] = {"Schänder der Seelen",nil, {"Beschafft Euch Skullocs Seele und bringt ihn zu Mog'dorg dem Weisen auf dem Turm beim Zirkel des Blutes im Schergrat."}},
[11001] = {"Vernichtet den Rabengott",nil, {"Tötet den Rabengott und kehrt dann zu Morthis Flügelraunen bei der Zuflucht des Cenarius zurück.nnDiese Aufgabe muss auf dem Schwierigkeitsgrad 'Heroisch' abgeschlossen werden."}},
[11002] = {"Magtheridons Untergang",nil, {"Bringt Magtheridons Kopf zu Truppenkommandant Danath Trollbann in der Ehrenfeste auf der Höllenfeuerhalbinsel zurück."}},
[11003] = {"Magtheridons Untergang",nil, {"Bringt Magtheridons Kopf zu Nazgrel in Thrallmar auf der Höllenfeuerhalbinsel."}},
[11004] = {"Schattenwelt",nil, {"Severin möchte, dass Ihr nach Skettis reist und 6 Einheiten Schattenstaub von den dort lebenden Arakkoa sammelt."}},
[11005] = {"Die Geheimnisse der Krallenpriester",nil, {"Beschafft Euch von Severin ein Elixier der Schatten und verwendet es, um Krallenpriester Ishaal, Krallenpriester Skizzik und Krallenpriester Zellek in Skettis ausfindig zu machen und sie zu töten. Kehrt nach Abschluss Eurer Aufgabe zu Kommandant Adaris zurück."}},
[11006] = {"Mehr Schattenstaub",nil, {"Der Kommandant scheint doch nicht aus einem Fieberwahn heraus gesprochen zu haben. Bringt mir mehr Staub und ich werde ein weiteres Elixier für Euch herstellen."}},
[11007] = {"Kael'thas und die tiefgrüne Sphäre",nil, {"Bringt die tiefgrüne Sphäre zu A'dal in Shattrath."}},
[11008] = {"Feuer über Skettis",nil, {"Haltet nach monströsen Kalirieiern oben in den Siedlungen von Skettis Ausschau und benutzt die Sprengladungen der Himmelswache, um sie in die Luft zu sprengen. Kehrt danach zu Himmelsoffizier Doryn zurück."}},
[11009] = {"Ogerhimmel",nil, {"Mog'dorg der Weise hat Euch gebeten, mit Chu'a'lor in Ogri'la im Schergrat zu sprechen."}},
[11010] = {"Bomberangriff",nil, {"Vernichtet mithilfe der Bomben der Himmelswache 15 Stapel mit Teufelskanonenkugeln."}},
[11011] = {"Ewige Wachsamkeit",nil, {"Sprecht mit Morthis Flügelraunen, um einen mit Essenz erfüllten Mondstein zu erhalten."}},
[11012] = {"Der Blutschwur der Netherschwingen",nil, {"Unser Land ist verloren. Man entführt unsere Kinder in der Nacht, versklavt sie... oder schlimmer...nn<Mordenais Stimme wirkt angespannt.>nnEs ist an der Zeit, mein Freund. Zeit, uns zurückzuholen, was einst unser war! Ihr habt Euch als tapferer Kämpfer und verständnisvolle Seele erwiesen.nnWerdet Ihr schwören, dem Drachenschwarm der Netherschwingen beim Kampf um unser Land und dem Wiederaufbau unserer Heimstätten zu helfen? Werdet Ihr schwören, über den Schwarm zu wachen, damit unser Fleisch und Blut aufs Neue zum Leben erwachen kann? Schwört und lasst uns im Namen unserer Heimat und unserer Familien kämpfen!"}},
[11013] = {"Im Dienste der Illidari",nil, {"Reist zur Netherschwingenscherbe und überreicht Oberanführer Mor'ghor beim Basislager des Drachenmals das Empfehlungsschreiben der Illidari."}},
[11014] = {"In der Höhle des Löwen",nil, {"Sprecht mit Zuchtmeister Varkule Drachenodem beim Basislager des Drachenmals."}},
[11015] = {"Kristalle der Netherschwingen",nil, {"Zuchtmeister Varkule Drachenodem beim Basislager des Drachenmals im Schattenmondtal möchte, dass Ihr ihm 30 Kristalle der Netherschwingen bringt."}},
[11016] = {"Balg eines Netherminenschinders",nil, {"Zuchtmeister Varkule Drachenodem beim Basislager des Drachenmals im Schattenmondtal möchte, dass Ihr ihm 35 Bälge eines Netherminenschinders bringt."}},
[11017] = {"Netherstaubpollen",nil, {"Zuchtmeister Varkule Drachenodem beim Basislager des Drachenmals im Schattenmondtal möchte, dass Ihr ihm 40 Netherstaubpollen bringt."}},
[11018] = {"Netheriterz",nil, {"Zuchtmeister Varkule Drachenodem beim Basislager des Drachenmals im Schattenmondtal möchte, dass Ihr ihm 40 Einheiten Netheriterz bringt."}},
[11019] = {"Freund unter Feinden",nil, {"Ihr habt doch nicht etwa geglaubt, dass wir Euch den Armeen der Höllenorcs ganz alleine überlassen würden, oder?nn<Yarzills Augen flackern in einem durchscheinenden Blau.>nnGenau, wir sind hier an Eurer Seite, <Name>. Bei jedem Dienst, den Ihr den Höllenorcs bei Eurem Versuch, die Weiten der Netherschwingen zurückzuerobern, erweist, werden wir dafür sorgen, dass sie einen ausreichenden Rückschlag erleiden.nnIhr müsst Euch sehr unauffällig verhalten, um keinerlei Aufmerksamkeit während Euren Sabotageeinsätzen auf Euch zu ziehen."}},
[11020] = {"Ein langsamer Tod",nil, {"Yarzill der Söldner beim Basislager des Drachenmals im Schattenmondtal möchte, dass Ihr Yarzills Hammel zusammen mit den Teufelsdrüsen verwendet, um 12 Peonlager des Drachenmals zu vergiften."}},
[11021] = {"Ishaals Almanach",nil, {"Bringt Ishaals Almanach zu Himmelskommandant Adaris im Norden von Skettis."}},
[11022] = {"Sprecht mit Mog'dorg",nil, {"Sprecht mit Mog'dorg dem Weisen. Er steht oben auf dem Turm auf der Ostseite des Zirkels des Blutes im Schergrat."}},
[11023] = {"Und wieder ein Bombenangriff!",nil, {"Vernichtet mithilfe der Bomben der Himmelswache 15 Stapel mit Teufelskanonenkugeln."}},
[11024] = {"Ein Verbündeter im Unteren Viertel",nil, {"Bringt Ishaals Almanach zu Rilak dem Erlösten im Unteren Viertel in Shattrath."}},
[11025] = {"Die Kristalle",nil, {"Chu'a'lor möchte, dass Ihr 5 Apexissplitter einsammelt. Bringt sie zu ihm in Ogri'la hoch oben im Schergrat."}},
[11026] = {"Bannt die Dämonen",nil, {"Setzt den Bannkristall ein, um 15 Dämonen beim Konstruktionslager: Wut oder Konstruktionslager: Terror zu bannen."}},
[11027] = {"Habt Ihr die Rune der Dunkelheit?",nil, nil},
[11028] = {"Countdown des Schicksals",nil, {"Kehrt zu Himmelskommandant Adaris ins Schattenwindlager in den Wäldern von Terokkar zurück und erzählt ihm die Neuigkeiten über Terokks Rückkehr."}},
[11029] = {"Eine schäbige Verkleidung",nil, {"Verwendet die schäbige Arakkoaverkleidung, um den Stammbaum der Feinde von Sahakk zu beschaffen. Kehrt anschließend zu Hazzik zurück."}},
[11030] = {"Unser Junge möchte der Himmelswache beitreten",nil, {"Beschafft ein instabiles Fläschchen der Bestie."}},
[11031] = {"Nicht mehr Erzmagier",nil, nil},
[11032] = {"Nicht mehr Beschützer",nil, nil},
[11033] = {"Nicht mehr Assassine",nil, nil},
[11034] = {"Nicht mehr Bewahrer",nil, {"Ihr habt Euch gegen Eure vorherige Wahl entschieden? Ich denke, dass ich Euch helfen kann, das Ganze wieder rückgängig zu machen. Dalaran geht es in erster Linie um Eure Loyalität und nicht darum, wie Ihr sie beweisen wollt.nnEs wird nicht gerade wenig kosten und Ihr müsst mir natürlich auch Euren alten Ring zurückgeben."}},
[11035] = {"Ein Schatten am Horizont",nil, {"Yarzill der Söldner beim Basislager des Drachenmals im Schattenmondtal möchte, dass Ihr 10 Relikte der Netherschwingen sicherstellt."}},
[11036] = {"Außerirdische Früchte!",nil, {"Bringt die konservierten Früchte zu Raketenchef Doppeldecker in Area 52."}},
[11037] = {"Eine seltsame Vision",nil, {"Sprecht mit Raketenchef Doppeldecker in Area 52."}},
[11038] = {"Unterstützung für Exarch Orelis",nil, {"Sprecht mit Exarch Orelis in Area 52."}},
[11039] = {"Meldung bei Spionagemeister Thalodien",nil, {"Meldet Euch bei Spionagemeister Thalodien in Area 52."}},
[11040] = {"Ersatzteile für den Raketenchef",nil, {"Bringt Bossis Ersatzteile zu Raketenchef Doppeldecker in Area 52."}},
[11041] = {"Eine unerledigte Aufgabe...",nil, {"Tötet 10 Felslandschinder und Barash die Höhlenmutter. Kehrt nach erfolgreicher Erledigung zu Oberanführer Mor'ghor beim Basislager des Drachenmals zurück."}},
[11042] = {"Eine verblüffende Vision",nil, {"Sprecht mit Raketenchef Doppeldecker in Area 52."}},
[11043] = {"Ein besserer Greif",nil, {"Sprecht mit Brunn Flammenbart in der Wildhammerfeste."}},
[11044] = {"Visionen voller Zerstörung",nil, {"Sprecht mit Schwadronskommandant Nuainn in der Wildhammerfeste."}},
[11045] = {"Zorus der Richter",nil, {"Sprecht mit Zorus dem Richter in der Wildhammerfeste."}},
[11046] = {"Chefapothekerin Hildagard",nil, {"Sprecht mit Chefapothekerin Hildagard in Schattenmond."}},
[11047] = {"Die Bitte des Gesellen",nil, {"Sprecht mit Dama Wildmähne im Dorf Schattenmond."}},
[11048] = {"Kroghans Bericht",nil, {"Sprecht mit Blutwache Gulmok im Dorf Schattenmond."}},
[11049] = {"Die große Eierjagd",nil, {"Yarzill der Söldner beim Basislager des Drachenmals im Schattenmondtal möchte, dass Ihr ein Ei der Netherschwingen einsammelt."}},
[11050] = {"Immer her mit den Eiern",nil, {"Diese Eier dürfen auf keinen Fall in die falschen Hände geraten, <Name>. Wenn Ihr noch mehr Eier findet, bringt sie mir.nnVerhaltet Euch auf jeden Fall unauffällig und erregt bloß keinen Verdacht!"}},
[11051] = {"Bannt mehr Dämonen",nil, {"Setzt den Bannkristall ein, um 15 Dämonen beim Konstruktionslager: Wut oder Konstruktionslager: Terror zu bannen."}},
[11052] = {"Akamas Versprechen",nil, {"Bringt das Medaillon von Karabor zu A'dal in Shattrath."}},
[11053] = {"Erhebt Euch, Aufseher!",nil, {"Sprecht mit Oberanführer Mor'ghor beim Basislager des Drachenmals im Schattenmondtal."}},
[11054] = {"Aufsehen und Ihr: Die richtige Wahl treffen",nil, {"Chefvorarbeiter Lehmklump beim Basislager des Drachenmals im Schattenmondtal möchte, dass Ihr ihm 10 Einheiten Knotenhautleder und 1 gehärteten Balg von Tyrantus bringt."}},
[11055] = {"Der Schuhmerang: Das Mittel gegen den wertlosen Peon",nil, {"Chefvorarbeiter Lehmklump beim Basislager des Drachenmals im Schattenmondtal möchte, dass Ihr 20 faule Peons des Drachenmals zur Ordnung ruft. Gebt anschließend auch Euren Schuhmerang wieder zurück."}},
[11056] = {"Hazziks Handel",nil, {"Sucht nach Hazziks Päckchen bei seiner Behausung im östlichen Skettis und kehrt damit zu ihm zurück."}},
[11057] = {"Schwierigkeiten in der Tiefe",nil, {"Sprecht mit Mog'dorg dem Weisen. Ihr findet ihn auf einem der Türme außerhalb des Zirkels des Blutes im Schergrat."}},
[11058] = {"Ein Apexisrelikt",nil, {"Erlangt Apexisschwingungen von einem Apexisrelikt."}},
[11059] = {"Wächter des Monuments",nil, {"Beschafft den Kopf eines Apexiswächters."}},
[11060] = {"Eine kristallgeschmiedete Rune der Dunkelheit",nil, nil},
[11061] = {"Die Pflichten eines Vaters",nil, {"Beschafft ihm ein instabiles Fläschchen des Zauberers."}},
[11062] = {"Der Außenposten der Himmelswache",nil, {"Sprecht mit Himmelskommandant Keller beim Außenposten der Himmelswache im Schergrat."}},
[11063] = {"Sich Flügel verdienen...",nil, {"Ihr möchtet also zu den Himmelsteilern gehören, ja? Ihr habt Euch mit Sicherheit als fähiger Anhänger des Drachenmals bewiesen, aber in den Lüften der Scherbenwelt ist dies bedeutungslos. Es gibt eine Frage zu klären: KÖNNT IHR FLIEGEN?nnUnd ich frage nicht etwa, ob Ihr auf einen Netherdrachen steigen und wie diese Transportertrottel eine Ladung Kristalle zu der Festung liefern könnt. Ich rede davon, der Beste zu sein... der Toporc...nnDort drüben im Westen gibt es sechs Reiter. Besiegt sie alle und ich werde Euch Flügel der Himmelsteiler geben."}},
[11064] = {"Drachenmalrennen: Murg \"Grufti\" Moorbiss",nil, {"Folgt Murg \"Grufti\" Moorbiss und versucht auf Eurem Reittier zu bleiben. Solltet Ihr Murg \"Grufti\" Moorbiss besiegen, berichtet Ja'y Nosliw beim Basislager des Drachenmals im Schattenmondtal von Eurem Erfolg."}},
[11065] = {"Bändigt ein paar Ätherrochen!",nil, {"Bändigt mithilfe des Bändigungsseils 5 Ätherrochen."}},
[11066] = {"Bändigt noch mehr Ätherrochen!",nil, {"Bändigt mithilfe des Bändigungsseils 5 Ätherrochen."}},
[11067] = {"Drachenmalrennen: Trope der Rußhuster",nil, {"Folgt Trope dem Rußhuster und versucht auf Eurem Reittier zu bleiben. Solltet Ihr Trope den Rußhuster besiegen, berichtet Ja'y Nosliw beim Basislager des Drachenmals im Schattenmondtal von Eurem Erfolg."}},
[11068] = {"Drachenmalrennen: Corlok der Veteran",nil, {"Folgt Corlok dem Veteranen und versucht auf Eurem Reittier zu bleiben. Solltet Ihr Corlok den Veteranen besiegen, berichtet Ja'y Nosliw beim Basislager des Drachenmals im Schattenmondtal von Eurem Erfolg."}},
[11069] = {"Drachenmalrennen: Schwadronskommandant Ichman",nil, {"Folgt Schwadronskommandant Ichman und versucht auf Eurem Reittier zu bleiben. Solltet Ihr Schwadronskommandant Ichman besiegen, berichtet Ja'y Nosliw beim Basislager des Drachenmals im Schattenmondtal von Eurem Erfolg."}},
[11070] = {"Drachenmalrennen: Schwadronskommandant Mulverick",nil, {"Folgt Schwadronskommandant Mulverick und versucht auf Eurem Reittier zu bleiben. Solltet Ihr Schwadronskommandant Mulverick besiegen, berichtet Ja'y Nosliw beim Basislager des Drachenmals im Schattenmondtal von Eurem Erfolg."}},
[11071] = {"Drachenmalrennen: Hauptmann Himmelsdonner",nil, {"Folgt Hauptmann Himmelsdonner und versucht auf Eurem Reittier zu bleiben. Solltet Ihr Hauptmann Himmelsdonner besiegen, berichtet Ja'y Nosliw beim Basislager des Drachenmals im Schattenmondtal von Eurem Erfolg."}},
[11072] = {"Feindesblut",nil, {"Findet die Schädelhaufen innerhalb der Beschwörungskreise in Skettis. Beschwört die Nachfahren, indem Ihr 10 zeitverlorene Schriftrollen am Schädelhaufen benutzt und bezwingt sie. Kehrt mit einem Beweisstück von jedem zu Hazzik am Schattenwindlager zurück."}},
[11073] = {"Terokks Fall",nil, {"Bringt die Zeitverlorene Opfergabe, die Hazzik angefertigt hat, zu dem Schädelhaufen im Zentrum von Skettis. Beschwört Terokk und bezwingt ihn. Habt Ihr Eure Aufgabe erfüllt, kehrt zu Himmelskommandant Adaris zurück."}},
[11074] = {"Zeichen der Nachfahren",nil, {"Sammelt zeitverlorene Schriftrollen von den zeitverlorenen Arakkoa in Skettis ein und bringt sie zu den Schädelhaufen innerhalb der Beschwörungskreise in Skettis. Beschwört und bezwingt die Nachfahren der Gegner Terokks und kehrt zu Hakkiz mit Akkarais Kralle, Garokks Stachel, Vekkaz' Schuppe und Gezzaraks Klaue zurück."}},
[11075] = {"Die Netherschwingenminen",nil, {"Sucht die Herrin der Minen auf der Netherschwingenscherbe auf."}},
[11076] = {"Die Dinge in den Griff bekommen...",nil, {"Die Herrin der Minen bei den Netherschwingenminen im Schattenmondtal möchte, dass Ihr 15 Kisten mit Fracht der Netherminen einsammelt."}},
[11077] = {"Drachen sind unsere geringste Sorge",nil, {"Der Vorarbeiter des Drachenmals bei den Netherschwingenminen im Schattenmondtal möchte, dass Ihr 15 Netherminenschinder und 5 Netherminenhetzer tötet."}},
[11078] = {"Den Himmel beherrschen",nil, {"Beschafft Euch Drachenzähne."}},
[11079] = {"Eine Teufelspeitsche für Gahk",nil, {"Beschafft Euch eine Teufelspeitsche."}},
[11080] = {"Die Strahlung des Relikts",nil, {"Erlangt Apexisstrahlung von einem Apexisrelikt."}},
[11081] = {"Der große Aufstand der Finsterblut",nil, {"Bringt die Fluchtpläne der Finsterblut zur Herrin der Minen."}},
[11082] = {"Suche nach der Wahrheit",nil, {"Findet einen Aufpasser der Finsterblut und verhört ihn. Kehrt mit den Informationen der Finsterblut und mit der Hand des Aufpassers zur Herrin der Minen zurück."}},
[11083] = {"Verrückt und verwirrt",nil, {"Ronag der Sklaventreiber in den Netherschwingenminen im Schattenmondtal möchte, dass Ihr den verrückten Vorarbeiter der Finsterblut und 5 verrückte Minenarbeiter der Finsterblut tötet."}},
[11084] = {"Seid gegrüßt, Hauptmann!",nil, {"Sprecht mit Oberanführer Mor'ghor beim Basislager des Drachenmals im Schattenmondtal."}},
[11085] = {"Flucht aus Skettis",nil, {"Begleitet die gefangene Himmelswache in Sicherheit und meldet Euch anschließend bei Himmelsoffizier Doryn."}},
[11086] = {"Schwächt das Portal des Zwielichts",nil, {"Oberanführer Mor'ghor beim Basislager des Drachenmals im Schattenmondtal möchte, dass Ihr 20 Agenten der Todesschatten tötet."}},
[11089] = {"Die Seelenkanone Reth'hedrons",nil, {"Illidarifürst Balthas beim Basislager des Drachenmals im Schattenmondtal möchte, dass Ihr ihm 2 Teufelsstahlbarren, 1 Adamantitrahmen, 1 Khoriumkraftkern und 1 makellose arkane Essenz bringt."}},
[11090] = {"Unterdrückt den Unterdrücker",nil, {"Illidarifürst Balthas beim Basislager des Drachenmals im Schattenmondtal hat Euch den Auftrag gegeben, die Seelenkanone auf Reth'hedron den Unterdrücker in Nagrand anzuwenden.nnBringt auch die Seelenkanone wieder zurück."}},
[11091] = {"Ein besonderer Dank an Euch",nil, {"Sprecht mit Jho'nass in Ogri'la im Schergrat."}},
[11092] = {"Glück auf, Kommandant!",nil, {"Sprecht mit Oberanführer Mor'ghor beim Basislager des Drachenmals im Schattenmondtal."}},
[11093] = {"Hungrige Netherrochen",nil, {"Benutzt den Netherrochenkäfig in den Wäldern südlich des Schattenwindlagers und erlegt in der Nähe des hungrigen Netherrochens einige Schattenwindhetzer. Kehrt anschließend zu Deesak dem Tierführer der Himmelswache zurück."}},
[11094] = {"Tötet sie alle!",nil, {"Überbringt Arkanist Thelis beim Sanktum der Sterne im Schattenmondtal die Neuigkeiten von Eurem Einsatz zusammen mit der Leuchtpistole des Drachenmals."}},
[11095] = {"Kommandant Hobb",nil, {"Bringt die Leuchtpistole des Drachenmals zu Kommandant Hobb beim Sanktum der Sterne im Schattenmondtal."}},
[11096] = {"Bedrohung von oben",nil, {"Tötet bei der Hügelwand 20 Oger der Gordunni und kehrt anschließend zu Yuula in Shattrath zurück."}},
[11097] = {"Die tödlichste Falle aller Zeiten",nil, {"Helft Kommandant Hobb und den Sehern bei der Verteidigung des Sanktums der Sterne. Sobald alle Streitkräfte des Drachenmals bezwungen wurden, kehrt zu Oberanführer Mor'ghor beim Basislager des Drachenmals mit den Medaillen der Seher zurück."}},
[11098] = {"Nach Skettis!",nil, {"Bringt das Paket mit Sprengladungen zu Himmelsoffizier Doryn beim Schattenwindlager außerhalb von Skettis."}},
[11099] = {"Tötet sie alle!",nil, {"Überbringt Anachoretin Ceyla beim Altar der Sha'tar die Neuigkeiten von Eurem Einsatz zusammen mit der Leuchtpistole des Drachenmals."}},
[11100] = {"Kommandant Arcus",nil, {"Bringt die Leuchtpistole des Drachenmals zu Kommandant Arcus beim Altar der Sha'tar im Schattenmondtal."}},
[11101] = {"Die tödlichste Falle aller Zeiten",nil, {"Helft Kommandant Arcus und den Aldor bei der Verteidigung des Altars der Sha'tar. Sobald alle Streitkräfte des Drachenmals bezwungen wurden, kehrt zu Oberanführer Mor'ghor beim Basislager des Drachenmals mit den Medaillen der Aldor zurück."}},
[11102] = {"Bomberangriff",nil, {"Vernichtet mithilfe der Bomben der Himmelswache 15 Stapel mit Teufelskanonenkugeln."}},
[11103] = {"Nicht mehr Weise",nil, {"Man kann die Kräfte Eures Ringes neu formen, doch wird es nicht einfach, Eure Auswahl rückgängig zu machen. Seid Ihr sicher, dass Ihr seine momentane Form durch eine andere ersetzen möchtet?"}},
[11104] = {"Nicht mehr Bewahrer",nil, {"The power within your ring is malleable, though reversing your choice is not easy. Are you sure you want to replace its current form?"}},
[11105] = {"Nicht mehr Held",nil, nil},
[11106] = {"Nicht mehr Verteidiger",nil, {"The power within your ring is malleable, though reversing your choice is not easy. Are you sure you want to replace its current form?"}},
[11107] = {"Verneigt Euch vor dem Hochlord",nil, {"Sprecht mit Oberanführer Mor'ghor beim Basislager des Drachenmals im Schattenmondtal."}},
[11108] = {"Fürst Illidan Sturmgrimm",nil, {"Trefft Euch mit Illidan Sturmgrimm. Solltet Ihr überleben, sucht Barthamus im Unteren Viertel von Shattrath auf."}},
[11109] = {"Jorus, der kobaltblaue Drache der Netherschwingen",nil, {"Es ist eine Ehre, Euch zu treffen, <Name>. Wir alle haben Eure Abenteuer mit großer Aufmerksamkeit verfolgt. Wir sind äußerst dankbar für alles, was Ihr für den Drachenschwarm der Netherschwingen getan habt.nnWenn Ihr mich zu Eurem Weggefährten macht, werde ich Euch bis ans Ende dieser Welt und wieder zurück fliegen. Was auch immer Ihr von mir verlangt, ich werde mein Bestes tun, um Euren Wunsch zu befolgen.nnIhr sollt jedoch wissen, dass Ihr nur einen von uns für Eure Reisen in der Scherbenwelt erwählen könnt. Habt Ihr Euch einmal entschieden, so könnt Ihr Eure Meinung nicht mehr ändern."}},
[11110] = {"Malfas, der lila Drache der Netherschwingen",nil, nil},
[11111] = {"Onyxien, der onyxfarbene Drache der Netherschwingen",nil, {"Es ist eine Ehre, Euch zu treffen, <Name>. Wir alle haben Eure Abenteuer mit großer Aufmerksamkeit verfolgt. Wir sind äußerst dankbar für alles, was Ihr für den Drachenschwarm der Netherschwingen getan habt.nnWenn Ihr mich zu Eurem Weggefährten macht, werde ich Euch bis ans Ende dieser Welt und wieder zurück fliegen. Was auch immer Ihr von mir verlangt, ich werde mein Bestes tun, um Euren Wunsch zu befolgen.nnIhr sollt jedoch wissen, dass Ihr nur einen von uns für Eure Reisen in der Scherbenwelt erwählen könnt. Habt Ihr Euch einmal entschieden, so könnt Ihr Eure Meinung nicht mehr ändern."}},
[11112] = {"Suraku, der azurblaue Drache der Netherschwingen",nil, nil},
[11113] = {"Voranaku, der violette Drache der Netherschwingen",nil, nil},
[11114] = {"Zoya, der viridiangrüne Drache der Netherschwingen",nil, {"Es ist eine Ehre, Euch zu treffen, <Name>. Wir alle haben Eure Abenteuer mit großer Aufmerksamkeit verfolgt. Wir sind äußerst dankbar für alles, was Ihr für den Drachenschwarm der Netherschwingen getan habt.nnWenn Ihr mich zu Eurem Weggefährten macht, werde ich Euch bis ans Ende dieser Welt und wieder zurück fliegen. Was auch immer Ihr von mir verlangt, ich werde mein Bestes tun, um Euren Wunsch zu befolgen.nnIhr sollt jedoch wissen, dass Ihr nur einen von uns für Eure Reisen in der Scherbenwelt erwählen könnt. Habt Ihr Euch einmal entschieden, so könnt Ihr Eure Meinung nicht mehr ändern."}},
[11117] = {"Fangt die wilden Wolpertinger!",nil, {"Goldark Spatzenjäger möchte, dass Ihr 5 betäubte Wolpertinger mit dem Wolpertingernetz einfangt. Er hat auch erwähnt, dass Ihr einen ordentlichen Rausch braucht, um die Biester sehen zu können."}},
[11118] = {"Parade der rosa Elekks",nil, {"Reist zu den Biergärten außerhalb von Sturmwind, Darnassus und der Exodar, zappt an jedem Ort drei Elekks und kehrt anschließend zu Goldark Spatzenjäger zurück. Um die rosa Elekks sehen zu können, müsst Ihr betrunken sein."}},
[11119] = {"Angriff auf den Landeplatz von Bash'ir!",nil, {"Sprecht mit dem Äthertechnikerlehrling im Außenposten der Himmelswache im Schergrat."}},
[11120] = {"Parade der rosa Elekks",nil, {"Reist zu den Biergärten außerhalb von Silbermond, Donnerfels und Unterstadt, zappt an jedem Ort drei Elekks und kehrt anschließend zu Glodrak Jägerspatz zurück. Um die rosa Elekks sehen zu können, müsst Ihr betrunken sein."}},
[11122] = {"Hin und zurück",nil, {"Holt ein Fässchen von Flynn Feuerbräu in Kharanos und bringt es zu Pol Bernruh zurück. Wenn Ihr Euren Widder verliert, könnt Ihr Neill Ramstein nach Ersatz fragen."}},
[11123] = {"Untersuchung der Ruinen",nil, {"Sprecht mit Inspektor Tarem beim Gasthaus Zur süßen Ruh."}},
[11124] = {"Untersuchung der Ruinen",nil, {"Sprecht mit Inspektor Tarem beim Gasthaus Zur süßen Ruh."}},
[11126] = {"Verräter unter uns",nil, {"Konfrontiert 5 Aufwiegler der Deserteure und kehrt zu Calia Hastings nahe der Docks in Theramore zurück."}},
[11128] = {"Propagandakrieg",nil, {"Bringt das Propagandamaterial der Deserteure zu Calia Hastings nahe der Docks in Theramore."}},
[11129] = {"Kyle wird vermisst!",nil, {"Ahab Weizhuf beim Dorf der Bluthufe in Mulgore möchte, dass Ihr seinen geliebten Hund Kyle den Wilden füttert.nnFüttert Kyle mit zartem Schreiterfleisch und kehrt anschließend zu Ahab Weizhuf zurück."}},
[11130] = {"Oh, es glitzert!",nil, {"Gaunah möchte, dass Ihr Gaunahs Notiz zu Budd in den Geisterlanden, direkt außerhalb von Zul'Aman, bringt."}},
[11131] = {"Löscht das Feuer!",nil, {"Die kostümierte Matrone bittet um Eure Hilfe. Sie möchte, dass Ihr alle Feuer im Dorf löscht. Sprecht noch einmal mit der kostümierten Matrone, sobald alle Feuer erloschen sind."}},
[11132] = {"Versprechen, Versprechen...",nil, {"Budd Winterhäldler in den Axthügeln möchte, dass Ihr bei Hohepriester Nalorakks Terrasse in Zul'Aman nach seiner Karte sucht."}},
[11133] = {"Diskreditierung der Deserteure",nil, {"Calia Hastings möchte, dass Ihr verfälschte Handzettel an 6 Wachen von Theramore verteilt. Erstattet ihr anschließend Bericht nahe der Docks in Theramore."}},
[11134] = {"Das Ende der Deserteure",nil, {"Nehmt Gavis Grauschild gefangen und erstattet anschließend Bericht bei Calia Hastings nahe der Docks in Theramore."}},
[11135] = {"Der kopflose Reiter",nil, {"Sucht nach dem Kürbisschrein des kopflosen Reiters."}},
[11136] = {"Beunruhigende Berichte",nil, {"Erstattet Hauptmann Wymor bei der Späherwacht Bericht."}},
[11137] = {"Defias in den Düstermarschen?",nil, {"Besorgt Euch die Befehle der Defias von Garn Mathers und bringt sie zu Hauptmann Wymor bei der Späherwacht."}},
[11138] = {"Renn McGill",nil, {"Bringt die Befehle der Defias zu Renn McGill in den Gewässern nahe der Schreckensmoorküste."}},
[11139] = {"Tauchausrüstung aus zweiter Hand",nil, {"Bringt den Werkzeugsatz und die beschädigte Tauchausrüstung zu Renn McGill in den Düstermarschen."}},
[11140] = {"Bergt die Fracht!",nil, {"Bringt 6 geborgene Geldkassetten zu Renn McGill in den Düstermarschen."}},
[11141] = {"Jaina muss davon erfahren",nil, {"Sprecht mit Lady Jaina Prachtmeer in Theramore."}},
[11142] = {"Beobachtet die Insel Alcaz",nil, {"Sprecht mit Cassa Purpurschwinge und nutzt einen ihrer Greifen, um die Insel Alcaz zu beobachten. Sprecht mit Lady Jaina Prachtmeer in Theramore, wenn Ihr die Beobachtung abgeschlossen habt."}},
[11143] = {"Der entscheidende Grimmweis",nil, {"Erstattet Hauptmann Darill bei der Nordwacht Bericht."}},
[11144] = {"Bestätigung des Verdachts",nil, {"Bringt die Schlachtpläne der Grimmtotem zu Hauptmann Darill bei der Nordwacht."}},
[11145] = {"Gefangene der Grimmtotem",nil, {"Unteroffizier Lukas bei der Nordwacht möchte, dass Ihr 5 Gefangene von Theramore befreit."}},
[11146] = {"Raptorenfänger",nil, {"Unteroffizier Lukas bei der Nordwacht möchte, dass Ihr 6 Raptoren einfangt. Ihr könnt entweder Blutsumpfraptoren oder Blutsumpfkreischer einfangen."}},
[11147] = {"Lasst die Raptoren los",nil, {"Unteroffizier Lukas bei der Nordwacht möchte, dass Ihr den Raptorenköder bei der Windmühle im Dorf der Schwarzhufe platziert."}},
[11148] = {"Waffen der Grimmtotem",nil, {"Hauptmann Darill bei der Nordwacht möchte, dass Ihr ihm 7 Waffen der Schwarzhufe bringt."}},
[11149] = {"Tabethas Unterstützung",nil, {"Sprecht mit Tabetha auf ihrem Hof."}},
[11150] = {"Brennt den Grollhornposten nieder!",nil, {"Brennt das nördliche, östliche und nordöstliche Zelt beim Grollhornposten nieder und kehrt anschließend zu Tabetha auf ihrem Hof zurück."}},
[11151] = {"Gerechtigkeit im Namen der Familie Hyal",nil, {"Sprecht mit Hauptmann Garran Mumm bei der Wehrzitadelle in Theramore."}},
[11152] = {"Endlich Frieden",nil, {"Hauptmann Garran Mumm bei der Wehrzitadelle möchte, dass Ihr den Kranz bei dem Monument der Familie Hyal niederlegt."}},
[11156] = {"Räuber des Grollhornpostens",nil, {"Tötet 12 Grimmtotem des Grollhornpostens und kehrt zu Lehrling Morlann auf Tabethas Hof zurück."}},
[11158] = {"Blutsumpffedern",nil, {"Bringt 5 Blutsumpffedern zu Brogg in Morastwinkel."}},
[11159] = {"Geister der Steinbrecherfeste",nil, {"Brogg in Morastwinkel möchte, dass Ihr 10 Geister der Steinbrecher tötet, um sie so ihrer letzten Ruhe zu überantworten."}},
[11160] = {"Das Banner der Steinbrecher",nil, {"Beschafft das Banner der Steinbrecher und bringt es Brogg in Morastwinkel."}},
[11161] = {"Die Essenz der Feindschaft",nil, {"Bringt 10 Essenzen eines schwarzen Drachkin zu Brogg in Morastwinkel."}},
[11162] = {"Herausforderung für den schwarzen Drachenschwarm",nil, {"Stellt das Banner der Steinbrecher nahe des Eingangs zu Onyxias Hort auf, wartet auf ihre Reaktion und kehrt anschließend zu Brogg in Morastwinkel zurück."}},
