[1823] = {"Habla con Ruga",nil, {"Habla con Ruga Furiatótem."}},
[1824] = {"Prueba en Tierra de Gigantes",nil, {"Lleva 5 antenas agitadas a Ruga Furiatótem de Campamento Taurajo."}},
[1825] = {"Habla con Thun'grim",nil, {"Habla con Thun'grim Vistafuego en Los Baldíos."}},
[1838] = {"Armadura brutal",nil, {"Lleva 15 lingotes de hierro humeantes, 10 azuritas en polvo, 10 barras de hierro y un vial de flogisto a Thun'grim Vistafuego."}},
[1839] = {"Ula'elek y los guanteletes brutales",nil, {"Habla con Ula'elek en Durotar."}},
[1840] = {"Orm Pezuñapétrea y el yelmo brutal",nil, {"Habla con Orm Pezuñapétrea en Cima del Trueno."}},
[1841] = {"Velora Noctis y las musleras brutal",nil, {"Habla con Velora Noctis en Entrañas."}},
[1842] = {"Pezuñas de sátiro",nil, {"Lleva 7 pezuñas de sátiro sin rajar a Ula'elek de Poblado Sen'jin en Durotar."}},
[1843] = {"Guanteletes brutales",nil, nil},
[1844] = {"Cuerno de quimera",nil, {"Lleva un cuerno galvanizado a Orm Pezuñapétrea de Cima del Trueno."}},
[1845] = {"Yelmo brutal",nil, {"Te concedo este yelmo.nnMe diste grandes gestas, <nombre>, y si la Madre Tierra quiere... aún te esperan gestas mayores."}},
[1846] = {"Tibias de Faucedraco",nil, {"Lleva 8 tibias de Faucedraco robustas a Velora Noctis de Entrañas."}},
[1847] = {"Musleras brutales",nil, nil},
[1848] = {"Camisote brutal",nil, {"Habrás tenido que soportar duras privaciones y habrás derramado mucha sangre cumpliendo mi tarea, <nombre>. Este es el producto de todo tu esfuerzo.nnUtilízalo bien y quizás un día vuelvas a contarme cuántas veces te ha sido de ayuda."}},
[1858] = {"Mano Destrozada",nil, {"Roba la llave de Tazan, abre su cartera y llévale el contenido a Therzok al Circo de las Sombras, en Orgrimmar."}},
[1859] = {"Therzok",nil, {"Ve a Orgrimmar, al Circo de las Sombras, y habla con Therzok."}},
[1860] = {"Habla con Jennea",nil, {"Habla con Jennea Cañón en Ventormenta."}},
[1861] = {"Lago Espejo",nil, {"Lleva una muestra del Lago Espejo a Jennea Cañón en Ventormenta."}},
[1878] = {"Cazador de faltriqueras de agua",nil, {"¿Ya has vuelto, <nombre>? Si tienes cinco bolsas más de agua te puedo dar ya mismo tu recompensa."}},
[1879] = {"Habla con Bink",nil, {"Habla con Bink en Forjaz."}},
[1880] = {"Trastónitor maguitronic",nil, {"Llévale a Bink su trastónitor maguitronic."}},
[1881] = {"Habla con Anastasia",nil, {"Habla con Anastasia Pozovenado."}},
[1882] = {"La Hacienda Balnir",nil, {"Lleva las Bocas de dragón de Balnir a Anastasia Pozovenado en el Barrio de los Magos de Entrañas."}},
[1883] = {"Habla con Un'thuwa",nil, {"Habla con Un'thuwa."}},
[1884] = {"Montones de Ju-Ju",nil, {"Destruye 4 montones de Ju-Ju y vuelve con Un'thuwa en Poblado Sen'jin."}},
[1885] = {"Mennet Carkad",nil, {"Habla con Mennet Carkad en el Barrio de los Pícaros de Entrañas."}},
[1886] = {"Los Mortacechadores",nil, {"Consigue la carta de presentación de Astor y devuélvesela a Mennet Carkad, que está en el Barrio de los Pícaros."}},
[1898] = {"Los Mortacechadores",nil, {"Habla con Andron Gant en Entrañas."}},
[1899] = {"Los Mortacechadores",nil, {"Llévale el libro de contabilidad de Andron a Mennet Carkad, que está en el Barrio de los Pícaros de Entrañas."}},
[1918] = {"El elemento contaminado",nil, {"Llévale el globo de agua contaminada a Mastok Sibilante en el Puesto del Hachazo, Vallefresno."}},
[1919] = {"Preséntate ante Jennea",nil, {"Habla con Jennea Cañón en Ventormenta."}},
[1920] = {"Investiga lo que pasa en El Ermitaño Azul",nil, {"Detrás de Jennea Cañón encontrarás un Canto de manifestación y un cofre de arcas de contención. Cógelos y lleva las arcas de contención llenas a Jennea, en el Sagrario del Mago."}},
[1921] = {"Recolección de materiales",nil, {"Consigue 10 paños de lino y 6 gemas cargadas de la falla para Wynne Larson en Ventormenta."}},
[1938] = {"Tratado de Ur sobre la Magia de las Sombras",nil, {"Lleva el Tratado de Ur sobre la Magia de las Sombras al sumo hechicero Andromath en Ventormenta."}},
[1939] = {"Sumo Hechicero Andromath",nil, {"Habla con el sumo hechicero Andromath."}},
[1940] = {"Seda prístina de araña",nil, {"Lleva 8 sedas prístinas de araña a Wynne Larson en Ventormenta."}},
[1941] = {"Toga Tejemaná",nil, {"Aquí está tu toga. ¡Es una de las mejores que he hecho nunca! Llévala con orgullo y cuando te pregunten... ¡di que te la hicieron en Diseños Larson!"}},
[1942] = {"Prendas de nudo astral",nil, {"¡Ya está lista! La seda que trajiste era tan delicada... que te hice la prenda un poco pequeña para quedarme yo con algo de sobra, ¿no te importa, verdad?nnEs una broma. Te quedará perfecta y será ideal tanto para tus aventuras como para una cena formal. ¡Que la disfrutes!"}},
[1943] = {"Habla con Deino",nil, {"Habla con Deino."}},
[1944] = {"Aguas de Xavian",nil, {"Lleva la muestra de agua de Xavian a Deino en Orgrimmar."}},
[1945] = {"Hermanas sonrientes",nil, {"Lleva 12 cabellos de hermana sonriente a Kil'hala en El Cruce."}},
[1946] = {"Prendas de encaje Abisal",nil, {"Aquí tienes tu encaje abisal, <nombre>. Es cómodo y duradero y alberga la magia del cabello de hermana sonriente.nnDisfrútalo y si lo encuentras adecuado menciónaselo a Deino, la maga. Me gustaría estar de su lado..."}},
[1947] = {"Viaje al pantano",nil, {"Habla con Tabetha."}},
[1948] = {"Objetos de poder",nil, {"Lleva 1 jade y la zarza electrificada a Tabetha en el Marjal Revolcafango."}},
[1949] = {"Secretos escondidos",nil, {"Habla con Magus Tirth en El Desierto de Sal."}},
[1950] = {"Santo y seña",nil, {"Averigua la frase para la caja fuerte de Tirth y regresa con Tirth."}},
[1951] = {"Rituales de poder",nil, {"Lleva el libro Rituales de poder a Tabetha en el Marjal Revolcafango."}},
[1952] = {"Varitas de mago",nil, {"Habla con Tabetha cuando termine su ritual."}},
[1953] = {"Regreso al pantano",nil, {"Habla con Tabetha."}},
[1954] = {"El orbe infernal",nil, {"Lleva un orbe infernal a Tabetha en el Marjal Revolcafango."}},
[1955] = {"El exorcismo",nil, {"Mata al demonio del orbe y habla con Tabetha."}},
[1956] = {"Poder en Uldaman",nil, {"Hazte con una fuente de poder obsidiano y llévasela a Tabetha en el Marjal Revolcafango."}},
[1957] = {"Oleadas de maná",nil, {"Mata 12 oleadas de maná dentro del tiempo permitido."}},
[1958] = {"Poder celestial",nil, nil},
[1959] = {"Preséntate ante Anastasia",nil, {"Habla con Anastasia Pozovenado."}},
[1960] = {"Investiga la tienda de alquimia",nil, {"Detrás de Anastasia Pozovenado encontrarás un Canto de manifestación y un cofre de arcas de contención. Cógelos y lleva 3 arcas de contención llenas, el cofre de arcas de contención y el canto de manifestación a Anastasia en Entrañas."}},
[1961] = {"Recolección de materiales",nil, {"Consigue 10 trozos de paño de lino y 6 gemas de maná de Dalaran para Josef Gregorian."}},
[1962] = {"Toga fuego de hechizo",nil, {"Aquí tienes tu toga, <nombre>. Utilízala bien."}},
[1963] = {"Mano Destrozada",nil, {"Mata a Tazan y llévale su cartera a Therzok al Circo de las Sombras, en Orgrimmar."}},
[1978] = {"Los Mortacechadores",nil, {"Entrégale el libro de contabilidad de Andron a Varimathras, que está en la Cámara de la Dama Oscura en Entrañas."}},
[1998] = {"Fenwick Thatros",nil, {"Mata a Fenwick Thatros y llévale su cabeza a Mennet Carkad, que está en el Barrio de los Pícaros de Entrañas."}},
[1999] = {"Herramientas de comercio",nil, {"Encuentra el informe de estado de Dalaran y devuélveselo a Mennet Carkad, que está en el Barrio de los Pícaros de Entrañas."}},
[2000] = {"Rokar Filosombra",nil, {"Habla con Rokar Filosombra en Durotar."}},
[2018] = {"La prueba de Rokar",nil, {"Mata al vigía Glibby y llévale su silbato a Rokar Filosombra a Durotar."}},
[2019] = {"Herramientas de comercio",nil, {"Habla con Therzok en el Circo de las Sombras de Orgrimmar."}},
[2038] = {"Suministros de Bingles",nil, {"Localiza las herramientas de Bingles y devuélveselas:nnllave inglesa, destornillador, martillo y cartucho."}},
[2039] = {"Encuentra a Bingles",nil, {"Encuentra a Bingles Pimpampum en Loch Modan."}},
[2040] = {"Asalto subterráneo",nil, {"Recupera la dentrituradora goblin de Las Minas de la Muerte y devuélvesela a Shoni la Shilenshiosha en Ventormenta."}},
[2041] = {"Habla con Shoni",nil, {"Habla con Shoni la Shilenshiosha en Ventormenta."}},
[2078] = {"La venganza de Gyromast",nil, {"Encuentra el Trillomajareitor 4100, el contramaestre de Gelkak, y condúcelo hasta él."}},
[2098] = {"La búsqueda de Gyromast",nil, {"Encuentra los tres fragmentos de la llave de Gelkak Gyromast y llévaselas."}},
[2118] = {"Tierras apestadas",nil, {"Captura un Oso Cardo rabioso vivo y llévaselo a Tharnariun. nnSi no consigues capturar un Oso Cardo rabioso y pierdes la trampa, pídele otra a Tharnariun Espinarbolado."}},
[2138] = {"La limpieza de la infección",nil, {"Libra al bosque de 20 osos Cardo rabiosos y ve a buscar a Tharnariun Espinarbolado a Costa Oscura."}},
[2139] = {"La esperanza de Tharnariun",nil, {"Encuentra a la madre del cubil y mátala."}},
[2158] = {"Descanso y relajación",nil, {"Habla con el tabernero Farley de la Posada Orgullo de León."}},
[2159] = {"Paquete de Dolanaar",nil, {"Llévale el paquete de Dolanaar al tabernero Keldamyr."}},
[2160] = {"Suministros para Tannok",nil, {"Lleva a Kharanos el cajón de suministros del mesón y entrégaselo a Tannok Martilloescarcha."}},
[2161] = {"Un cargamento de peón",nil, {"Lleva la carga de Ukor al tabernero Grosk a Cerrotajo."}},
[2178] = {"Guiso de zancudo",nil, {"Llévale 5 piezas de carne de zancudo a Alanndarian Arrullanoche a Auberdine."}},
[2198] = {"El collar hecho añicos",nil, {"Busca al creador del collar hecho añicos y descubre para qué sirve."}},
[2199] = {"Esa información tiene un precio",nil, {"Lleva 5 lingotes de plata a Talvash del Kissel a Forjaz."}},
[2200] = {"El regreso a Uldaman",nil, {"Busca pistas sobre el paradero del collar de Talvash en Uldaman. Dijo que un paladín muerto fue su último dueño."}},
[2201] = {"Encuentra las gemas",nil, {"Encuentra el rubí, el zafiro y el topacio que están desperdigados por Uldaman. Cuando los tengas, contacta con Talvash del Kissel mediante la ampolla de adivinación que él te dio.nnA través del diario, sabes que...n*El rubí está escondido en una zona de Forjatiniebla con barricadas.n*El topacio permanece oculto en una urna en una de las zonas de los troggs, cerca de algunos enanos de la Alianza.nn*El zafiro obra en poder de Grimlok, el líder de los enanos, según él mismo afirma."}},
[2202] = {"Componentes de Uldaman",nil, {"Llévale 12 setas magenta a Jarkal Musgofusión en Kargath."}},
[2203] = {"Componentes de Tierras Inhóspitas II",nil, {"Usa las vasijas de taumaturgia vacías con los dragones guardianes agostados de las Tierras Inhóspitas. Cuando las hayas llenado, llévaselas a Jarkal Musgofusión en Kargath."}},
[2204] = {"Restaurar el collar",nil, {"Obtén una fuente de energía del constructo más poderoso que encuentres en Uldaman, y entrégasela a Talvash del Kissel en Forjaz."}},
[2205] = {"Busca el SI: 7",nil, {"Entrega el paquete de Keryn Sylvius al maestro Mathias Shaw que está en el Cuartel de Ventormenta."}},
[2206] = {"El robo",nil, {"Encuentra al maestro de embarcadero Defias y recupera el calendario de envíos para el maestro Mathias Shaw.|n"}},
[2218] = {"El camino hacia la salvación",nil, {"Ve a Forjaz y habla con Hulfdan Barbanegra."}},
[2238] = {"Un simple subterfugio",nil, {"Encuentra a Onin MacHammar cerca de Gnomeregan."}},
[2239] = {"El informe de Onin",nil, {"Lleva el informe de Onin a Hulfdan a Forjaz."}},
[2240] = {"La cámara oculta",nil, {"Lee el diario de Baelog, inspecciona la cámara oculta y ve a informar al prospector Pico Tormenta."}},
[2241] = {"Cuando cae la manzana",nil, {"Llévale la rosa de Jannok a Syurna, que está en Darnassus."}},
[2242] = {"La llamada del destino",nil, {"Encuentra a Sethir el Ancestro y ve a contarle todo lo que descubras a Syurna.|n"}},
[2258] = {"Componentes de Tierras Inhóspitas",nil, {"Llévale 5 mollejas de águila ratonera, 10 colmillos de coyote del risco y 5 fragmentos de elemental de roca a Jarkal Musgofusión en Kargath, en las Tierras Inhóspitas."}},
[2259] = {"Erion Sombrusurro",nil, {"Localiza a Erion Sombrusurro en Darnassus.|n"}},
[2260] = {"La orden de Erion",nil, {"Viaja a la Ciudad de Ventormenta y habla con Renzik \"Renzik \"Chafarote\"."}},
[2278] = {"Los discos de platino",nil, {"Habla con el vigía de piedra y descubre qué conocimiento antiguo alberga. Cuando hayas adquirido el conocimiento que te ofrece, activa los Discos de Norgannon."}},
[2279] = {"Los discos de platino",nil, {"Lleva la reproducción en miniatura de los Discos de Norgannon a la Liga de Expedicionarios de Forjaz."}},
[2280] = {"Los discos de platino",nil, {"Lleva la reproducción en miniatura de los Discos de Norgannon a uno de los sabios de Cima del Trueno."}},
[2281] = {"Cita en Crestagrana",nil, {"Adéntrate en Villa del Lago, en las Montañas Crestagrana, y habla con Lucius.|n"}},
[2282] = {"Molino de Alther",nil, {"Abre el arcón de Lucius y llévale la muestra de robo a Villa del Lago."}},
[2283] = {"La recuperación del collar",nil, {"Busca el collar en la excavación de Uldaman y llévaselo a Dran Droffers a Orgrimmar. Puede que el collar esté estropeado."}},
[2284] = {"La recuperación del collar, 2ª parte",nil, {"Busca pistas sobre el paradero de las gemas en las profundidades de Uldaman."}},
[2298] = {"Una regia sacudida",nil, {"Viaja a Ventormenta y busca a Renzik \"Chafarote\"."}},
[2299] = {"¡Ve a hablar con Hulfdan!",nil, {"Viaja a Forjaz y habla con Hulfdan Barbanegra."}},
[2300] = {"IV:7",nil, {"Viaja a Ventormenta y busca a Renzik \"Chafarote\"."}},
[2318] = {"La traducción del diario",nil, {"Encuentra a alguien que pueda traducir el diario del paladín. El lugar más cercano en el que podrás encontrar a alguien es Kargath, en las Tierras Inhóspitas."}},
[2338] = {"La traducción del diario",nil, {"Préstale el collar a Jarkal. A cambio, te traducirá el diario."}},
[2339] = {"Encuentra las gemas y la fuente de alimentación",nil, {"Recupera las 3 gemas y una fuente de energía para el collar de Uldaman y llévalo todo a Jarkal Musgofusión en Kargath. Jarkal cree que es posible que la fuente de energía más poderosa se halle en un constructo de Uldaman.nnPor el diario sabes...n* Que han ocultado el rubí en una zona cerrada con barricadas en Forjatiniebla.n* Que el topacio se ha ocultado en una urna en un área trogg, cerca de los enanos de la Alianza.n* Que Grimlok, el jefe trogg, se ha quedado con el zafiro."}},
[2340] = {"Entregar las gemas",nil, {"Entrégale el collar y la gema salvaje a Dran Droffers en Orgrimmar."}},
[2341] = {"La recuperación del collar, 3ª parte",nil, {"Habla con Jarkal Musgofusión en Kargath."}},
[2342] = {"Los tesoros reclamados",nil, {"Coge el tesoro de la familia de Patrick Garrett del cofre de su familia que se encuentra en la Sala Comunal Sur de Uldaman y llévasela a él a Entrañas."}},
[2358] = {"Cuernos de Nez'ra",nil, {"Lleva los Cuernos de Nez'ra a Arantir a Villa del Lago."}},
[2359] = {"La torre de Klaven",nil, {"Roba la Llave de la Torre Defias, entra en la Torre Defias y descubre qué contiene el Cofre del Bosque del Ocaso. Lleva toda la información que encuentres a Mathias Shaw de Ventormenta. Lee el diario de Kearnen para saber cómo acceder a la torre."}},
[2360] = {"Mathias y los Defias",nil, {"Ve a Páramos de Poniente y encuentra a la agente Kearnen para dar parte."}},
[2361] = {"Restaurar el collar",nil, nil},
[2378] = {"Encuentra a los Mano Destrozada",nil, {"Viaja a Orgrimmar y habla con Shenthul, que estará en el Circo de las Sombras.|n"}},
[2379] = {"Zando'zan",nil, {"Habla con Zando'zan en el Circo de las Sombras."}},
[2380] = {"Ve a Orgrimmar",nil, {"Ve a Orgrimmar, al Circo de las Sombras, y habla con Shenthul."}},
[2381] = {"Saquear a los saqueadores",nil, {"Llévale el tesoro de los Mares del Sur a Desdio el Desdichado en Trinquete. No olvides conseguir una G.E.A.Q. y herramientas de ladrón del aparato Gizmotrónico de Wrenix. Necesitarás todo eso para completar tu misión.nnSi te atacaran unos loros anormalmente agresivos, ¡usa la G.E.A.Q.!"}},
[2382] = {"Desdio de Trinquete",nil, {"Ve a Trinquete y habla con Desdio el Desdichado."}},
[2383] = {"Papiro simple",nil, {"Lee el papiro simple y habla con Frang en el Valle de los Retos."}},
[2398] = {"Los enanos desaparecidos",nil, {"Encuentra a Baelog en Uldaman."}},
[2399] = {"Flora en brote",nil, {"Las hojas que Denalan plantó en su jardín han retoñado y crecido. Esperan con impaciencia a que alguien las coja..."}},
[2418] = {"Las piedras de energía",nil, {"Llévale 8 piedras de energía de dentrio y 8 piedras de energía An'Alleum a Aparejez en las Tierras Inhóspitas."}},
[2438] = {"El cazasueños esmeralda",nil, {"Llévale el cazasueños esmeralda a Tallonkai Cruzarraíz, que está en Dolanaar."}},
[2439] = {"Los discos de platino",nil, {"Lleva tu vale de recompensa a Dinita Mantopétreo, que está en las Arcas de Forjaz."}},
[2440] = {"Los discos de platino",nil, {"Llévale este vale de recompensa a Bena Pezuña Invernal, que está en Cima del Trueno."}},
[2458] = {"Agente encubierto",nil, {"Ve a la torre de Ventura y Cía., al norte de El Fangal, y localiza al capataz Fizzule, el agente de Mano Destrozada. nnCuando veas la torre, usa la pistola de bengalas para avisar de tu llegada al capataz Fizzule. Lanza 2 bengalas y acércate al capataz para darle el /saludo de los Mano Destrozada. nnTienes que hacerlo en este orden, porque Fizzule te matará si no te reconoce."}},
[2459] = {"Ferocitas el Comesueños",nil, {"Tallonkai Cruzarraíz de Dolanaar quiere que mates a 7 místicos Tuercepinos y encuentres la joya perdida."}},
[2460] = {"El saludo de los Mano Destrozada",nil, {"Da el saludo de los Mano Destrozada a Shenthul."}},
[2478] = {"Misión: posible, pero no probable",nil, {"Roba la llave de la torre del supervisor Silixiz, mata a 2 zánganos mutados de Ventura y Cía., 2 patrullas de Ventura y Cía. y 2 oteadores de Ventura y Cía.nnAsesina al Gran Supervisor Puzik Gallywix y quédate con su cabeza, fuerza la cerradura del arcón de Gallywix y roba la carretilla de mezcla de Zanzil modificada que allí está oculta. Lleva el alijo y la cabeza de Gallywix a Shenthul en Orgrimmar; él te dará más instrucciones."}},
[2479] = {"La ayuda de Hinott",nil, {"Ve a Molino Tarren, en Laderas de Trabalomas, para entregar la muestra de la mezcla de Zanzil a Serge Hinott. nnPara ir, coge el zepelín a Entrañas y sigue el camino hacia el sur por el Bosque de Argénteos. ¡Sigue las indicaciones!"}},
[2480] = {"La ayuda de Hinott",nil, {"Espera a que Serge Hinott te cure."}},
[2498] = {"El regreso a Denalan",nil, {"Rellian Verdeguja quiere que hables con Denalan, que está en el Lago Al'Ameth."}},
[2499] = {"Roblegüello",nil, {"Denalan, que está en el Lago Al'Ameth, quiere que cojas el tumor gigantesco que tiene Roblegüello."}},
[2500] = {"Componentes de Tierras Inhóspitas",nil, {"Ve a las Tierras Inhóspitas a buscar los ingredientes que te ha pedido Ghak Sanadón y llévaselos a Thelsamar."}},
[2501] = {"Componentes de Tierras Inhóspitas II",nil, {"Usa las vasijas de taumaturgia en los dragones guardianes agostados de las Tierras Inhóspitas. Llévaselas a Ghak Sanadón a Thelsamar."}},
[2518] = {"Las lágrimas de la Luna",nil, {"La sacerdotisa A'moora del Templo de la Luna de Darnassus quiere que le lleves las espineretas de plata de Lady Sathrah."}},
[2519] = {"El templo de la Luna",nil, {"La hermana Aquinne quiere que hables con la sacerdotisa A'moora en el Templo de la Luna."}},
[2520] = {"El Sacrificio de Sathrah",nil, {"La sacerdotisa A'moora quiere que coloques las espineretas de plata de Lady Sathrah en la fuente que hay en el interior del templo, y que después vayas a verla de nuevo."}},
[2521] = {"Al servicio de Kum'isha",nil, {"En tus viajes por Las Tierras Devastadas, si te encontraras con una esfera draenetista perfecta, llévasela a Kum'isha el Coleccionista.nnTen cuidado, esta gema es uno de los cristales más raros de todo Azeroth. Cualquier criatura de estas tierras podría tener una.nnSe te recompensará por cada esfera draenetista perfecta que consigas."}},
[2522] = {"Los esfuerzos de Kum'isha",nil, {"Encuentra una esfera draenetista perfecta y llévasela a Kum'isha el Coleccionista."}},
[2523] = {"Melodía corrupta",nil, {"Has encontrado una versión enfermiza y corrupta de lo que parece ser una melodía. De la planta emana un olor acre y malsano. Necesita desesperadamente ser atendida para recuperar su estado normal."}},
[2541] = {"El druida durmiente",nil, {"Llévale un talismán de vudú chamán a Oben Garrafuria al Túmulo de Ban'ethil."}},
