[4300] = {"Armas con filo de hueso",nil, {"Lleva 8 garras blancas de ravasaurio a Jes'rimon a Orgrimmar."}},
[4301] = {"El poderoso U'cha",nil, {"Llévale la pelambre de U'cha a Torwa Abrecaminos, que está a la entrada del Cráter de Un'Goro."}},
