[4324] = {"Yuka Llavenrosca",nil, {"Habla con Yuka Llavenrosca en Las Estepas Ardientes."}},
[4341] = {"Kharan Martillazo",nil, {"Ve a las Profundidades de Roca Negra y encuentra a Kharan Martillazo.nnEl rey dijo que estaba prisionero allí; busca una cárcel."}},
