[5220] = {"El regreso al Campamento del Orvallo",nil, {"Lleva la botella del Llanto de Dalson llena a la suma sacerdotisa MacDonnell en el Campamento del Orvallo, en las Tierras de la Peste del Oeste."}},
[5221] = {"La caldera del Llanto de Dalson",nil, nil},
