[5721] = {"La Batalla de Villa Darrow",nil, {"Coloca el Fardo de reliquias en la plaza de Villa Darrow.nnImpide que Davil Fuegoluz muera antes que Horgus.nnImpide que el capitán Rutagrana muera hasta que aparezca Rutagrana el Corrompido.nnDerrota a Rutagrana el Corrompido.nnHabla con el espíritu de Joseph Rutagrana cuando aparezca en la Plaza de la Ciudad y luego habla con Pamela en su casa."}},
[5722] = {"Buscando la cartera perdida",nil, {"Inspecciona la Sima Ígnea en busca del cuerpo de Maur Tótem Siniestro y comprueba si porta algún objeto de interés."}},
[5723] = {"Midiendo fuerzas con el enemigo",nil, {"Localiza la Sima Ígnea en Orgrimmar y después mata a 8 troggs Furia Ardiente y 8 chamanes Furia Ardiente y después ve a ver de nuevo a Rahauro a Cima del Trueno."}},
[5724] = {"La vuelta de la cartera perdida",nil, {"Lleva la cartera de Tótem Siniestro hasta Rahauro, en Cima del Trueno."}},
[5725] = {"Poder destructivo",nil, {"Llévale los libros Hechizos de las Sombras y Encantamientos desde el infierno a Varimathras, que está en Entrañas."}},
[5726] = {"Enemigos ocultos",nil, {"Lleva una insignia de Teniente a Thrall a Orgrimmar."}},
[5727] = {"Enemigos ocultos",nil, {"Lleva la insignia de Teniente a Neeru Hojafuego. Averigua si cree que eres miembro de Filo Ardiente; luego ve a ver a Thrall a Orgrimmar."}},
[5728] = {"Enemigos ocultos",nil, {"Mata a Bazzalan y a Jergosh el Convocador y ve a ver a Thrall a Orgrimmar."}},
[5729] = {"Enemigos ocultos",nil, {"Habla con Neeru Hojafuego en Orgrimmar."}},
[5730] = {"Enemigos ocultos",nil, {"Habla con Thrall en Orgrimmar y cuéntale lo que has averiguado."}},
[5741] = {"El Cetro de Luz",nil, {"Consigue el Cetro de Luz y llévaselo a Azore Aldamort a la torre de Ethel Rethor.|n"}},
[5742] = {"Redención",nil, {"Escucha lo que Tirion Vadín tiene que contarte."}},
[5761] = {"Matar a la bestia",nil, {"Ve a la Sima Ígnea, mata a Taragaman el Hambriento y llévale su corazón a Neeru Hojafuego a Orgrimmar."}},
[5762] = {"Hemet Nesingwary Jr.",nil, {"Lleva el cajón de Kravel a Hemet Nesingwary Jr. a Tuercespina."}},
[5763] = {"La caza en Tuercespina",nil, {"Lleva el cuerno de kodo de Roon a Hemet Nesingwary Jr. a Tuercespina."}},
[5781] = {"De recuerdos olvidados",nil, {"Viaja a La Subgranja, en la sección más meridional de las Tierras de la Peste del Este, y recupera el Martillo de Taelan. Cuando hayas cumplido tu objetivo regresa con Tirion Vadín."}},
[5801] = {"La forja del Penacho en Llamas",nil, {"Lleva el molde para llave esqueleto y 2 barras de torio a la cima de la Cresta del Penacho en Llamas en el Cráter de Un'Goro. Utiliza el molde para llave esqueleto en el lago de lava para forjar la llave esqueleto sin terminar.nnLlévale la llave esqueleto sin terminar al alquimista Arbington al Alto del Orvallo, en las Tierras de la Peste del Oeste."}},
[5802] = {"La forja del Penacho en Llamas",nil, {"Lleva el molde para llave esqueleto y 2 barras de torio a la cima de la Cresta del Penacho en Llamas en el Cráter de Un'Goro. Utiliza el molde para llave esqueleto en el lago de lava para forjar la llave esqueleto sin terminar.nnLlévale la llave esqueleto sin terminar al boticario Dithers a El Baluarte, en las Tierras de la Peste del Oeste."}},
[5803] = {"El escarabeo de Araj",nil, {"Destruye a Araj el Invocador y lleva el Escarabeo de Araj al alquimista Arbington en el Alto del Orvallo, en las Tierras de la Peste del Oeste."}},
[5804] = {"El escarabeo de Araj",nil, {"Destruye a Araj el Invocador y lleva el Escarabeo de Araj al boticario Dithers en El Baluarte, en las Tierras de la Peste del Oeste."}},
[5805] = {"¡Bienvenido!",nil, {"Lleva el vale regalo de Villadorada a Merissa Fontana en Villadorada."}},
[5821] = {"Guardaespaldas de alquiler",nil, {"Escolta la caravana Gizelton a través del poblado de los centauros Kolkar. Habla con Smeed en el Campamento de los Mezclatornillos para recibir tu recompensa."}},
[5841] = {"¡Bienvenido!",nil, {"Lleva el vale regalo de Kharanos a Yori Yelmopartido en Kharanos."}},
[5842] = {"¡Bienvenido!",nil, {"Lleva el vale regalo de Dolanaar a Orenthil Susurravientos en Dolanaar."}},
[5843] = {"¡Te damos la bienvenida!",nil, {"Lleva el vale regalo de Cerrotajo a Magga en Cerrotajo."}},
[5844] = {"¡Bienvenido!",nil, {"Lleva el vale regalo del Poblado Pezuña de Sangre a Vorn Videncielo en el Poblado Pezuña de Sangre."}},
[5845] = {"Del honor perdido",nil, {"Viaja a Vallenorte, en la zona noreste de las Tierras de la Peste del Este, y recupera el Símbolo de Honor perdido. Cuando hayas cumplido tu objetivo regresa con Tirion Vadín."}},
[5846] = {"Del amor y la familia",nil, {"Viaja a la isla de Castel Darrow, en la región sur del centro de las Tierras de la Peste. Allí, busca pistas sobre el paradero del cuadro."}},
[5847] = {"¡Bienvenido!",nil, {"Lleva el vale regalo de Rémol a Claire Sauzal en Rémol."}},
[5848] = {"Del amor y la familia",nil, {"Viaja a Stratholme, en la zona norte de las Tierras de la Peste. En El Bastión Escarlata encontrarás el cuadro 'Del amor y la familia', oculto tras otra pintura que representa las lunas gemelas de nuestro mundo.nnDevuelve la pintura a Tirion Vadín."}},
[5861] = {"Encuentra a Myranda",nil, {"Viaja a la Tumba de Uther, en el sur de las Tierras de la Peste. Enseña a Myranda la Fada el obsequio de Tirion."}},
[5862] = {"El subterfugio Escarlata",nil, {"Habla con Myranda para conseguir la Ilusión Escarlata. Bajo el poder de la Ilusión Escarlata viaja a la Vega del Amparo y entrega el Regalo de Tirion al alto señor Taelan Vadín.nnLa Vega del Amparo está al Norte, Taelan seguramente estará en el interior de la Fortaleza de Mardenholde."}},
[5863] = {"La Base Machacaduna",nil, {"Andi Lynn de Gadgetzan quiere que destruyas la Base Machacaduna y para conseguirlo tendrás que matar 10 toscos Machacadunas, 10 déspotas Machacadunas y a Gor'marok el Devastador."}},
[5881] = {"La sustitución",nil, {"Maggran quiere que le entregues una carta a Grish Largocamino en El Gran Elevador, en Los Baldíos."}},
[5882] = {"Bálsamo por caza",nil, {"Una antorcha Cenarion te permite ver un fragmento de alma corrupto en esas bestias corruptas que deben ser sacrificadas por un bien mayor. Yo pulverizo los fragmentos y los convierto en un componente con el que se crea el bálsamo de planta de Cenarion. Y es lo que utilizaremos para sanar a las plantas corruptas.nnA cambio de esos fragmentos, te daré algunos bálsamos de planta de Cenarion que ya tengo preparados."}},
