[5892] = {"Suministros de Ferrohondo",nil, {"Lleva 10 suministros de Ferrohondo al intendente de la Alianza en Dun Baldar."}},
[5893] = {"Suministros de Dentefrío",nil, {"Lleva 10 suministros de Dentefrío al intendente de la Horda en el Bastión Lobo Gélido."}},
[5901] = {"Una Peste en la Cruzada",nil, {"Utiliza la Jarra de termitas vacía en los Montones de termitas de las Tierras de la Peste del Este. Cuando hayas reunido 100 termitas de las Tierras de la Peste, vuelve con Mickey Levine, que está en El Baluarte, en Claros de Tirisfal."}},
[5902] = {"Una Peste en la Cruzada",nil, {"Encuentra un lugar adecuado en el centro del Aserradero Crestanorte, en las Tierras de la Peste del Oeste, para colocar la barrica de termitas de las Tierras de la Peste."}},
[5903] = {"Una Peste en la Cruzada",nil, {"Utiliza la Jarra de termitas vacía en los Montones de termitas de las Tierras de la Peste del Este. Cuando hayas reunido 100 termitas de las Tierras de la Peste, vuelve con Nathaniel Dumah al Campamento del Orvallo en las Tierras de la Peste del Oeste."}},
[5904] = {"Una Peste en la Cruzada",nil, {"Encuentra un lugar adecuado en el centro del Aserradero Crestanorte, en las Tierras de la Peste del Oeste, para colocar la barrica de termitas de las Tierras de la Peste."}},
[5921] = {"Claro de la Luna",nil, {"Utiliza el hechizo \"teletransportarse: Claro de la Luna\" para viajar a Claro de la Luna. Cuando llegues, ve al poblado Amparo de la Noche y habla con Dendrita Rafagaestelar."}},
[5922] = {"Claro de la Luna",nil, {"Utiliza el hechizo \"Teletransporte: Claro de la Luna\" para viajar a Claro de la Luna. Cuando llegues, ve al poblado Amparo de la Noche y habla con Dendrita Rafagaestelar."}},
[5923] = {"Escucha la llamada",nil, {"Ve a Darnassus, al Enclave Cenarion y habla con Mathrengyl Garroso."}},
[5924] = {"Escucha la llamada",nil, {"Habla con Mathrengyl Garroso en el Enclave Cenarion, en Darnassus."}},
[5925] = {"Atendiendo a la llamada",nil, {"Habla con Mathrengyl Garroso en el Enclave Cenarion de Darnassus."}},
[5926] = {"Escucha la llamada",nil, {"Habla con Turak Runatótem en el Alto de los Ancestros de Cima del Trueno."}},
