[8250] = {"Pura magia",nil, {"Busca a Sanath Lim-yo y obtén permiso para ver al archimago Xylem."}},
[8251] = {"El polvo mágico",nil, {"Llévale 10 puñados de polvo relumbrante al archimago Xylem."}},
[8252] = {"El coral de las sirenas",nil, {"Reúne 6 corales encantados y llévaselos al archimago Xylem."}},
