[8324] = {"Seguir creyendo",nil, {"¿Has encontrado más textos encriptados? Tráeme todos los que puedas, este trabajo de descodificación lleva su tiempo."}},
[8325] = {"Recuperar la Isla del Caminante del Sol",nil, {"Mata 8 vermis de maná; están cerca de los cristales ardientes que hay junto a la torre y la glorieta. Luego ve a ver a la magistrix Erona a la torre de la Isla del Caminante del Sol."}},
[8326] = {"Medidas extremas",nil, {"Coge 8 colleras de lince de los linces y cachorros Garragil muertos. Luego ve a ver a la magistrix Erona a Isla del Caminante del Sol."}},
[8327] = {"Informando a Lanthan Perilon",nil, {"Habla con Lanthan Perilon en la Isla del Caminante del Sol. Está cerca del sendero que se aleja de La Aguja del Sol hacia el oeste."}},
[8328] = {"Entrenamiento de magos",nil, {"Habla con Julia Golpesol, la instructora de magos, en la Aguja del Sol en Isla del Caminante del Sol."}},
[8329] = {"Instrucción de guerrero",nil, {"Habla con Delios, el instructor de guerreros, en la Aguja del Sol en Isla del Caminante del Sol."}},
[8330] = {"Las posesiones de Solanian",nil, {"Recoge el orbe de visión, el pergamino de magia de la Plaga y el diario del vigía del pozo Solanian. Están en Isla del Caminante del Sol cerca de la charca, la fuente y de uno de los cristales ardientes. Llévaselo todo a La Aguja del Sol en la Isla del Caminante del Sol."}},
