[8415] = {"Campamento del Orvallo",nil, {"Habla con el comandante Ashlam Puñovalor en el Campamento del Orvallo."}},
[8416] = {"Piedras de la Plaga inertes",nil, {"Lleva la Piedra de la Plaga inerte al comandante Ashlam Puñovalor en el Alto del Orvallo."}},
[8417] = {"Un espíritu afligido",nil, {"Habla con el Héroe caído de la Horda en la entrada de Las Tierras Devastadas."}},
[8418] = {"La forja de la piedra de poderío",nil, {"Lleva las plumas vudú a Ashlam Puñovalor."}},
[8419] = {"Pedido de un diablillo",nil, {"Lleva una pieza de tela vil a Diblis en Frondavil."}},
[8420] = {"Caliente y picante",nil, {"Lleva un retal de tela vil a Diblis a Frondavil."}},
[8421] = {"La mercancía equivocada",nil, {"Lleva 10 maderas putrefactas y 4 esencias de veneno a Diblis a Frondavil."}},
[8422] = {"Plumas de trol",nil, {"Reúne un total de 6 plumas vudú en el Templo Sumergido."}},
[8423] = {"Parentesco guerrero",nil, {"Mata a 7 jabinfernos de Las Tierras Devastadas y vuelve junto al Héroe caído de la Horda."}},
[8424] = {"La guerra contra los Sombra Jurada",nil, {"Acaba con los Sombra Jurada en las Tierras Devastadas y regresa junto al Héroe Caído de la Horda."}},
[8425] = {"Plumas Vudú",nil, {"Llévale las plumas vudú de los trols del Templo Sumergido al Héroe caído de la Horda."}},
[8426] = {"La batalla por Garganta Grito de Guerra",nil, {"Entrega 3 marcas de honor de Garganta Grito de Guerra a un Belisario de la Horda fuera de los campos de batalla."}},
[8427] = {"La batalla por Garganta Grito de Guerra",nil, {"Entrega 3 marcas de honor de Garganta Grito de Guerra a un Belisario de la Horda fuera de los campos de batalla."}},
