[8733] = {"Eranikus, el Tirano del Sueño",nil, {"Viaja al continente de Teldrassil y busca al agente de Malfurion. Lo encontrarás a lo largo de los muros de Darnassus.|n"}},
[8734] = {"Tyrande y Remulos",nil, {"Ve a Claro de la Luna y habla con el guardián Remulos."}},
[8735] = {"La corrupción de Pesadilla",nil, {"Ve a los 4 Portales del Sueño Esmeralda de Azeroth y recoge un fragmento de Corrupción de Pesadilla en cada uno. Habla otra vez con el guardián Remulos en Claro de la Luna cuando hayas completado la tarea.|n"}},
[8736] = {"La Pesadilla se manifiesta",nil, {"Defiende Amparo de la Noche de Eranikus. No dejes morir al guardián Remulos. No mates a Eranikus. Defiéndete. Espera a Tyrande.|n"}},
[8737] = {"Templario azur",nil, {"Invoca y mata a un Templario azur y preséntate ante Bor Bravacrín en el Fuerte Cenarion. También debes llevar el Informe de tareas tácticas I para poder completar la misión."}},
[8738] = {"Informe de exploración de Colmen'Regal",nil, {"Contacta con el explorador Cenarion Landion en Colmen'Regal y entrega el informe de exploración de Colmen'Regal a Clamavientos Cuernorgullo en el Fuerte Cenarion. También debes llevar el Informe de tareas tácticas VII para poder completar la misión."}},
[8739] = {"Informe de exploración de Colmen'Ashi",nil, {"Contacta con el explorador Cenarion Jalia en Colmen'Ashi y entrega el informe de exploración de Colmen'Ashi a Clamavientos Cuernorgullo en el Fuerte Cenarion. También debes llevar el Informe de tareas tácticas VIII para poder completar la misión."}},
[8740] = {"Los merodeadores Crepusculares",nil, {"Mata a la maleante Crepuscular Morna y a 2 maleantes Crepusculares. Informa a Clamavientos Cuernorgullo cuando hayas completado tu misión. También debes llevar el Informe de tareas tácticas IX para poder completar la misión."}},
