[8780] = {"Refuerzos para armadura para el campo de batalla",nil, {"Lleva 8 complementos bastos para armadura y 8 complementos pesados para armadura a Janela Martillotenaz en la Avanzada de la Brigada de Forjaz, cerca de Colmen'Zora, en Silithus. También debes llevar el Informe de tareas logísticas VII para poder completar la misión."}},
[8781] = {"Armas para el campo de batalla",nil, {"Lleva 2 espadas de acero lunar a Janela Martillotenaz en la Avanzada de la Brigada de Forjaz, en las afueras de Colmen'Zora. También debes llevar el Informe de tareas logísticas VI para poder completar la misión."}},
[8782] = {"Suministros para uniformes",nil, {"Lleva 1 tela lunar, 2 madejas de paño rúnico y 1 seda de araña Tejehierro a Clamavientos Cuernorgullo en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas logísticas VIII para poder completar la misión."}},
[8783] = {"Materiales encantados",nil, {"Lleva 2 barras de torio encantado y 2 cueros encantados a Vargus en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas logísticas IX para poder completar la misión."}},
[8784] = {"Los secretos de los qiraji",nil, {"Llévale el artefacto antiguo qiraji a los dragones ocultos cerca de la entrada del templo.|n"}},
[8785] = {"¡La Legión de Orgrimmar necesita mojo!",nil, {"Lleva 6 mojos potentes, 6 frascos de gran mojo y 8 aceites de inmolación a la sacerdotisa de las Sombras Shai cerca de Colmen'Regal, en Silithus. También debes llevar el Informe de tareas logísticas IV para poder completar la misión."}},
[8786] = {"Armas para el campo de batalla",nil, {"Lleva 3 hachas de hierro macizo a Merok Zancolargo en el campamento de la Legión de Orgrimmar, en las afueras de Colmen'Regal. También debes llevar el Informe de tareas logísticas VI para poder completar la misión."}},
[8787] = {"Refuerzos para armadura para el campo de batalla",nil, {"Lleva 8 refuerzos para armadura bastos y 8 refuerzos para armadura pesados a Merok Zancolargo, cerca de Colmen'Regal. También debes llevar el Informe de tareas logísticas VII para poder completar la misión."}},
[8788] = {"Un regalo un poco agitado",nil, {"Parece que han agitado este regalo un par de veces. Su etiqueta dice:nnPara un <raza> <clase> muy especial."}},
[8789] = {"Armamentos imperiales qiraji",nil, {"Arygos en el Templo de Ahn'Qiraj creará armamento imbuido de Elementium para ti si le llevas armamento imperial qiraji y 3 menas de Elementium."}},
[8790] = {"Atavío imperial qiraji",nil, {"Merithra del Sueño en el Templo de Ahn'Qiraj creará armamento imbuido de Elementium para ti si le llevas regalos imperiales qiraji y 3 menas de Elementium."}},
[8791] = {"La caída de Osirio",nil, {"Entrégale la cabeza de Osirio el Sinmarcas al comandante Mar'alith de Fuerte Cenarion en Silithus.|n"}},
[8792] = {"¡La Horda te necesita!",nil, {"Habla con el Señor de la Guerra Gorchuk en el Valle de los Espíritus de Orgrimmar."}},
[8793] = {"¡La Horda te necesita!",nil, {"Habla con el Señor de la Guerra Gorchuk en el Valle de los Espíritus de Orgrimmar."}},
[8794] = {"¡La Horda te necesita!",nil, {"Habla con el Señor de la Guerra Gorchuk en el Valle de los Espíritus de Orgrimmar."}},
