[8884] = {"Aquí, pescadito...",nil, {"Consigue 8 cabezas de murlocs Malaescama. Llévaselas a Hathvelion Mirasol en el Bosque Canción Eterna, encima de la Orilla Tranquila."}},
[8885] = {"El anillo de Mmmrrrggglll",nil, {"Consigue el anillo de Mmmrrrggglll de las garras sin vida del jefe Malaescama. Llévaselo a Hathvelion Mirasol en el Bosque Canción Eterna, en el acantilado que domina la Orilla Tranquila."}},
[8886] = {"¡Piratas Malaescama!",nil, {"Recupera 6 piezas de la carga de la capitana Kelisendra y llévaselas al campamento fortificado que hay al este del Fondeadero Vela del Sol en el Bosque Canción Eterna."}},
[8887] = {"Rutas perdidas de la capitana Kelisendra",nil, {"Devuelve los planes de navegación perdidos a la capitana Kelisendra, que está en el campamento fortificado que hay al este del Fondeadero Vela del Sol, en el Bosque Canción Eterna."}},
[8888] = {"La aprendiza",nil, {"Habla con la aprendiza Loralthalis cerca de la Aguja Ocaso Marchito en el Bosque Canción Eterna."}},
[8889] = {"Desactivando la Aguja",nil, {"Desactiva las 3 fuentes de poder de la Aguja Ocaso Marchito. Después devuelve la joya desactivadora a la aprendiza Loralthalis en el Bosque Canción Eterna."}},
[8890] = {"Un mensaje de la Aguja",nil, {"Regresa junto al magister Ocaso Marchito en El Retiro del Errante, en el Bosque Canción Eterna."}},
[8891] = {"Investigación abandonada",nil, {"Devuélvele su diario al magister Ocaso Marchito, que se encuentra en El Retiro del Errante, en el Bosque Canción Eterna."}},
[8892] = {"Problemas en el Fondeadero Vela del Sol",nil, {"Mata a 5 matones y 5 gamberros Desdichados e informa al forestal Degolien en el Bosque Canción Eterna."}},
[8893] = {"El Superhuevomático",nil, {"El Superhuevomático es uno de los últimos inventos de Curgle Saltamanivela. Se trata de una máquina impresionante.nnEl Superhuevomático chisporrotea cuando te acercas al panel de control."}},
[8894] = {"Limpieza a fondo",nil, {"Mata 6 serpientes de maná y 6 malignos de éter y ve a ver al encargado Wyllithen a las Tierras Ocaso Marchito, en el Bosque Canción Eterna."}},
[8895] = {"Entrega para el Sagrario del Norte",nil, {"Entrégale la carta sin marcar al vigilante-Ley Caidanis en el Sagrario del Norte en el Bosque Canción Eterna."}},
[8896] = {"El espía enano",nil, {"Encuentra al prospector Guardayunque en el Sagrario del Norte, mátalo, y vuelve a informar a Aeldon Marcasol en el Bosque Canción Eterna."}},
