[9556] = {"Al victori...",nil, {"Hazte con una botella de agua Sagrada de Stratholme en la ciudad con el mismo nombre y entrégala al mago de Lordaeron en el Barrio de los Magos de Ventormenta o Entrañas una vez consigas la victoria para tu bando en las Tierras de la Peste."}},
[9557] = {"Descifrar el libro",nil, {"Llévale el diario antiguo al Anacoreta Paetheus en la Avanzada de Sangre."}},
[9558] = {"Los Barbalarga",nil, {"Busca a Gremni Barbalarga al noroeste de El Cubil de Haal'esh, hacia el oeste del Bastión del Honor en la Península del Fuego Infernal."}},
[9559] = {"El Bastión Semprepino",nil, {"Habla con el gran jefe Semprepino en el Bastión Semprepino."}},
