[9566] = {"Cristales de sangre",nil, {"Habla con el gran jefe Semprepino en el Bastión Semprepino."}},
[9567] = {"Conoce a tu enemigo",nil, {"Encuentra el monumento de Nazzivus en Nazzivian y recupera un glifo de dicho monumento. Regresa junto a la vindicadora Aalesia en la Avanzada de Sangre cuando tengas uno de los glifos."}},
[9568] = {"A la ofensiva",nil, {"La vindicadora Aalesia en la Avanzada de Sangre quiere que mates a 10 sátiros Nazzivus, 5 pícaros Nazzivus y 5 jurapenas Nazzivus."}},
[9569] = {"Contener la amenaza",nil, {"Mata a 5 acechasombras Axxarien, 5 clamainfernos Axxarien y a Zevrax, recolecta entonces 5 cristales corruptos y llévaselos a la vindicadora Aalesia en la Avanzada de Sangre."}},
[9570] = {"El Kurken acecha",nil, {"Kurz el Revelador del Bastión Semprepino en la Isla Bruma Azur quiere que mates a El Kurken y que le lleves su pellejo."}},
