[9572] = {"Debilitar la muralla",nil, {"Mata al guardián vigía Gargolmar, a Omor el Sinmarcas y el draco, Nazan. Llévale la mano de Gargolmar, la pezuña de Omor y la cabeza de Nazan a Caza'rez en Thrallmar, en la Península del Fuego Infernal."}},
[9573] = {"El cabecilla Oomooroo",nil, {"Semprepino el Menor del Bastión Semprepino en la Isla Bruma Azur quiere que mates al cabecilla Oomooroo y a 9 lechúcico salvaje enloquecido.|n"}},
[9574] = {"Víctimas de la corrupción",nil, {"Llévale 6 trozos de corteza cristalizada a Morae en la Avanzada de Sangre."}},
[9575] = {"Debilitar la muralla",nil, {"Mata al guardián vigía Gargolmar, a Omor el Sinmarcas y el draco, Nazan. Llévale la mano de Gargolmar, la pezuña de Omor y la cabeza de Nazan a Pistolete en el Bastión del Honor, en la Península del Fuego Infernal."}},
[9576] = {"El colgante de Cruelfin",nil, {"Llévale el colgante de cristal rojo a Morae en la Avanzada de Sangre."}},
