[9686] = {"La segunda prueba",nil, {"Completa la segunda prueba derrotando a los Campeones Furiasangre, Pautaluz, Hojágil y Golpesol en el pabellón cerca del maestro Kelerun Lamento de Sangre."}},
[9687] = {"Recuperando huesos sacros",nil, {"El príncipe Toreth de Isla Cicatriz de Vermis quiere que le lleves 8 huesos de dragón."}},
[9688] = {"Entrando en el Sueño",nil, {"El príncipe Toreth de Isla Cicatriz de Vermis quiere que mates 5 crías y 5 estirpes viridianas."}},
[9689] = {"Tajobuche",nil, {"El Príncipe Toreth de la Isla Cicatriz de Vermis ha pedido que mates a Tajobuche."}},
[9690] = {"La segunda prueba",nil, {"Preséntate ante el maestro Kelerun Lamento de Sangre."}},
[9691] = {"Retorno a Lunargenta",nil, {"Ve a ver al Lord caballero Sangrevalor en Ciudad de Lunargenta."}},
[9692] = {"El camino de los adeptos",nil, {"Lleva 1 gema kor corrupta, 1 caja de lingotes forjados con sangre, sangre del Colérico y 1 insignia de Caballero de sangre al Lord caballero Sangrevalor a Ciudad de Lunargenta."}},
[9693] = {"¿Qué es Argus para ti?",nil, {"El exarca Admetius de la Avanzada de Sangre quiere que hables con el vindicador Boros."}},
[9694] = {"Avanzada de Sangre",nil, {"El vindicador Boros de la Avanzada de Sangre quieres que mates a 10 espías Halcón del Sol."}},
[9696] = {"Traducciones",nil, {"Lleva la misiva de los halcones del Sol a la interrogadora Elysia a la Avanzada de Sangre."}},
[9697] = {"La vigía Leesa'oh",nil, {"Ve al Puesto de Vigilancia Cenarion de la Marisma de Zangar y habla con la vigía Leesa'oh."}},
[9698] = {"Audiencia con el Profeta",nil, {"Lleva la traducción de la carta de los Halcón del Sol al profeta Velen a El Exodar."}},
[9699] = {"Verdad o mentira",nil, {"Lleva las órdenes de Velen al vindicador Boros a la Avanzada de Sangre."}},
[9700] = {"Disparando magia en la oscuridad",nil, {"El vindicador Boros de la Avanzada de Sangre quiere que encuentres el portal del Sol y mates 5 anomalías del vacío."}},
[9701] = {"Observación de esporinos",nil, {"Investiga La Cañada Emergente en la Marisma de Zangar e informa a la vigía Leesa'oh del Puesto de Vigilancia Cenarion en la Marisma de Zangar de tus pesquisas."}},
