[9703] = {"El Crionúcleo",nil, {"El vindicador Kuros de la Avanzada de Sangre quiere que recuperes 12 cajas de suministros médicos."}},
[9704] = {"Ejecutados por los desdichados",nil, {"Ve hacia el sur a las Ruinas de Ciudad de Lunargenta y busca a la avanzada asesinada en Calle del Alba."}},
[9705] = {"Recuperación de paquete",nil, {"Regresa junto a la avanzada Alarion con el paquete de Erona."}},
[9706] = {"El diario de Galaen: El destino del vindicador Saruan",nil, {"Lleva el diario de Galaen al vindicador Kuros a la Avanzada de Sangre.|n"}},
[9707] = {"Forjando el arma",nil, {"Lleva el cajón de materiales a Bemarrin a Ciudad de Lunargenta."}},
[9708] = {"Ese hongo me suena",nil, {"Recolecta 15 muestras de champiñones y llévaselas a la vigía Leesa'oh al Puesto de Vigilancia Cenarion de la Marisma de Zangar."}},
[9709] = {"Volver a robar los champiñones",nil, {"Recolecta 10 cajas de champiñones y llévaselas a la vigía Leesa'oh al Puesto de Vigilancia Cenarion en la Marisma de Zangar."}},
[9710] = {"La corcesca templada en sangre",nil, {"Espera mientras Bemarrin forja tu arma."}},
[9711] = {"Matis el Cruel",nil, {"El vindicador Kuros de Avanzada de Sangre quiere que captures a Matis el Cruel."}},
[9712] = {"El caballo de guerra thalassiano",nil, {"Habla con el Lord caballero Sangrevalor para aprender a invocar a tu caballo de guerra thalassiano."}},
[9713] = {"Recogiendo fluochampiñones",nil, nil},
[9714] = {"¡Tráeme otra mata!",nil, nil},
[9715] = {"¡Otra mata!",nil, {"Recolecta 5 hibiscos sanguinos y llévaselos a Gzhun'tt en Esporaggar en la Marisma de Zangar."}},
[9716] = {"Problemas en el Lago Umbropantano",nil, {"Investiga el origen de la pérdida de agua en Lago Umbropantano. Luego ve a ver a Ysiel Vientocantor al Refugio Cenarion, en la Marisma de Zangar."}},
[9717] = {"El esqueje",nil, {"Recolecta un esqueje de sotoespora y llévaselo a T'shu a Esporaggar en la Marisma de Zangar."}},
[9718] = {"A vuelo de pájaro",nil, {"Ysiel Vientocantor quiere que uses el amuleto de cuervo tormentoso para explorar los lagos principales de la Marisma de Zangar."}},
[9719] = {"El acechador acechado",nil, {"Lleva el cerebro de la acechadora negra a Khn'nix en Esporaggar en la Marisma de Zangar."}},
[9720] = {"Restaurar el equilibrio",nil, {"Ysiel Vientocantor quiere que utilices las semillas de Parraférrea en los controles de las bombas de vapor en el Lago Serpiente, el Lago Umbropantano, el Lago Luz Pantanosa y La Laguna. A continuación vuelve a verla al Refugio Cenarion en la Marisma de Zangar y llévale las sobras que tengas."}},
[9721] = {"Lord Solanar te convoca",nil, {"Habla con Lord Solanar Sangre Colérica en la Ciudad de Lunargenta."}},
[9722] = {"El camino del maestro",nil, {"Habla con Lord Solanar Sangre Colérica y acepta su ofrecimiento de patrocinio."}},
[9723] = {"Un gesto de entrega",nil, {"Lleva 40 paños rúnicos, 6 barras de arcanita, 10 soleas, 5 runas oscuras y 150 piezas de oro a Lord Solanar Sangre Colérica en la Ciudad de Lunargenta."}},
[9724] = {"Aviso al Círculo Cenarion",nil, {"Habla con Amythel Caminaniebla en el Puesto Cenarion, en la Península del Fuego Infernal."}},
[9725] = {"Una demostración de lealtad",nil, {"Lord Solanar Sangre Colérica en la Ciudad de Lunargenta quiere que destruyas 3 carros de carne de la Plaga y mates a 15 ingenieros de asedio de la Plaga."}},
[9726] = {"Ahora que somos amigos...",nil, {"Mata a 12 explotadores Escamas de Sangre y a 6 encantadoras Escamas de Sangre, y vuelve junto a Gzhun'tt en Esporaggar, en la Marisma de Zangar."}},
[9727] = {"Ahora que aún somos amigos...",nil, {"Mata a 12 explotadores Escamas de Sangre y a 6 encantadoras Escamas de Sangre, y vuelve junto a Gzhun'tt en Esporaggar, en la Marisma de Zangar."}},
[9728] = {"Una cálida bienvenida",nil, {"El celador Hamoot del Refugio Cenarion quiere que le lleves 30 garras de naga."}},
[9729] = {"¡Fhwoor da el golpe!",nil, {"Escolta a Fhwoor hasta la zona de la bomba de vapor de la Luz Pantanosa para recuperar el Arca de Ssslith. Asegúrate de que Fhwoor y el arca vuelven sin peligro e informa a Gzhun'tt en Esporaggar, en la Marisma de Zangar."}},
[9730] = {"El líder de los Cumbre Oscura",nil, {"Mata al rajá Brujobruma en La Laguna y regresa junto al celador Hamoot de la Expedición Cenarion, en la Marisma de Zangar, por tu recompensa."}},
[9731] = {"Planos de conducto",nil, {"Busca en el fondo del Lago Serpiente signos de un conducto. Regresa junto a Ysiel Vientocantor en el Refugio Cenarion con las noticias de lo que descubras."}},
[9732] = {"Regreso a la marisma",nil, {"Regresa junto a Ysiel Vientocantor en el Refugio Cenarion, en la Marisma de Zangar."}},
[9733] = {"Advertir al Círculo Cenarion",nil, {"Habla con Mahuram Pezuñarrecia en Thrallmar, en la Península del Fuego Infernal."}},
[9734] = {"Retorno a la marisma",nil, {"Ve a ver a Ysiel Vientocantor al Refugio Cenarion en la Marisma de Zangar."}},
[9735] = {"Verdaderos maestros de la Luz",nil, {"Lord Solanar Sangre Colérica, en la Ciudad de Lunargenta, quiere le lleves 1 vial de agua sagrada de la Mano de Tyr."}},
[9736] = {"Verdaderos maestros de la Luz",nil, {"Lord Solanar Sangre Colérica de Ciudad de Lunargenta quiere que le lleves 1 catalizador arcano, 1 partículas Crepusculares, 1 diamante de Azeroth y 1 diamante negro prístino."}},
[9737] = {"Verdaderos maestros de la Luz",nil, {"Utiliza la mezcla de extinción cerca de la llama eterna de la Capilla de Alonsus para eliminar la protección de la Luz. Prepárate para luchar contra cualquiera que pretenda defender la capilla."}},
[9738] = {"Desaparecidos en combate",nil, {"Averigua qué le ha ocurrido al vinculador terrestre Rayge, al naturalista Mordisco, al desherbador Pulgarverde y al clamavientos Zarpa. Después, vuelve junto al vigía Jhang en la Reserva Colmillo Torcido, en la Marisma de Zangar."}},
[9739] = {"La aflicción de los esporinos",nil, {"Fahssn, de La Cañada Emergente, quiere que reúnas 10 bolsas de esporas maduras."}},
[9740] = {"El portal solar",nil, {"El vindicador Aesom de la Avanzada de Sangre quiere que destruyas el portal solar."}},
[9741] = {"Alimañas del vacío",nil, {"Mata 12 alimañas del vacío e informa luego al vindicador Aesom en la Avanzada de Sangre.|n"}},
[9742] = {"Más bolsas de esporas",nil, nil},
[9743] = {"Enemigos naturales",nil, {"Fahssn, de La Cañada Emergente en la Marisma de Zangar, quiere que le lleves 6 zarcillos de señor de la marisma."}},
[9744] = {"¡Más zarcillos!",nil, nil},
[9746] = {"Los límites del agotamiento",nil, {"El vindicador Aesom de la Avanzada de Sangre quiere que mates a 10 pirománticos de los halcones del Sol y a 10 defensores de los halcones del Sol.|n"}},
[9747] = {"La tribu Umbropantano",nil, {"Ikeyen del Refugio Cenarion quiere que vayas a la Aldea Umbropantano y mates a Kataru, 8 videntes de Umbropantano, 6 médicos brujos de Umbropantano y 6 oráculos de Umbropantano."}},
[9748] = {"Agua que no has de beber...",nil, {"El vindicador Aesom de la Avanzada de Sangre quiere que uses el frasco para recoger una muestra de agua de Bruma de Sangre."}},
[9749] = {"¡Viven! A lo mejor...",nil, {"El investigador Cornelius ha pedido que liberes 5 investigadores de la expedición."}},
[9750] = {"UNUSED Urgent Delivery",nil, nil},
[9751] = {"El legado de la Sangre Maldita",nil, {"El almirante Odesyus te ha pedido que encuentres al capitán Edward Hanes y hables con él."}},
[9752] = {"Evasión de Umbropantano",nil, {"Acompaña a Kayra Melenalarga al Refugio Cenarion en la Marisma de Zangar. Después ve a ver a Ysiel Vientocantor."}},
