[9762] = {"La profecía no escrita",nil, nil},
[9763] = {"La guarida del Señor de la Guerra",nil, {"El vigía Jhang quiere que encuentres y mates al Señor de la Guerra Kalithresh dentro de la Reserva Colmillo Torcido."}},
[9764] = {"Órdenes de Lady Vashj",nil, {"Entrega las órdenes de Lady Vashj a Ysiel Vientocantor en el Refugio Cenarion, en la Marisma de Zangar."}},
[9765] = {"Preparativos de guerra",nil, {"Consigue armamentos Colmillo Torcido de los nagas en la Reserva Colmillo Torcido y llévaselos a Ysiel Vientocantor en el Refugio Cenarion, en la Marisma de Zangar."}},
[9766] = {"Armamentos de Colmillo Torcido",nil, nil},
[9767] = {"Conoce a tu enemigo",nil, {"Consigue un libro de hechizos Colmillo Torcido de los nagas de la Reserva Colmillo Torcido y llévaselo a Ashyen en el Refugio Cenarion en la Marisma de Zangar."}},
[9769] = {"Antes muertos que sencillos",nil, {"Lleva ocho alas diáfanas de cualquier especie de aguijonero de la Marisma de Zangar a Magasha, en la Avanzada Rata del Pantano."}},
