[9792] = {"Un mensaje para Telaar",nil, {"Habla con Nahuud en Telaar, en Nagrand."}},
[9793] = {"El destino de Tuurem",nil, {"Lleva la carta del vindicador Idaar a Andarl en el Bastión Allerian, en el Bosque de Terokkar."}},
[9794] = {"No hay tiempo para la curiosidad",nil, {"Lleva la carta de Timothy a Kialon en Sylvanaar, en las Montañas Filospada."}},
[9795] = {"La amenaza de los ogros",nil, {"Ve al Bastión Señor del Trueno en las Montañas Filospada y habla con Nekthar."}},
[9796] = {"Noticias de la Marisma de Zangar",nil, {"Habla con Mokasa en el Bastión Rompepedras, en el Bosque de Terokkar."}},
[9797] = {"Refuerzos para Garadar",nil, {"Habla con el capitán Kroghan en Garadar, en Nagrand."}},
[9798] = {"Planes de los elfos de sangre",nil, {"Lleva los planes de los elfos de sangre al vindicador Aldar en el Lugar del Accidente, en el Valle Ammen."}},
[9799] = {"Trabajo de campo",nil, {"Recolecta 3 flores corruptas y llévaselas a la aprendiza Vishael a Lugar del Accidente, en Valle Ammen."}},
[9800] = {"Una extraña judía",nil, {"El elementalista Lo'ap en El Trono de los Elementos, en Nagrand, quiere que le consigas 15 anacardos digeridos."}},
[9801] = {"Recogiendo componentes",nil, {"El anacoreta Ahuurn de Telredor quiere que le lleves 4 ojos de esporiélago y 4 tentáculos de zancudo pantanoso."}},
[9802] = {"Plantas de Marisma de Zangar",nil, {"Lleva 10 partes de plantas sin identificar a Lauranna Thar'well al Refugio Cenarion en Marisma de Zangar."}},
[9803] = {"Mensajero de los Feropantanos",nil, {"Bebe el elixir de Ahuurn cuando estés en las Ruinas Boha'mu. Habla con el ancestro Kuruti cuando hayas adoptado la forma de un arakkoa."}},
[9804] = {"Espíritus agitados de Sol Celeste",nil, {"El elementalista Lo'ap en El Trono de los Elementos, en Nagrand, quiere que destruyas 12 espíritus del lago."}},
[9805] = {"Bendición de Incineratus",nil, {"El elementalista Lo'ap en El Trono de los Elementos, en Nagrand, quiere que destruyas la Cabaña Grande, la Cabaña del Oeste, la Cabaña del Sur y la Cabaña del Este en la Aldea Junco Alabeado.nnDevuelve el fuego viviente al elementalista Lo'ap cuando hayas completado tu tarea."}},
[9806] = {"Esporas fértiles",nil, {"Gshaff quiere que reúnas 6 esporas fértiles entre los esporiélagos y los caminantes de marismas de la Marisma de Zangar. Vuelve a ver a Ghsaff en Esporaggar cuando hayas completado la tarea."}},
[9807] = {"Más esporas",nil, nil},
[9808] = {"Fluochampiñones",nil, {"Lleva 10 fluochampiñones a Msshi'fn a Esporaggar en Marisma de Zangar."}},
[9809] = {"Más fluochampiñones",nil, nil},
[9810] = {"El espíritu contaminado",nil, {"El elementalista Lo'ap en El Trono de los Elementos, en Nagrand, quiere que destruyas la esencia contaminada de Watoosun y 10 mareas del lago."}},
[9811] = {"Amigos de los sin'dorei",nil, {"Lleva la cabeza de Dar'Khan a Lor'themar Theron a Ciudad de Lunargenta."}},
[9812] = {"Enviado de la Horda",nil, {"Lleva la carta de Lor'themar Theron a Lady Sylvanas Brisaveloz en Entrañas, la capital de los Renegados, en los Claros de Tirisfal."}},
[9813] = {"Encuentro con el Jefe de Guerra",nil, {"Lady Sylvanas Brisaveloz quiere que lleves la carta sellada por Sylvanas a Thrall en Orgrimmar, capital de Durotar."}},
[9814] = {"¡Champiñones fulminantes, colega!",nil, {"Lleva al médico brujo Tor'gash 6 champiñones fulminantes."}},
[9815] = {"Bucear en lodo",nil, {"El elementalista Lo'ap en El Trono de los Elementos, en Nagrand, quiere que consigas 5 núcleos de lodo."}},
[9816] = {"Mascotas adorables",nil, {"Coloca la jaula de múrlocs en la piedra plana cerca de la Aldea Dagapantano."}},
[9817] = {"Líder de los Escamas de Sangre",nil, {"Mata a Rajis Fyashe en el Lago Serpiente y ve a ver al celador Hamoot en el Refugio Cenarion, en la Marisma de Zangar para obtener la recompensa."}},
[9818] = {"Bajo la tierra",nil, {"Comunícate con Gordawg."}},
[9819] = {"La tierra torturada",nil, {"Gordawg de El Trono de los Elementos en Nagrand ha pedido que destruyas a 15 espíritus de tierra torturados."}},
[9820] = {"SE BUSCA: jefe Grog'ak",nil, {"Lleva la cabeza del jefe Grog'ak al cazador de las Sombras Denjai en Zabra'jin."}},
[9821] = {"Comiendo condenación",nil, {"Gordawg en El Trono de los Elementos, en Nagrand, te ha pedido que le lleves 15 núcleos de trituradores iracundos."}},
[9822] = {"Ataque inminente",nil, {"Lleva los planes de ataque de Ango'rosh al cazador de las Sombras Denjai en Zabra'jin."}},
[9823] = {"Nosotros o ellos",nil, {"Mata al Señor Supremo Puño Sajante, 10 aplastadores Ango'rosh y 10 devoralmas Ango'rosh y preséntate ante el cazador de las Sombras Denjai en Zabra'jin."}},
[9824] = {"Alteraciones arcanas",nil, {"Utiliza el cristal de visión violeta cerca de fuentes subterráneas de agua en El Sótano del Maestro y vuelve a ver al archimago Alturus en las afueras de Karazhan."}},
[9825] = {"Actividad incansable",nil, {"Lleva 10 esencias fantasmales al archimago Alturus en las afueras de Karazhan."}},
[9826] = {"Contacto de Dalaran",nil, {"Lleva el informe de Alturus al archimago Cedric en la afueras de Dalaran."}},
[9827] = {"Basidio marchito",nil, {"Lleva el basidio marchito a Ruam en Telredor."}},
[9828] = {"Basidio marchito",nil, {"Lleva el basidio marchito a Reavij en la Avanzada Rata del Pantano."}},
[9829] = {"Khadgar",nil, {"Lleva el informe de Alturus a Khadgar en la Ciudad de Shattrath, en el Bosque de Terokkar."}},
[9830] = {"Veneno letal",nil, {"Lleva 6 unidades de veneno de sangrador Luz pantanosa a Puluu en el Puerto Orebor."}},
[9831] = {"La entrada a Karazhan",nil, {"Khadgar quiere que entres en el Laberinto de las Sombras en Auchindoun y recuperes el primer trozo de la llave de un contenedor arcano que está escondido allí."}},
[9832] = {"El segundo y tercer trozo",nil, {"Consigue el segundo trozo de la llave, que está en un contenedor arcano en la Reserva Colmillo Torcido, y el tercer trozo de la llave, que está en otro contenedor arcano en El Castillo de la Tempestad. Vuelve a ver a Khadgar en la Ciudad de Shattrath cuando lo hayas hecho."}},
[9833] = {"Líneas de comunicación",nil, {"Puluu, en el Puerto Orebor quiere que mates 12 cercenadores Colmillo de la marisma."}},
[9834] = {"Armadura natural",nil, {"Lleva 8 pellejos de Zarpacieno a Maktu en el Puerto Orebor."}},
[9835] = {"Invasión de los Ango'rosh",nil, {"Mata 10 ogros Ango'rosh y 5 chamanes Ango'rosh. Después vuelve a ver a Ikuti en el Puerto Orebor."}},
[9836] = {"El toque del maestro",nil, {"Ve a las Cavernas del Tiempo y convence a Medivh para que active tu llave de aprendiz restaurada."}},
[9837] = {"Khadgar de nuevo",nil, {"Vuelve a ver a Khadgar en la Ciudad de Shattrath y enséñale la llave del maestro."}},
[9838] = {"El Ojo Violeta",nil, {"Habla con el archimago Alturus fuera de Karazhan."}},
[9839] = {"Señor Supremo Puño Sajante",nil, {"Ikuti quiere que mates al Señor Supremo Puño Sajante y a 10 aplastadores Ango'rosh. Vuelve al Puerto Orebor cuando hayas completado la tarea."}},
[9840] = {"Evaluando la situación",nil, {"Encuentra a Koren en Karazhan."}},
[9841] = {"Aguijoneando los aguijoneros",nil, {"Mata 8 sangradores Luz Pantanosa y vuelve a ver a Gambarinka en Zabra'jin."}},
[9842] = {"Las hojas más afiladas",nil, {"Lleva 10 hojas de cercenadores Colmillo de la marisma a Gambarinka en Zabra'jin."}},
[9843] = {"Los apuntes de Keanna",nil, {"Busca los apuntes de Keanna en Los Aposentos de los Invitados, en Karazhan, y llévaselos al archimago Alturus fuera de Karazhan."}},
[9844] = {"Una presencia demoníaca",nil, {"El archimago Alturus quiere que destruyas la presencia demoníaca de la parte superior de Karazhan."}},
[9845] = {"Competición de pesca",nil, {"Mata 10 vándalos Zarpacieno y vuelve a ver a Zurjaya en Zabra'jin."}},
[9846] = {"Espíritus de los Feropantanos",nil, {"Entrega 10 tótems de protección Feropantano al vidente Janidi en Zabra'jin."}},
[9847] = {"¿Un espíritu aliado?",nil, {"Lleva el Tótem Feropantano hasta el pie de las escaleras de las Ruinas Boha'mu y colócalo en el suelo. Informa sobre tus descubrimientos al vidente Janidi en Zabra'jin."}},
[9848] = {"Secretos de los Dagapantano",nil, {"Lleva el manual de venenos de Dagapantano y un Vial de veneno de Dagapantano a Timothy Daniels en el Puerto Orebor."}},
[9849] = {"Rompiendo el velo",nil, {"Gordawg de El Trono de los Elementos en Nagrand te ha pedido que mates a 30 esbirros de Gurok."}},
[9850] = {"Cacería de uñagrietas",nil, {"Mata a 30 uñagrietas dominantes y ve a hablar con Hemet Nesingwary en el Safari Nesingwary, en Nagrand, sobre tu progreso."}},
