[10390] = {"Campamento Forja: Mageddon",nil, {"Nazgrel, en Thrallmar, quiere que ataques el Campamento Forja: Mageddon, mates a 10 sirvientes Gan'arg y vuelvas con la cabeza de Navajafilada."}},
