[10394] = {"Desbarajuste en el Campamento Forja: Mageddon",nil, {"Mata a 10 sirvientes Gan'arg y a Navajafilada, en el Campamento Forja: Mageddon y regresa junto al mariscal de campo Brock en el Alto del Honor."}},
