[10396] = {"Enemigo de mi enemigo...",nil, {"Destruye 3 cañones viles MKl en el Campamento Forja: Ira y regresa después junto al mariscal de campo Brock en el Alto del Honor."}},
[10397] = {"Punto de invasión: Aniquilador",nil, {"Mata a Arix'Amal. Coge la llave del portal de la Legión Ardiente. Usa la llave del portal de la Legión Ardiente con la runa de maldad."}},
