[10699] = {"Un sello de honor",nil, nil},
[10700] = {"Diez sellos de honor",nil, nil},
[10701] = {"Infiltrarse en la Roca Abisal",nil, {"Mata a Roca Abisal y preséntate después al jefe de cohetes Fuselaje del Área 52, en la Tormenta Abisal."}},
[10702] = {"Un trabajo ingrato...",nil, {"El Señor Supremo Or'barokh de la Aldea Sombraluna, en el Valle Sombraluna, quiere que mates a 20 ejemplares de la fauna y flora del Valle Sombraluna.nnEntre estas criaturas encontrarás el jabalí vil, la quimera Alavil y el tenazario de caparazón agostado. Cualquier combinación valdrá."}},
[10703] = {"Ponte las rodilleras...",nil, {"El Señor feudal Yoregar en el Bastión Martillo Salvaje, en el Valle Sombraluna, quiere que mates 20 animales del Valle Sombraluna.nnTiene que haber algún jabalí vil, quimera Alavil y tenazario caparazón quemado. Cualquier combinación será correcta."}},
[10704] = {"Cómo infiltrarse en El Arcatraz",nil, {"A'dal te ha encargado que recuperes los fragmentos inferior y superior de la llave de Arcatraz. Devuélveselos y con ellos recreará la llave de El Arcatraz para ti."}},
[10705] = {"El vidente Udalo",nil, {"Encuentra al vidente Udalo en el interior de El Arcatraz, en El Castillo de la Tempestad."}},
[10706] = {"Un misterioso portento",nil, {"Regresa junto a Akama en la Jaula de la Guardiana, en el Valle Sombraluna."}},
[10707] = {"El bancal de Ata'mal",nil, {"Sube a lo más alto del Bancal de Ata'mal en el Valle Sombraluna y consigue el corazón de furia. Regresa junto a Akama, a Jaula de la Guardiana en el Valle Sombraluna cuando completes la misión."}},
[10708] = {"La promesa de Akama",nil, {"Llévale el medallón de Karabor a A'dal en la Ciudad de Shattrath."}},
[10709] = {"Reunión",nil, {"Vuelve a ver a Rexxar en el Bastión Señor del Trueno, en las Montañas Filospada."}},
[10710] = {"Vuelo de prueba: La Cresta Canto",nil, {"Firma la renuncia de Tally y, después, habla con Rally Pillazas para usar el Cefirium Capacitorium."}},
[10711] = {"Vuelo de prueba: Zona de Aterrizaje Razaan",nil, {"Habla con Rally Pillazas para usar el Cefirium Capacitorium."}},
[10712] = {"Vuelo de prueba: La Foresta Ruuan",nil, {"Habla con Rally Pillazas para usar el Cefirium Capacitorium. Mientras vuelas a la Foresta Ruuan, gira la veleta abisal. Entrega la veleta giratoria abisal a O'Mally Pillazas en Soto Eterno."}},
[10713] = {"...y tiempos de acción",nil, {"Adéntrate en la Foresta de Ruuan y mata a 10 taladores del culto vermis y luego regresa junto al depositario de árboles Chawn en Soto Eterno en las Montañas Filospada."}},
[10714] = {"Las alas de Espíritu",nil, {"Busca a un capataz Machacasangre y a un veritas Machacasangre que estén hablando. Después utiliza el silbato de Rexxar para invocar a Espíritu para que los espíe. Cuando lo hayas hecho, vuelve a ver a Rexxar en la Aldea Señor del Trueno, en las Montañas Filospada."}},
[10715] = {"En el interior de la Garganta Bulliciosa",nil, {"El Barón Sablecrín te ha dicho que, para hacer el veneno que Rexxar necesita, debes conseguir 7 glándulas de tósigo de revientacortezas. Ve a verle al Anillo de Sangre, en las Montañas Filospada cuando lo hayas hecho."}},
[10716] = {"Vuelo de prueba: El Bosque del Cuervo <needs reward>",nil, {"Habla con Rally Pillazas para usar el Cefirium Capacitorium. Regresa junto a Tally Pillazas en la Estación de Toshley."}},
[10717] = {"Robar a un ladrón",nil, {"Samia Pálpito ha pedido que consigas 5 redes del culto vermis y se las lleves a Soto Eterno en las Montañas Filospada."}},
[10718] = {"Los espíritus tienen voces",nil, {"Garm Hermanolobo te ha dicho donde están los Mok'Nathal, el pueblo de Rexxar. Cuéntaselo a Rexxar en el Bastión Señor del Trueno, en las Montañas Filospada."}},
[10719] = {"¿Has conseguido la nota?",nil, {"Entrega la nota de cita al depositario de árboles Chawn en Soto Eterno en las Montañas Filospada."}},
[10720] = {"Las criaturas más pequeñas",nil, {"Utiliza el tambor de pellejo de kodo en la madriguera de marmotas. Cuando estés controlando a la marmota, envenena los tres barriles del Campamento Machacasangre. Cuando lo hayas hecho, ve a ver a Rexxar en el Bastión Señor del Trueno en las Montañas Filospada."}},
[10721] = {"Un jabalí para Grulloc",nil, {"Consigue el saco de Grulloc y llévaselo al Barón Sablecrín en el Anillo de Sangre, en las Montañas Filospada."}},
[10722] = {"Encuentro en el Aquelarre Alanegra",nil, {"Recolecta suficientes retales de los trajes de varios fieles del culto vermis para crear un disfraz de sobrestante. Usa el disfraz para asistir a la reunión con Kolphis Escamaoscura y vuelve a Soto Eterno en las Montañas Filospada a informar de lo que averigües al depositario de árboles Chawn."}},
[10723] = {"Gorgrom el Comedragones",nil, {"Ve a Peña'mok y coge 3 Tótems repelentes de los ogros Peña'mok. Utiliza la trampa de Sablecrín en el altar de Gorgrom para colocar la trampa, invocar y matar a Gorgrom el Comedragones.nnCuando Gorgrom esté muerto, coloca los 3 Tótems repelentes cerca de su cadáver y después vuelve a ver a Rexxar en el Bastión Señor del Trueno, en las Montañas Filospada."}},
[10724] = {"Prisionero de los Aguja del Filo",nil, {"Mata a ogros Aguja del Filo para conseguir la llave de la jaula del dracoleón. Cuando la tengas, ve a la jaula y utiliza el silbato de Espíritu para invocar a Espíritu, que abrirá la jaula. Cuando Leokk haya sido liberado, vuelve a ver a Rexxar en la Aldea Señor del Trueno, en las Montañas Filospada."}},
[10725] = {"Eminencia ante El Ojo Violeta",nil, {"Entrega tu sello Violeta a la archimaga Leryda en el Paso de la Muerte tras obtener reputación exaltada con El Ojo Violeta."}},
[10726] = {"Eminencia ante El Ojo Violeta",nil, {"Entrega tu sello Violeta a la archimaga Leryda en el Paso de la Muerte tras obtener reputación exaltada con El Ojo Violeta."}},
