[10736] = {"Por el camino Violeta",nil, {"Entrega tu sello Violeta a la archimaga Leryda en el Paso de la Muerte tras obtener reputación honorable con El Ojo Violeta."}},
[10737] = {"El toque del maestro",nil, {"Ve a las Cavernas del Tiempo y convence a Medivh para que active tu llave de aprendiz restaurada."}},
[10738] = {"Servicio distinguido",nil, {"Entrega tu sello Violeta a la archimaga Leryda en el Paso de la Muerte tras obtener reputación venerado con El Ojo Violeta."}},
[10739] = {"Servicio distinguido",nil, {"Entrega tu sello Violeta a la archimaga Leryda en el Paso de la Muerte tras obtener reputación venerado con El Ojo Violeta."}},
[10740] = {"Servicio distinguido",nil, {"Entrega tu sello Violeta a la archimaga Leryda en el Paso de la Muerte tras obtener reputación venerado con El Ojo Violeta."}},
