[10742] = {"Enfrentamiento",nil, {"Utiliza el cuerno de batalla de Rexxar en el altar de Goc y después mata a Goc. Vuelve a ver a Rexxar a la Aldea Señor del Trueno en las Montañas Filospada cuando lo hayas hecho."}},
[10744] = {"Noticias victoriosas",nil, {"Ve al Bastión Martillo Salvaje y habla con Kurdran Martillo Salvaje."}},
[10745] = {"Noticias victoriosas",nil, {"Regresa a la Aldea Sombraluna y habla con el señor supremo Or'barokh."}},
[10747] = {"Crías del culto vermis",nil, {"Samia Pálpito te ha pedido que utilices la red de cría negra para capturar 10 crías negras Culto Vermis. Llévaselas a Soto Eterno en las Montañas Filospada."}},
[10748] = {"¡Maxnar debe morir!",nil, {"En nombre de los druidas de la Expedición Cenarion de Soto Eterno y de la vida indígena de la Foresta de Ruuan, el depositario de árboles Chawn te ha pedido que mates a Maxnar el Fauceniza. Vuelve a verle a Soto Eterno en las Montañas Filospada cuando lo hayas hecho."}},
[10749] = {"El veneno del Barón Sablecrín",nil, {"Lleva el veneno del Barón Sablecrín a Rexxar al Bastión Señor del Trueno en las Montañas Filospada."}},
[10750] = {"El Sendero de la Conquista",nil, {"Ve a El Sendero de la Conquista en el Valle Sombraluna. Cuando hayas hecho una evaluación preliminar de la zona, utiliza la pistola de bengalas Kor'kron para enviar una señal a un jinete del viento Kor'kron.nnHabla con el Señor Supremo Or'barokh si pierdes tu pistola de bengalas Kor'kron para que te dé otra."}},
[10751] = {"Despejar el camino",nil, {"Mata 20 soldados eclipsianos, 10 hidalgos eclipsianos y 10 vinculahechizos eclipsianos. A continuación utiliza la pistola de bengalas Kor'kron para avisar a otro jinete del viento Kor'kron y que pueda informar de que has completado la misión.nnHabla con el Señor Supremo Or'barokh en la Aldea Sombraluna, en el Valle Sombraluna, si pierdes la pistola de bengalas Kor'kron para que te dé otra."}},
[10752] = {"A Vallefresno",nil, {"Habla con Raene Correlobo en Astranaar."}},
[10753] = {"Matanza de las criaturas",nil, {"Mata a 4 jurapenas Alaescama, 4 jurapenas Faucedaga y 2 corruptores viles."}},
[10754] = {"Entrada a la Ciudadela",nil, {"Lleva el molde de llave preparado al Comandante en Jefe Danath al Bastión del Honor en la Península del Fuego Infernal."}},
[10755] = {"Entrada a la Ciudadela",nil, {"Lleva el molde de llave preparado a Nazgrel a Thrallmar en la Península del Fuego Infernal."}},
[10756] = {"Gran maestro Rohok",nil, {"Lleva el molde de llave preparado a Rohok a Thrallmar, en la Península del Fuego Infernal."}},
[10757] = {"La petición de Rohok",nil, {"Lleva 4 barras de hierro vil, 2 unidades de polvo arcano y 4 motas de fuego a Rohok en Thrallmar, en la Península del Fuego Infernal."}},
[10758] = {"Más caliente que el infierno",nil, {"Destruye un atracador vil en la Península del Fuego Infernal e introduce el molde de llave sin templar en sus entrañas. Lleva el molde de llave carbonizado a Rohok en Thrallmar."}},
[10759] = {"Encuentra al desertor",nil, {"Localiza a Parshah en los Campos de Magma y habla con él."}},
[10760] = {"Las Ruinas de Sketh'lon",nil, {"Recupera la página 1 del diario del comandante Sketh'lon, la página 2 del diario del comandante Sketh'lon y la página 3 del diario del comandante Sketh'lon de los arakkoa del Cónclave Oscuro, en las Ruinas de Sketh'lon, para el sargento Kargrul en la Aldea Sombraluna."}},
[10761] = {"Encontrar al desertor",nil, {"Localiza a Parshah en los Campos de Magma y habla con él."}},
[10762] = {"Gran Maestro Dumphry",nil, {"Lleva el molde de llave primordial a Dumphry, al Bastión del Honor."}},
[10763] = {"La petición de Dumphry",nil, {"Lleva cuatro barras de hierro vil, dos polvos Arcanos y cuatro motas de fuego a Dumphry, al Bastión del Honor, de la Península del Fuego Infernal."}},
[10764] = {"Más caliente que el infierno",nil, {"Destruye un atracador vil en la Península del Fuego Infernal y sumerge el molde de llave sin templar en sus restos. Lleva el molde de llave carbonizado a Dumphry, al Bastión del Honor."}},
[10765] = {"Enfrentamiento verbal...",nil, {"Mata a la canciller Hoja Sangrienta y a Corok el Poderoso y recupera la orden de Illidan. Utiliza la pistola de bengalas Kor'kron para avisar a otro jinete del viento Kor'kron cuando hayas cumplido la tarea.nnHabla con el Señor Supremo Or'barokh en la Aldea Sombraluna del Valle Sombraluna si pierdes la pistola de bengalas Kor'kron para que te dé otra."}},
[10766] = {"Punto de Invasión: Cataclismo",nil, {"Preséntate ante Plexi en el Punto de Invasión: Cataclismo."}},
[10767] = {"Punto de Invasión: Cataclismo",nil, {"Informa a Nakansi en el Punto de Invasión: Cataclismo."}},
[10768] = {"Tabardos de los Illidari",nil, {"Consigue 10 tabardos Illidari."}},
[10769] = {"Disensión entre las filas...",nil, {"Mata 5 Colosos enloquecidos cuando tengas puesto el disfraz de elfo de sangre Illidari y ve a ver al Señor Supremo Or'barokh a la Aldea Sombraluna, en el Valle Sombraluna. También tienes que devolverle su pistola de bengalas Kor'kron."}},
[10770] = {"Pequeñas ascuas",nil, {"Maderamusgo el Anciano quiere que mates 8 diablillos agostadores y después vuelvas a verle a la Foresta de Ruuan."}},
[10771] = {"Desde las cenizas",nil, {"Maderamusgo el Anciano quiere que vayas a Skald y plantes semillas de raíz férrea en tres montículos diferentes de tierra volcánica fértil y que después vuelvas a verle a la Foresta de Ruuan."}},
[10772] = {"El Sendero de la Conquista",nil, {"Ve a El Sendero de la Conquista en el Valle Sombraluna. Cuando hayas hecho una evaluación preliminar de la zona, utiliza la pistola de bengalas Martillo Salvaje para enviar una señal a un jinete de grifos Martillo Salvaje.nnHabla con el Señor feudal Yoregar del Bastión Martillo Salvaje del Valle Sombraluna si pierdes tu pistola de bengalas Martillo Salvaje para que te dé otra."}},
[10773] = {"Despejar el camino",nil, {"Mata 20 soldados eclipsianos, 10 hidalgos eclipsianos y 5 Hijos de Corok."}},
[10774] = {"Elfo de sangre + gigante = ???",nil, {"Mata a la canciller Hoja Sangrienta y a Corok el Poderoso y recupera la orden de Illidan."}},
[10775] = {"Tabardos de los Illidari",nil, {"Consigue 10 tabardos Illidari.|n"}},
[10776] = {"Disensión en las filas…",nil, {"Mata 5 Colosos enloquecidos cuando tengas puesto el disfraz de agente de Illidan y ve a ver al Señor feudal Yoregar al Bastión Martillo Salvaje, en el Valle Sombraluna. También tienes que devolverle su pistola de bengalas Martillo Salvaje."}},
[10777] = {"El tótem de Asghar",nil, {"Lleva el tótem de guerra de Sketh'lon a Parshah en los Campos de Magma."}},
[10778] = {"La vara de Lianthe",nil, {"Consigue la llave de Lianthe de cualquiera de los elfos de sangre de Punta Eclipse y utilízala para abrir la caja fuerte de Lianthe y conseguir la vara de Lianthe. Lleva la vara a Parshah a los Campos de Magma."}},
[10779] = {"El camino del cazador",nil, {"Habla con la cazadora Kella Arconocturno en LOCATION."}},
[10780] = {"Plumas de Sketh'lon",nil, {"Consigue 8 plumas de Sketh'lon y llévaselas a Parshah en los Campos de Magma."}},
[10781] = {"La batalla de la Atalaya Carmesí",nil, {"¡Lucha contra las tropas de Illidan en la Atalaya Carmesí y sobrevive!nnVe a ver a A'dal a la Ciudad de Shattrath si consigues tener éxito."}},
[10782] = {"Tratando la celada",nil, {"Equipa la celada inacabada, ve a El Altar de Condenación y utilízala cerca de Gul'dan. Cuando la celada haya recibido la energía, vuelve a ver a Parshah en los Campos de Magma."}},
[10783] = {"Barón Sablecrín",nil, {"Habla con el Barón Sablecrín en el Anillo de Sangre, en las Montañas Filospada."}},
[10784] = {"Destrozar el Campamento Machacasangre",nil, {"Tor'chunk Dosgarras te ha pedido que mates a 10 aplastadores Machacasangre y a 5 brujos Machacasangre. A continuación, vuelve a verle al Bastión Señor del Trueno, en las Montañas Filospada."}},
[10785] = {"¡Es una trampa!",nil, {"Lleva la trampa de fuego de dragón a Rexxar en el Bastión Señor del Trueno en las Montañas Filospada."}},
[10786] = {"Carnicería en Peña'mok",nil, {"Mata 5 toscos Peña'mok y 3 chamanes Peña'mok. A continuación ve a ver a Tor'chunk Dosgarras al Bastión Señor del Trueno, en las Montañas Filospada."}},
[10788] = {"Volver con Talionia",nil, {"Habla con Talionia en la Ciudad de Lunargenta."}},
[10789] = {"Volver con Carendin Halgar",nil, {"Habla con Carendin Halgar en Entrañas."}},
[10790] = {"Volver con Gan'rul Ojosangre",nil, {"Habla con Gan'rul Ojosangre, en Orgrimmar."}},
[10791] = {"Acoger al espíritu lobo",nil, {"Usa el incienso ceremonial cerca de las velas de la cabaña de Malukaz y observa la ceremonia. Cuando la ceremonia haya terminado, vuelve a hablar con Malukaz."}},
[10792] = {"¡Zeth'Gor debe arder!",nil, {"Mata peones Foso Sangrante para conseguir sus antorchas, enciéndelas en la forja de Zeth'Gor y, después, lanza las antorchas ardientes a los edificios de Zeth'Gor."}},
[10793] = {"El diario de Val'zareq: augurios de guerra",nil, {"Localiza a Marcus Auralion, alto general de los Sha'tar en la Atalaya Carmesí.nnBúscale en una prisión cristalina."}},
[10794] = {"Pícaros de la Mano Destrozada",nil, {"Habla con Shenthul en Orgrimmar."}},
[10795] = {"Cita con Dorgok",nil, {"El comandante Haephus Muropiedra te ha pedido que mates a Dorgok en el Campamento Machacasangre. Vuelve a verle a Soto Eterno en las Montañas Filospada cuando hayas completado la tarea."}},
[10796] = {"¡Destruye el Campamento Machacasangre!",nil, {"La teniente Buentiempo te ha pedido que mates 10 aplastadores Machacasangre y 5 brujos Machacasangre. A continuación, ve a Soto Eterno en las Montañas Filospada."}},
[10797] = {"El favor del gronn",nil, {"Lleva el favor de Gorgrom al comandante Haephus Muropiedra en Soto Eterno en las Montañas Filospada."}},
[10798] = {"Una visita al barón",nil, {"Habla con el barón Sablecrín en el Anillo de Sangre, en las Montañas Filospada."}},
[10799] = {"En el interior de la Garganta Bulliciosa",nil, {"Consigue 7 glándulas de tósigo de revientacortezas."}},
[10800] = {"Buenas noches, Gronn",nil, {"Consigue el saco de Grulloc."}},
[10801] = {"¡Es una trampa!",nil, {"Lleva la trampa de fuego de dragón al comandante Haephus Muropiedra en Soto Eterno en las Montañas Filospada."}},
[10802] = {"Gorgrom el Comedragones",nil, {"Ve a Peña'mok y consigue 3 Tótems repelentes de los ogros Peña'mok. Utiliza la trampa de Sablecrín en el altar de Gorgrom para invocar y matar a Gorgrom el Comedragones.nnCuando esté muerto, coloca los 3 Tótems repelentes cerca de su cadáver. Por último, ve a ver al comandante Haephus Muropiedra a Soto Eterno en las Montañas Filospada."}},
[10803] = {"Carnicería en Peña'mok",nil, {"Mata 5 toscos Peña'mok y 3 chamanes Peña'mok."}},
[10804] = {"Bondad",nil, {"Mordenai, de los Campos del Ala Abisal, en el Valle Sombraluna, quiere que alimentes a ocho dracos Ala Abisal adultos."}},
[10805] = {"Masacre en la Guarida de Gruul",nil, {"El Barón Sablecrín quiere que tú y tus amigos matéis 10 gorilas Aguja del Filo, 5 magos de batalla Aguja del Filo, 5 devastadores Aguja del Filo y a su líder, Fingrom. Cuando lo hayas conseguido, vuelve a verle al Anillo de Sangre, en las Montañas Filospada."}},
[10806] = {"Enfrentamiento",nil, {"Utiliza el sello de Sablecrín en el altar de Goc y mata a Goc."}},
[10807] = {"Los Tábidos Lengua de ceniza",nil, {"Varen el Reivindicador quiere que vayas a las Ruinas de Baa'ri y mates a 3 tratantes Lengua de ceniza, 4 guerreros Lengua de ceniza y 6 chamanes Lengua de ceniza.nnCompletar misiones para los Arúspices hará que disminuya tu nivel de reputación con los Aldor."}},
[10808] = {"Frustra los planes del Cónclave",nil, {"Mata a los ritualistas del Cónclave Oscuro y usa el bastón de Parshah para acabar con el ritual de invocación del Cónclave Oscuro. Después, vuelve a Parshah, a los Campos de Magma."}},
[10809] = {"Se busca: maestro de huargos Kruush",nil, {"Mata al maestro de huargos Kruush y entrega su cabeza al capitán Aullaoscuro en Avanzada Rompeloma."}},
[10810] = {"La máscara dañada",nil, {"Entrega la máscara dañada a O'Mally Pillazas en la Foresta de Ruuan."}},
[10811] = {"Buscar a Neltharaku",nil, {"Busca a Nelthakaru, patrón de los dracos Ala Abisal."}},
[10812] = {"La máscara misteriosa",nil, {"Lleva la máscara misteriosa al Señor Salvaje Antelarion en la Foresta de Ruuan."}},
[10813] = {"Los ojos de Grillok",nil, {"Usa el fragmento de Zezzak para capturar un ojo de Grillok y acércate a la caldera de Zezzak para extraerlo. Una vez que lo hayas sacado, devuélvele el fragmento a Zezzak."}},
[10814] = {"La historia de Neltharaku",nil, {"Habla con Neltharaku y escucha su historia."}},
[10815] = {"El diario de Val'zareq: augurios de guerra",nil, {"Localiza a Marcus Auralion, alto general de los Sha'tar en la Atalaya Carmesí."}},
[10816] = {"Reclamar los terrenos sagrados",nil, {"El vindicador Aluumen quiere que mates 8 asesinos Sombraluna, 8 elegidos Sombraluna y 4 tejeoscuros Sombraluna.nnCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
[10817] = {"El gran castigo",nil, {"Varen el Reivindicador quiere que mates 8 asesinos Sombraluna, 8 elegidos Sombraluna y 4 tejeoscuros Sombraluna.nnCompletar misiones para los Arúspices hará que disminuya tu nivel de reputación con los Aldor."}},
[10818] = {"El barón Sablecrín ha solicitado tu presencia",nil, {"Habla con el barón Sablecrín en el Anillo de Sangre, en las Montañas Filospada."}},
[10819] = {"La máscara de gas jurapenas",nil, {"Ponte la máscara de gas Jurapenas y usa el comunicador de la Legión en Campamento Forja: Inquina."}},
[10820] = {"Engañar al enemigo",nil, {"Mata 4 auxiliares de la Forja Maldita y 4 ingenieros de la Forja Maldita."}},
[10821] = {"¡Largo de aquí!",nil, {"Mata a Fatalidad y vuelve para informar al Señor Salvaje Antelarion en Soto Eterno."}},
[10822] = {"Sello Furia del Sol único",nil, {"Even the smallest contribution to our cause is noted, <nombre>. Our enemies are many, but we shall prevail!"}},
[10823] = {"Más sellos Furia del Sol",nil, {"How fares the battle against Kael's forces?"}},
[10824] = {"Sellos Furia del Sol",nil, {"La maga de batalla Vyara en el Sagrario de las Estrellas quiere que le lleves 10 sellos Furia del Sol.nnCompletar misiones para los Arúspices hará que disminuya tu nivel de reputación con los Aldor."}},
[10825] = {"La verdad sin orbes",nil, {"Entrega el Orbe de los Grishna al depositario de árboles Chawn, de la Foresta de Ruuan, en las Montañas Filospada."}},
[10826] = {"Marcas de Sargeras",nil, {"Lleva 10 marcas de Sargeras al presagista Saronen en el Valle Sombraluna.nnCompletar misiones para los Aldor hará que tu reputación Arúspice disminuya."}},
[10827] = {"Más marcas de Sargeras",nil, {"Do you bear more of our enemies' insignias, <class>? The Burning Legion continues to be a threat and we continue to pursue them anywhere that we find them."}},
[10828] = {"Marca de Sargeras única",nil, {"I'll take any leftover marks you might have. Do not worry, your actions will be duly noted."}},
[10829] = {"Troncárbol debe saber",nil, {"El depositario de árboles Chawn te ha pedido que hables con Troncárbol, un anciano del Conocimiento que vive en el Bosque del Cuervo de las Montañas Filospada."}},
[10830] = {"El exorcismo de los árboles",nil, {"Consigue 5 orbes de Grishnath y 5 alfileres con pluma atroz y después combínalos para crear plumas de exorcismo. Utiliza las plumas para realizar el exorcismo y matar a 5 espíritus koi-koi de los hojabarba del Bosque del Cuervo. Cuando lo hayas hecho, vuelve a ver a Troncárbol en el Bosque del Cuervo de las Montañas Filospada."}},
[10831] = {"Convertirse en un sastre de tela lunar",nil, {"Utiliza el retal de tejido abisal imbuido cuando estés en la Poza de la Luna del Refugio Cenarion para crear una muestra de tela lunar primigenia. Lleva la prueba a Nasmara Canción de Luna en el Bajo Arrabal de la Ciudad de Shattrath. nn*¡ATENCIÓN!* Solo puedes seleccionar una especialidad de sastrería."}},
[10832] = {"Convertirse en sastre fuego de hechizo",nil, {"Lleva una muestra de esencia de ánima abisal a Gidge Tejehechizos en el Bajo Arrabal de Shattrath.nn*¡ATENCIÓN!* Solo puedes seleccionar una especialidad de sastrería."}},
[10833] = {"Convertirse en un sastre de tejido de sombra",nil, {"Utiliza el cristal de sombras profundas cerca de El Altar de las Sombras para estar más en sintonía con el altar. A continuación, vuelve a ver a Andrion Giroscuro en el Bajo Arrabal de Shattrath.nn*¡ATENCIÓN!* Solo puedes seleccionar una especialidad de sastrería."}},
[10834] = {"Grillok \"Ojosombra\"",nil, {"Mata a Grillok Ojosombra y lleva el parche del ojo de Grillok a Zezzak en la Avanzada Rompeloma."}},
[10835] = {"El boticario Antonivich",nil, {"Lleva los hallazgos de Albreck al boticario Antonivich de Thrallmar."}},
[10836] = {"Infiltrarse en la Fortaleza Faucedraco",nil, {"Neltharaku, que sobrevuela los Campos del Ala Abisal en el Valle Sombraluna, quiere que mates a quince orcos Faucedraco.nnBastará con cualquier orco Faucedraco de la Fortaleza Faucedraco."}},
[10837] = {"¡Al Arrecife del Ala Abisal!",nil, {"Neltharaku, que sobrevuela los Campos del Ala Abisal en el Valle Sombraluna, quiere que recojas doce cristales de vid abisal del Arrecife del Ala Abisal.|n"}},
[10838] = {"El Arúspice Demoníaco",nil, {"Usa el Arúspice Demoníaco en la Cuenca del Fuego Infernal, espera a que se sintonice, pide una trascripción.nnDevuelve el Arúspice Demoníaco y la trascripción del Arúspice Demoníaco al boticario Antonivich de Thrallmar."}},
[10839] = {"Velo Skith: Rocanegra de Terokk",nil, {"Usa la vara de purificación sobre la Rocanegra de Terokk, en Velo Skith, y vuelve a Kirrik a la Caravana de refugiados de El Vertedero de Huesos."}},
[10840] = {"La Tumba de las Luces",nil, {"El sumo sacerdote Orglum quiere que vayas a la Tumba de las Luces y mates a 10 abisálicos etéreos y 10 desvalijadores etéreos. A continuación vuelve a verle a la Caravana de Refugiados."}},
[10841] = {"El heraldo vengativo",nil, {"[PH] Activa el objeto."}},
[10842] = {"El heraldo vengativo",nil, {"El Sumo Sacerdote Orglum quiere que viajes a la Tumba de las Luces y uses la reliquia de tumba draenei cerca del antiguo altar draenei.nnDefiende al guardián de tumbas y vence al presagista vengativo. Cuando el ritual termine, habla con el ascendente draenei en el antiguo altar draenei."}},
[10843] = {"Desde tiempos olvidados...",nil, {"Para demostrar tu valía ante los Mok'Nathal, Leoroxx te ha ordenador que mates a Gnosh Brognat, líder de los ogros en la Avanzada Aguja del Filo. Vuelve con Leoroxx a la Aldea Mok'Nathal de las Montañas Filospada después de haberlo hecho, o no vuelvas."}},
[10844] = {"Campamento Forja: Inquina",nil, nil},
[10845] = {"Matar a la madre del linaje",nil, {"Leoroxx te ha retado a que mates a Alatemible. Vuelve con él a la Aldea Mok'Nathal cuando hayas conseguido matarla."}},
[10846] = {"Comprender a los Mok'Nathal",nil, {"Para empezar tu comprensión de los Mok'Nathal, mata a Vekh, líder de los arakkoa Vekh'nir. Después, vuelve con Leoroxx a la Aldea Mok'Nathal, en las Montañas Filospada."}},
[10847] = {"Los ojos de Skettis",nil, {"Lleva el ojo de Reskk y el ojo de Shienor a Rilak el Redimido en el Bajo Arrabal, en Shattrath."}},
[10848] = {"Velo Rhaze: el mal no viviente",nil, {"Mata 4 espíritus garramuerte, 4 espíritus estridentes, 2 espíritus de cuervo y después ve a ver a Kirrik a la Caravana de Refugiados."}},
[10849] = {"Busca a Kirrik",nil, {"Lleva la misiva de Rilak a Kirrik en la Caravana de Refugiados, en El Vertedero de Huesos."}},
[10850] = {"Gas abisal en una máquina de fuego vil",nil, nil},
[10851] = {"Los tótems de mi enemigo",nil, {"Recoge cinco tótems Aguja del Filo para el clamaespíritus Dohgar de la Aldea Mok'Nathal en las Montañas Filospada."}},
[10852] = {"Amigos desaparecidos",nil, {"Ethan quiere que encuentres a doce de sus amigos desaparecidos en Velo Skith y que luego vuelvas con él a la Caravana de Refugiados."}},
[10853] = {"Llamamiento de espíritus",nil, {"El clamaespíritus Dohgar te ha pedido que uses los Tótems Llamada de espíritu para recoger ocho espíritus de draco abisal inferiores y para que se los devuelvas a la Aldea Mok'Nathal, en las Montañas Filospada."}},
[10854] = {"La fuerza de Neltharaku",nil, {"Neltharaku, que sobrevuela los Campos del Ala Abisal, en el Valle Sombraluna, quiere que liberes a 5 dracos Ala Abisal esclavizados."}},
[10855] = {"¡Atracadores viles, no gracias!",nil, {"Consigue 5 unidades de gas abisal condensado de los mekingenieros Gan'arg de la Base Forja: Olvido, al noroeste de La Flecha de la Tormenta y mételas en un atracador vil cercano inactivo. Vuelve a ver al acechador abisal Nauthis cuando hayas completado la tarea."}},
[10856] = {"La mejor defensa",nil, {"El acechador abisal Nauthis quiere que mates 12 encolerizadores en la Base Forja: Gehenna, al norte de La Flecha de la Tormenta."}},
[10857] = {"¡Teletransporta esto!",nil, {"El acechador abisal Nauthis quiere que utilices la Vara de interferencia mental con los Mo'arg en la Base Forja: Gehenna y uses su habilidad de detonar teletransportadores en los tres teletransportadores del campamento Forja."}},
[10858] = {"Karynaku",nil, {"Busca a Karynaku en la Fortaleza Faucedraco."}},
[10859] = {"Reunir los orbes",nil, {"El clamaespíritus Dohgar te ha pedido que uses el tótem colector de orbes para recoger 15 orbes de Luz razaani. Vuelve con él a la Aldea Mok'Nathal, en las Montañas Filospada, cuando hayas acabado."}},
[10860] = {"Regalos de Mok'Nathal",nil, {"La matrona Varah te ha dado una lista de ingredientes para guisar sus dos platos favoritos, entre los que se encuentran 3 costillas de raptor y 3 piezas de carne de serpiente. Llévaselos a la Aldea Mok'Nathal, en las Montañas Filospada."}},
[10861] = {"Velo Lítico: ataque preventivo",nil, {"Rompe los huevos malditos de Velo Lítico. Redime a tres proles y mata a tres proles malévolos antes de volver con Kirrik a la Caravana de Refugiados."}},
[10862] = {"Ríndete ante la Horda",nil, {"Rokag quiere que busques a Rilak el Redimido en Shattrath y exijas que él y los suyos se rindan ante la Horda."}},
[10863] = {"Secretos de los arakkoas",nil, {"Lady Dena Kennedy quiere que busques a Rilak el Redimido a Shattrath y descubras los secretos que sabe sobre los arakkoas."}},
[10864] = {"Una carga de almas",nil, {"Mata a 20 orcos Mano Destrozada y vuelve con el boticario Antonivich, a Thrallmar."}},
[10865] = {"¡Informa a Leoroxx!",nil, {"El clamaespíritus Dohgar te ha implorado que informes a Leoroxx sobre lo que los etéreos de Razaani ethereal están tramando. Se le puede encontrar en la Aldea Mok'Nathal, en las Montañas Filospada."}},
[10866] = {"Zuluhed el Demente",nil, {"Mata a Zuluhed el Demente y consigue la llave de Zuluhed. Utiliza la llave de Zuluhed en las cadenas de Zuluhed para liberar a Karynaku."}},
[10867] = {"Solo puede haber una respuesta",nil, {"Leoroxx te ha suplicado que recuperes la colección de almas y que la devuelvas al clamaespíritus Dohgar de la Aldea Mok'Nathal, en las Montañas Filospada."}},
[10868] = {"La ruta de guerra de los arakkoas",nil, {"La consejera Faila te ha pedido que mates a 14 garrosos Shienor, 6 hechiceros Shienor y que, después, vuelvas a verla en el Bastión Rompepedras."}},
[10869] = {"Disminuir la manada",nil, {"El teniente Gravamartillo te ha pedido que mates 14 garrosos Shienor, 6 hechiceros Shienor y que, después, vuelvas a verle en el Bastión Allerian."}},
[10870] = {"Aliado del Ala Abisal",nil, {"Deja que Karynaku te envíe de vuelta a Mordenai en los Campos del Ala Abisal."}},
[10871] = {"Aliado del Ala Abisal",nil, {"Deja que Karynaku te envíe de vuelta a Mordenai en los Campos del Ala Abisal."}},
[10872] = {"Zuluhed el Demente",nil, {"Mata a Zuluhed el Demente y consigue la llave de Zuluhed. Utiliza la llave de Zuluhed en las cadenas de Zuluhed para liberar a Karynaku."}},
[10873] = {"Tomados en la noche",nil, {"El explorador Navrin quiere que vayas a la Cresta Red Abisal, liberes a 6 guerreros atrapados con telaraña de Sha'tar y vuelvas con él a la Avanzada de Sha'tar."}},
[10874] = {"Velo Shalas: señales de fuego",nil, {"Kirrik de la Caravana de Refugiados quiere que extingas las cuatro señales de fuego de Velo Shalas."}},
[10875] = {"Informar a Nazgrel",nil, {"Habla con Nazgrel en Thrallmar."}},
[10876] = {"El pie de la ciudadela",nil, {"Mata al comandante en jefe Gorax, coloca el Desafío de la Horda sobre su cadáver, mata a la Mano de Kargath, y lleva la cabeza de la Mano de Kargath a Nazgrel, en Thrallmar."}},
[10877] = {"La reliquia de pavor",nil, {"Roblun quiere que viajes al este, hasta la Caravana Derelicta para recuperar la reliquia de pavor. Vuelve con Roblun cuando hayas completado la tarea."}},
[10878] = {"Antes de que caiga la oscuridad",nil, {"Mekeda, de la Caravana de Refugiados del Bosque de Terokkar, quiere que mates a 8 pendencieros de la Cábala, 4 tejehechizos de la Cábala y 2 iniciados a la Cábala."}},
[10879] = {"La ofensiva de Skettis",nil, {"Resiste a los atacantes y habla con Rilak."}},
[10880] = {"Órdenes de la Cábala",nil, {"Lleva las órdenes de la Cábala a Mekeda, en la Caravana de Refugiados del Bosque de Terokkar."}},
[10881] = {"La Tumba Umbría",nil, {"Ve a la Tumba Umbría, al oeste de la Caravana de Refugiados, y recupera el mantón de Arunen, el mazo de K'alen y el pergamino de Atalor. Vuelve con Mekeda a la Caravana de Refugiados después de que hayas completado esta tarea."}},
[10882] = {"Presagista de la Condena",nil, {"Se te ha encomendado que vayas al satélite Arcatraz de El Castillo de la Tempestad y mates al Presagista Cieloriss. Vuelve con A'dal al Bancal de la Luz, en la Ciudad de Shattrath, cuando lo hayas hecho."}},
[10883] = {"La llave de la tempestad",nil, {"Habla con A'dal en la Ciudad de Shattrath.|n"}},
[10884] = {"Prueba de los naaru: piedad",nil, {"A'dal de la Ciudad de Shattrath quiere que recuperes el hacha del Verdugo sin usar en Las Salas Arrasadas de la Ciudadela del Fuego Infernal.nnEsta misión debe completarse en dificultad de mazmorra heroica."}},
[10885] = {"Prueba de los naaru: fuerza",nil, {"A'dal de la Ciudad de Shattrath quiere que recuperes el tridente de Kalithresh y la esencia de Murmullo.nnEsta misión debe completarse en dificultad de mazmorra heroica."}},
[10886] = {"Prueba de los naaru: tenacidad",nil, {"A'dal, de la Ciudad de Shattrath, quiere que rescates a Molino Tormenta de Maná del Arcatraz de El Castillo de la Tempestad.nnEsta misión debe completarse en dificultad de mazmorra heroica."}},
[10887] = {"Escapar de la tumba",nil, {"Ayuda a Akuno a encontrar su camino hasta la Caravana de Refugiados en el Bosque de Terokkar. Habla con Mekeda cuando hayas completado esta misión."}},
[10888] = {"Prueba de los naaru: Magtheridon",nil, {"A'dal en la Ciudad de Shattrath quiere que mates a Magtheridon."}},
[10889] = {"Volver a Shattrath",nil, {"Viaja a la Ciudad de Shattrath y habla con Rilak."}},
[10891] = {"Armadura de placas imperiales",nil, {"Habla con Derotain Sorbelodo en Gadgetzan.|n"}},
[10892] = {"Armadura de placas imperiales",nil, {"Habla con Derotain Sorbelodo en Gadgetzan.|n"}},
[10893] = {"Largacola es el eje",nil, {"Elimina a Draaca Largacola, la líder de los fieles del culto vermis, en el Túnel Calavermis. Vuelve para informar al vigía Sombraluna, en el Túnel Calavermis de las Montañas Filospada, cuando lo hayas hecho."}},
[10894] = {"Vigía Calavermis",nil, {"Entrega la nota de reunión al vigía Sombraluna en la entrada al Túnel Calavermis, en las Montañas Filospada."}},
[10895] = {"¡Zeth'Gor debe arder!",nil, {"Utiliza la baliza de humo en cada una de las torres de Zeth'Gor y, después, vuelve con el comandante del aire Dabir'ee a Punta de Expedición."}},
[10896] = {"Los protectores infestados",nil, {"Lakotae quiere que mates 25 cucarachas de bosque y después vuelvas a verle a la Caravana de Refugiados. Puedes encontrar a las cucarachas viviendo en los cuerpos de furibundos de bosque putrefactos y caminantes de las raíces infectados."}},
[10897] = {"Maestro en pociones",nil, {"Lauranna Thar'well quiere que vayas a El Invernáculo de El Castillo de la Tempestad y consigas la Guía de Campo para Botánicos del gran botánico Freywenn. Además, quiere que le lleves 5 superpociones de sanación, 5 superpociones de maná y 5 pociones de letargo sin sueño sublimes.nn*¡ADVERTENCIA!* Solo podrás elegir una especialización de alquimia."}},
[10898] = {"Alacielo",nil, {"Escolta a Alacielo y preséntate ante Rilak el Redimido en la Ciudad de Shattrath."}},
[10899] = {"Maestro de transmutación",nil, {"Zarevhi, de la Flecha de la Tormenta, en Tormenta Abisal, quiere que le lleves 4 muestras de poderío primigenio.nn*¡ATENCIÓN!* Solo puedes seleccionar una especialidad de alquimia."}},
[10900] = {"La marca de Vashj",nil, nil},
[10901] = {"La cayada de Kar'desh",nil, {"Skar'this el Herético, en El Reciento de los Esclavos heroico, en la Reserva Colmillo Torcido, quiere que le lleves el sello terráneo y el sello llameante."}},
[10902] = {"Maestro en elixires",nil, {"Ve a La Ciénaga Negra, en las Cavernas del Tiempo, y consigue 10 esencias de infinidad de los señores de la falla y los vigilantes de la falla. Llévalas, junto con 5 elixires de defensa sublime, 5 elixires de maestría y 5 elixires de agilidad sublime a Lorokeem, en el Bajo Arrabal de Shattrath.nn*¡ATENCIÓN!* Solo puedes seleccionar una especialidad de alquimia."}},
[10903] = {"Volver al Bastión del Honor",nil, {"Habla con el ayudante Klatu, en la posada del Bastión del Honor."}},
[10904] = {"Recoger la munición vil",nil, {"Recoge cinco balas de cañón vil."}},
[10905] = {"Maestro en pociones",nil, {"Habla con Lauranna Thar'well en el Refugio Cenarion en la Marisma de Zangar.nn*¡ADVERTENCIA!* Solo podrás elegir una especialización de alquimia."}},
[10906] = {"Maestro en elixires",nil, {"Habla con Lorokeem en el Bajo Arrabal de Shattrath.nn*¡ADVERTENCIA!* Solo podrás elegir una especialización de alquimia."}},
[10907] = {"Maestro de transmutación",nil, {"Habla con Zarevhi en La Flecha de la Tormenta, en la Tormenta Abisal.nn*¡ADVERTENCIA!* Solo podrás elegir una especialización de alquimia."}},
[10908] = {"Hablar con Rilak el Redimido",nil, {"Kirrik el Despierto quiere que hables con Rilak el Redimido en el distrito del Bajo Arrabal de Shattrath."}},
[10909] = {"Espíritus viles",nil, {"Klatu, del Bastión del Honor quiere que uses la reliquia anacoreta en la Avanzada Gor'gaz, al suroeste del Bastión. Mata a todos los rabiosos Mano Destrozada que encuentres cerca de la reliquia y luego mata a 10 espíritus viles liberados para vengar al coronel Jules."}},
[10910] = {"La Puerta de la Muerte",nil, {"El Señor Salvaje Antelarion te ha suplicado que viajes a la Puerta de la Muerte. Cuando llegues, usa la señal para druida para llamar a un druida Soto Eterno para poder hablar con él."}},
[10911] = {"¡Fuego a discreción!",nil, {"Usa la munición naturalizada para controlar los cañones viles de la Puerta de la Muerte. Úsalos para destruir el portal de distorsión del sur y el portal de distorsión del norte."}},
[10912] = {"El Maestro de canes",nil, {"Mata a Baelmon, el Maestro de canes."}},
[10913] = {"Un entierro indecoroso",nil, {"El comandante Ra'vaj, de la Avanzada Sha'tari, en El Vertedero de Huesos del Bosque de Terokkar, quiere que quemes a 8 guerreros Auchenai asesinados y a 8 vindicadores Sha'tar asesinados."}},
[10914] = {"Se necesita un héroe",nil, {"El comandante Ra'vaj de la Avanzada Sha'tari, en El Vertedero de Huesos, del Bosque de Terokkar, quiere que mates a 12 iniciados Auchenai y a 5 decidores del destino Auchenai."}},
[10915] = {"El exarca caído",nil, {"El comandante Ra'vaj de la Avanzada Sha'tari, en El Vertedero de Huesos del Bosque de Terokkar, quiere que destruyas el contenido del ataúd de Auchenai."}},
[10916] = {"Desenterrando abalorios de oración",nil, {"Recupera los abalorios de oración Draenei y llévaselas al ayudante Klatu, al Bastión del Honor."}},
[10917] = {"La situación apremiante del marginado",nil, {"Lleva 30 plumas de arakkoa a Vekax en el Bajo Arrabal de Shattrath."}},
[10918] = {"Más plumas",nil, nil},
[10919] = {"El tesoro de Fei Fei",nil, nil},
[10920] = {"Por los caídos",nil, {"El vindicador Haylen quiere que mates a 20 viudas Colminferno y, después, vuelvas con él, justo al oeste de Cresta Red Abisal."}},
[10921] = {"Terokkarántula",nil, {"El vindicador Haylen quiere que mates a la Terokkarántula y, después, vuelvas con él, al oeste de Cresta Red Abisal."}},
[10922] = {"Excavando huesos",nil, {"Escolta a Letoll y a sus hombres y protégelos mientras excavan. Vuelve a ver a Sesoenanof a la Avanzada Sha'tari si tienes éxito.|n"}},
[10923] = {"El mal se acerca",nil, {"Roblun quiere que lleves la reliquia de pavor al Alcor Tortuoso. Cuando estés allí tienes que matar médiums y decidores del destino Auchenai para conseguir 20 calaveras malditas. Después tendrás que encontrar el círculo de invocación del Alcor Tortuoso y usar la reliquia de pavor para invocar y destruir a Teribus el Maldito. Vuelve a ver a Roblun cuando lo hayas hecho."}},
[10924] = {"¡Totalmente im-posible!",nil, {"Lleva 8 viales de sangre de persecutor de distorsión a Sab'aoth, en Cosmotirón."}},
[10925] = {"El mal se acerca",nil, nil},
[10926] = {"Volver al Campamento Sha'tari",nil, {"El vindicador Haylen quiere que vuelvas al Campamento Sha'tari y hables con el explorador Navrin."}},
[10927] = {"Matando a los reptadores",nil, {"El centinela Susurrolunar en el túnel entre la Marisma de Zangar y las Montañas Filospada quiere que mates 6 reptadores de cavernas y te presentes ante el comandante Cielosombra en Sylvanaar."}},
[10928] = {"Matando a los reptadores",nil, {"Bruto Grahk en el túnel entre la Marisma de Zangar y las Montañas Filospada quiere que mates 6 reptadores de cavernas y te presentes ante Tor'chunk Dosgarras en el Bastión Señor del Trueno."}},
[10929] = {"Gusanos",nil, {"Sesoenanof en Campamento Sha'tari, en El Vertedero de Huesos, quiere que le lleves 8 cadáveres de matizahuesos adultos."}},
[10930] = {"El gran gusano de hueso",nil, {"Sesoenanof de Campameto Sha'tari, en El Vertedero de Huesos, quiere que consigas los órganos del gran gusano de hueso.nnTambién quiere su tambor.|n"}},
[10931] = {"Regreso a El Exodar",nil, {"Habla con Caedmos en El Exodar."}},
[10932] = {"Regreso a El Exodar",nil, {"Habla con Caedmos en El Exodar."}},
[10933] = {"Regreso a Lunargenta",nil, {"Habla con Aldrae en La Ciudad de Lunargenta."}},
[10934] = {"Regreso a Lunargenta",nil, {"Habla con Aldrae en La Ciudad de Lunargenta."}},
[10935] = {"El exorcismo del coronel Jules",nil, {"Habla con el anacoreta Barada. Utiliza los abalorios de oración para ayudar con el ritual y después habla con el coronel Jules cuando esté bien. Por último, vuelve a ver al ayudante Klatu."}},
[10936] = {"Aterratrols te está buscando",nil, {"El asistente Klatu te ha dicho que el Comandante en Jefe Danath Aterratrols te está buscando. Ve a verle al cuartel del Bastión del Honor, en la Península del Fuego Infernal."}},
[10937] = {"Acaba con el maestro de maniobras",nil, {"El Comandante en Jefe Danath Aterratrols te ha pedido que mates al maestro de maniobras Zurok inmediatamente. Vuelve a ver al Comandante en Jefe del Bastión del Honor en la Península del Fuego Infernal cuando hayas matado al maestro de maniobras."}},
[10938] = {"La colección de Bendiciones de la Luna Negra",nil, {"Lleva la colección de Bendiciones a la Feria de la Luna Negra cuando pase por la ciudad."}},
[10939] = {"La colección de Tormentas de la Luna Negra",nil, {"Lleva la colección de Tormentas a la Feria de la Luna Negra cuando pase por la ciudad."}},
[10940] = {"La colección de Furias de la Luna Negra",nil, {"Lleva la colección de Furias a la Feria de la Luna Negra cuando pase por la ciudad."}},
[10941] = {"La colección de Locuras de la Luna Negra",nil, {"Lleva la colección de Locuras a la Feria de la Luna Negra cuando pase por la ciudad."}},
[10942] = {"Semana de los Niños",nil, {"Usa el silbato de huérfano elfo de sangre para llamar a la niña a la que cuidarás durante la Semana de los Niños."}},
[10943] = {"Semana de los Niños",nil, {"Usa el silbato de huérfano draenei para llamar a la niña a la que cuidarás durante la Semana de los Niños."}},
[10944] = {"El secreto en peligro",nil, {"Viaja hasta la Jaula de la Guardiana del Valle Sombraluna y habla con Akama."}},
[10945] = {"Hch'uu y el pueblo champiñón",nil, {"Lleva a tu huérfana, Salandria a Esporaggar en la Marisma de Zangar. Asegúrate de llamarla si no está presente cuando llegues. Luego habla con Hch'uu."}},
[10946] = {"Artimaña de los Lengua de Ceniza",nil, {"Viaja hasta El Castillo de la Tempestad y mata a Al'ar con la capucha de Lengua de Ceniza puesta. Vuelve junto a Akama en el Valle Sombraluna cuando hayas terminado."}},
[10947] = {"Un artefacto del pasado",nil, {"Ve a las Cavernas del Tiempo en Tanaris y consigue entrar en la Batalla del Monte Hyjal. Una vez dentro, vence a Ira Fríoinvierno y llévale la filacteria de fase temporal a Akama en el Valle Sombraluna."}},
[10948] = {"El alma rehén",nil, {"Viaja hasta la Ciudad de Shattrath y háblale a A'dal de lo que pide Akama."}},
[10949] = {"La entrada a El Templo Oscuro",nil, {"Viaja hasta la entrada del Templo Oscuro en el Valle Sombraluna y habla con Xi'ri."}},
[10950] = {"Auchindoun y El Círculo de la Observancia",nil, {"Lleva a tu huérfana, Dornaa, a la roca de encuentro que hay en el Círculo de la Observancia, en medio de Auchindoun. Auchindoun, por su parte, está en medio de El Vertedero de Huesos del Bosque de Terokkar. Asegúrate de que la llamas si no está presente cuando llegues."}},
[10951] = {"Un viaje al Portal Oscuro",nil, {"Lleva a tu huérfana, Salandria, a ascender por Los Peldaños del Destino para plantarse delante del Portal Oscuro en la Península del Fuego Infernal. Asegúrate de llamarla si no está ahí cuando llegues."}},
[10952] = {"Un viaje al Portal Oscuro",nil, {"Lleva a tu huérfana, Dornaa, a ascender por Los Peldaños del Destino para plantarse delante del Portal Oscuro en la Península del Fuego Infernal. Asegúrate de llamarla si no está ahí cuando llegues."}},
[10953] = {"Visita al Trono de los Elementos",nil, {"Lleva a tu huérfana, Salandria, hasta El Trono de los Elementos en Nagrand. Asegúrate de llamarla si no está ya allí cuando llegues. Luego, habla con el elementalista Sharvak."}},
[10954] = {"¡Jheel está en el Desembarco Aeris!",nil, {"Lleva a tu huérfana, Dornaa, al Desembarco Aeris en Nagrand. Asegúrate de llamarla si no está allí cuando llegues. Luego habla con Jheel."}},
[10955] = {"Morthis Alasusurro",nil, {"Busca a Morthis Alasusurro en el Refugio Cenarion de la Marisma de Zangar."}},
[10956] = {"El Trono de los Naaru",nil, {"Lleva a tu huérfana, Dornaa, hasta O'ros a los pies del Trono de los Naaru, dentro de El Exodar, que está en la Isla Bruma Azur. Asegúrate de que la llamas si no está ya allí cuando llegues.nnRecuerda que puedes usar el mapa dentro de la ciudad."}},
[10957] = {"Redención de los Lengua de Ceniza",nil, {"Ayuda a Akama a recuperar el control de su alma derrotando a la Sombra de Akama dentro del Templo Oscuro. Vuelve junto al vidente Kanai cuando hayas completado esta misión."}},
[10958] = {"Búsqueda de los Lengua de Ceniza",nil, {"Encuentra a los Juramorte de Akama dentro del Templo Oscuro."}},
[10959] = {"La caída de El Traidor",nil, {"El vidente Kanai quiere que derrotes a Illidan dentro del Templo Oscuro."}},
[10960] = {"Cuando sea mayor...",nil, {"Lleva a tu huérfana, Salandria, a los instructores de paladines de la Plaza del Errante de la Ciudad de Lunargenta. Asegúrate de llamarla si no está presente cuando llegues. Luego, habla con Lady Liadrin, la matriarca elfa de sangre.nnRecuerda que puedes usar tu mapa dentro de la ciudad y puedes hablar con los guardias para encontrar a los instructores de paladines."}},
[10961] = {"El resguardo de despertar",nil, {"Lleva 10 cardos de Mantar, 10 líquenes antiguos y 10 glorias de ensueño a Morthis Alasusurro al Refugio Cenarion."}},
[10962] = {"Hora de visitar las Cavernas",nil, {"Lleva a tu huérfana, Dornaa, ante Zaladormu, el dragón del estrado que se encuentra en el centro de las Cavernas del Tiempo. Asegúrate que la llamas si no está presente cuando llegas.nnLuego, cómprale un dragón de juguete al intendente de los Vigilantes del Tiempo, Alurmi, cerca del fin del túnel de la entrada."}},
[10963] = {"Hora de visitar las Cavernas",nil, {"Lleva a tu huérfana, Salandria, ante Zaladormu, el dragón del estrado que se encuentra en el centro de las Cavernas del Tiempo. Asegúrate que la llamas si no está presente cuando llegas.nnLuego, cómprale un dragón de juguete al intendente de los Vigilantes del Tiempo, Alurmi, cerca del fin del túnel de la entrada."}},
[10964] = {"Despertar al durmiente",nil, {"Usa el resguardo de despertar para ayudar a Clintar Caminasueños a salir del Sueño Esmeralda. Cuando recobre el conocimiento, habla con él."}},
[10965] = {"Se acabó el sueño",nil, {"Protege a Clintar Caminasueños mientras reúne las reliquias de Aviana, luego habla con el celador de sueños Lurosa."}},
[10966] = {"De vuelta al orfanato",nil, {"Devuelve a tu pupila al orfanato de la Ciudad de Shattrath. Entrégale el silbato de draenei huérfano a la supervisora de huérfanos Piedad en el Bajo Arrabal."}},
[10967] = {"De vuelta al orfanato",nil, {"Devuelve a tu pupila al orfanato de la Ciudad de Shattrath. Entrégale el silbato de huérfano elfo de sangre a la supervisora de huérfanos Piedad en el Bajo Arrabal."}},
[10968] = {"Llamada al clarividente",nil, {"Lleva a tu huérfana, Dornaa, a visitar al Clarividente Nobundo en la Sala de Cristal, dentro de El Exodar, que está en la Isla Bruma Azur. Asegúrate de que la llamas si no está ya allí cuando llegues.nnRecuerda que puedes usar el mapa dentro de la ciudad."}},
[10969] = {"Busca a Ameer",nil, {"Busca al comandante Ameer a la Avanzada del Protectorado."}},
[10970] = {"Una misión de piedad",nil, {"Consigue la Llave de la prisión de El Etereum rescatada para el comandante Ameer en la Avanzada del Protectorado de Tormenta Abisal."}},
[10971] = {"Secretos de El Etereum",nil, {"El comandante Ameer de la Avanzada del Protectorado de Tormenta Abisal quiere que le lleves una etiqueta de identificación de prisionero de El Etereum."}},
[10972] = {"Catálogo de identificación de prisioneros de El Etereum",nil, {"Necesito más etiquetas de identificación, <nombre>. ¡Lo que he descubierto hasta ahora es que no solo sirven para catalogar a los prisioneros sino que también sirven para documentar lugares!nnCuando descifre este código, podremos localizar dónde están retenidos exactamente todos nuestros aliados y liberarlos con la mínima actividad hostil."}},
[10973] = {"Mil mundos",nil, {"El comandante Ameer de la Avanzada del Protectorado de Tormenta Abisal quiere que le lleves 5 llaves de prisión de El Etereum."}},
[10974] = {"Cámaras de Estasis de Bash'ir",nil, {"Usa la tablilla de llave de El Etereum - Alfa para abrir la Cámara de Estasis Alfa en el Alto Bash'ir de las Montañas Filospada.nnVuelve con evidencias de Alfa a la imagen del comandante Ameer, que está en el Alto Bash'ir."}},
[10975] = {"Purgar las cámaras de Bash'ir",nil, {"Hemos llegado a punto muerto, <nombre>. Los datos que has recopilado nos han proporcionado suficiente información para salvar a nuestros aliados de las prisiones pequeñas de Tormenta Abisal, pero debemos continuar y erradicar la gran amenaza de El Etereum del Alto Bash'ir.nn¡Únete a la batalla contra El Etereum! Tráeme llaves de prisión de El Etereum y forjaré llaves de Cámaras de Estasis de El Etereum para que las uses en las Cámaras de Bash'ir."}},
[10976] = {"La marca del rey-nexo",nil, {"La imagen del comandante Ameer en el Alto Bash'ir de las Montañas Filospada quiere que le traigas una marca del rey-nexo."}},
[10977] = {"Cámaras de Estasis de las Tumbas de Maná",nil, {"La imagen del comandante Ameer del Alto Bash'ir en las Montañas Filospada quiere que uses la llave de la Cámara de Estasis de las Tumbas de Maná en las Tumbas de Maná de Auchindoun.nnEsta misión solo se puede completar en modo heroico."}},
[10978] = {"Regresa junto a Morthis Alasusurro",nil, {"Llévale las reliquias de Aviana a Morthis Alasusurro al Refugio Cenarion de la Marisma de Zangar."}},
[10979] = {"A Soto Eterno",nil, {"Habla con Arthorn Son del Viento de Soto Eterno en las Montañas Filospada."}},
[10980] = {"El Libro del Cuervo",nil, {"Usa el ojo de raya de éter con tu piedra de vidente para que se muestren los fantasmas del culto del cuervo. Busca La Cumbre del Vórtice para el espíritu de Sai'kkal el Anciano e interrógalo sobre el Libro del Cuervo. Después vuelve a Arthorn Son del Viento."}},
[10981] = {"Cámara personal del príncipe-nexo Shaffar",nil, nil},
[10982] = {"El ojo de Haramad",nil, nil},
[10983] = {"Mog'dorg el Marchito",nil, {"Visita a Mog'dorg el Marchito en lo alto de una de las torres que hay justo fuera del Anillo de Sangre en las Montañas Filospada."}},
[10984] = {"Habla con el ogro",nil, {"Habla con Grok, el ogro, en el Bajo Arrabal de la Ciudad de Shattrath."}},
[10985] = {"Una distracción para Akama",nil, {"Asegúrate de que Akama y Maiev entran en El Templo Oscuro, en el Valle Sombraluna, cuando las fuerzas de Xi'ri creen una distracción."}},
[10986] = {"Ojos en el cielo",nil, {"Habla con la vigía Elaira en La Cresta del Crepúsculo en Nagrand."}},
[10987] = {"Atrapar un gavilán",nil, {"Usa la red para gavilanes para capturar un gavilán salvaje y llévaselo a la vigía Elaira en la Cresta del Crepúsculo."}},
[10988] = {"Las piedras del Cuervo",nil, {"Reúne 8 Piedras del Cuervo de Skettis y llévaselas a Arthorn Son del Viento a Soto Eterno, en las Montañas Filospada."}},
[10989] = {"Mog'dorg el Marchito",nil, {"Visita a Mog'dorg el Marchito, que se encuentra en lo alto de la torre donde está Chort, justo fuera del Anillo de Sangre en las Montañas Filospada."}},
[10990] = {"La esencia del Águila",nil, {"Derrota al guardián del Águila y obtén la esencia del Águila. Usa el silbato para gavilanes de Arthorn para llamar al gavilán de Arthorn para que lleve las noticias de tu éxito.nnSi pierdes el silbato de gavilanes de Arthorn, vuelve a ver a Arthorn Son del Viento en Soto Eterno para que te dé otro."}},
[10991] = {"La esencia del Falcón",nil, {"Derrota al guardián del Falcón y obtén la esencia del Falcón. Usa el silbato para gavilanes de Arthorn para llamar al gavilán de Arthorn para que lleve las noticias de tu éxito.nnSi pierdes el silbato de gavilanes de Arthorn, vuelve a ver a Arthorn Son del Viento en Soto Eterno para que te dé otro."}},
[10992] = {"La esencia del Halcón",nil, {"Derrota al guardián del Halcón y lleva la esencia del Halcón a Arthorn Son del Viento en Soto Eterno."}},
[10993] = {"Regresa al Refugio Cenarion",nil, {"Lleva el paquete de Arthorn a Morthis Alasusurro al Refugio Cenarion."}},
[10994] = {"Tras la piedra lunar",nil, {"Lleva una piedra lunar de Furia del Sur a Morthis Alasusurro al Refugio Cenarion."}},
[10995] = {"Grulloc tiene dos calaveras",nil, {"Consigue la calavera de dragón de Grulloc."}},
[10996] = {"La arqueta de Maggoc",nil, {"Consigue la arqueta de Maggoc."}},
[10997] = {"Incluso los gronn tienen confalones",nil, {"Consigue el confalón de Slaag."}},
[10998] = {"Un asunto grimoso",nil, {"Consigue el grimorio de Vim'gol el Vil."}},
[11000] = {"Dentro del Moledor de Almas",nil, {"Recoge el alma de Skulloc."}},
[11001] = {"Derrota al Dios Cuervo",nil, {"Mata al Dios Cuervo y vuelve con Morthis Alasusurro en el Refugio Cenarion.nnEsta misión se debe completar en el modo de dificultad de mazmorras Heroico."}},
[11002] = {"La caída de Magtheridon",nil, {"Devuelve la cabeza de Magtheridon al Comandante en Jefe Danath Aterratrols en el Bastión del Honor en la Península del Fuego Infernal."}},
[11003] = {"La caída de Magtheridon",nil, {"Devuelve la cabeza de Magtheridon a Nazgrel en Thrallmar en la Península del Fuego Infernal."}},
[11004] = {"Mundo de Sombras",nil, {"Severin quiere que te adentres en Skettis y consigas 6 muestras de polvo de las Sombras de los arakkoa que allí moran."}},
[11005] = {"Secretos de los sacerdotes de la Garra",nil, {"Obtén el elixir de Sombras de Severin y úsalo para encontrar y matar a los sacerdotes de la garra Ishaal, Skizzik y Zellek en Skettis. Cuando completes la tarea, informa al comandante Adaris."}},
[11006] = {"Más polvo de las sombras",nil, {"Parece que, después de todo, el comandante no deliraba. Tráeme más polvo y prepararé otro elixir para ti."}},
[11007] = {"Kael'thas y la esfera verdeante",nil, {"Lleva la esfera verdeante a A'dal en la Ciudad de Shattrath."}},
[11008] = {"Fuego sobre Skettis",nil, {"Busca los huevos de kaliri monstruosos sobre los hogares de Skettis y usa las cargas explosivas de la Guardia del cielo para destruirlos. Preséntate ante el sargento del cielo Doryn."}},
[11009] = {"El cielo de los ogros",nil, {"Mog'dorg el Marchito te ha pedido que hables con Chu'a'lor en Ogri'la, en las Montañas Filospada."}},
[11010] = {"¡Bomba va!",nil, {"Utiliza bombas de la Guardia del Cielo para destruir 15 montones de balas de cañón vil."}},
[11011] = {"Vigilancia eterna",nil, {"Habla con Morthis Alasusurro para que te dé una piedra lunar imbuida de esencia."}},
[11012] = {"Juramento de sangre de los Ala Abisal",nil, {"Nuestras tierras están perdidas. Nuestros hijos desaparecen por la noche y son esclavizados... o algo peor.nn<La voz de Mordenai se quiebra.>nnEl momento ha llegado, mi aliada. ¡El momento de retomar lo que una vez fue nuestro! Has demostrado tu valía en combate y la compasión de tu alma.nn¿Juras ayudar al Vuelo Ala Abisal a recuperar nuestras tierras y a reconstruir nuestro hogar? ¿Juras proteger al Vuelo y permitir que la línea de sangre se renueve? ¡Júralo y lucharemos por nuestro hogar y por nuestros seres queridos!"}},
[11013] = {"Al servicio de los Illidari",nil, {"Viaja hasta el Arrecife del Ala Abisal y entrega los documentos de servicio Illidari al señor supremo Mor'ghor en el Campamento Faucedraco.|n"}},
[11014] = {"Presentación ante el capataz",nil, {"Habla con el capataz Varkule Aliento de Dragón en el Campamento Faucedraco."}},
[11015] = {"Cristales de Ala Abisal",nil, {"El capataz Varkule Aliento de Dragón del Campamento Faucedraco en el Valle Sombraluna quiere que le traigas 30 cristales de Ala Abisal."}},
[11016] = {"Pellejo de despellejador mina abisal",nil, {"El capataz Varkule Aliento de Dragón del Campamento Faucedraco en el Valle Sombraluna quiere que le traigas 35 pellejos de despellejadores mina abisal."}},
[11017] = {"Polen de polvo abisal",nil, {"El capataz Varkule Aliento de Dragón del Campamento Faucedraco en el Valle Sombraluna quiere que le lleves 40 unidades de polen de polvo abisal."}},
[11018] = {"Mineral de abisalita",nil, {"El capataz Varkule Aliento de Dragón del Campamento Faucedraco en el Valle Sombraluna quiere que le lleves 40 unidades de mineral de abisalita."}},
[11019] = {"Tu amigo en el interior",nil, {"No creíste que te dejaríamos enfrentarte al ejercito orco vil tú solo, ¿verdad?nn<Los ojos de Yarzill tienen un brillo azul translúcido>nnSí, estamos aquí contigo, <nombre>. En cada servicio que realices para los orcos viles con la intención de recuperar las tierras del Ala Abisal, estaremos junto a ti para asegurarnos de que los orcos viles sufran suficiente retraso.nnTendrás que actuar con discreción para no atraer atención indeseada hacia ti durante tus misiones de sabotaje."}},
[11020] = {"Una muerte lenta",nil, {"Yarzill el Condotiero se encuentra en el Campamento Faucedraco del Valle Sombraluna y quiere que uses su añojo y glándulas viles para envenenar a los peones Faucedraco de 12 asentamientos.|n"}},
[11021] = {"El anuario de Ishaal",nil, {"Lleva el anuario de Ishaal al comandante del cielo Adaris al norte de Skettis."}},
[11022] = {"Habla con Mog'dorg",nil, {"Habla con Mog'dorg el Marchito. Se encuentra en lo alto de la torre al este del Anillo de Sangre, en las Montañas Filospada."}},
[11023] = {"¡Bombardéalos de nuevo!",nil, {"Utiliza bombas de la Guardia del Cielo para destruir 15 montones de balas de cañón vil."}},
[11024] = {"Un aliado en el Bajo Arrabal",nil, {"Lleva el anuario de Ishaal a Rilak el Redimido en el Bajo Arrabal dentro de Shattrath."}},
[11025] = {"Los cristales",nil, {"Chu'a'lor te ha encargado que reúnas 5 fragmentos apexis. Llévaselos a Ogri'la, en lo alto de las Montañas Filospada."}},
[11026] = {"Destierra a los demonios",nil, {"Usa el cristal desterrador para desterrar a 15 demonios en el Campamento Forja: Cólera o en el Campamento Forja: Terror."}},
[11027] = {"¿Tú tener runaoscura?",nil, nil},
[11028] = {"Cuenta atrás hacia la Muerte",nil, {"Vuelve donde el comandante del cielo Adaris, en el Alto de los Vientonegro, en el Bosque de Terokkar, e infórmale acerca del regreso de Terokk."}},
[11029] = {"Un disfraz raído",nil, {"Usa el disfraz arakkoa desgastado para conseguir la Línea de Sangre de Adversarios de Sahaakk y vuelve junto a Hazzik."}},
[11030] = {"Nuestro chico quiere ser un forestal Guardia del cielo",nil, {"Consigue un frasco inestable de la bestia."}},
[11031] = {"Ya no es archimago",nil, nil},
[11032] = {"Ya no es protector",nil, nil},
[11033] = {"Ya no es asesino",nil, nil},
[11034] = {"Ya no es restaurador",nil, nil},
[11035] = {"Los cielos no tan amistosos...",nil, {"Yarzill el Condotiero del Campamento Faucedraco en el Valle Sombraluna quiere que recuperes 10 reliquias de Ala Abisal."}},
[11036] = {"¡Un producto de fuera de este mundo!",nil, {"Llévale la fruta en conserva al jefe de cohetes Fuselaje al Área 52, en Tormenta Abisal."}},
[11037] = {"Una extraña visión",nil, {"Habla con el jefe de cohetes Fuselaje en el Área 52."}},
[11038] = {"Ayuda al exarca Orelis",nil, {"Habla con el exarca Orelis en el Área 52."}},
[11039] = {"Informa al maestro de espías Thalodien",nil, {"Preséntate ante el maestro de espías Thalodien en el Área 52."}},
[11040] = {"Piezas para el jefe de cohetes",nil, {"Llévale al jefe de cohetes Fuselaje al Área 52 las piezas que le sobran a Bossi."}},
[11041] = {"Un trabajo sin terminar...",nil, {"Mata 10 despellejadores sobremina y a Barash, la madre del cubil. Si tienes éxito, regresa a donde Mor'ghor al Campamento Faucedraco."}},
[11042] = {"Una visión mistificadora",nil, {"Habla con el jefe de cohetes Fuselaje en el Área 52."}},
[11043] = {"Crear un grifo mejor",nil, {"Habla con Brunn Barballamas en el Bastión Martillo Salvaje."}},
[11044] = {"Visiones de destrucción",nil, {"Habla con el comandante del aire Nuainn en el Bastión Martillo Salvaje."}},
[11045] = {"Zorus el Justiciero",nil, {"Habla con Zorus el Justiciero en el Bastión Martillo Salvaje."}},
[11046] = {"Boticaria jefe Hildagard",nil, {"Habla con la boticaria jefe Hildagard en la Aldea Sombraluna."}},
[11047] = {"La petición del aprendiz",nil, {"Habla con Dama Bravacrín en la Aldea Sombraluna."}},
[11048] = {"El informe de Kroghan",nil, {"Habla con el Guardia de sangre Gulmok en la Aldea Sombraluna."}},
[11049] = {"La gran búsqueda de huevos de Ala Abisal",nil, {"Yarzill el Condotiero en el Campamento Faucedraco en el Valle Sombraluna quiere que recuperes un huevo de Ala Abisal."}},
[11050] = {"Aceptar todos los huevos",nil, {"No queremos que esos huevos caigan en las manos equivocadas, <nombre>. Tráeme todos los huevos adicionales que encuentres.nn¡Tan solo recuerda ser discreto y evita levantar sospechas!"}},
[11051] = {"Destierra más demonios",nil, {"Usa el cristal desterrador para desterrar a 15 demonios en el Campamento Forja: Cólera o en el Campamento Forja: Terror."}},
[11052] = {"La promesa de Akama",nil, {"Llévale el medallón de Karabor a A'dal en la Ciudad de Shattrath."}},
[11053] = {"¡Levanta, sobrestante!",nil, {"Habla con el señor supremo Mor'ghor en el Campamento Faucedraco en el Valle Sombraluna."}},
[11054] = {"La supervisión y tú: cómo tomar las decisiones adecuadas",nil, {"El sobrestante jefe Limo, del Campamento Faucedraco en el Valle Sombraluna, quiere que le lleves 10 trozos de cuero de pellejo nudoso y un pellejo endurecido de Tyrantus.|n"}},
[11055] = {"El botarang: una cura para el insignificante trabajador común",nil, {"El sobrestante jefe Limo, del Campamento Faucedraco en el Valle Sombraluna, quiere que disciplines a 20 peones Faucedraco vagos. También tendrás que devolver el botarang."}},
[11056] = {"La ganga de Hazzik",nil, {"Obtén el paquete de Hazzik en su hogar del este de Skettis y llévaselo."}},
[11057] = {"El problema abajo",nil, {"Habla con Mog'dorg el Marchito en lo alto de una de las torres que hay a las afueras del Anillo de Sangre en las Montañas Filospada.|n"}},
[11058] = {"Una reliquia apexis",nil, {"Consigue vibraciones apexis de una reliquia apexis."}},
[11059] = {"Guardián del monumento",nil, {"Obtén la cabeza de un guardián apexis."}},
[11060] = {"Una runaoscura de cristal forjado",nil, nil},
[11061] = {"La obligación de un padre",nil, {"Consigue un frasco inestable del hechicero."}},
[11062] = {"La Avanzada de la Guardia del Cielo",nil, {"Habla con el comandante del cielo Keller en la Avanzada de la Guardia del Cielo, en lo alto de las Montañas Filospada."}},
[11063] = {"Ganándote las alas...",nil, {"Así que quieres unirte a los Rompecielos, ¿eh? Ciertamente has probado tu valía como Faucedraco, pero eso no significa nada en el cielo de Terrallende. Has de responder a la pregunta: ¿PUEDES VOLAR?nnY no me refiero a ser capaz de subirse a un draco abisal y llevar un cargamento de cristales al fuerte como hacen esos zafios transportistas. Me refiero a ser el mejor... El mejor orco...nnEn el oeste hay seis jinetes. Véncelos y te concederé las alas de Rompecielos."}},
[11064] = {"Carrera Faucedraco: la balada del Viejo Viejete",nil, {"Sigue a Murg Quijada Sucia \"el Viejo\" mientras intentas mantenerte en tu montura voladora. Si vences a Murg Quijada Sucia \"el Viejo\", informa de tu Victoria a Ja'y Nosliw en el Campamento Faucedraco, en el Valle Sombraluna."}},
[11065] = {"¡Enlaza algunas rayas de éter!",nil, {"Utiliza el lazo para capturar 5 rayas de éter."}},
[11066] = {"¡Atrapa más rayas de éter!",nil, {"Utiliza el lazo para capturar 5 rayas de éter."}},
[11067] = {"Carrera Faucedraco: Trope el Vomitador",nil, {"Sigue a Trope el Vomitador mientras intentas mantenerte en tu montura voladora. Si vences a Trope el Vomitador, informa de tu Victoria a Ja'y Nosliw en el Campamento Faucedraco, en el Valle Sombraluna."}},
[11068] = {"Carrera Faucedraco: Corlok el Veterano",nil, {"Sigue a Corlok el Veterano mientras intentas mantenerte en tu montura voladora. Si vences a Corlok el Veterano, informa de tu Victoria a Ja'y Nosliw en el Campamento Faucedraco, en el Valle Sombraluna."}},
[11069] = {"Carrera Faucedraco: Comandante del aire Ichman",nil, {"Sigue al comandante del aire Ichman mientras intentas mantenerte en tu montura voladora. Si vences al comandante del aire Ichman, informa de tu Victoria a Ja'y Nosliw en el Campamento Faucedraco, en el Valle Sombraluna."}},
[11070] = {"Carrera Faucedraco: Comandante del aire Mulverick",nil, {"Sigue al comandante del aire Mulverick mientras intentas mantenerte en tu montura voladora. Si vences al comandante del aire Mulverick, informa de tu Victoria a Ja'y Nosliw en el Campamento Faucedraco, en el Valle Sombraluna.|n"}},
[11071] = {"Carrera Faucedraco: Capitán Devastador del Cielo",nil, {"Sigue al capitán Devastador del Cielo mientras intentas mantenerte en tu montura voladora. Si vences al capitán Devastador del Cielo, informa de tu Victoria a Ja'y Nosliw en el Campamento Faucedraco, en el Valle Sombraluna."}},
[11072] = {"Sangre adversaria",nil, {"Encuentra los montones de cráneos en el centro de los círculos de invocación de Skettis. Invoca y derrota a cada uno de los descendientes usando 10 Pergaminos perdidos en el Tiempo en el montón de cráneos. Llévale a Hazzik una señal de cada uno al Alto de los Vientonegro."}},
[11073] = {"La caída de Terokk",nil, {"Lleva las ofrendas tiempo perdido que ha preparado Hazzik al montón de cráneos en el centro de Skettis e invoca y vence a Terokk. Cuando termines, vuelve junto al comandante del cielo Adaris."}},
[11074] = {"Atributos de los descendientes",nil, {"Recopila pergaminos perdidos en el tiempo de los arakkoa tiempo perdido en Skettis y llévalos a un montón de cráneos dentro de un círculo de invocación en Skettis. Invoca y derrota a los descendientes de los adversarios de Terokk y llévale a Hazzik la garfa de Akkarai, la espina de Karrog, la escama de Vekkaz y la garra de Gezzarak."}},
[11075] = {"Las Minas del Ala Abisal",nil, {"Busca a la Maestra de las minas en el Arrecife del Ala Abisal."}},
[11076] = {"Recogiendo los pedazos...",nil, {"La maestra de las minas en las Minas del Ala Abisal, en el Valle Sombraluna, quiere que recuperes 15 cajas de carga de mina abisal."}},
[11077] = {"Los dragones son el menor de nuestros problemas",nil, {"El supervisor Faucedraco de las Minas del Ala Abisal en el Valle Sombraluna quiere que mates 15 despellejadores mina abisal y 5 devastadores mina abisal."}},
[11078] = {"Reinar en los cielos",nil, {"Consigue dientes de dragón."}},
[11079] = {"Un látigo vil para Gahk",nil, {"Consigue un látigo vil."}},
[11080] = {"La emanación de la reliquia",nil, {"Consigue emanaciones apexis de una reliquia apexis."}},
[11081] = {"La gran revuelta Sangreoscura",nil, {"Lleva los planes de fuga de los Sangreoscura a la Maestra de las minas."}},
[11082] = {"En busca de la verdad",nil, {"Encuentra a un sobrestante Sangreoscura e interrógalo. Regresa junto a la maestra de las minas con la información sobre los Sangreoscura y la mano del sobrestante."}},
[11083] = {"Locura y confusión",nil, {"Ronag el Explotador de las Minas del Ala Abisal del Valle Sombraluna quiere que mates al supervisor Sangreoscura enloquecido y a los 5 mineros Sangreoscura enloquecidos."}},
[11084] = {"¡La cabeza bien alta, Capitán!",nil, {"Habla con el señor supremo Mor'ghor en el Campamento Faucedraco en el Valle Sombraluna."}},
[11085] = {"Fuga de Skettis",nil, {"Escolta al prisionero de la Guardia del cielo hasta un lugar seguro e informa al sargento del cielo Doryn."}},
[11086] = {"Perturbar el Portal Crepuscular",nil, {"El señor supremo Mor'ghor del Campamento Faucedraco en el Valle Sombraluna quiere que mates a 20 agentes Sombra de Muerte."}},
[11089] = {"El cañón de almas de Reth'hedron",nil, {"El Señor de los Illidari Balthas, en el Campamento Faucedraco del Valle Sombraluna, quiere que le lleves 2 barras de acero vil, 1 marco de adamantita, 1 núcleo de poder de korio y 1 esencia arcana inmaculada."}},
[11090] = {"Avasallar al Avasallador",nil, {"El Señor de los Illidari Balthas del Campamento Faucedraco en el Valle Sombraluna te ha encargado que uses el cañón de almas contra Reth'hedron el Avasallador en Nagrand.nnTambién debes devolver el cañón de almas."}},
[11091] = {"Un agradecimiento especial",nil, {"Habla con Jho'nass en Ogri'la, en lo alto de las Montañas Filospada."}},
[11092] = {"¡Saludos, comandante!",nil, {"Habla con el señor supremo Mor'ghor en el Campamento Faucedraco en el Valle Sombraluna."}},
[11093] = {"Rayas abisales hambrientas",nil, {"Usa la jaula de raya abisal en los bosques al sur del Alto de los Vientonegro y mata persecutores de distorsión Vientonegro cerca de la raya abisal hambrienta. Vuelve junto al cuidador de la Guardia del cielo Deesak cuando hayas completado tu misión."}},
[11094] = {"¡Mátalos a todos!",nil, {"Envía noticias de tu misión junto con la pistola de bengalas Faucedraco al arcanista Thelis que se encuentra en el Sagrario de las Estrellas en el Valle Sombraluna."}},
[11095] = {"Comandante Hobb",nil, {"Entrega la bengala Faucedraco al comandante Hobb, que se encuentra en el Sagrario de las Estrellas, en el Valle Sombraluna."}},
[11096] = {"Amenaza desde el cielo",nil, {"Mata 20 ogros Gordunni en las Colinas Barrera y regresa junto a Yuula en la Ciudad de Shattrath."}},
[11097] = {"La trampa más mortal jamás tendida",nil, {"Ayuda al Comandante Hobb y a los Arúspices a defender el Sagrario de las Estrellas. Una vez que hayáis acabado con todas las fuerzas de los Faucedraco, preséntate ante el señor supremo Mor'ghor en el Campamento Faucedraco con las medallas Arúspices.|n"}},
[11098] = {"¡A Skettis!",nil, {"Lleva el paquete de explosivos al sargento del cielo Doryn al Alto de los Vientonegro, a las afueras de Skettis."}},
[11099] = {"¡Mátalos a todos!",nil, {"Envía noticias de tu misión junto con la pistola de bengalas Faucedraco a la anacoreta Ceyla que se encuentra en el Altar de Sha'tar."}},
[11100] = {"Comandante Arcus",nil, {"Entrega la bengala Faucedraco al comandante Arcus, que se encuentra en el Altar de Sha'tar, en el Valle Sombraluna."}},
