[11545] = {"Una donación caritativa",nil, {"La anacoreta Ayuri de Tramo del Sol, en la Isla de La Fuente del Sol, quiere que dones 10 oros."}},
[11546] = {"Abierto al negocio",nil, {"Mar'nah del Puerto de Tramo del Sol quiere que recojas 5 bayas de sangre de los arbustos de Quel'Danas."}},
[11547] = {"Conoce tus líneas Ley",nil, {"El astromante Darnarian, en el Sagrario de Tramo del Sol, quiere que uses el cristal de astromante en el cristal de sangre, el portal de La Plaza Crepuscular y el sagrario naga en la Costa Branquia Verde."}},
[11548] = {"Tu incesante apoyo",nil, {"La anacoreta Ayuri de Tramo del Sol, en la Isla de La Fuente del Sol, quiere que dones 10 oros."}},
[11549] = {"Un benefactor magnánimo",nil, {"El anacoreta Kairthos quiere que dones 1000 oros para ayudar a la causa de la anacoreta Ayuri. Si completas esta misión, serás conocido como <nombre> del Sol Devastado."}},
[11550] = {"Adelante, Falsario...",nil, {"Preséntate ante el archimago Ne'thul en la Isla de Quel'Danas, al norte de Quel'Thalas.nnLos maestros de vuelo de Forjaz y de Ciudad de Lunargenta te llevarán hasta tu destino."}},
[11554] = {"Un amigo en primera línea",nil, {"Tu ayuda en la ofensiva no ha pasado desapercibida, <nombre>. Combinadas, las fuerzas de los Aldor y de los Arúspices te expresan su agradecimiento."}},
[11555] = {"Honrado por tus aliados",nil, {"Sigues demostrando tu valía en esta batalla de gran importancia, <nombre>. Acepta esto como prenda de nuestra constante gratitud."}},
[11556] = {"Venerado en el campo de batalla",nil, {"No podríamos haber llegado hasta aquí sin tu ayuda, <nombre>. Las fuerzas combinadas de los Aldor y los Arúspices no son suficiente para aplastar a la Legión y a Kael. Es una suerte que podamos contar con aliados de tu talla entre nosotros."}},
[11557] = {"Exaltado entre todos los combatientes",nil, {"Eres una de las mejores, <nombre>. Tu nombre será conocido entre nuestras filas durante años.nnAcepta este pequeño presente de parte de nuestras fuerzas."}},
