[175] = {"Les yeux dans les étoiles",nil, {"Trouver Marie et la persuader de prêter une surface réfléchissante pour le projet de Viktori."}},
[176] = {"On recherche : \"Lardeur\"",nil, {"Liquider le gnoll Lardeur et apporter sa Grande griffe au maréchal Dughan."}},
[177] = {"Les yeux dans les étoiles",nil, {"Prendre le miroir de Marie l'Aveugle à la Goule folle et l'apporter à Viktori de Sombre-comté."}},
