[179] = {"Tailleur chez les nains",nil, {"Sten Rudebras voudrait 8 morceaux de Viande de loup coriace."}},
[180] = {"On recherche : lieutenant Tripecroc",nil, {"Tuer le lieutenant Tripecroc et rapporter sa patte au magistrat Salomon de Comté-du-lac."}},
[181] = {"Les yeux dans les étoiles",nil, {"Localiser Zzarc'Vul dans le tertre des ogres au sud du bois de la Pénombre et rapporter son Monocle à Viktori de Sombre-comté."}},
