[185] = {"Maîtrise de la chasse au tigre",nil, {"Ajeck Rouack de l'Expédition de Nesingwary veut que vous chassiez 10 Jeunes tigres de Strangleronce."}},
[186] = {"Maîtrise de la chasse au tigre",nil, {"Ajeck Rouack de l'Expédition de Nesingwary veut que vous chassiez 10 Tigres de Strangleronce."}},
[187] = {"Maîtrise de la chasse au tigre",nil, {"Ajeck Rouack de l'Expédition de Nesingwary veut que vous chassiez 10 Anciens tigres de Strangleronce."}},
