[203] = {"La seconde rébellion",nil, {"Tuer 15 Guerriers de la jungle.nnRetourner voir le sergent Yohwa au Camp rebelle."}},
[204] = {"Mauvaise médecine",nil, {"Apporter 7 Remèdes tropicaux et 1 Extrait de fougère vénéneuse au sergent Yohwa au Camp rebelle."}},
