[206] = {"Mai'Zoth",nil, {"Apporter la Vue de l'Esprit au frère Nimetz au Camp rebelle."}},
[207] = {"Le mystère de Kurzen",nil, {"Trouver les 4 tablettes et rapporter ces légendes au frère Nimetz."}},
[208] = {"La chasse au gros gibier",nil, {"Hemet Nesingwary Jr veut que vous lui rameniez la tête du roi Bangalash, le grand tigre blanc."}},
