[215] = {"Les secrets de la jungle",nil, {"Parler au lieutenant Doren au Camp rebelle."}},
[216] = {"Entre un roc et un Crin-de-Chardon",nil, {"Tuer 8 Vengeurs Crins-de-Chardon et 8 Chamans Crins-de-Chardon ; la plupart d'entre eux se trouvent à l'est de la grève de Zoram, dans le village des Crins-de-Chardon. Retourner voir Karang Amakkar, à l'avant-poste de Zoram'gar, à Orneval, une fois que vous aurez terminé."}},
[217] = {"À la défense des terres du roi",nil, {"Tuer le chef des troggs, Grawmug, et ses deux gardes, Grinceur et Bagarreur, puis rendre compte de la situation au capitaine Rudagite à la Tour de garde méridionale."}},
[218] = {"Le journal volé",nil, {"Grelin Blanchebarbe veut que vous tuiez Grik'nir le Froid et que vous lui rapportiez son journal."}},
[219] = {"Porté disparu",nil, {"Protéger le caporal Keeshan sur le chemin des Carmines."}},
[220] = {"L'appel de l'eau",nil, {"Apporter la Fiole d'eau la plus pure à Islen Waterseer dans les Tarides."}},
[221] = {"Worgens dans les bois",nil, {"Tuer 12 worgens Messagers Plaie-de-nuit pour Calor de Sombre-comté."}},
[222] = {"Worgens dans les bois",nil, {"Tuer 8 Crocs noirs Plaie-de-nuit et 8 Contaminés Plaie-de-nuit pour Calor de Sombre-comté."}},
[223] = {"Worgens dans les bois",nil, {"Apporter la note de Calor à Jonathan Carevin."}},
[224] = {"À la défense des terres du roi",nil, {"Le Montagnard Pave-silex de la Tour de garde méridionale veut que vous tuiez 10 Troggs et 10 Eclaireurs Brisepierre."}},
[225] = {"La tombe effritée",nil, {"Parler à Sirra Von'Indi à Sombre-comté."}},
[226] = {"Des loups aux trousses",nil, {"Tuer 12 Loups redoutables affamés et 8 Loups redoutables enragés, puis retourner auprès de Lars au camp de Sven à la frontière ouest du bois de la Pénombre."}},
[227] = {"Morgan Ladimore",nil, {"Parler au commandant Althea Bouclenoire à Sombre-comté."}},
[228] = {"Mor'Ladim",nil, {"Tuer Mor'Ladim, puis rapporter son crâne au commandant Althea Bouclenoire à Sombre-comté."}},
[229] = {"La fille qui survécut",nil, {"Parler au Gardien Sarah Ladimore à Sombre-comté."}},
[230] = {"Le camp de Sven",nil, {"Apporter à Sven le Livre de la ferme de Sven."}},
[231] = {"L'amour d'une fille",nil, {"Déposer la bague de Sarah Ladimore sur la tombe de Morgan Ladimore."}},
[232] = {"Course pour l'apothicaire Zinge",nil, {"Apporter le bon de commande à Alessandro Luca à Fossoyeuse."}},
[233] = {"La distribution du courrier de la Vallée des Frigères",nil, {"Apporter la pile de lettres à Talin Œil-perçant."}},
[234] = {"La distribution du courrier de la Vallée des Frigères",nil, {"Remettre la lettre à Grelin Blanchebarbe."}},
[235] = {"La Chasse d'Orneval",nil, {"Parler à Senani Cœur-de-tonnerre, au Poste de Bois-brisé, à Orneval."}},
[237] = {"À la défense des terres du roi",nil, {"Le Montagnard Gravegalle de la Tour de garde méridionale veut que vous tuiez 10 Brise-crâne et 10 Prophètes Brisepierre, puis que vous retourniez le voir."}},
[238] = {"Course pour l'Apothicaire Zinge",nil, {"Apportez une Trousse d'analyse de terrain à l'Apothicaire Zinge à Undercity."}},
[239] = {"Au secours de la garnison du ruisseau de l'ouest !",nil, {"Se rendre à la garnison du ruisseau de l'ouest et parler à l'Adjoint Rainer."}},
[240] = {"Retour vers la Tremblotte",nil, {"Retourner voir la Tremblotte avec des Gâteaux au crabe brun."}},
[243] = {"Sur le terrain",nil, {"Parler à l'Ingénieur en chef Bilgewhizzle à Gadgetzan afin d'obtenir une source d'énergie pour la trousse d'analyse."}},
[244] = {"Des gnolls encombrants",nil, {"Informer l'Adjoint Feldon."}},
[245] = {"Les plaies à huit pattes",nil, {"Tuer 15 Araignées Tisse-venin pygmées, puis rendre compte au Gardien Dodds au bois de la Pénombre."}},
[246] = {"L'appréciation du danger",nil, {"Tuer 10 Bâtards des Carmines et 6 Braconniers des Carmines, et aller faire un rapport à l'Adjoint Feldon à Comté-du-lac."}},
[247] = {"La chasse achevée",nil, {"<nom>, vous avez tué les trois bêtes qui composent la Chasse d'Ashenvale. Vous m'avez apporté les preuves de leur mort ; je ne doute plus de votre force ni de votre ténacité. Le sang d'un chasseur coule dans vos veines, et je vous en félicite !nnS'il vous plaît, acceptez ceci pour vous récompenser de la prouesse que vous avez accomplie pour la Horde, à Ashenvale. Que vos ancêtres vous protègent !"}},
[248] = {"En regardant plus loin",nil, {"Escalader la Tour de guet intacte près du Donjon Guet-de-Pierre.nnTrouver un contenant adéquat pour le Glyphe d'Azora."}},
[249] = {"Morganth",nil, {"Battre Morganth.nnApporter sonBring his Pendentif des ombres à Theocritus à la Tour d’Azora dans la Forêt d'Elwynn."}},
[250] = {"Une sombre menace se dessine",nil, {"Enquêter près de la rampe est du barrage et rapporter à l'ingénieur en chef Hinderweir un indice qui permettrait de deviner les intentions des nains Sombrefer."}},
[251] = {"La traduction de la lettre d'Abercrombie",nil, {"Remettre la Lettre à Ello à Sirra Von'Indi."}},
[252] = {"La traduction pour Ello",nil, {"Remettre la lettre traduite au seigneur Ello Bouclenoire."}},
[253] = {"La fiancée de l'Embaumeur",nil, {"Trouver la tombe d'Eliza, retrouver le cœur de l'Embaumeur, puis retourner voir Ello Bouclenoire."}},
[254] = {"En creusant la terre",nil, {"Vous creusez dans la saleté molle au-dessus de la tombe... et vous trouvez les restes d'un cercueil.nnVous sentez le cercueil vibrer sous l'effet de ce qui semble être des griffes qui raclent à l'intérieur, et vous entendez le grincement de dents en décomposition...nnAllez-vous ouvrir le cercueil et récupérer le cœur de l'Embaumeur ?"}},
[255] = {"Mercenaires",nil, {"Le magistrat Courtnez de Thelsamar vous a <embauché/embauchée> pour tuer 4 Ogres, 4 Brutes et 4 Massacreurs Mo'grosh."}},
[256] = {"ON RECHERCHE : Chok'sul",nil, {"Tuer Chok'sul et rapporter sa tête au Magistrat Courtnez de Thelsamar."}},
[257] = {"La fanfaronnade d'un chasseur",nil, {"Tuer 6 Busards des montagnes et retourner auprès de Daryl le Jeune au pavillon des pérégrins en moins de 15 minutes."}},
[258] = {"Le défi d'un chasseur",nil, {"Tuer 5 Anciens sangliers des montagnes et retourner auprès de Daryl le Jeune au pavillon des pérégrins en 12 minutes."}},
[261] = {"Sur le chemin écarlate",nil, {"Détruire 30 Ravageurs morts-vivants, puis retourner voir Frère Anton, à la Combe de Nijel."}},
[262] = {"L'obscure silhouette",nil, {"Montrer le livre que vous avez trouvé à la ferme de Sven aux habitants de Sombre-comté."}},
[263] = {"À la défense des terres du roi",nil, {"Le Montagnard Cognemur de la Tour de garde méridionale veut que vous tuiez 10 Chamans et 10 Briseurs d'Os Brisepierre."}},
[264] = {"Jusqu'à ce que la mort nous sépare",nil, {"Placer le pendentif de Clarice sur la tombe de Yuriv, dans la forêt des Pins argentés."}},
[265] = {"L'obscure recherche se poursuit",nil, {"Voir si d'autres habitants ont des indices concernant l'obscure silhouette."}},
[266] = {"Une enquête à l'auberge",nil, {"Chercher à l'auberge des indices concernant l'obscure silhouette."}},
[267] = {"La menace trogg",nil, {"Apporter 8 Dents de pierre de troggs au capitaine Rudagite à la Tour de garde méridionale."}},
[268] = {"Retourner voir Sven",nil, {"Retourner voir Sven avec le journal complété de la Tremblotte."}},
[269] = {"À la recherche de la sagesse",nil, {"Donner le Journal complété de la Tremblotte à l'évêque Farthing à Hurlevent."}},
[270] = {"La Flotte perdue",nil, {"Aller au port de Menethil, dans les Paluns, et parler à Glorin Front-d'acier."}},
[271] = {"La vengeance de Vyrin",nil, {"Tuer le Vieux Noiraud puis montrer le résultat à Daryl le Jeune au pavillon des Pérégrins."}},
[272] = {"L'épreuve du lion de mer",nil, {"Trouver le Demi-pendentif d'agilité aquatique et le Demi-pendentif d'endurance aquatique. Parler aux habitants de Reflet-de-Lune pour obtenir des indices quant à l'endroit où ces objets se trouvent.nnReformer le Pendentif du lion de mer à partir des deux moitiés. Vous devez vous trouver à proximité du sanctuaire de Remulos pour faire ceci.nnApporter le Pendentif reformé à Dendrite Brasétoile, au village de Havrenuit, à Reflet-de-Lune."}},
[273] = {"Approvisionnement du site de fouilles",nil, {"Aller voir Huldar."}},
[274] = {"Une sombre menace se dessine",nil, {"Ashlan Rictus-de-pierre vous demande de retourner voir l'Ingénieur en chef Hinderweir pour l'informer de la nouvelle découverte."}},
[275] = {"Fièvre sur le pays",nil, {"Tuer 12 Rampants des tourbières, puis retourner voir Rethiel, le Gouverneur vert, dans les Paluns."}},
[276] = {"Piétinements de pattes",nil, {"Tuer 15 Gnolls Poils-moussus et 10 Bâtards Poils-moussus puis retourner voir Rethiel, le Gouverneur vert, dans les Paluns."}},
[277] = {"Feu interdit",nil, {"Rapporter à Rethiel, le Gouverneur vert, 9 Silex bruts."}},
[278] = {"Une sombre menace se dessine",nil, {"Collecter du Venin de rôdeuse, un Cristal mo'grosh et une Larme de crocilisque pour l'Ingénieur en chef Hinderweir."}},
[279] = {"Griffes des profondeurs",nil, {"Tuer 12 Murlocs Branchies-bleues.nnTuer Enfourneur et prendre sa tête.nnL'apporter à Karl Boran au port de Menethil."}},
[280] = {"Une sombre menace se dessine",nil, {"L'Ingénieur en chef Hinderweir vous demande de nager jusqu'à la base du barrage pour localiser le tonnelet de poudre et d'y verser la mixture de désamorçage pour empêcher une explosion."}},
[281] = {"Récupération de marchandises",nil, {"Rechercher les caisses et les barils dans les camps murlocs le long de la côte."}},
[282] = {"Les observations de Senir",nil, {"Aller voir le Montagnard Thalos."}},
[283] = {"Une sombre menace se dessine",nil, {"La Mixture de désamorçage semble avoir fonctionné. Retourner voir l'Ingénieur en chef Hinderweir pour lui annoncer la bonne nouvelle."}},
[284] = {"La recherche continue",nil, {"Rechercher la Statuette de Menethil."}},
[285] = {"À la recherche d'autres taudis",nil, {"Continuer à fouiller les taudis des murlocs pour retrouver la Statuette de Menethil."}},
[286] = {"Rapporter la statuette",nil, {"Apporter la Statuette de Menethil à Karl Boran."}},
[287] = {"Le repaire des Frostmane",nil, {"Explorer le repaire des Crins-de-givre et tuer 5 Chasseurs de tête Crins-de-givre pour Senir Blanchebarbe à Kharanos."}},
[288] = {"La IIIe flotte",nil, {"Acheter une Cruche d'hydromel pour le Premier officier Fitzsimmons."}},
[289] = {"L'équipage maudit",nil, {"Tuer 13 Marins maudits, 5 Soldats de marine maudits et le Premier officier Snellig. Rapporter la Tabatière de Snellig au Premier officier Fitzsimmons au port de Menethil."}},
[290] = {"Levée de la malédiction",nil, {"Obtenir du capitaine Halyndor la Clé du coffre-fort de \"l'Intrépide\".nnUtiliser la clé pour ouvrir le Coffre verrouillé de l'Intrépide."}},
[291] = {"Les rapports",nil, {"Livrer le rapport de Senir au Sénateur Barin Rougepierre à Forgefer."}},
[292] = {"L'Œil de Paleth",nil, {"Apporter l'Oeil maudit de Paleth à Glorin Steelbrow."}},
[293] = {"Purifier l'Œil",nil, {"Apporter l'Oeil maudit de Paleth à l'archevêque Benedictus, à la cathédrale de la Lumière de la ville de Stormwind."}},
[294] = {"La vengeance d'Ormer",nil, {"Ormer Tressefer, à l'Excavation de Whelgar, vous demande de tuer 10 Hurleurs tachetés et 10 Raptors tachetés."}},
[295] = {"La vengeance d'Ormer",nil, {"Tuer 10 Faucheurs tachetés et 10 Tranchegueules tachetés pour Ormer Tressefer, puis aller le voir à l'excavation de Whelgar."}},
[296] = {"La vengeance d'Ormer",nil, {"Ormer Tressefer, de l'Excavation de Whelgar, vous demande de tuer Grondecroc et de revenir avec l'une de ses serres une fois la tâche accomplie."}},
[297] = {"Réunir les idoles",nil, {"Apporter 8 Idoles de pierre sculptée à Magmar Tombebranche."}},
[298] = {"Compte rendu d'avancement des fouilles",nil, {"Apporter le Compte rendu d'avancement de Baguefer à Jern Casque-à-cornes à Thelsamar."}},
[299] = {"Découverte du passé",nil, {"À la demande du prospecteur Whelgar, parcourir les Excavations à la recherche des 4 fragments de tablette manquants : Ados, Modr, Golm et Neru."}},
[301] = {"Rapport à Forgefer",nil, {"Porter le Compte rendu d'avancement de Baguefer au Prospecteur Foudrepique."}},
[302] = {"De l'explosif pour Baguefer",nil, {"Parler à Jern Casque-à-cornes à Thelsamar."}},
[303] = {"La guerre contre les Sombrefer",nil, {"Motley Garmaçon de Dun Modr vous demande de tuer 10 Nains Sombrefer, 5 Tunneliers Sombrefer, 5 Saboteurs Sombrefer et 5 Démolisseurs Sombrefer."}},
[304] = {"Une tâche sinistre",nil, {"Tuer Balgaras le Crasseux et rapporter son oreille à Tresse-longue le Macabre à Dun Modr."}},
[305] = {"À la recherche de l'équipe de fouilles",nil, {"À la demande de Tarrel Tisseroche, aller jusqu'aux Excavations et entrer en contact avec Merrin Tisseroche."}},
[306] = {"À la recherche de l'équipe de fouilles",nil, {"Retourner voir Tarrel Tisseroche avec la lettre de Merrin."}},
[307] = {"Bas les pattes !",nil, {"Aller à la Mine du Ru d'argent pour prendre 4 lots d'Equipement de mineur.nnRetourner voir le Montagnard Foudrepique."}},
[308] = {"Distraire Jarven",nil, {"Hum ! Toute cette boisson stockée là, et dire que j'ai l'interdiction formelle d'y toucher ! Si seulement je pouvais goûter un peu de notre Thunder Ale... Ça me donnerait un esprit plus vif, sans mentir !"}},
[309] = {"Protection de la cargaison",nil, {"S'assurer que Miran et la cargaison parviennent au Site de fouilles puis en informer le Prospecteur Baguefer."}},
[310] = {"Bières rivales",nil, {"Aller au sous-sol de la distillerie Tonnebière à Kharanos et remplacer un Tonneau de Thunder Ale par un Tonneau de Brasselorge Scalder."}},
[311] = {"Retour chez Marleth",nil, {"Apporter le Tonneau de Thunder Ale à Marleth Brasselorge au Village de Brassetout."}},
[312] = {"La réserve volée de Tundra MacGrann",nil, {"Récupérer la caisse de viande séchée de Tundra MacGrann dans la Caverne du Vieux Barbeglace."}},
[313] = {"L'antre Gris",nil, {"Réunir 8 Peaux de wendigo et les apporter au Pilote Engrepierre."}},
[314] = {"Protection du troupeau",nil, {"Sur demande de Rudra Distillambre, tuer Vagash et rapporter un de ses crocs à la ferme des Distillambre."}},
[315] = {"La bière brune parfaite",nil, {"Apporter 6 Brins de chatoyante à Rejold Brasselorge, au village de Brassetout."}},
[317] = {"Ravitailler le Crache-vapeur",nil, {"Réunir 4 morceaux de Viande de sanglier et 2 Fourrures d'ours épaisses et les livrer au pilote Claquesoufflet au dépôt de Grillacier."}},
[318] = {"L'éternol",nil, {"Demander un tonneau d'Eternol à Rejold Brasselorge au village de Brassetout."}},
[319] = {"Une faveur pour un tonneau d’Evershine",nil, {"Tuer 6 Ours Griffeglace, 8 Anciens sangliers des rochers et 8 Léopards des neiges, puis retourner voir Rejold Brasselorge à Brassetout."}},
[320] = {"Retour vers Bellowfiz",nil, {"Donner le Tonneau d'éternol au Pilote Claquesoufflet au Dépôt de Grillacier."}},
[321] = {"Le fer de sancteforge",nil, {"Fouiller l'épave de l'Albatros, dans les Paluns."}},
[322] = {"Béni soit ce bras",nil, {"Apporter la Caisse de lingots de sancteforge à Grimand Elmore, à Stormwind."}},
[323] = {"Prouver votre valeur",nil, {"Tuer 15 Écumeurs squelettes, 3 Soigneurs squelettes et 3 Gardiens squelettes puis retourner voir Sven."}},
[324] = {"Les lingots perdus",nil, {"Collecter 5 Lingots de sancteforge puis retourner voir Glorin Front-d'acier, dans les Paluns."}},
