[837] = {"Empiètement",nil, {"Tuer 4 hurans Tranchecrin, 4 Eclaireurs Tranchecrin, 4 Lève-poussière Tranchecrin et 4 Gardes de guerre Tranchecrin pour Gar'Thok de Tranchecolline."}},
[838] = {"Scholomance",nil, {"Parler à l'Apothicaire Dithers à la Barricade, dans les Maleterres de l'ouest."}},
[840] = {"Sous les drapeaux de la Horde",nil, {"Suivre la route de l'ouest, celle qui part de Tranchecolline, et qui arrive aux Tarides par un pont.nnS'arrêter à l'avant-poste orc sur le pont.nnDonner votre lettre de recrutement à Kargal Balafre-glorieuse aux Tarides."}},
[841] = {"Une autre Source d'énergie ?",nil, {"Apporter 10 Outres de Bat-le-désert à l'ingénieur en chef Bilgewhizzle à Gadgetzan pour qu'il les échange contre une autre source d'énergie."}},
[842] = {"Conscription à la Croisée",nil, {"Suivre la route à l'ouest à partir du Poste de garde extérieur de Kargal.nnA l'intersection en T, tourner à gauche et suivre la route vers le sud.nnTrouver Sergra Sombreronce où les routes se croisent à l'intérieur de la Croisée."}},
[843] = {"La reconquête de Gann",nil, {"Gann Cime-de-pierre veut que vous tuiez 15 Excavateurs de Bael'dun et 5 Surveillants de Bael'dun.nnApporter le Journal de Khazgorm à Gann Cime-de-pierre."}},
[844] = {"La menace des trotteurs des plaines",nil, {"Collecter 7 Becs de trotteurs des plaines et les apporter à Sergra Sombreronce à la Croisée."}},
[845] = {"Les zhévras",nil, {"Tuer des Messagers zhévras et apporter 4 Sabots de zhévra à Sergra Sombreronce à la Croisée."}},
[846] = {"La revanche de Gann",nil, {"Gann Cime-de-pierre veut que vous lui apportiez 6 Fioles de Nitroglycérine, 6 Paquets de Pulpe de bois et 6 échantillons de Nitrate de sodium."}},
[847] = {"La ruse du raptor",nil, {"Parler à Tor'gan."}},
[848] = {"Spores fongiques",nil, {"Apporter 4 Spores fongiques à l'Apothicaire Helbrim à la Croisée."}},
[849] = {"La revanche de Gann",nil, {"Détruire la Machine volante à Bael Modan et retourner voir Gann Cime-de-pierre."}},
[850] = {"Les chefs des Kolkar",nil, {"Apporter la Tête de Barak à Regthar Seuil-de-mort, à l'ouest de la Croisée."}},
[851] = {"Verog le Derviche",nil, {"Apporter la Tête de Verog à Regthar Seuil-de-mort, à l'ouest de la Croisée."}},
