[869] = {"Les raptors voleurs",nil, {"Apporter 12 Têtes de raptor à Gazrog, à la Croisée."}},
[870] = {"Les Bassins oubliés",nil, {"Rendre compte de vos découvertes à Tonga Totem-runique."}},
[871] = {"Stopper les attaques des hurans",nil, {"Dans la tribu Tranchecrin, tuer 8 Sourciers, 8 Tisseuses d'épines et 3 Chasseurs, puis retourner voir Thork."}},
[872] = {"La perturbation cesse",nil, {"Tuer 8 Géomanciennes Tranchecrin, 8 Défenseurs Tranchecrin et Kreenig Grondegroin.nnPuis apporter la Défense de Kreenig Grondegroin à Thork à la Croisée."}},
[873] = {"Isha Awak",nil, {"Apporter le Cœur d'Isha Awak à Mahren Skyseer."}},
[874] = {"Mahren Skyseer",nil, {"Parler à Mahren Oeil-des-cieux."}},
