[881] = {"Echeyakee",nil, {"Apporter la Peau d'Echeyakee à Sergra Sombreronce à la Croisée."}},
[882] = {"Ishamuhale",nil, {"Apporter le Croc d'Ishamuhale à Jorn au Camp Taurajo."}},
[883] = {"Lakota'mani",nil, {"Apporter le Sabot de Lakota'mani à Jorn au Camp Taurajo."}},
[884] = {"Owatanka",nil, {"Apporter l'Epine caudale d'Owatanka à Jorn au Camp Taurajo."}},
[885] = {"Washte Pawne",nil, {"Apporter la Plume de Washte Pawne à Jorn Oeil-des-cieux au Camp Taurajo."}},
[886] = {"Les oasis des Tarides",nil, {"Parler à Tonga Totem-runique à la Croisée."}},
[887] = {"Les flibustiers des Mers du sud",nil, {"Tuer 12 Brigands et 6 Canonniers des Mers du Sud pour Gazleu de Cabestan."}},
