[899] = {"Consumé par la haine",nil, {"Apporter 60 Défenses de huran Dos-hirsute à Mankrik, à la Croisée."}},
[900] = {"Samophlange",nil, {"Fermer la Valve de contrôle du carburant, la Valve de réglage et la Valve de contrôle principale, puis utiliser à nouveau la console de contrôle."}},
[901] = {"Samophlange",nil, {"Obtenir la Clé de la console du bricoleur Brokarde, puis l'utiliser sur la console de contrôle."}},
