[903] = {"Les rôdeurs des Tarides",nil, {"Collecter 7 Griffes de Rôdeur des savanes pour Sergra Sombreronce à la Croisée."}},
[905] = {"Les Faucheurs furieux",nil, {"Tuer des raptors solécaille et récupérer leurs plumes. Placer ces plumes dans les 3 nids de Faucheurs solécaille, puis retourner voir Sergra Sombreronce à la Croisée."}},
[906] = {"Une trahison de l'intérieur",nil, {"Apporter le Crâne de Lok à Thork à la Croisée des Tarides."}},
[907] = {"Les lézards-tonnerre enragés",nil, {"Apporter 3 doses de Sang de lézard-tonnerre à Jorn Oeil-des-cieux, au Camp Taurajo."}},
