[917] = {"L'œuf d'araignée Tissebois",nil, {"Apporter un Œuf d'araignée Tissebois à Gilshalan à Aldrassil."}},
[918] = {"Les graines de sylvain",nil, {"Apporter 8 Graines de sylvain à Denalan au lac Al'Ameth."}},
[919] = {"Les pousses de sylvain",nil, {"Apporter 12 Pousses de sylvain à Denalan au lac Al'Ameth."}},
