[927] = {"Le cœur moussu",nil, {"Apporter le Cœur moussu à Denalan au lac Al'Ameth."}},
[928] = {"La couronne de la Terre",nil, {"Apporter le Récipient à moitié rempli à Corithras Ragelune à Dolanaar."}},
[929] = {"La couronne de la Terre",nil, {"Remplir la Fiole de jade et la rapporter à Corithras Ragelune à Dolanaar."}},
[930] = {"Le fruit luminescent",nil, {"Apporter le Fruit luminescent à Denalan au lac Al'Ameth."}},
[931] = {"Les racines flamboyantes",nil, {"Apporter les Racines flamboyantes à Denalan au lac Al'Ameth."}},
[932] = {"Haine folle",nil, {"Tuer le seigneur Melenas et apporter sa tête à Tallonkai Viveracine à Dolanaar."}},
[933] = {"La couronne de la Terre",nil, {"Remplir la Fiole de tourmaline et la rapporter à Corithras Ragelune à Dolanaar."}},
[934] = {"La couronne de la Terre",nil, {"Remplir la Fiole d'améthyste et la rapporter à Corithras Ragelune à Dolanaar."}},
[935] = {"La couronne de la Terre",nil, {"Apporter le Récipient rempli à l'archidruide Fandral Forteramure à Darnassus."}},
[936] = {"L'aide à l'archidruide Totem-runique",nil, {"Parler à l'archidruide Hamuul Totem-runique aux Pitons du Tonnerre."}},
[937] = {"La clairière enchantée",nil, {"Acquérir 6 Ceintures plumesang et les apporter à la Sentinelle Arynia Eclairciel à la clairière de l'Oracle."}},
[938] = {"Brume",nil, {"Escorter Brume vers la Sentinelle Arynia Eclairciel au puits de lune, près de l'arbre-oracle."}},
[939] = {"Flûte de Xavaric",nil, {"Apporter la Flûte de Xavaric et 5 échantillons de Lien maudit Jadefeu à Eridan Ventebleu, dans le sud de Gangrebois."}},
[940] = {"Teldrassil",nil, {"Remettre le rapport de l'arbre-oracle à l'archidruide Fandral Forteramure à Darnassus."}},
[941] = {"Une belle plante",nil, {"Planter le Cœur corrompu dans le Pot de Denalan."}},
[942] = {"Les absences du Prospecteur",nil, {"Apporter le mystérieux Fossile à l'Archéologue Outrepanse au port de Menethil."}},
[943] = {"Les absences du Prospecteur",nil, {"L'archéologue Outrepanse au port de Menethil veut que vous lui apportiez la Pierre de Relu et le Fossile d'Outrepanse."}},
[944] = {"Le Glaive du Maître",nil, {"Trouver des informations, puis utiliser la Fiole de divination pour créer une Coupe de divination. Utiliser la coupe pour parler à Onu."}},
[945] = {"L'évasion de Therylune",nil, {"Aider Therylune à s'échapper, puis dire à Therysil au Sanctuaire d'Aessina que sa sœur est saine et sauve."}},
