[952] = {"Bosquet des Anciens",nil, {"Livrer le message de Fandral à Onu, au bosquet des Anciens, à Sombrivage, au sud d’Auberdine."}},
[953] = {"La Chute d'Ameth'Aran",nil, {"Étudier les tablettes qui parlent d'Ameth'Aran et de sa chute, puis retourner voir la Sentinelle Tysha Lamelune à Sombrivage."}},
[954] = {"Bashal'Aran",nil, {"Trouver la source de l'aura bleue dans les Ruines de Bashal'Aran."}},
[955] = {"Bashal'Aran",nil, {"Acquérir 8 Boucles d'oreilles de grell pour Asterion à Bashal'Aran."}},
[956] = {"Bashal'Aran",nil, {"Obtenir l'Ancien sceau de pierre de lune et l'apporter à Asterion à Bashal'Aran."}},
[957] = {"Bashal'Aran",nil, {"Détruire l'Ancien sceau de pierre de lune à la flamme antique qui se trouve dans Ameth'Aran, puis retourner voir Asterion à Bashal'Aran."}},
[958] = {"Les outils des Bien-nés",nil, {"Retrouver 7 Reliques de Bien-né pour Thundris Tissevent à Auberdine."}},
[959] = {"Fuite de porto aux docks",nil, {"Le grutier Bigglefuzz de Cabestan veut que vous repreniez la Bouteille de porto vieille de 99 ans à Magglish le Dingue, qui se cache dans les Cavernes des lamentations."}},
[960] = {"Onu réfléchit",nil, {"Je réfléchis, <nom>. Je réfléchis aux raisons de la présence du Marteau du crépuscule et des nagas.nnLorsque vous serez prêt utilisez la fiole de divination pour créer une coupe de divination.nnSi vous avez perdu votre fiole de divination, en voici un autre."}},
[961] = {"Onu réfléchit",nil, {"La découverte de sectateurs du Marteau du crépuscule est inquiétante. Je dois réfléchir à leurs intentions…nnLorsque vous aurez plus d'informations, parlez-moi à travers une coupe de divination. Si vous avez besoin d'une nouvelle fiole de divination pour créer une coupe... en voici une."}},
[962] = {"Fleur de serpent",nil, {"L'Apothicaire Zamah des Pitons du Tonnerre veut que vous récoltiez 10 Fleurs de serpent."}},
[963] = {"Un amour éternel",nil, {"Libérer l'esprit d'Anaya Coursaurore et rapporter son pendentif à Cerellean Blanchegriffe à Auberdine."}},
[964] = {"Fragments de squelette",nil, {"Apporter 15 Fragments de squelette à l'Apothicaire Dithers à la Barricade, dans les Maleterres de l'ouest."}},
[965] = {"La Tour d'Althalaxx",nil, {"Trouver Balthule Frappelombre près de la Tour d'Althalaxx à Sombrivage."}},
[966] = {"La Tour d'Althalaxx",nil, {"Collecter 4 Parchemins passés pour Balthule Frappelombre près de la Tour d'Althalaxx."}},
[967] = {"La Tour d'Althalaxx",nil, {"Remettre la Lettre de Balthule à Delgren le Purificateur dans la forêt d'Orneval."}},
[968] = {"Les puissances d'en bas",nil, {"Apporter le Livre : Les puissances d'en bas à Gerrig Poigne-d'os, dans la Caverne lugubre de Forgefer."}},
[969] = {"Que la chance soit avec vous",nil, {"Collecter 4 Éclats cognegivre pour le sorcier-docteur Mau'ari à Long-guet."}},
[970] = {"La Tour d'Althalaxx",nil, {"Trouver une Gemme d'âme luminescente et la rapporter à Delgren le Purificateur au poste de Maestra."}},
[971] = {"La connaissance des profondeurs",nil, {"Apporter le « Manuscrit de Lorgalis » à Gerrig Poigne-d'os dans la Caverne lugubre de Forgefer."}},
[972] = {"Le sapta d'eau",nil, {"Prenez le sapta d'eau et trouvez l'étendue d'eau corrompue dans la forêt des Pins argentés, <nom>. J'ai une grande confiance en vos compétences."}},
[973] = {"La Tour d'Althalaxx",nil, {"Apporter le Tome d'Ilkrud Magthrull à Delgren le Purificateur au Poste de Maestra."}},
[974] = {"À la recherche de la source",nil, {"Krakle, au cratère d'Un'Goro, vous demande de découvrir la zone la plus chaude de la crête de la Fournaise.nnQuand vous trouvez une zone de chaleur, cliquez avec le bouton droit de la souris sur le thermomètre afin de prendre la température. Continuez vos recherches jusqu'à avoir trouvé la zone la plus chaude."}},
[975] = {"Cache de Mau'ari",nil, {"Attendre un instant, puis reparler au sorcier-docteur Mau'ari pour recevoir le Charme de chance."}},
[976] = {"De l’approvisionnement pour Auberdine",nil, {"Parler à Delgren le Purificateur, au Poste de Maestra, après avoir escorté Feero à travers la Forêt d'Orneval."}},
[977] = {"Yéti ? Y es-tu ?",nil, {"Collecter 2 Cornes de yéti en parfait état pour Umi Sarcafroisse, à Long-guet"}},
[978] = {"Les Indomptables lunaires",nil, {"Collecter 10 Plumes de Chouettard lunaire au Berceau-de-l'Hiver, puis retourner voir Erelas Ambreciel, à Rut'theran."}},
[979] = {"Trouver Ranshalla",nil, {"Trouver Ranshalla dans l'est du Berceau-de-l'Hiver."}},
[980] = {"Les nouvelles sources",nil, {"Voyager jusqu'au Berceau-de-l'Hiver et parler à Donova Antreneige."}},
[981] = {"La Tour d'Althalaxx",nil, {"Aller voir Delgren le Purificateur au Poste de Maestra."}},
[982] = {"Océan profond, mer immense",nil, {"Récupérer le Coffret de l'Aube argentée et le Coffret du Voile de brume, pour Gorbold Main-d'acier, à Auberdine. Ces deux objets se trouvent à bord des épaves des navires respectifs, au nord du village."}},
[983] = {"Bigobox 827",nil, {"Réunir 6 Pattes de clampants et les mettre dans la bigobox 827."}},
[984] = {"Quelle est l'ampleur de la menace ?",nil, {"Trouver un camp de furbolgs corrompus à Sombrivage et retourner voir Terenthis dans Auberdine."}},
[985] = {"Quelle est l'ampleur de la menace ?",nil, {"Tuer 8 Guides et 5 Messagers du vent Noirbois puis retourner voir Terenthis à Auberdine."}},
[986] = {"Un maître perdu",nil, {"Trouver 5 Belles peaux de traquelune et les apporter à Terenthis, à Auberdine."}},
[990] = {"Voyage vers Ashenvale",nil, {"Trouver Raene Mène-loup dans Orneval."}},
[991] = {"Le nettoyage selon Raene",nil, {"Trouver Teronis dans Orneval."}},
[992] = {"Étude des eaux de Gadgetzan",nil, {"Utiliser la Fiole de sourcier vide à la mare près du Guet de Tristesable. Une fois l'échantillon récolté, remettre la Fiole de sourcier pleine au grand géomètre Radiésthéflop à Gadgetzan."}},
[993] = {"Un maître perdu",nil, {"Trouver Volcor, à Sombrivage, et lui donner la Cape enchantée de traquelune."}},
[994] = {"Évasion par la force",nil, {"Protéger Volcor jusqu'à la route puis aller voir Terenthis à Auberdine."}},
[995] = {"Évasion par la ruse",nil, {"S'échapper de la grotte des furbolgs et aller voir Terenthis à Auberdine."}},
