[1779] = {"Le Tome de divinité",nil, {"Prendre le Symbole de vie et parler à Muiredon Forgeguerre à Forgefer."}},
[1780] = {"Le Tome de divinité",nil, {"Parler à Duthorian Rall à Hurlevent."}},
[1781] = {"Le Tome de divinité",nil, {"Porter le Symbole de vie à Gazin Tenorm à Hurlevent."}},
[1782] = {"L'armure de Furen",nil, {"Voici votre armure, <nom>. Elle résistera au plus dur des coups ou je ne m'appelle pas Furen Longbeard !nnEt je dois vous remercier. J'ai beaucoup appris en travaillant les matériaux que vous m'avez apportés. Je pense que je ferai une autre découverte capitale un de ces jours !nnJe vais transmettre ce que j'ai appris aux autres forgerons de l'Alliance.nnJe leur dirai que vous m'avez été d'une grande aide, et peut-être qu'ils vous récompenseront."}},
[1783] = {"Le Tome de divinité",nil, {"Prendre le Symbole de vie et ressusciter Narm Faulk dans Dun Morogh."}},
[1784] = {"Le Tome de divinité",nil, {"Récupérer un Script des nains Sombrefer près du lac du Lit d'Helm et le rapporter à Muiredon Forgeguerre à Forgefer."}},
[1785] = {"Le Tome de divinité",nil, {"Parler à Tiza Forgeguerre à Forgefer."}},
[1786] = {"Le Tome de divinité",nil, {"Apporter le Symbole de vie et ressusciter Henze Faulk à Elwynn."}},
[1787] = {"Le Tome de divinité",nil, {"Retrouver un Script Défias sur les Sorciers-voleurs défias d'Elwynn et le rapporter à Gazin Tenorm à Hurlevent."}},
[1788] = {"Le Tome de divinité",nil, {"Parler à Duthorian Rall à Hurlevent."}},
[1789] = {"Le Symbole de vie",nil, nil},
[1790] = {"Le Symbole de vie",nil, {"Hâtez-vous, <nom>, de nombreuses vies seront en danger si vous échouez.nnSi pour une raison quelconque vous n'arrivez pas à aider Gazin et Henze, revenez me voir et je vous donnerai un autre Symbole de vie pour cette entreprise.nnNe prenez pas cela pour de la charité : vous devez prouver que vous êtes un <classe> aussi brave que les autres, et des échecs répétés auraient des répercussions plus tard."}},
[1791] = {"La Vigie des vents",nil, {"Parler à Bath'rah la Vigie des vents."}},
[1792] = {"Arme Tourbillon",nil, {"J'ai le Cœur de tourbillon, et je suis prêt à le placer dans le réceptacle de votre choix.nnChoisissez l'arme, <nom>, et sachez que ce pouvoir est le pouvoir du Cyclonien."}},
[1793] = {"Le Tome de la bravoure",nil, {"Il a été prouvé que la Lumière était avec vous, <nom>, mais en ces temps de grands dangers, il est primordial de savoir quelles sont vos forces. Parce que des démons, des dragons et toutes sortes de créatures malfaisantes convoitent nos terres, votre courage sera mis à l'épreuve. Serez-vous assez fort ?nnLa bravoure, quelle que soit la situation, est une part importante de la voie du <classe>, au même titre que la Lumière, votre armure et votre arme. Quand vous étudiez dans la cathédrale de la Lumière, les deux leçons que vous devez apprendre sont : protéger les faibles et ne jamais perdre la foi."}},
[1794] = {"Le Tome de la bravoure",nil, nil},
[1795] = {"Le lien",nil, {"Utiliser le Tome de la Cabale pour invoquer et soumettre un Chasseur corrompu, puis rapporter le Tome de la Cabale à Strahad Farsan, à Cabestan."}},
[1796] = {"Les composants de la robe sanglante dorée enchantée",nil, {"Apporter une Robe d'Arcana à Menara Taillevide, dans les Tarides."}},
[1798] = {"À la recherche de Strahad",nil, {"Parler à Strahad Farsan dans les Tarides."}},
[1799] = {"Fragments de l'Orbe d'Orahil",nil, {"Parler aux acolytes de Menara dans la tour qui domine Cabestan et choisir de suivre l'une de leurs voies.nnEnsuite, apporter un Orbe infernal à Tabetha, dans le marécage d'Âprefange."}},
[1800] = {"La salle du trône de Lordaeron",nil, {"Emmenez votre protégé à la vieille salle du trône de Lordaeron, qui se trouve juste avant l'endroit où il faut descendre pour aller à Fossoyeuse."}},
[1801] = {"Tome de la Cabale",nil, {"Parler à Jorah Annison à Fossoyeuse."}},
[1802] = {"Tome de la Cabale",nil, {"Retrouver le Tome moisi et le Manuscrit en lambeaux pour Krom Rudebras à Forgefer."}},
[1803] = {"Tome de la Cabale",nil, {"Retrouver le Tome moisi et le Manuscrit en lambeaux pour Jorah Annison à Fossoyeuse."}},
[1804] = {"Tome de la Cabale",nil, {"Apporter le Tome recomposé et 3 Bâtonnet de canalisation à Strahad Farsan à Ratchet."}},
[1805] = {"Tome de la Cabale",nil, {"Apporter le Tome recomposé et 3 Bâtonnets de canalisation à Strahad Farsan à Cabestan."}},
[1806] = {"Le test de droiture",nil, {"Attendre que Jordan Morpuits finisse de forger votre arme."}},
[1818] = {"Parler à Dillinger",nil, {"Parler au Nécrogarde Dillinger à Brill."}},
[1819] = {"Ulag le Fendeur",nil, {"Tuer Ulag le Fendeur, puis parler au Nécrogarde Dillinger."}},
[1820] = {"Parler à Coleman",nil, {"Parler à Coleman Farthing."}},
[1821] = {"L'héritage des Agamand",nil, {"Rapporter à Coleman Farthing la Hache de la famille Agamand, l'Epée de la famille Agamand, la Masse de la famille Agamand et la Dague de la famille Agamand."}},
[1822] = {"Une arme en héritage",nil, nil},
[1823] = {"Parler à Ruga",nil, {"Parler à Ruga Totem-de-rage."}},
[1824] = {"L'épreuve du Champ des géants",nil, {"Apporter 5 Antennes mobiles à Ruga Totem-de-rage au Camp Taurajo."}},
[1825] = {"Parler à Thun'grim",nil, {"Parler à Thun'grim Mirefeu dans les Tarides."}},
[1838] = {"Armure de brutalité",nil, {"Apporter à Thun'grim Mirefeu 15 Lingots de Fer cendré, 10 Azurites en poudre, 10 Barres de Fer et une Fiole de Phlogiston."}},
[1839] = {"Ula'elek et les Gantelets de brutalité",nil, {"Parler à Ula'elek en Durotar."}},
[1840] = {"Orm Sabot-de-pierre et le Camail de brutalité",nil, {"Parler à Orm Sabot-de-pierre aux Pitons du Tonnerre."}},
[1841] = {"Velora Nitely et les Jambières de brutalité",nil, {"Parler à Velora Nitely à Fossoyeuse."}},
[1842] = {"Les sabots de satyre",nil, {"Rapporter 7 Sabots fendus de satyre à Ula'elek au village de Sen'jin en Durotar."}},
[1843] = {"Gantelets de brutalité",nil, {"Merci, <nom>. Ces Gantelets sont ma plus belle réussite. Prenez-les. En les portant, vous nous honorerez, moi et mes ancêtres."}},
[1844] = {"La corne de chimère",nil, {"Apporter une Corne galvanisée à Orm Sabot-de-pierre aux Pitons du Tonnerre."}},
[1845] = {"Camail de brutalité",nil, {"Je vous donne ce casque.nnCe sont vos actes héroïques qui vous ont conduit jusqu'à moi, <nom>, et la volonté de notre Mère la Terre... Des actes encore plus héroïques vous attendent."}},
[1846] = {"Les tibias de Dragonmaw",nil, {"Apporter 8 Solides tibias de Gueule-de-dragon à Velora Nitely, à Fossoyeuse."}},
[1847] = {"Jambières de brutalité",nil, {"Voici vos jambières, <nom>. Dites-moi si cela vous convient ou si vous avez besoin de modifications. J'essaie toujours de satisfaire mon client ! Du moins j'essayais.nnEn ce moment, je ne me soucie pas beaucoup de mon métier. Mais ça met hors jeu d'être un stupide pion du Fléau ! nnJe suppose…"}},
[1848] = {"Le haubert de brutalité",nil, {"Je sais que vous avez enduré beaucoup de privations et versé beaucoup de sang à mon service, <nom>. Voici le fruit de votre dur labeur.nnPuissiez-vous bien le porter et revenir ici un jour pour me conter les services qu'il vous aura rendus."}},
[1858] = {"La Main brisée",nil, {"Voler la Clé de Tazan puis l'utiliser pour ouvrir sa Sacoche et livrer son contenu à Therzok dans la Faille de l'ombre à Orgrimmar."}},
[1859] = {"Therzok",nil, {"Aller voir Therzok dans la Faille de l'ombre à Orgrimmar."}},
[1860] = {"Parler à Jennea",nil, {"Parler à Jennea Cannon à Hurlevent."}},
[1861] = {"Le lac Miroir",nil, {"Apporter un échantillon d'Eau du lac Miroir à Jennea Cannon à Hurlevent."}},
[1878] = {"Prime de la poche à eau",nil, {"Vous revoici, <nom> ? Si vous avez cinq outres de Bat-le-désert de plus, alors j'ai un paquet qui porte votre nom !"}},
[1879] = {"Parler à Bink",nil, {"Parler à Bink à Forgefer."}},
[1880] = {"Le Bidulotron de youplala",nil, {"Rapporter à Bink son Bidulotron de Youplala."}},
[1881] = {"Parler à Anastasia",nil, {"Aller voir Anastasia Hartwell."}},
[1882] = {"La ferme des Balnir",nil, {"Apporter des Gueules-de-loup du jardin des Balnir à Anastasia Hartwell dans le Quartier des mages de Fossoyeuse."}},
[1883] = {"Parler à Un'thuwa",nil, {"Aller voir Un'thuwa."}},
[1884] = {"Les Tas de Ju-Ju",nil, {"Détruire 4 Tas de Ju-Ju, puis retourner voir Un'thuwa au village de Sen'jin."}},
[1885] = {"Mennet Carkad",nil, {"Aller voir Mennet Carkad dans le Quartier des voleurs de Fossoyeuse."}},
[1886] = {"Les Traqueurs noirs",nil, {"Trouver la Lettre de recommandation d'Astor et l'apporter à Mennet Carkad dans le Quartier des voleurs."}},
[1898] = {"Les Traqueurs noirs",nil, {"Aller voir Andron Gant à Fossoyeuse."}},
[1899] = {"Les Traqueurs noirs",nil, {"Apporter le Grand Livre d'Andron à Mennet Carkad dans le Quartier des voleurs de Fossoyeuse."}},
[1918] = {"Les éléments souillés",nil, {"Apporter le Globe d'eau souillée à Mastok Torvesiffle, au Poste de Bois-brisé, à Orneval."}},
[1919] = {"Rendre compte à Jennea",nil, {"Parler à Jennea Cannon à Hurlevent."}},
[1920] = {"Trouver le Solitaire bleu",nil, {"Obtenir un Chant de manifestation et une Caisse de coffres de retenue placée derrière Jennea Cannon. Apporter 3 Coffres de retenue remplis à Jennea Cannon au Sanctuaire du sorcier."}},
[1921] = {"Rassembler les matériaux",nil, {"Apporter 10 morceaux d'Etoffe de lin et 6 Gemmes chargées de la faille à Wynne Larson à Hurlevent."}},
[1938] = {"Traité d'Ur sur la Magie des Ombres",nil, {"Apporter le « Traité d'Ur sur la magie des Ombres » au Grand Sorcier Andromath à Hurlevent."}},
[1939] = {"Grand sorcier Andromath",nil, {"Aller voir le Grand Sorcier Andromath."}},
[1940] = {"Soie d'araignée en parfait état",nil, {"Apporter 8 Soies d'araignée en parfait état à Wynne Larson à Hurlevent."}},
[1941] = {"La robe en tisse-mana",nil, {"Voici votre robe. C'est l’une des plus belles que j'ai confectionnées ! Portez-la avec fierté. Et si les gens vous interrogent, dites-leur qu'elle sort de l’atelier des couturiers Larson !"}},
[1942] = {"L'Habit du noeud astral",nil, {"Votre habit est prêt. Cette soie que vous avez apportée était si belle... Ça ne vous fait rien si je l'ai cousu un peu court pour pouvoir en conserver un morceau ? nnMais non, je plaisante ! Il vous ira comme un gant, au plus fort de l'aventure comme dans les occasions plus mondaines ! Profitez-en bien !"}},
[1943] = {"Parler à Deino",nil, {"Aller voir Deino."}},
[1944] = {"Les Eaux de Xavian",nil, {"Apporter l'Échantillon d'Eau de Xavian à Deino à Orgrimmar."}},
[1945] = {"Les sœurs rieuses",nil, {"Apporter 12 Cheveux de sœurs rieuses à Kil'hala à la Croisée."}},
[1946] = {"L'Habit du néant",nil, {"Voici votre robe du Néant, <nom>. À la fois confortable et solide, elle contient la magie des cheveux des sœurs rieuses. nnSi elle vous plaît, parlez-en à Deino. C'est un <classe> d'une telle puissance que je préfère être de son côté !"}},
[1947] = {"Voyager vers le Marais",nil, {"Aller voir Tabetha."}},
[1948] = {"Les Objets de Puissance",nil, {"Apporter 1 Jade et le Roncier de foudre à Tabetha, dans le marécage d'Âprefange."}},
[1949] = {"Des secrets cachés",nil, {"Parler au magus Tirth dans les Salines."}},
[1950] = {"Trouver le Scoop",nil, {"Trouver l'Incantation pour ouvrir le coffre-fort de Tirth puis retourner voir Tirth."}},
[1951] = {"Rituels du Pouvoir",nil, {"Apporter le livre des « Rituels du pouvoir » à Tabetha dans le marécage d'Âprefange."}},
[1952] = {"La baguette du mage",nil, {"Parler à Tabetha après son rituel."}},
[1953] = {"Retourner dans le Marais",nil, {"Aller voir Tabetha."}},
[1954] = {"L'orbe infernal",nil, {"Apporter un Orbe infernal à Tabetha dans le marécage d'Âprefange."}},
[1955] = {"L'Exorcisme",nil, {"Tuer le démon de l'Orbe puis parler à Tabetha."}},
[1956] = {"L'Energie d'Uldaman",nil, {"Récupérer une Source d'énergie en obsidienne et la rapporter à Tabetha dans le marécage d'Âprefange."}},
[1957] = {"Les éruptions de mana",nil, {"Tuer 12 Eruptions de mana dans la limite de temps imposée."}},
[1958] = {"L'énergie céleste",nil, {"Vous pouvez ressentir de la fierté, <nom>. Bien des mages auraient échoué face aux créatures que vous avez affrontées !nnL'Orbe céleste est prêt. Je peux aussi le fixer sur un bâton, c'est à vous de choisir. Moi, je choisirais l'Orbe, mais qui suis-je pour avoir un avis ?"}},
[1959] = {"Rendre compte à Anastasia",nil, {"Aller voir Anastasia Hartwell."}},
[1960] = {"Enquêter dans le magasin de l'Alchimiste",nil, {"Obtenir une Incantation de Manifestation et une Caisse de Coffres de retenue placée derrière Anastasia Hartwell. Apporter 3 Coffres de retenue remplis, la Caisse de Coffres de retenue et l'Incantation de Manifestation à Anastasia Hartwell à Undercity."}},
[1961] = {"Rassembler les matériaux",nil, {"Apporter 10 morceaux d'Etoffe de lin et 6 Gemmes de mana de Dalaran à Josef Gregorian."}},
[1962] = {"Robe du feu-sorcier",nil, {"Voici votre robe, <nom>. Portez-la bien."}},
[1963] = {"La Main brisée",nil, {"Tuer Tazan et apporter sa Sacoche à Therzok, à la Faille de l'ombre à Orgrimmar."}},
[1978] = {"Les Traqueurs noirs",nil, {"Livrer le Grand Livre d'Andron à Varimathras au Hall de la Dame noire à Fossoyeuse."}},
[1998] = {"Fenwick Thatros",nil, {"Tuer Fenwick Thatros et rapporter sa Tête à Mennet Carkad dans le Quartier des voleurs de Fossoyeuse."}},
[1999] = {"Les outils du métier",nil, {"Trouver le Rapport de situation de Dalaran et le remettre à Mennet Carkad dans le Quartier des voleurs de Fossoyeuse."}},
[2000] = {"Rokar Ombrelame",nil, {"Aller voir Rokar Ombrelame en Durotar."}},
[2018] = {"L'épreuve de Rokar",nil, {"Tuer le Surveillant Glibby et apporter son Sifflet à Rokar Ombrelame en Durotar."}},
[2019] = {"Les outils du métier",nil, {"Retourner voir Therzok à la Faille de l'Ombre à Orgrimmar."}},
[2038] = {"Les fournitures perdues de Bingles",nil, {"Trouver et rapporter à Bingles son matériel :nnLa Clé de Bingles, le Tournevis de Bingles, le Marteau de Bingles et le Capsuleur soufflant de Bingles."}},
[2039] = {"Trouvez Bingles",nil, {"Trouver Bingles Explonheimer, au Loch Modan."}},
[2040] = {"Assaut souterrain",nil, {"Récupérer l'Indicateur Gnoam dans les mortemines et le ramener à Shoni la Silencieuse à Hurlevent."}},
[2041] = {"Allez voir Shoni",nil, {"Aller voir Shoni la Silencieuse à Hurlevent."}},
[2078] = {"La vengeance de Gyromast",nil, {"Trouver le Premier officier de Gelkak, le Destructomembreur 4100, et le ramener à Gelkak."}},
[2098] = {"La recherche de Gyromast",nil, {"Trouver et rapporter les 3 Morceaux de la clé de Gelkak à Gelkak Gyromast."}},
[2118] = {"Terres malades",nil, {"Capturer vivant un Ours des chardons enragé, puis le ramener à Tharnariun."}},
[2138] = {"Nettoyage de l'infection",nil, {"Débarrasser la forêt de 20 Ours des chardons enragés et retourner voir Tharnariun Soigneflore, à Sombrivage."}},
[2139] = {"Espoir de Tharnariun",nil, {"Trouver et tuer la Mère de la tanière."}},
[2158] = {"Repos et détente",nil, {"Parler à l'aubergiste Farley, à l'auberge de la Fierté du lion."}},
[2159] = {"Livraison à Dolanaar",nil, {"Apporter la Livraison de Dolanaar à l'aubergiste Keldamyr."}},
[2160] = {"Des provisions pour Tannok",nil, {"Livrer la Caisse de ravitaillement à l'auberge de Tannok Martel-de-givre à Kharanos."}},
[2161] = {"Un Fardeau de Péon",nil, {"Apporter le Fardeau d'Ukor à l'aubergiste Grosk à Tranchecolline."}},
[2178] = {"Brouet de trotteur",nil, {"Apporter 5 morceaux de Viande de trotteur à Alanndarian Chantenuit, à Auberdine."}},
[2198] = {"Le Collier brisé",nil, {"Chercher celui qui a fabriqué le Collier brisé pour découvrir sa valeur potentielle."}},
[2199] = {"Le Prix de la Connaissance",nil, {"Apporter 5 Barres d'Argent à Talvash del Kissel, à Forgefer."}},
[2200] = {"Retour à Uldaman",nil, {"Chercher des indices sur le Collier de Talvash à Uldaman. Le paladin dont il a parlé est la dernière personne à l'avoir porté."}},
[2201] = {"Trouver les Gemmes",nil, {"Trouvez le rubis, le saphir et la topaze qui sont éparpillés à travers Uldaman. Une fois que vous les aurez, contactez Talvash del Kissel en utilisant la Fiole de divination qu’il vous a donnée précédemment.nnC'est dans le journal, vous savez...n* Le rubis a été caché dans une zone barricadée des Ombreforge.n* La topaze a été dissimulée dans une urne, dans l’une des zones trogg, pas très loin de nains de Alliance.n* Le saphir est en possession du chef trogg, Grimlok."}},
[2202] = {"Rechercher les composants à Uldaman",nil, {"Apporter 12 Champignons magenta à Jarkal Fondemousse à Kargath."}},
[2203] = {"Les réactifs des Terres ingrates II",nil, {"Utiliser les 3 Récipients thaumaturgiques vides sur des dragons gardiens des terres brûlées dans les Terres ingrates. Une fois remplis, les rapporter à Jarkal Fondemousse à Kargath."}},
[2204] = {"Réparer le Collier",nil, {"Obtenir une Source d'énergie de la construction la plus puissante que vous pouvez trouver à Uldaman, puis la remettre à Talvash del Kissel à Ironforge."}},
[2205] = {"Chercher le SI:7",nil, {"Remettre le colis de Keryn Sylvius à maître Mathias Shaw à la Caserne de Hurlevent."}},
[2206] = {"Effraction",nil, {"Trouver le maître des docks défias et récupérer le programme d'embarquement, pour le compte de maître Mathias Shaw."}},
[2218] = {"La voie du salut",nil, {"Parler à Hulfdan Barbe-noire à Forgefer."}},
[2238] = {"Simple subterfuge",nil, {"Trouver Onin MacHammar à Gnomeregan."}},
[2239] = {"Rapport d'Onin",nil, {"Porter le rapport d'Onin à Hulfdan à Forgefer."}},
[2240] = {"La Chambre secrète",nil, {"Lire le journal de Baelog, explorer la Chambre secrète, puis rendre compte au Prospecteur Foudrepique."}},
[2241] = {"Pomme d'amour",nil, {"Porter la Rose de Jannok à Syurna à Darnassus."}},
[2242] = {"L'appel du destin",nil, {"Trouver Sethir l'Ancien et rapporter tout indice découvert à Syurna."}},
[2258] = {"Les réactifs des Terres ingrates",nil, {"Apporter 5 Gésiers de busard, 10 Crocs de coyote des rochers et 5 Fragments d'élémentaire de roche à Jarkal Fondemousse, à Kargath, dans les Terres ingrates."}},
[2259] = {"Erion Murmombre",nil, {"Contacter Erion Murmombre à Darnassus."}},
[2260] = {"L'ordre d'Erion",nil, {"Voyager vers Hurlevent pour y rencontrer Renzik \"le Kriss\"."}},
[2278] = {"Les Disques de platine",nil, {"Parler au Gardien des pierres pour connaître le Savoir qu'il défend. Une fois découvert ce qu'ils peuvent offrir, activer les Disques de Norgannon."}},
[2279] = {"Les Disques de platine",nil, {"Apporter la version miniature des Disques de Norgannon à la Ligue des Explorateurs à Forgefer."}},
[2280] = {"Les Disques de platine",nil, {"Apporter la version miniature des Disques de Norgannon à l'un des Sages de Thunder Bluff."}},
[2281] = {"Le Rendez-vous des Carmines",nil, {"S'aventurer vers Comté-du-lac dans les Carmines et parler à Lucius."}},
[2282] = {"La scierie d'Alther",nil, {"Ouvrir le Coffret de Lucius, récupérer le Gage de voleur et le rapporter à Lucius à Comté-du-lac."}},
[2283] = {"Réparer le Collier",nil, {"Rechercher dans l'excavation d'Uldaman un Collier de prix et le rapporter à Dran Droffers à Orgrimmar. Ce Collier peut être endommagé."}},
[2284] = {"Réparer le Collier, deuxième prise",nil, {"Trouver, dans les profondeurs d'Uldaman, des indices sur la localisation des Gemmes."}},
[2298] = {"Kingly Shakedown",nil, {"Aller à Hurlevent et chercher Renzik \"le Kriss\"."}},
[2299] = {"Cap sur Hulfdan !",nil, {"Se rendre à Forgefer et parler à Hulfdan Barbe-noire."}},
[2300] = {"SI:7",nil, {"Aller à Hurlevent et chercher Renzik \"le Kriss\"."}},
[2318] = {"La Traduction du Journal",nil, {"Trouver quelqu'un qui puisse traduire le journal du paladin. L'endroit le plus proche est Kargath, dans les Terres ingrates."}},
[2338] = {"La traduction du Journal",nil, {"Laisser Jarkal emprunter le Collier en échange de la traduction du Journal."}},
[2339] = {"Trouver les Gemmes et la Source d'énergie",nil, {"Récupérer, dans Uldaman, les trois gemmes et une source d'énergie pour le collier, puis les rapporter à Jarkal Fondemousse, à Kargath. Jarkal pense que vous pouvez trouver une source d'énergie sur la machine la plus puissante d'Uldaman.nnC'est dans le journal, vous savez...n* Le rubis a été caché dans une zone barricadée des Ombreforge.n* La topaze a été dissimulée dans une urne, dans l'une des zones trogg, pas très loin de nains de Alliance.n* Le saphir est possession du chef trogg, Grimelok."}},
[2340] = {"Livrer les Gemmes",nil, {"Apporter Les gemmes et le collier récupérés à Dran Droffers, à Orgrimmar."}},
[2341] = {"Réparer le collier, troisième prise",nil, {"Rendre visite à Jarkal Fondemousse à Kargath pour constater (ou non) son succès."}},
[2342] = {"Des Trésors à recouvrer",nil, {"Rapporter à Patrick Garrett, à Fossoyeuse, son Trésor de famille, qui se trouve dans le Coffre de famille dans les communs du sud d'Uldaman."}},
[2358] = {"Cornes de Nez'ra",nil, {"Rapporter les Cornes de Nez'ra à Arantir, à Comté-du-Lac."}},
[2359] = {"La Tour de Klaven",nil, {"Voler la Clé de la Tour défias, pénétrer dans la Tour et trouver ce que contient le Coffre du bois de la Pénombre. Rapporter les informations à Mathias Shaw à Hurlevent. Lire le journal de Kearnen pour savoir ce qui se passe dans la tour."}},
[2360] = {"Mathias et les Défias",nil, {"Se rendre dans la marche de l'Ouest et trouver l'Agent Kearnen pour un débriefing."}},
[2361] = {"Réparer le Collier",nil, {"C'est avec joie que je vous donne ce collier, <classe>. Vous m'avez rendu un service inestimable. Portez-le avec fierté et n'hésitez pas à citer fréquemment et à voix haute le nom du fabriquant ! nnEt si vous pouviez préciser au passage qu'il n'est pas maudit, ce serait parfait."}},
[2378] = {"Trouver la Main brisée",nil, {"Voyager vers Orgrimmar pour y parler à Shenthul, dans la Faille de l'ombre."}},
[2379] = {"Zando'zan",nil, {"Parler à Zando'zan à la Faille de l'ombre."}},
[2380] = {"Cap sur Orgrimmar !",nil, {"S'adresser à Shenthul à la Faille de l'ombre à Orgrimmar."}},
