[3443] = {"Forger le manche",nil, {"Apporter 8 Dagues plaquées en thorium à Kalaran Lamevent."}},
[3444] = {"Le Cercle de pierres",nil, {"Récupérer le Cercle de pierres à l'Atelier de Marvon Chercherivet à Cabestan."}},
[3445] = {"Le Temple englouti",nil, {"Trouver Marvon Chercherivet à Tanaris."}},
[3446] = {"Dans les profondeurs",nil, {"Trouver l'Autel d'Hakkar dans le Temple englouti du marais des Chagrins."}},
[3447] = {"Le secret du cercle",nil, {"Voyager jusqu'au Temple englouti et découvrir le secret du cercle de statues."}},
[3448] = {"La transmission du fardeau",nil, {"Parler au mage Tymor à Forgefer."}},
[3449] = {"Les runes des arcanes",nil, {"À l'aide de la Trousse de dessin, effectuer des reproductions de la Rune de Beth'Amara, de la Rune de Jin'yael, de la Rune de Markri, de la Rune de Sael'hai, puis se diriger vers la petite île près de la Péninsule méridionale d'Azshara et signaler au Pilote Xiggs Allumèche de venir les prendre."}},
[3450] = {"Une collecte facile",nil, {"Parler à Xiggs Allumèche à Forgefer."}},
[3451] = {"Signal de la récupération",nil, {"Parler à Xiggs Allumèche à Forgefer et en obtenir un Lance-fusée standard."}},
[3452] = {"L'enveloppe de la flamme",nil, {"Trouver et apporter un Symbole de Ragnaros à Kalaran Lamevent."}},
[3453] = {"La Torche de vindicte",nil, {"Attendre que Kalaran Lamevent termine la Torche de vindicte."}},
[3454] = {"La Torche de vindicte",nil, {"Prendre la Torche de vindicte."}},
[3461] = {"Le retour vers Tymor",nil, {"Rapporter la Trousse de dessin du mage Tymor au mage Tymor, à Forgefer."}},
[3462] = {"L'écuyer Maltrake",nil, {"Parler à l'écuyer Maltrake."}},
[3463] = {"Allumer le feu !",nil, {"Mettre le feu aux Tours de garde nord, sud, est et ouest en utilisant la Torche de vindicte à l'intérieur de chaque tour."}},
[3481] = {"Bibelots...",nil, {"Ouvrir le coffre."}},
[3482] = {"<NYI> <TXT> The Pocked Black Box",nil, {"Porter la boîte à Kravel."}},
[3483] = {"Signal de la récupération",nil, {"Oh, un autre lance-fusées, hein ? Je peux le faire.nnN'oubliez pas, la pointe sud d'Azshara. C'est une toute petite île transformée en piste d'atterrissage. Vous ne pouvez pas la manquer si vous êtes au bon endroit."}},
[3501] = {"Tout se compte en grandes quantités",nil, {"Apporter des Fragments imparfaits de draenéthyste à Kum'isha et toucher une récompense pour chaque fragment rapporté."}},
[3502] = {"Au rebut pour les draeneï",nil, {"Apporter des Fragments imparfaits de draenéthyste à Kum'isha. N'importe quel monstre des Terres foudroyées peut détenir un fragment."}},
[3503] = {"Réunion avec le Maître",nil, {"Autrefois, je pouvais demander tout ce que je voulais à ceux qui souhaitaient voir mon maître. À présent, vous n'avez qu'à demander et je vous enverrai à lui.nnCela me chagrine mais je ne discute pas ses ordres. Le seul plaisir qu'il me reste est de me moquer des vêtements ridicules que vous portez et de votre mine pitoyable. Même cela m'ennuie...nnMêmes ces bavardages m'ennuient ! Souhaitez-vous voir mon maître ou non ?"}},
[3504] = {"Trahison",nil, {"Apporter la Lettre scellée à Ag'tor Poing-de-sang en Azshara."}},
[3505] = {"Trahison",nil, {"Tuer 10 Vengeurs elfes de sang et 10 Géomètres elfes de sang avant de rechercher le campement du magus Rimtori. Une fois le camp trouvé, chercher quelque chose d'important pour elle et qui peut être utilisé pour l'obliger à se battre."}},
[3506] = {"Trahison",nil, {"Détruire le Cristal de focalisation des arcanes lié au magus Rimtori et l'assassiner ensuite. Puis apporter la tête du magus Rimtori à Ag'tor Poing-de-sang en Azshara."}},
[3507] = {"Trahison",nil, {"Aller voir Belgrom Cogneroche à Orgrimmar."}},
[3508] = {"Briser l'Amulette",nil, {"Attendre que Loramus ait achevé le rituel de Lumière."}},
[3509] = {"Le nom de la bête",nil, {"Aller voir le seigneur Arkkoroc en Azshara. Commencer la recherche au bord de l'eau pour trouver le temple d'Arkkoran."}},
[3510] = {"Le nom de la bête",nil, {"Assassiner Hetaera et apporter la Tête ensanglantée d'Hetaera, la Tête battue d'Hetaera et la Tête meurtrie d'Hetaera au seigneur Arkkoroc."}},
[3511] = {"Le nom de la bête",nil, {"Rapporter le durcisseur à Loramus Thalipedes."}},
[3512] = {"Par les mots d'Eranikus",nil, {"Se rendre dans la partie orientale du Berceau-de-l'Hiver et aller voir Umbranse l'implorateur d'esprit."}},
[3513] = {"Le Parchemin runique",nil, {"Apporter le Parchemin runique à la tour de garde Nord dans les Tarides."}},
[3514] = {"Présence de la Horde",nil, {"Tuer le chef de l'Antre des Serres, Rynthariel le Maître des clés."}},
[3517] = {"L'art du vol",nil, {"Trouver la Tablette de Beth'Amara, la Tablette de Jin'yael, la Tablette de Markri et la Tablette de Sael'hai puis retourner voir Jediga en Azshara."}},
[3518] = {"Livraison à Magatha",nil, {"Apporter la Tablette de Beth'Amara à Magatha aux Pitons du Tonnerre."}},
[3519] = {"Un ami dans le besoin",nil, {"Parler à Dirania Brillargent, à Sombrevallon."}},
[3520] = {"Esprits de Hurleurs",nil, {"Capturer l'Esprit de 3 Hurleurs en Féralas, puis retourner voir Yeh'kinya à Port Gentepression."}},
[3521] = {"L'antidote d'Iverron",nil, {"Trouver 7 Champignons jacinthe, 4 Lys pétales de lune et 1 Ichor d'araignée Tissebois pour Dirania Brillargent dans Sombrevallon."}},
[3522] = {"Antidote d'Iverron",nil, {"Apporter l'Antidote d'Iverron à Iverron avant que la limite de temps ne soit atteinte. Iverron se trouve près de la grotte, au nord."}},
[3523] = {"Le Fléau des Souilles",nil, {"Pour aider Belnistrasz, lui parler de nouveau et lui rendre la Pierre de voeu qu'il vous avait donnée."}},
[3524] = {"Echouage",nil, {"Récupérer des Os de créature marine sur la créature échouée, au sud d'Auberdine, puis les apporter à Gwennyth Bly'Leggonde, à Auberdine."}},
[3525] = {"L'extinction de l'idole",nil, {"Escorter Belnistrasz jusqu'à l'Idole des hurans dans les Souilles de Tranchebauge.nnProtéger Belnistrasz tandis qu'il exécute le rituel pour arrêter l'idole."}},
[3526] = {"Ingénierie des gobelins",nil, {"Pour en apprendre davantage sur l'ingénierie gobeline, apporter le Manuel d'Ingénierie à Nixx Elastirouage dans Gadgetzan."}},
[3527] = {"La prophétie de Mosh'aru",nil, {"Apporter les Première et Deuxième tablettes Mosh'Aru à Yeh'kinya, à Tanaris."}},
[3528] = {"Le dieu Hakkar",nil, {"Apporter l'Oeuf Rempli d'Hakkar à Yeh'kinya dans Tanaris."}},
[3541] = {"Livraison pour Jes'rimon",nil, {"Apporter la Tablette de Jin'yael à Jes'rimon à Orgrimmar."}},
[3542] = {"Livraison à Andron Gant",nil, {"Apporter la Tablette de Markri à Andron Gant à Fossoyeuse."}},
[3561] = {"Livraison à l'archimage Xylem",nil, {"Apporter la Tablette de Sael'hai à l'archimage Xylem en Azshara."}},
[3562] = {"Le Paiement de Magatha à Jediga",nil, {"Apporter la Note de Magatha à Jediga dans Azshara."}},
[3563] = {"Le Paiement de Jes'rimon à Jediga",nil, {"Apporter la Note de Jes'rimon à Jediga en Azshara."}},
[3564] = {"Le Paiement d'Andron à Jediga",nil, {"Apporter la Note d'Andron à Jediga en Azshara."}},
[3565] = {"Le Paiement de Xylem à Jediga",nil, {"Apporter la Note de Xylem à Jediga en Azshara."}},
[3566] = {"Debout, Obsidion !",nil, {"Assassiner Lathoric le Noir et Obsidion, puis retourner voir Thorius, à Forgefer, avec la Tête de Lathoric le Noir et le Coeur d'Obsidion."}},
[3567] = {"Au sommet",nil, {"Souhaitez-vous admirer la vue depuis le sommet de l'un des Colosses jumeaux ? Je peux vous y envoyer, <race>.nnRéfléchissez quand même à la façon de redescendre. C'est un voyage que l'on ne peut faire que dans un seul sens."}},
[3568] = {"Corruption Suintante",nil, {"Avant de retourner voir le Chimiste Cuely, remplir les 4 Fioles vides dans les cuvettes de marées le long de la côte des Ruines d'Eldarath, en Azshara."}},
[3569] = {"Corruption Suintante",nil, {"Donner l'élixir de Cuely à Thersa Chant-du-vent, à Fossoyeuse."}},
[3570] = {"Corruption Suintante",nil, {"Je n'aurais jamais rêvé que l'élixir puisse prendre forme aussi rapidement. Merci, <nom>. Je vais travailler sur le reste de ces échantillons tout de suite. Je suis sûr qu'ils seront bientôt utilisables par Dame Sylvanas et les Réprouvés."}},
[3601] = {"Kim'jael évidemment !",nil, {"Fouiller le Camp de base thalassien pour trouver la Boussole, la Lunette, le Poulet farci et le Wizzlegoober de Kim'Jael, puis les lui rapporter en Azshara."}},
[3602] = {"Azsharite",nil, {"Trouver 20 Azsharites cristallisées au sud d'Azshara. Utiliser éventuellement le gangrechien pour 'renifler' l'emplacement où sont intelligemment cachés les dépôts.nnRetourner voir Loramus une fois votre tâche accomplie."}},
[3621] = {"La Formation de Felbane",nil, {"Apporter la cargaison à Galvan l'Ancien dans la vallée de Strangleronce."}},
[3625] = {"Armes Fel en Azsharite enchantée",nil, {"Attendre que Galvan ait fini de forger les armes."}},
[3626] = {"Retour aux Terres foudroyées",nil, {"Retourner voir le Héros de la Horde tombé au combat."}},
[3627] = {"Réunir les morceaux de l'Amulette brisée.",nil, {"Assassiner Grol le Destructeur, dame Sevine et l'archimage Allistarj. Prendre sur leurs cadavres l'Amulette de Grol, l'Amulette de Sevine et l'Amulette d'Allistarj.nnN'oubliez pas d'UTILISER vos armes gangrenées en azsharite enchantée pour affaiblir la triade (ils ne peuvent pas être tués autrement), mais faites-le sagement..."}},
[3628] = {"Vous êtes Rakh'likh, démon",nil, {"Détruire Razelikh le Souilleur et apporter la Corne brisée du Souilleur au Héros de la Horde tombé au combat. Rendre aussi l'Amulette du Souilleur au Héros pour qu'il puisse la détruire, afin de l'empêcher de tomber dans de mauvaises mains."}},
[3629] = {"Ingénierie des gobelins",nil, {"Pour en apprendre davantage sur l'ingénierie gobeline, apporter le Manuel d'Ingénierie à Nixx Elastirouage dans Gadgetzan."}},
[3630] = {"Ingénierie des gnomes",nil, {"Pour en apprendre davantage sur l'ingénierie gnome, apporter le Manuel d'Ingénierie au maître-artisan Suprétincelle, à Forgefer."}},
[3631] = {"Invocation d'un palefroi corrompu",nil, {"Parler à Strahad Farsan, dans les Tarides."}},
[3632] = {"Ingénierie des gnomes",nil, {"Pour en apprendre davantage sur l'ingénierie gnome, apporter le Manuel d'Ingénierie au maître-artisan Suprétincelle, à Forgefer."}},
[3633] = {"Ingénierie des gobelins",nil, {"Pour en apprendre davantage sur l'ingénierie gobeline, apporter le Manuel d'Ingénierie à Nixx Elastirouage, à Gadgetzan."}},
[3634] = {"Ingénierie des gnomes",nil, {"Pour en apprendre davantage sur l'ingénierie gnome, apporter le Manuel d'Ingénierie au maître-artisan Suprétincelle, à Forgefer."}},
[3635] = {"Ingénierie des gnomes",nil, {"Pour en apprendre davantage sur l'ingénierie gnome, apporter le Manuel d'Ingénierie à Orguignard Odipnoticus à Baie-du-Butin."}},
[3636] = {"Apportez la Lumière",nil, {"Tuer Amnennar le Porte-froid, aux Souilles de Tranchebauge, pour l'Archevêque Benedictus."}},
[3637] = {"Ingénierie des gnomes",nil, {"Pour en apprendre davantage sur l'ingénierie gnome, apporter le Manuel d'Ingénierie à Orguignard Odipnoticus à Baie-du-Butin."}},
[3638] = {"Le Vœu du secret",nil, {"Pour devenir un Ingénieur gobelin, cliquer avec le bouton droit sur le Vœu de secret et retourner voir une fois encore Nixx Elastirouage dans Gadgetzan."}},
[3639] = {"Montrez votre travail",nil, {"Apporter 20 Grandes Bombes en fer, 20 Dynamites Solides et 5 Moutons Explosifs à Nixx Elastirouage dans Gadgetzan."}},
[3640] = {"Le Vœu du secret",nil, {"Pour devenir un Ingénieur gnome, cliquer avec le bouton droit sur le Vœu de secret et retourner voir une fois encore le maître-artisan Suprétincelle, à Forgefer."}},
[3641] = {"Montrez votre travail",nil, {"Apporter 6 Tubes en mithril, 1 Lunette de précision et 2 Leurres perfectionnés au Maître artisan Suprétincelle à Forgefer."}},
[3642] = {"Le Vœu du secret",nil, {"Pour devenir un Ingénieur gnome, cliquer avec le bouton droit sur le Vœu de secret et retourner voir une fois encore Oglethrope Odipnoticus à Baie-du-Butin."}},
[3643] = {"Montrez votre travail",nil, {"Apporter 6 Tubes en mithril, 1 Lunette de précision et 2 Leurres perfectionnés à Orguignard Odipnoticus à Baie-du-Butin."}},
[3644] = {"Le renouvellement de la Carte de membre",nil, {"Pour renouveler votre carte de membre, vous devez vous acquitter du prix de renouvellement qui est de deux pièces d'or."}},
[3645] = {"Le renouvellement de la Carte de membre",nil, {"Pour renouveler votre carte de membre, vous devez vous acquitter du prix de renouvellement qui est de deux pièces d'or."}},
[3646] = {"Le renouvellement de la Carte de membre",nil, {"Pour renouveler votre carte de membre, vous devez vous acquitter du prix de renouvellement qui est de deux pièces d'or."}},
[3647] = {"Le renouvellement de la Carte de membre",nil, {"Pour renouveler votre carte de membre, vous devez vous acquitter du prix de renouvellement qui est de deux pièces d'or."}},
[3661] = {"Dans les faveurs d'Elune ?",nil, {"Collecter 15 Plumes d'Indomptable dans les Hinterlands, pour Erelas Ambreciel au village de Rut'theran."}},
[3681] = {"Le Tome de divinité",nil, {"Parler à Duthorian Rall à Hurlevent."}},
[3701] = {"Les ruines fumantes de Thaurissan",nil, {"Aller dans les ruines de Thaurissan, dans les Steppes ardentes. Récupérer des informations sur les Reliques de Thaurissan, puis retourner auprès de l'Historienne royale Archesonus."}},
[3702] = {"Les ruines fumantes de Thaurissan",nil, {"Écouter l'historienne royale Archesonus raconter l'histoire de Thaurissan."}},
[3721] = {"Un OOX rien que pour vous",nil, {"Vous avez récupéré trois de mes robots guidés OOX ! Vous avez fait bien plus que le nécessaire, aussi vais-je vous donner quelque chose de spécial pour vous remercier. C'est une version miniature du robot qu'à présent vous devez connaître et aimer : l'OOX !nnNormalement, je ne laisserais à personne un appareil aux capacités si dangereuses, mais vous m'avez été d'un réel secours. De plus, les mécanismes de combat et le système de guidage ont été mis hors service.nnMais il n'en reste pas moins qu'il s'agit bien là d'un OOX, bon sang !"}},
[3741] = {"Le collier d'Hilary",nil, {"Trouver le Collier d'Hilary et le rapporter à Hilary à Comté-du-Lac."}},
[3761] = {"L'humus d'Un'Goro",nil, {"Apporter 20 Échantillons d'humus d'Un'Goro à Ghede, aux Pitons du Tonnerre."}},
[3762] = {"L'aide à l'archidruide Totem-runique",nil, {"Parler à l'archidruide Hamuul Totem-runique aux Pitons du Tonnerre."}},
[3763] = {"L'aide à l'archidruide Forteramure",nil, {"Parler à l'archidruide Fandral Forteramure à Darnassus."}},
[3764] = {"L'humus d'Un'Goro",nil, {"Apporter 20 échantillons d'Humus d'Un'Goro à Jenal dans l'Enclave cénarienne à Darnassus."}},
[3765] = {"La dépravation au loin",nil, {"Aller voir Gershala Murmenuit à Auberdine."}},
[3781] = {"Recherches sur la fibre d'aurore",nil, {"Apporter le Bon pour des graines de l'archidruide à Mathrengyl Pasdelours dans l'Enclave cénarienne à Darnassus."}},
[3782] = {"La recherche sur la Fibre d'aurore",nil, {"Apporter le Bon pour des graines à Bashana Totem-runique aux Pitons du Tonnerre."}},
[3783] = {"Yéti ? Y es-tu ?",nil, {"Collecter 10 Fourrures épaisses de yéti pour Umi Sarcafroisse, à Long-guet."}},
[3784] = {"L'aide à l'archidruide Totem-runique",nil, {"Parler à l'archidruide Hamuul Totem-runique aux Pitons du Tonnerre."}},
[3785] = {"Recherches sur la Fibre d'aurore",nil, {"Utiliser une Sacoche de printemps avec une pochette de graines de Tharlendis et deux Echantillons d'Humus d'Un'Goro pour essayer de cultiver des Fibres d'aurore.nnApporter 10 Fibres d'aurore à Mathrengyl Bearwalker à Darnassus."}},
[3786] = {"Recherches sur la Fibre d'aurore",nil, {"Utiliser une Sacoche de printemps avec une pochette de graines de Tharlendis et deux Echantillons d'Humus d'Un'Goro pour essayer de cultiver des Fibres d'aurore.nnApporter 10 Fibres d'aurore à Bashana Runetotem à Thunder Bluff."}},
[3787] = {"La requête de Gentecime",nil, {"Parler à Quintis Gentecime dans le bastion de Pennelune."}},
[3788] = {"La requête de Gentecime",nil, {"Parler à Quintis Gentecime dans le bastion de Pennelune."}},
[3789] = {"Assister l'archidruide Staghelm",nil, {"Parler à l'archidruide Fandral Forteramure à Darnassus."}},
[3790] = {"Assister l'archidruide Staghelm",nil, {"Parler à l'archidruide Fandral Forteramure à Darnassus."}},
[3791] = {"Le Mystère des Fibres d'aurore",nil, {"Apporter 10 Fibres d'aurore à Quintis Jonespyre au bastion de Feathermoon."}},
[3792] = {"De la Fibre d'aurore au bastion de Feathermoon",nil, {"Je vous salue à nouveau, <nom>. Mes recherches progressent plutôt bien, mais si vous aviez cinq Fibres d'aurore supplémentaires, je serais heureux de vous en débarrasser. Mais “heureux” n'est qu'une figure de style. Plus j'en ai autour de moi, et plus je me sens mal à l'aise. Mes recherches nécessitent cependant que je me hâte et que j'en finisse."}},
[3801] = {"Héritage Sombrefer",nil, {"Parler à Franclorn Le Forgebusier si obtenir une clé de la Cité majeure vous intéresse."}},
[3802] = {"Héritage Sombrefer",nil, {"Tuer Fineous Sombrevire et récupérer le grand marteau, Souillefer. Apporter Souillefer au sanctuaire de Thaurissan et le placer sur la statue de Franclorn Le Forgebusier."}},
[3803] = {"De la fibre d'aurore pour Darnassus",nil, {"L'archidruide cherche davantage de Fibres d'aurore pour que le Cercle cénarien puisse poursuivre ses recherches. Nous les prenons par lot de dix. Nous récompenserons votre cueillette par des objets qui pourront être utiles. Lorsque vous en aurez dix, faites-le-moi savoir et je vous récompenserai en conséquence."}},
[3804] = {"Des Fibres d'aurore pour les Pitons du Tonnerre",nil, {"L'archidruide cherche davantage de Fibre d'aurores pour que le Cercle cénarien puisse poursuivre ses recherches sur le cratère d'Un'Goro. Nous récompenserons votre cueillette par des objets qui pourront être utiles. Lorsque vous en aurez dix, faites-le-moi savoir et je vous récompenserai en conséquence."}},
[3821] = {"Le Rocher des Cognepeurs",nil, {"S'aventurer dans les Steppes ardentes et découvrir ce qu'il est advenu de Sha'ni Fière-défense."}},
[3822] = {"Krom'Grul",nil, {"Récupérer l'Anneau de nez de Sha'ni sur Krom'Grul, et le rapporter à Thal'trak, à Kargath. Fouiller les différentes grottes et mines du Rocher des Cognepeurs."}},
[3823] = {"Exterminer les Brûlentrailles",nil, {"Tuer 15 Ogres-mages Brûlentrailles, 7 Ogres Brûlentrailles et 7 Brutes Brûlentrailles, puis retourner voir Oralius."}},
[3824] = {"Le seigneur-brute Gor'tesh",nil, {"Trouver Gor'tesh, quelque part à l'ouest du rocher des Cognepeurs. Le tuer et rapporter la Tête coupée de Gor'tesh à Oralius, dans les Steppes ardentes."}},
[3825] = {"Une tête d'ogre au bout d'une pique = une fête",nil, {"Prendre la Tête coupée de Gor'tesh et la placer au sommet du Rocher des Cognepeurs. Chercher un monticule de terre meuble pour planter la pique."}},
[3841] = {"Un orphelin à la recherche d'un foyer",nil, {"Parler à Quentin aux Mille pointes."}},
[3842] = {"Une courte incubation",nil, {"Apporter 2 Elixirs de Robustesse à Quentin, aux Mille pointes."}},
[3843] = {"Un tout nouveau membre dans la famille",nil, {"Emmenez votre Oeuf fragile de Fée fléchetteuse dans les Hinterlands et parlez à Agnar Beastamer. Dépêchez-vous, vous n’avez qu’une heure pour le trouver avant que le petit ne meure dans l’oeuf."}},
[3844] = {"C'est un secret pour tout le monde",nil, {"Rechercher dans l'étang un indice sur l'identité du propriétaire du radeau brisé."}},
[3845] = {"C'est un secret pour tout le monde",nil, {"Examiner les objets du paquet et les apporter à leur propriétaire."}},
[3861] = {"COT-COT-CODEC !",nil, {"Trouver de la Nourriture spéciale pour poulet et l'apporter à la poule que vous voulez aider. Lorsque vous l'aurez trouvée, encouragez-la (/acclame) avant d'essayer de lui donner la Nourriture spéciale pour poulet."}},
[3881] = {"Le sauvetage de l'expédition",nil, {"Apporter une Caisse de nourriture et de l'Equipement de recherche à Williden Marshal dans le Cratère d'Un'Goro."}},
[3882] = {"Faites rouler les os",nil, {"Apporter 8 Os de dinosaure à Etincelle Minerien, au cratère d'Un'Goro."}},
[3883] = {"Écologie étrangère",nil, {"Utiliser la Fiole de raclement pour récolter un Échantillon de paroi de la ruche dans une des nurseries de la ruche Gorishi, à l'intérieur du cratère d'Un'Goro. Chercher les chambres qui contiennent des larves suspendues.nnApporter l'Échantillon de paroi de la ruche à Hol'anyee Marshal dans le cratère d'Un'Goro."}},
[3884] = {"Le journal de Williden",nil, {"Rapporter le Journal à Williden Marshal quelque part dans le Cratère d'Un'Goro."}},
[3901] = {"Faites cliqueter les thorax !",nil, {"Tuez 12 Squelettes Cliquethorax, puis retournez voir le Prêtre des ombres Sarvis au Glas."}},
[3902] = {"La chasse aux trésors",nil, {"Fouiller Le Glas et ses alentours pour trouver 6 caisses de Fournitures de récupération, et les ramener au Nécrogarde Saltain."}},
[3903] = {"Milly Osworth",nil, {"Parler à Milly Osworth."}},
[3904] = {"Les vendanges de Milly",nil, {"Apporter 8 seaux de la récolte de Milly à Milly Osworth à l'Abbaye de Comté-du-nord."}},
[3905] = {"Manifeste des vendanges",nil, {"Apporter le Manifeste des vendanges à Frère Neals dans l'Abbaye de Comté-du-nord."}},
[3906] = {"Discordance des flammes",nil, {"Partir pour la carrière dans le mont Rochenoire et tuer le Grand seigneur Pyron. Retourner voir Cœur-de-tonnerre une fois votre mission accomplie."}},
[3907] = {"Discordance des flammes",nil, {"Pénétrez dans les Profondeurs de Rochenoire et traquez le Seigneur Incendius. Tuez-le puis donner tous les renseignements que vous pourrez trouver à Cœur-de-tonnerre."}},
[3908] = {"C'est un Secret pour tout le monde",nil, {"Apporter l'Epée de Linken à Donova Antreneige au Berceau-de-l'Hiver."}},
[3909] = {"L'élixir de Videre",nil, {"Rechercher Gregan Gerbebière au nord de Féralas. Apprendre de lui comment vous pouvez acquérir l'élixir de Videre, puis retourner voir Donova Antreneige, au Berceau-de-l'Hiver."}},
[3911] = {"Le dernier élément",nil, {"Partir pour les Profondeurs de Rochenoire et trouver 10 Essences des éléments. Commencer vos recherches par les golems et par les créateurs de golems. Vivian Lagrave a aussi marmonné quelque chose à propos d'élémentaires."}},
[3912] = {"Rendez-vous à la tombe",nil, {"Se rendre au cimetière au-dehors de Gadgetzan et boire l'élixir de Videre. Suivre le reste des instructions de Donova pour trouver Gaeriyan."}},
[3913] = {"Une situation grave",nil, {"Ressusciter, puis rechercher la pierre tombale que Gaeriyan vous a demandé de trouver."}},
[3914] = {"L'épée de Linken",nil, {"Remettre l'Epée excellente de Linken à Linken dans le cratère d'Un'Goro."}},
[3921] = {"Wenikee Boltbucket",nil, {"Apporter le Samophlange cassé à Wenikee Bakaboulon."}},
[3922] = {"Les Petites pièces",nil, {"Apporter 15 Rondelles de métal à Wenikee Bakaboulon dans les Tarides."}},
[3923] = {"Rilli Greasygob",nil, {"Apporter le Samophlange cassé à Rilli Grassougob à Orgrimmar."}},
[3924] = {"Manuel du Samophlange",nil, {"Apporter le Manuel du Samophlange à Rilli Grassougob à Orgrimmar."}},
[3941] = {"Un gnome dans le besoin",nil, {"Trouver J.D. Collie dans la caverne du refuge des Marshal."}},
[3942] = {"Les souvenirs de Linken",nil, {"Trouver Eridan Ventebleu dans le sud de Gangrebois."}},
[3961] = {"L'aventure de Linken",nil, {"Apporter le Totem d'argent d'Aquementas à Linken au refuge des Marshal."}},
[3962] = {"C'est dangereux d'y aller seul",nil, {"Partir pour la crête de la Fournaise, au sud du refuge des Marshal.nnTrouver Coursembrase et utiliser le Totem pour dissiper son aura protectrice. Une fois Coursembrase vaincu, se procurer la Flamme dorée."}},
[3981] = {"Le commandant Gor'shak",nil, {"Trouver le commandant Gor'shak dans les Profondeurs de Rochenoire.nnVous vous souvenez que le dessin maladroit représentait également des barreaux au-dessus du portrait. Peut-être devriez-vous rechercher un genre de prison."}},
[3982] = {"Que se passe-t-il ?",nil, {"Défendre Gor'shak."}},
[4001] = {"Que se passe-t-il ?",nil, {"Parler à Kharan Force-martel et obtenir autant de renseignements que possible sur l'enlèvement de la princesse Moira Barbe-de-bronze. Apporter ces renseignements à Thrall à Orgrimmar.nnGor'shak a dit que Kharan était détenu dans une cellule proche."}},
[4002] = {"Les Royaumes de l'est",nil, {"Parler à Thrall si vous êtes <prêt/prête> à vous charger de la mission qu'il a prévue."}},
[4003] = {"Un sauvetage royal",nil, {"Tuer l'Empereur Thaurissan et libérer la Princesse Moira Barbe-de-bronze de son sort maléfique."}},
[4004] = {"La Princesse sauvée ?",nil, {"Retourner voir Thrall !"}},
[4005] = {"Aquementas",nil, {"Apporter le Livre d'Aquor, les Griffes argentées et le Coeur d'Arbrefer à Tanaris.nnUtiliser le Livre d'Aquor pour invoquer Aquementas.nnAprès avoir terminé le totem, l'apporter à J.D. Collie au refuge des Marshal."}},
[4021] = {"La contre-attaque !",nil, {"Apporter un Morceau de la bannière de Krom'zar à Regthar Seuil-de-mort, à l'ouest de la Croisée."}},
[4022] = {"Un goût de flammes",nil, {"Montrer à Cyrus Therepentous l'Ecaille des dragons noirs que vous avez reçue de Kalaran Windblade."}},
[4023] = {"Un goût de flammes",nil, {"Montrer à Cyrus Lerepenti une preuve de votre valeur.nnVous avez le sentiment que Cyrus sait déjà que vous êtes indigne."}},
[4024] = {"Un goût de flammes",nil, {"Partez pour les Profondeurs de Rochenoire et tuez Bael'Gar.nnVous savez seulement que le géant demeure à l'intérieur des Profondeurs de Rochenoire. N'oublier pas d'utiliser l'Ecaille des dragons noirs altérée pour capturer l'Essence flamboyante.nnApporter l'Essence flamboyante enchâssée à Cyrus Lerepenti."}},
[4041] = {"L'élixir de Videre",nil, {"Apportez-moi de l'Evoroot, et je vous ferai de l'élixir de Videre sans problème.nnJe connais toutes sortes de choses à propos des herbes et de l'alchimie… Tenez, par exemple, laissez-moi vous raconter la fois où j'ai transformé le fromage que j'étais sur le point de manger au déjeuner en or... Hé... Où allez-vous ?"}},
[4061] = {"La révolte des machines",nil, {"S'aventurer dans les Steppes ardentes et récupérer 10 Fragments d'élémentaires cassés pour la Hiérophante Theodora Mulvadania.nnTheodora vous a indiqué que les golems et les élémentaires étaient la source de ces fragments."}},
[4062] = {"La révolte des machines",nil, {"Apporter l'Échantillon de fragment d'élémentaire à Lotwil Veriatus.nnTheodora vous a dit qu'il se trouvait dans un camp à l'est."}},
[4063] = {"La révolte des machines",nil, {"Trouver et tuer le Seigneur des golems Argelmach. Rapporter sa tête à Lotwil. Collecter également 10 Noyaux d'élémentaire intacts sur les Golems ravarage et les Assemblages porteguerre qui protègent Argelmach. Vous savez cela car vous êtes médium."}},
[4081] = {"TUER À VUE : nains Sombrefer",nil, {"Pénétrez dans les Profondeurs de Rochenoire et tuez les vils agresseurs !nnLe chef de guerre Sangredent veut que vous tuiez 15 Gardes Ragenclumes, 10 Gardiens Ragenclumes et 5 Fantassins Ragenclumes. Retourner le voir une fois votre mission accomplie."}},
[4082] = {"TUER À VUE : Officiers de haut rang Sombrefer",nil, {"Pénétrez dans les Profondeurs de Rochenoire et tuez les vils agresseurs !nnLe Chef de guerre Sangredent veut que vous tuiez 10 Médecins Ragenclumes, 10 Soldats Ragenclumes et 10 Officiers Ragenclumes. Retourner le voir une fois votre mission accomplie."}},
[4083] = {"Le Calice spectral",nil, {"Le Calice spectral flotte dans les airs, descendant et remontant tout doucement… au rythme d'un cœur agonisant."}},
[4084] = {"Coeur d'argent",nil, {"Trouver 11 Griffes d'argent et 1 Cœur d'Arbrefer pour Eridan Ventebleu à Gangrebois."}},
[4101] = {"Purifiez Gangrebois",nil, {"Apporter 15 Ambres de sang à Arathandris Argeciel à Gangrebois."}},
[4102] = {"Purifiez Gangrebois",nil, {"Apporter 15 Ambres de sang à Maybess Brise-des-rivières à Gangrebois."}},
[4103] = {"Du baume en chassant",nil, {"Si vous possédez une Balise cénarienne, vous pourrez discerner un fragment d'esprit corrompu sur les bêtes souillées que nous supprimons pour le plus grand bien de tous. Je broie ces fragments jusqu'à obtenir un réactif dont je me sers pour confectionner le Baume végétal cénarien qui permet de rendre santé et vigueur à ces plantes corrompues.nnEn échange de ces fragments, je vous donnerai du Baume cénarien que j'ai déjà préparé."}},
[4104] = {"Du baume par la pioche",nil, {"Si vous avez une Balise cénarienne et que vous minez dans Gangrebois, peut-être trouverez-vous du Vitriol contaminé dans un filon de minerai brut. Je sais comment purifier ce vitriol et le transformer en un composant qui sert à faire du Baume végétal cénarien. Vous pourrez ensuite l'utiliser pour libérer les plantes corrompues que vous rencontrerez.nnSi vous avez collecté du Vitriol contaminé, je vous l'échange contre du Baume végétal cénarien que j'ai déjà préparé."}},
[4105] = {"Du baume contre une récolte",nil, {"Grâce à une balise cénarienne, les herboristes expérimentés peuvent distinguer l'insidieuse gangrimpante, un parasite malfaisant produit par la corruption de Gangrebois. Mon travail ici m'a appris à les purifier avec un réactif... utilisé dans ce que nous appelons le baume végétal cénarien.nnLorsque vous cueillerez des herbes à Gangrebois, vous devriez remarquer les brins de gangrimpante si vous avez la balise cénarienne. En échange je vous donnerai du baume cénarien que j'ai déjà préparé."}},
[4106] = {"Du baume contre des peaux",nil, {"Une balise cénarienne permet à un dépeceur bien entraîné de repérer des morceaux de peau contaminée dans le butin ramassé à Gangrebois. Je transforme ces morceaux apparamment inutiles en pâte épaisse qui sert d'agent accélérateur au baume végétal cénarien. Quand vous dépecez vos prises ici à Gangrebois, assurez-vous d'avoir toujours une balise sur vous !nnLorsque vous ramassez des morceaux de peau contaminée, apportez-les-moi et je vous donnerai des doses de baume végétal cénarien que j'ai en réserve."}},
[4107] = {"Du baume contre des désenchantements",nil, {"Le pouvoir d'un objet magique désenchanté est éprouvé par l'essence qu'il crée. J'ai conçu un moyen de transformer l'essence brute utilisée par les enchanteurs en un réactif qui permet de créer du Baume végétal cénarien. Vous n'avez pas besoin de Balise cénarienne dans ce cas. N'importe quel objet désenchanté qui produit des essences inférieures fera parfaitement l'affaire.nnJe n'ai besoin que d'une seule essence inférieure. En échange, je vous donnerai du Baume végétal cénarien. L'essence est très puissante. Il en faut peu pour faire beaucoup !"}},
[4108] = {"Du baume en chassant",nil, {"Si vous possédez une Balise cénarienne, vous pourrez discerner un fragment d'esprit corrompu sur les bêtes souillées que nous supprimons pour le plus grand bien de tous. Je broie ces fragments jusqu'à obtenir un réactif dont je me sers pour confectionner le Baume végétal cénarien qui permet de rendre santé et vigueur à ces plantes corrompues.nnEn échange de ces fragments, je vous donnerai du Baume cénarien que j'ai déjà préparé."}},
