[5892] = {"Fournitures de Gouffrefer",nil, {"Apporter 10 Fournitures de Gouffrefer à l'Intendant de l'Alliance de Dun Baldar."}},
[5893] = {"Fournitures de Froide-dent",nil, {"Apporter 10 Fournitures de Froide-dent à l'intendant de la Horde, au donjon Loup-de-givre."}},
[5901] = {"Une Plaie sur la Croisade",nil, {"Utiliser le Pot de Termites vide sur la Termitière, dans les Maleterres de l'est. Après avoir récupéré 100 Termites des Maleterres, retourner voir Mickey Levine, à la Barricade, dans les Clairières de Tirisfal."}},
[5902] = {"Une Plaie sur la Croisade",nil, {"Trouver un endroit judicieux au centre de la scierie de la Crête du nord, dans les Maleterres de l'ouest, pour placer le Tonneau de Termites des Maleterres."}},
[5903] = {"Une Plaie sur la Croisade",nil, {"Utiliser le Pot de Termites vide sur la Termitière, dans les Maleterres de l'est. Après avoir récupéré 100 Termites des Maleterres, retourner voir Nathaniel Dumah, au camp du Noroît, dans les Maleterres de l'ouest."}},
