[5921] = {"Reflet-de-Lune",nil, {"Utiliser le sort \"Téléportation : Reflet-de-Lune\" pour aller à Reflet-de-Lune. Une fois sur place, parler à Dendrite Brasétoile, au village de Havrenuit."}},
