[5929] = {"L'esprit du Grand ours",nil, {"Aller voir l'esprit du Grand ours, au nord-ouest de Reflet-de-Lune et apprendre ce qu'il peut vous enseigner sur la nature de l'ours. Quand cela est fait, retourner voir Dendrite Brasétoile, à Havrenuit, dans Reflet-de-Lune."}},
[5930] = {"Esprit du Grand ours",nil, {"Aller voir l'esprit du Grand ours, au nord-ouest de Reflet-de-Lune et apprendre ce qu'il peut vous enseigner sur la nature de l'ours. Quand cela est fait, retourner voir Dendrite Brasétoile, à Havrenuit, dans Reflet-de-Lune."}},
[5931] = {"Retour à Darnassus",nil, {"Retourner à Darnassus et parler à Mathrengyl Pasdelours, dans l'Enclave cénarienne."}},
[5932] = {"Retour vers les Pitons du Tonnerre",nil, {"Retourner aux Pitons du Tonnerre, et parler à Turak Totem-runique, sur la Cime des Anciens."}},
[5941] = {"Retourner voir Chromie",nil, {"Apporter la Liasse de reliques à Chromie, à Andorhal."}},
[5942] = {"Trésors cachés",nil, {"Utiliser la Clé de Joseph sur le Coffre de Joseph."}},
[5943] = {"La caravane de Gizelton",nil, {"Escorter la caravane de Gizelton à travers le convent de Mannoroc. Parler à Smeed, au camp de Fouillevis, pour toucher la récompense."}},
[5944] = {"En rêves",nil, {"Escortez Taelan Fordring jusqu'à la sortie d'Âtreval."}},
[5961] = {"Le champion de la reine banshee",nil, {"Parler à Nathanos le Flétrisseur, dans les Maleterres de l'est. Il se trouve actuellement à la ferme des Marris."}},
[5981] = {"Géants déchaînés",nil, {"Rivern Givrevent voudrait que vous tuiez 4 Géants Cognegivre et 4 Protecteurs Cognegivre."}},
[6001] = {"Corps et cœur",nil, {"Utiliser la Poussière de lune cénarienne sur la Pierre des séléniens d'Auberdine pour invoquer Lunegriffe. Affronter Lunegriffe, et gagner la force physique et spirituelle qu'il possède.nnQuand cela est fait, parler à Mathrengyl Pasdelours, à Darnassus."}},
[6002] = {"Corps et cœur",nil, {"Utiliser la Poussière lunaire cénarienne sur la Pierre des séléniens, entre Mulgore et les Tarides, pour invoquer Lunegriffe. Affronter Lunegriffe, et gagner la force physique et spirituelle qu'il possède.nnQuand cela est fait, parler à Turak Totem-runique, aux Pitons du Tonnerre."}},
[6004] = {"Un travail inachevé",nil, {"Tuer deux Médecins écarlates, deux Chasseurs écarlates, deux Magi écarlates et deux Chevaliers écarlates avant de revenir vers Kirsta Fondombre dans les Maleterres de l'ouest."}},
[6021] = {"Zaeldarr le Banni",nil, {"Apporter la Tête de Zaeldarr au Conservateur Alen, à la chapelle de l'Espoir de Lumière."}},
[6022] = {"Tuer utile",nil, {"Allez à l'est à la Croisée de Corin et détruisez les membres du Fléau qui habitent la ville. Rassemblez 7 morceaux de Pourriture vivante en moins de 10 minutes. Utilisez le Mortier et le Pilon pour réduire la Pourriture vivante en Pourriture coagulée."}},
[6023] = {"Un travail inachevé",nil, {"Tuer le veneur Radley et le cavalier Durgen, avant de retourner voir Kirsta Fondombre, dans les Maleterres de l'ouest."}},
[6024] = {"L'appel d'Hameya",nil, {"Tuer l'Infiltrateur Hameya. Utiliser sa clé sur le Monticule de poussière, derrière le caveau de Zaeldarr."}},
[6025] = {"Un travail inachevé",nil, {"Aller à Âtreval, et trouver un poste d'observation élevé d'où voir la ville et jauger les forces de la Croisade écarlate, puis retourner voir Kirsta Fondombre, dans les Maleterres de l'ouest."}},
[6026] = {"Voilà qui est beaucoup demander",nil, {"Smokey LaRue veut que vous lui apportiez 2 Barres de thorium, 1 Bâtonnet doré, 8 Bombes explosives et 8 Déclencheurs instables.nnVous devrez trouver un forgeron et un ingénieur pour obtenir ces objets."}},
[6027] = {"Le Livre des Anciens",nil, {"Obtenir le Livre des Anciens, puis le rapporter à Azore Aldamort, dans la tour d'Ethel Rethor."}},
[6028] = {"Le rapport de Long-guet",nil, {"Apportez le Rapport de Long-guet à l'officier de l'Aube d'argent Purecarde au Camp du Noroît, dans les Maleterres de l'ouest."}},
[6029] = {"Le rapport de Long-guet",nil, {"Livrer le Rapport de Long-guet à l'officier Garush de l'Aube d'argent, à la Barricade, dans les Maleterres de l'ouest."}},
[6030] = {"Le duc Nicholas Zverenhoff",nil, {"Apportez le livre \" Etudes sur la langue des Esprits \" au duc Nicholas Zverenhoff, à la chapelle de l'Espoir de Lumière, dans les Maleterres de l'est."}},
[6031] = {"L'étoffe runique",nil, {"Apportez 30 pièces d'étoffe runique à Meilosh au Repaire des Grumegueules."}},
[6032] = {"Le vêtement sacré",nil, {"Apporter 2 pièces d'Etoffe lunaire à Meilosh, au Repaire des Grumegueules."}},
[6041] = {"Quand Smokey chante, je deviens violent",nil, {"Aller à Pestebois, au nord-ouest de la chapelle de l'Espoir de Lumière. Détruire 8 Bâtiments du Fléau en utilisant le Mélange spécial de Smokey sur les Marques de détonation que portent les bâtiments destinés à être détruits. Smokey a fait marquer les ziggourats et les abattoirs.nnUne fois la bombe placée, quitter le bâtiment et contempler le résultat de votre opération.nnGarder en tête que le Mélange spécial de Smokey est très... instable."}},
[6042] = {"Les petits soucis de la non-vie",nil, {"Tuer 20 Chauves-souris pestiférées nuisibles et 10 Chauves-souris pestiférées monstrueuses. Retourner voir Nathanos le Flétrisseur quand ce sera fait."}},
[6061] = {"Dompter la bête",nil, {"Utiliser le Bâtonnet de domptage pour dompter un trotteur des plaines adulte. S'entraîner à combattre à ses côtés, puis rapporter le Bâtonnet de domptage à Yaw Crin-tranchant, à Sabot-de-sang."}},
[6062] = {"Dompter la bête",nil, {"Utiliser le Bâtonnet de domptage pour dompter un sanglier tacheté redoutable. S'entraîner à combattre à ses côtés, puis rapporter le Bâtonnet de domptage à Thotar de Tranchecolline."}},
[6063] = {"Dompter la bête",nil, {"Utiliser le Bâtonnet de domptage pour dompter une Rôdeuse Tissebois. S'entraîner à combattre à ses côtés, puis rapporter le Bâtonnet de domptage à Dazalar, à Dolanaar."}},
[6064] = {"Dompter la bête",nil, {"Utiliser le Bâtonnet de domptage pour dompter un Grand sanglier des rochers. S'entraîner à combattre à ses côtés, puis rapporter le Bâtonnet de domptage à Grif Cœur-sauvage, à Kharanos."}},
[6065] = {"La voie du chasseur",nil, {"Parler à Yaw Crin-tranchant, à Sabot-de-sang."}},
[6066] = {"La voie du chasseur",nil, {"Parler à Yaw Sharpmane, au village Bloodhoof."}},
[6067] = {"La voie du chasseur",nil, {"Parler à Yaw Crin-tranchant, à Sabot-de-sang."}},
[6068] = {"La voie du chasseur",nil, {"Parler à Thotar, à Tranchecolline."}},
[6069] = {"La voie du chasseur",nil, {"Parler à Thotar, à Tranchecolline."}},
[6070] = {"La voie du chasseur",nil, {"Parler à Thotar, à Tranchecolline."}},
[6071] = {"La voie du chasseur",nil, {"Parler à Dazalar, à Dolanaar."}},
[6072] = {"La voie du chasseur",nil, {"Parler à Dazalar, à Dolanaar."}},
[6073] = {"La voie du chasseur",nil, {"Parler à Dazalar, à Dolanaar."}},
[6074] = {"La voie du chasseur",nil, {"Parler à Grif Cœur-sauvage, à Kharanos."}},
[6075] = {"La voie du chasseur",nil, {"Parler à Grif Cœur-sauvage, à Kharanos."}},
[6076] = {"La voie du chasseur",nil, {"Parler à Grif Cœur-sauvage, à Kharanos."}},
[6081] = {"Entraîner la bête",nil, {"Adressez-vous à Ormak Trait-sinistre à Orgrimmar."}},
[6082] = {"Dompter la bête",nil, {"Domptez un Scorpide blindé à l'aide du Bâtonnet de domptage. Entraînez-vous puis retournez voir Thotar à Tranchecolline."}},
[6083] = {"Dompter la bête",nil, {"Utiliser le Bâtonnet de domptage pour dompter un Clampant de l'écume. S'entraîner à combattre à ses côtés, puis rapporter le Bâtonnet de domptage à Thotar, à Tranchecolline."}},
[6084] = {"Dompter la bête",nil, {"Domptez un Léopard des neiges à l'aide du Bâtonnet de domptage. Entraînez-vous puis retournez voir Grif Cœur-sauvage à Kharanos."}},
[6085] = {"Dompter la bête",nil, {"Domptez un ours Griffeglace à l'aide du Bâtonnet de domptage. Entraînez-vous puis retournez voir Grif Cœur-sauvage à Kharanos."}},
[6086] = {"Entraîner la bête",nil, {"Adressez-vous à Belia Tonnegranit à Forgefer."}},
[6087] = {"Dompter la bête",nil, {"Utiliser le Bâtonnet de domptage pour dompter un Traqueur des prairies. S'entraîner à combattre à ses côtés, puis rapporter le Bâtonnet de domptage à Yaw Crin-tranchant, à Sabot-de-sang."}},
[6088] = {"Dompter la bête",nil, {"Utiliser le Bâtonnet de domptage pour dompter un Rapace. S'entraîner à combattre à ses côtés, puis rapporter le Bâtonnet de domptage à Yaw Crin-tranchant, à Sabot-de-sang."}},
[6089] = {"Entraîner la bête",nil, {"Parler à Holt Corne-tonnerre, sur la Cime des Chasseurs, aux Pitons du Tonnerre."}},
[6101] = {"Dompter la bête",nil, {"Utiliser le Bâtonnet de domptage pour dompter un Rôdeur sabre-de-nuit. S'entraîner à combattre à ses côtés, puis rapporter le Bâtonnet de domptage à Dazalar, à Dolanaar."}},
[6102] = {"Dompter la bête",nil, {"Utiliser le Bâtonnet de domptage pour dompter un Hurleur Strigid. S'entraîner à combattre à ses côtés, puis rapporter le Bâtonnet de domptage à Dazalar, à Dolanaar."}},
[6103] = {"Entraîner la bête",nil, {"Parler à Jocaste, à l'Enclave cénarienne de Darnassus."}},
[6121] = {"Nouvelles leçons",nil, {"Aller à Reflet-de-Lune, et parler à Dendrite Brasétoile, au village de Havrenuit."}},
[6122] = {"Le principe originel",nil, {"Utiliser une Fiole d'échantillon des Chutes de la Bondissante vide pour collecter un échantillon d'eau, à l'entrée de la grotte, à proximité des Chutes.nnApporter la Fiole d'échantillon des Chutes de la Bondissante remplie à Alanndarian Chantenuit, à Auberdine, dans Sombrivage."}},
[6123] = {"Rassembler les ingrédients du remède",nil, {"Apporter 5 Terrestrines et 12 Champignons lunaires à Alanndarian Chantenuit, à Auberdine ; elle pourra ainsi commencer à préparer son remède."}},
[6124] = {"Soigner la maladie",nil, {"Utiliser le Remède pour animaux sur 10 Daims malades qui se trouvent dans Sombrivage ; ceci les soignera. Les Daims malades ont été aperçus d'abord entre le sud de la rivière Bondissante et le nord d'Auberdine ; on en trouve également tout au sud de Sombrivage, à la frontière d'Orneval."}},
[6125] = {"Pouvoir contre le poison",nil, {"Retourner voir Mathrengyl Pasdelours, dans l'Enclave cénarienne, à Darnassus."}},
[6126] = {"Nouvelles leçons",nil, {"Aller à Reflet-de-Lune, et parler à Dendrite Brasétoile, au village de Havrenuit."}},
[6127] = {"Le principe originel",nil, {"Utiliser la Fiole d'échantillon vide du Pic de Brume-funeste pour collecter un échantillon de l'eau d'un bassin, au sommet du Pic de Brume-funeste.nnApporter ensuite la Fiole d'échantillon remplie à Tonga Totem-runique, à la Croisée, dans les Tarides."}},
[6128] = {"Rassembler les ingrédients du remède",nil, {"Apporter 5 Terrestrines et 5 Cornes de kodo à Tonga Totem-runique, à la Croisée, pour qu'il puisse préparer un baume curatif."}},
[6129] = {"Soigner la maladie",nil, {"Utiliser le Remède pour animaux sur 10 Gazelles malades qui se trouvent dans la partie nord des Tarides ; ceci les soignera. Les Gazelles malades ont été vues au nord de la route orientée est-ouest qui traverse la Croisée."}},
[6130] = {"Pouvoir contre le poison",nil, {"Retourner voir Turak Totem-runique, sur la Cime des Anciens, aux Pitons du Tonnerre."}},
[6131] = {"Allié des Grumegueules",nil, {"Grifleur veut que vous prouviez votre valeur en tuant 5 Guerriers Mort-bois, 5 Guides Mort-bois et 5 Jardiniers Mort-bois."}},
[6132] = {"Faites-moi sortir de là !",nil, {"Escorter Melizza Razzkankan, puis parler à Hornizz Razzkankan au cimetière des Kodos en Désolace."}},
[6133] = {"L'ordre du seigneur forestier",nil, {"Aller vers la frontière nord des Maleterres de l'est et récupérer le Registre de Quel'Thalas. L'objet se trouve quelque part dans le gîte de Quel'Lithien.nnTuer également 8 Forestiers, 8 Eclaireurs et 8 Bûcherons."}},
[6134] = {"Chasse à l'ectoplasme",nil, {"Apporter 8 Fantôm-o-plasmes et la Caisse d'aimants fantômes à Hornizz Razzkankan, en Désolace."}},
[6135] = {"Crépuscaile, oh comme je te hais !",nil, {"Trouver Crépuscaile et la tuer. Récupérer une Touffe de fourrure de Crépuscaile sur son cadavre, puis l'apporter à Nathanos le Flétrisseur.nnIl paraît que Crépuscaile hante les terres qui se trouvent au nord de la ferme des Marris."}},
[6136] = {"Le corpulent",nil, {"Trouver Creusechair, dans les terres au nord des Maleterres de l'est, et le tuer. Retourner voir Nathanos le Flétrisseur quand la mission sera terminée."}},
[6141] = {"Frère Anton",nil, {"Adressez-vous au frère Anton, en Désolace."}},
[6142] = {"Des praires comme appât",nil, {"Trouver 10 morceaux de Chair de praire et les apporter à Mai'Lahii, à Proie-de-l'Ombre."}},
[6143] = {"D'autres poissons-chats à fouetter",nil, {"Tuer 7 Myrmidons Ondulame, 7 Nagas Ondulame et 5 Sorcières Ondulame, puis parler à Drulzegar, à Proie-de-l'Ombre, en Désolace."}},
[6144] = {"L'appel du commandement",nil, {"Aller à Fossoyeuse et parler à Varimathras."}},
[6145] = {"Le messager cramoisi",nil, {"Retourner dans les Maleterres de l'est et traquer le Messager cramoisi, le tuer et récupérer les Ordres du Grand croisé.nnApporter les Ordres à Nathanos le Flétrisseur pour obtenir des instructions supplémentaires."}},
[6146] = {"La ruse de Nathanos",nil, {"Aller à la Main de Tyr, au sud-est de la ferme des Marris. Une fois sur place, mordre dans la Pomme pourrie. Profiter d'avoir revêtu l'apparence d'un membre de la Croisade écarlate pour apporter les Ordres du Grand Croisé au Grand maître Valdelmar."}},
[6147] = {"Retourner voir Nathanos",nil, {"Retourner voir Nathanos le Flétrisseur avec la nouvelle information."}},
[6148] = {"L'Oracle écarlate, Demetria",nil, {"La Croisade écarlate fait mouvement. Quelque part le long de la route, entre la Main de Tyr et Stratholme, vous trouverez l'Oracle Demetria et sa fratrie. La tuer et retourner voir Nathanos le Flétrisseur."}},
[6161] = {"Revendiquer le trésor de Rackmore !",nil, {"Trouver la Clé d'argent de Rackmore. Trouver la Clé d'or de Rackmore. Trouver et ouvrir le Coffre de Rackmore."}},
[6162] = {"La dernière bataille d'un mari",nil, {"Tuer le Seigneur Ror et apporter sa Griffe à Dreka'Sur, au Poste de la Vénéneuse, en Gangrebois."}},
[6163] = {"Ramstein",nil, {"Aller à Stratholme et tuer Ramstein Grandgosier. Prendre sa tête, et l’apporter à Nathanos."}},
[6164] = {"Le livre de compte d'Augustus",nil, {"Apporter le Livre de compte d'Augustus à Augustus le Touché à Val-Terreur."}},
[6181] = {"Un message rapide",nil, {"Apporter la Note de Lewis à Thor, le Maître des griffons."}},
[6182] = {"Le premier et le dernier",nil, {"Parler à Mathias Shaw, dans la vieille ville de Hurlevent. Il se trouve dans la caserne."}},
[6183] = {"Honorer la mort",nil, {"Reparlez à Mathias Shaw, quand vous aurez décidé d'accepter son travail."}},
[6184] = {"Flint Shadowmore",nil, {"Allez au camp du Noroît, dans les Maleterres de l’ouest, et rencontrez votre contact, Flint Shadowmore."}},
[6185] = {"Les Maleterres de l'Est",nil, {"Fouillez les Maleterres de l'est pour trouver des indices relatifs au \"Flétrisseur\" et aux agents du SI:7 portés disparus. Si vous trouvez un quelconque Insigne du SI:7, rapportez-le à Flint Ombrelongue, au camp du Noroît."}},
[6186] = {"La venue du Flétrisseur",nil, {"Retourner à Hurlevent et informer le généralissime Bolvar Fordragon du destin de Nathanos Marris."}},
[6187] = {"L'ordre doit être rétabli",nil, {"Montez une armée et portez-vous vers les Maleterres de l'est. Lancez un assaut total sur Nathanos le Flétrisseur et détruisez tout misérable élément de la Horde qui tenterait de le défendre.nnGardez votre présence d'esprit, <nom> : la Horde le défendra jusqu'à la mort."}},
[6221] = {"Les Mort-bois du nord",nil, {"Nafien veut que vous tuiez 5 Protecteurs Mort-bois, 5 Vengeurs Mort-bois et 5 Chamans Mort-bois."}},
[6241] = {"Les Tombe-hiver s'agitent",nil, {"Salfa voudrait que vous tuiez 6 Totémiques, 6 Gardes et 6 Guides de la tribu Tombe-hiver."}},
[6261] = {"Dungar Grangodet",nil, {"Apportez la Caisse d'Osric à Dungar Grangodet, le Maître des griffons."}},
[6281] = {"Continuer vers Stormwind",nil, {"Achetez un voyage en griffon à Thor, Maître des griffons, puis apportez la Note de Lewis à Osric Strang au magasin \"Immunité limitée\", dans la vieille ville de Hurlevent."}},
[6282] = {"La menace des harpies",nil, {"Maggran Lieur-de-terre, de la Retraite de Roche-Soleil, vous demande de tuer sept Harpies, sept Embusquées, sept Tueuses et sept Volplumes Rougefuries."}},
[6283] = {"La lignée Rougefurie",nil, {"Maggran, à la Retraite de Roche-Soleil, vous demande de détruire la Déchiqueteuse Rougefurie et de rapporter son corps comme preuve de votre exploit."}},
[6284] = {"Arachnophobie",nil, {"Tuer Besseleth et apporter le Croc de Besseleth à Maggran, à la Retraite de Roche-Soleil."}},
[6285] = {"Aller voir Lewis",nil, {"Achetez un voyage en griffon pour la Colline des sentinelles à Dungar Grangodet, Maître des griffons, puis apportez la Caisse d'Osric à Lewis à la Colline des sentinelles."}},
[6301] = {"Cycle de la renaissance",nil, {"Tammra Champ-des-vents, de la Retraite de Roche-Soleil, vous demande de récolter 10 Graines de Gaïa."}},
[6321] = {"Approvisionner le Sépulcre",nil, {"Apporter l'Ordre de Podrig à Karos Razok."}},
[6322] = {"Michael Garrett",nil, {"Apporter la Caisse de Gordon à Michael Garret."}},
[6323] = {"Voyage vers Undercity",nil, {"Acheter un voyage en chauve-souris pour Fossoyeuse, auprès du Maître des chauves-souris, Karos Razok, puis apporter l'Ordre de Podrig à Gordon Wendham, à Fossoyeuse."}},
[6324] = {"Retour vers Podrig",nil, {"Acheter un voyage en chauve-souris pour le Sépulcre, auprès du Maître des chauves-souris Michael Garret, puis apporter la Caisse de Gordon au Nécrogarde Podrig, au Sépulcre."}},
[6341] = {"Le don de Teldrassil",nil, {"Apporter la Collection de Nessa à Vesprystus."}},
[6342] = {"Vol vers Auberdine",nil, {"Prenez un hippogriffe vers Auberdine chez le maître des hippogriffes Vesprystus, puis apportez la Collection de Nessa à Laird."}},
[6343] = {"Retourner à Nessa",nil, {"Réponse de Laird à Nessa Chantelombre."}},
[6344] = {"Nessa Chantelombre",nil, {"Parler à Nessa Chantelombre."}},
[6361] = {"Un tas de peaux",nil, {"Apporter le Tas de peaux au Maître des coursiers du vent Devrak, à la Croisée."}},
[6362] = {"Voyage vers les Pitons du Tonnerre",nil, {"Acheter un voyage pour les Pitons du Tonnerre, auprès du Maître des coursiers du vent, Devrak, puis apporter le Tas de peaux à Ahanu, aux Pitons du Tonnerre."}},
[6363] = {"Tal le Maître des Cavaliers célestes",nil, {"Apporter les Marchandises en cuir d'Ahanu à Tal, aux Pitons du Tonnerre."}},
[6364] = {"Retourner vers Jahan",nil, {"Acheter un voyage pour la Croisée auprès du maître des coursiers du vent Tal, puis apporter les Marchandises en cuir d'Ahanu à Jahan Aile-de-faucon."}},
[6365] = {"Viandes vers Orgrimmar",nil, {"Apporter la Chair de Zargh à Devrak, à la Croisée."}},
[6381] = {"Nouvelle vie",nil, {"Planter 10 Graines de Gaïa sur les Monticules de Gaïa, puis rapporter les Graines de Gaïa enchantées restantes à Tammra, à la Retraite de Roche-Soleil."}},
[6382] = {"La Chasse d'Ashenvale",nil, {"Parler à Senani Cœur-de-tonnerre, au Poste de Bois-brisé, à Orneval."}},
[6383] = {"La Chasse d'Orneval",nil, {"Pour clore la Chasse d’Ashenvale, vous devrez affronter trois créatures légendaires. Vous allez devoir les rechercher et vous mesurer à leur malice et leur force. En accomplissant cela, vous apprendrez des choses sur vous. Ces créatures sont : l'ours Ursangous, le sabre-de-nuit Shadumbra et l'hippogryphe Serres-tranchantes.nnLes créatures de la Chasse d'Ashenvale sont puissantes, et vous aurez peut-être besoin d'aide pour les vaincre. Si vous les battez, apportez-en-moi la preuve."}},
[6384] = {"Trajet vers Orgrimmar",nil, {"Acheter un voyage pour Orgrimmar, auprès du Maître des coursiers du vent, Devrak, puis apporter la Chair de Zargh à Gryshka, à Orgrimmar."}},
[6385] = {"Doras le Maître des Cavaliers célestes",nil, {"Apporter la Lettre de Gryshka à Doras, à Orgrimmar."}},
[6386] = {"Retour à la Croisée",nil, {"Acheter un voyage pour la Croisée auprès du Maître des coursiers du vent Doras, puis apporter la Lettre de Gryshka à Zargh, à la Croisée."}},
[6387] = {"L'honneur des étudiants",nil, {"Apportez la Liste de Brock à Thorgrum Borrelson à Thelsamar."}},
[6388] = {"Gryth Thurden",nil, {"Apportez les Pioches d'honneur à Gryth Thurden à Forgefer."}},
[6389] = {"Une Plaie sur la Croisade",nil, {"Libérez les Termites des Maleterres dans la scierie de la Crête du nord, puis retournez voir Nathaniel Dumah, au camp du Noroît, dans les Maleterres de l'ouest, avec le Tonneau de Termites des Maleterres."}},
[6390] = {"Une Plaie sur la Croisade",nil, {"Libérez les Termites des Maleterres dans la scierie de la Crête du nord, puis retournez voir Mickey Levine, à la Barricade, dans les Clairières de Tirisfal, avec le Tonneau de Termites des Maleterres."}},
[6391] = {"Voyage vers Ironforge",nil, {"Acheter un billet de griffon pour Forgefer, auprès du Maître des griffons, Thorgrum Borrelson, puis apporter la Liste de Brock à Golnir Rochorteil, à Forgefer."}},
[6392] = {"Retourner vers Brock",nil, {"Acheter un billet de griffon pour Thelsamar, auprès du Maître des griffons Gryth Thurden, puis apporter les Pioches d'honneur à Brock Cherchepierre, à Thelsamar."}},
[6393] = {"Guerre élémentaire",nil, {"Apporter 10 Incendrites à Tsunaman à la Retraite de Roche-Soleil."}},
[6394] = {"La pioche de Thazz'ril",nil, {"Apportez la Pioche de Thazz'rill au contremaître Thazz'rill."}},
[6395] = {"La dernière volonté de Marla",nil, {"Apporter le Cadavre de Samuel Fipps sur la Tombe de Marla, puis retourner voir la Novice Elreth."}},
[6401] = {"Kaya est en vie",nil, {"Apprendre la bonne nouvelle à Tammra Champ-des-vents à la Retraite de Roche-Soleil."}},
[6402] = {"Le rendez-vous à Stormwind",nil, {"Voyagez jusqu'à Hurlevent et rendez-vous aux portes de la ville. Parlez à l'écuyer Rowe, afin qu'il informe le maréchal Windsor de votre arrivée."}},
[6403] = {"La grande mascarade",nil, {"Suivre Reginald Windsor dans Hurlevent. Le protéger contre toute menace !"}},
[6421] = {"Le ravin des Éboulis",nil, {"Explorer la grotte du ravin des Éboulis et apporter 10 Cristaux de résonite à Mor'rogal à la Retraite de Roche-Soleil."}},
[6441] = {"Cornes de satyre",nil, {"Collecter 16 Cornes de satyre pour Pixel, au Poste de Bois-brisé."}},
[6442] = {"Des nagas sur la grève de Zoram",nil, {"Apporter 20 Têtes d'Irequeue à Marukai, le long de la grève de Zoram."}},
[6461] = {"Suceurs de sang",nil, {"Xen'zilla, à Malaka'Jin, voudrait que vous tuiez 10 Rampants des mousses profondes et 7 Venimeux des mousses profondes."}},
[6462] = {"Charme troll",nil, {"Apporter 8 Charmes trolls à Mitsuwa, à l'Avant-poste de Zoram'gar."}},
[6481] = {"Un Terrestre se lève",nil, {"Ouvrir la Gangue de résonite avec le Cristal de résonite enchanté, puis tuer Goggeroc. Retourner voir Mor'rogal pour l'informer et lui rapporter le Cristal de résonite enchanté."}},
[6482] = {"Liberté pour Ruul",nil, {"Escorter Ruul, puis parler à Yama Sabot-de-neige, au Poste de Bois-brisé."}},
[6501] = {"L'Œil de dragon",nil, {"Vous devez écumer le monde pour pouvoir rétablir le pouvoir du Fragment de l'Oeil de dragon. La seule information dont vous disposez à propos de cette chose, c'est qu'elle existe."}},
[6502] = {"Amulette drakefeu",nil, {"Récupérer le Sang du Champion des dragons noirs sur le général Drakkisath. Il se trouve dans sa salle du trône, derrière les Halls d'Ascension, sur le Pic Rochenoire."}},
[6503] = {"Les estafettes d'Orneval",nil, {"Tuer 9 Estafettes d'Orneval, puis retourner voir Kuray'bin, à l'avant-poste de Bois-brisé."}},
[6504] = {"Les pages perdues",nil, {"Trouver les 12 pages manquantes du Manuel d'utilisation du Déchiqueteur, puis les rassembler pour constituer les Chapitres 1, 2, et 3. Ramener les pages à Gurda Ragentaille près du poste de Bois-brisé."}},
[6521] = {"Une alliance impie",nil, {"Apporter la Tête de l'Ambassadeur Malcin à Varimathras, à Fossoyeuse."}},
[6522] = {"Une alliance impie",nil, {"Apporter le Petit parchemin à Varimathras, à Fossoyeuse."}},
[6523] = {"Protéger Kaya",nil, {"Escorter Kaya Sabot-plat au Camp Aparaje, puis retourner voir Makaba Sabot-plat, à côté de la frontière sud-est des Serres-Rocheuses."}},
[6541] = {"Rapport à Kadrak",nil, {"Faire un rapport à Kadrak, dans la tour de guet, dans le nord des Tarides."}},
[6542] = {"Rapport à Kadrak",nil, {"Faire un rapport à Kadrak, dans la tour de guet, dans le nord des Tarides."}},
[6543] = {"Les rapports Chanteguerre",nil, {"Apporter les Rapports du clan Chanteguerre à l'Eclaireur Chanteguerre, au Messager Chanteguerre et au Voltigeur Chanteguerre. Rapporter leurs comptes rendus mis à jour à Kadrak, dans la tour de guet nord, dans les Tarides."}},
[6544] = {"L'assaut de Torek",nil, {"Escorter Torek lors de son attaque contre l'Avant-poste d'Aile-argent et Duriel Lunéclat, puis faire un rapport à Ertog Ragembroche, au Poste de Bois-brisé."}},
[6545] = {"Mise à jour du Messager Warsong",nil, {"Donc vous êtes ici avec mes ordres ? C'est toujours un plaisir de voir une nouvelle recrue avec une nature exubérante et qui persévère.nnVous devez apprendre vite si vous voulez rester ici. La menace que représentent les nagas grandit, <nom>. Je me suis occupé de la défense lors de plusieurs attaques contre cet avant-poste depuis que je suis ici.nnMais si vous voulez vous y atteler, parlez à quelqu'un d'autre à l'avant-poste."}},
[6546] = {"Mise à jour du voltigeur Warsong",nil, {"Il y a beaucoup de dangers sur ce chemin, <nom>. Vous avez intérêt à ne pas l'oublier quand vous traverserez ces terres. La menace que représente l'Alliance est très proche.nnNous avons des ennemis sur tous les fronts. Les satyres sont une force redoutable et très répandue dans l'est d'Ashenvale. De plus, j'ai découvert que les nagas ont envahi une grande partie des terres d'Azshara.nnIl est réconfortant de voir que nos effectifs augmentent. Il faut se préparer à ce qui nous attend, <nom>."}},
[6547] = {"Mise à jour de l'Eclaireur Warsong",nil, {"Gardez l'oeil ouvert, <nom>. Les elfes se cachent souvent dans les ombres...nnNous devons toujours être à l'affût. Nous avons travaillé dur pour gagner cette terre, et les elfes cherchent à nous la voler.nnJ'imagine que vous m'avez apporté mes prochains ordres provenant de Kadrak ?"}},
[6548] = {"Vengez mon village !",nil, {"Tuez 8 Forbans du Totem-sinistre et 6 Mercenaires du Totem-sinistre, puis retournez voir Makaba Sabot-plat, près de la lisière sud-est des Serres-Rocheuses."}},
[6561] = {"L'infamie de Brassenoire",nil, {"Apporter la tête du Seigneur du crépuscule Kelris à Bashana Totem-runique, aux Pitons du Tonnerre."}},
[6562] = {"Problèmes dans les profondeurs",nil, {"Parler à Je'neu Sancrea, à Orneval."}},
[6563] = {"L'Essence d'Aku'Mai",nil, {"Apporter 20 Saphirs d'Aku'Mai à Je'neu Sancrea, à Orneval."}},
[6564] = {"Allégeance aux Dieux très anciens",nil, {"Apporter la Note humide à Je'neu Sancrea, à Orneval."}},
[6565] = {"Allégeance aux Dieux très anciens",nil, {"Tuer Lorgus Jett, dans les Profondeurs de Brassenoire et retourner voir Je'neu Sancrea, à Orneval."}},
[6566] = {"Ce que le vent apporte",nil, {"Écouter Thrall."}},
[6567] = {"Le Champion de la Horde",nil, {"Chercher Rokaro au village Proie-de-l'Ombre en Désolace."}},
[6568] = {"Maîtresse en tromperie",nil, {"Apporter la Lettre de Rokaro à Myranda la Mégère, dans les Maleterres de l'ouest."}},
[6569] = {"Illusions d'Occulus",nil, {"Aller au Pic Rochenoire et collecter 20 Yeux de draconide noir. Retourner voir Myranda la Mégère quand la tâche sera terminée."}},
[6570] = {"Brandeguerre",nil, {"Aller à la tourbière du Ver, dans le marécage d'Âprefange, et chercher la tanière de Brandeguerre. Une fois à l'intérieur, porter l'Amulette de subversion draconique, et parler à Brandeguerre."}},
[6571] = {"Fournitures Chanteguerre",nil, {"Collecter les Fournitures Chanteguerre : Lames du clan Chanteguerre, Huile Chanteguerre, et la Cargaison de Haches chanteguerres ayant pour destinataire Locke Okarr, au Poste de Bois-brisé."}},
[6581] = {"Lames du clan Warsong",nil, {"En provenance directe de Ratchet, des lames du clan Warsong ! Ouais, je les ai ici.nnMais qu'avez-vous en contrepartie pour moi ? Vous ne pouvez pas les emporter comme ça, vous savez ! Mon temps coûte cher !nnCe dont j'ai besoin, c'est d'une bonne arme à feu pour mes voyages. Une fille doit se protéger savez-vous ? Il y en a une que j'aime particulièrement, et je suis sûre qu'un ingénieur n'aura aucun problème à la fabriquer.nnVoilà, si vous m'apportez un Tromblon mortel, je vous donnerai la cargaison de lames. Alors, marché conclu ?"}},
[6582] = {"L'épreuve des crânes, Clairvoyant",nil, {"Vous devez trouver Clairvoyant, le drake champion du Vol bleu, et le tuer. Arracher son crâne à son cadavre, et le rapporter à Brandeguerre.nnClairvoyant se trouve au Berceau-de-l'Hiver."}},
[6583] = {"L'épreuve des crânes, Somnus",nil, {"Détruire le Champion drake du Vol vert, Somnus. Arracher son crâne à son cadavre, puis le rapporter à Brandeguerre."}},
[6584] = {"L'épreuve des crânes, Chronalis",nil, {"Chronalis, enfant de Nozdormu, garde les Grottes du temps, dans le Désert de Tanaris. Tuez-le et rapportez son crâne à Brandeguerre."}},
[6585] = {"L'épreuve des crânes, Axtroz",nil, {"Aller à Grim Batol et traquer Axtroz, le Champion drake du Vol rouge. Le tuer et arracher son crâne, puis l'apporter à Brandeguerre."}},
[6601] = {"Ascension...",nil, {"Il semble que la comédie soit finie. Vous savez que l'Amulette de subversion draconique, créée par Myranda la Mégère, ne fonctionnera pas à l'intérieur du pic Rochenoire. Peut-être devriez-vous trouver Rokaro et lui exposer votre fâcheuse situation. Montrez-lui l'Amulette drakefeu terne. Avec un peu de chance, il saura quoi faire."}},
[6602] = {"Le sang du champion des dragons noirs",nil, {"Aller au Pic Rochenoire, et tuer le général Drakkisath. Récupérer son sang et l'apporter à Rokaro."}},
[6603] = {"Troubles au Berceau-de-l'Hiver",nil, {"Trouver Donova Antreneige, à côté des sources chaudes du Berceau-de-l'Hiver."}},
[6604] = {"Les Indomptables enragés",nil, {"Parler à Jaron Sculptepierre, au village de Pluie-d'Étoiles."}},
[6605] = {"Une étrange personne",nil, {"Parler à Winna Hazzard, à Gangrebois."}},
[6606] = {"Un peu de chance",nil, {"Parler à la Mau'ari, à Long-guet."}},
[6607] = {"Nat Pagle, pêcheur de l'extrême",nil, {"Nat Pagle voudrait que vous attrapiez les poissons suivants :nnLe Mahi Mahi de Brumejonc, de la grève de Brumejonc, dans le marais des Chagrins.nnUn Frappeur de Sar'theris, de la grève de Sar'theris en Désolace.nnL'Ahi de Féralas, de la rivière Verdantis de Féralas.nnLe Sailfin bleu de la Côte sauvage, de la Côte sauvage de la vallée de Strangleronce.nnRetournez voir Nat Pagle quand la collecte sera terminée."}},
[6608] = {"Vous êtes trop bon",nil, {"Vous pensez que Lumak essaie de vous dire qu'il ne peut plus vous aider à améliorer votre compétence de pêche. Vous devriez essayer de vous adresser maintenant à son ami, Nat Pagle, un pêcheur humain des îles au sud de Theramore (c'est du moins ce que vous croyez comprendre du discours de Lumak)."}},
[6609] = {"Il ne me reste rien !",nil, {"Aller à Kalimdor, dans la ville côtière de Theramore. À partir de là, chercher une île au sud de la ville principale. Nat Pagle vous attend !"}},
[6610] = {"Surprise de palourdes",nil, {"Dirge Hachillico voudrait que vous lui apportiez les objets suivants :nn12 Oeufs géants. Vous les trouverez sur les Rocs qui hantent Tanaris ou sur toute autre espèce de grand oiseau.nn10 morceaux de Chair de palourde piquante. Vous pouvez trouver la chair de palourde sur... les palourdes, naturellement !nn20 Emmentals d'Alterac. Vous les trouverez dans une fromagerie !nnCollectez tous ces objets, puis retournez voir Dirge."}},
[6611] = {"Vers Gadgetzan vous allez !",nil, {"Aller à Gadgetzan, dans le Désert de Tanaris, et parler de votre carrière de cuisinier à Dirge Hachillico."}},
[6612] = {"Je connais un gars...",nil, {"Aller à Gadgetzan, dans le Désert de Tanaris, et parler de votre carrière de cuisinier à Dirge Hachillico."}},
[6621] = {"Le roi des Vile-sylve",nil, {"Placer la Bannière de Karang sur le Monticule du totem Vile-sylve. Ne pas laisser les furbolgs détruire la Bannière. Tuer le chef Murgut, et apporter le Totem de Murgut à Karang Amakkar, à Zoram'gar."}},
[6622] = {"Triage",nil, {"Sauver des patients en utilisant des Bandages de Triage, afin de panser leurs blessures. Vous devez sauver 15 patients avant que 6 d'entre eux ne meurent de leurs blessures.nnRappelez-vous l'ordre du triage ! Les Soldats agonisants meurent les premiers, suivis des Soldats grièvement blessés. Les Soldats blessés sont ceux qui meurent les derniers, et ils doivent être soignés APRÈS ceux qui agonisent et ceux qui sont grièvement blessés ; selon le tri que vous avez fait."}},
[6623] = {"Service de traumatologie de la Horde",nil, {"Aller au Trépas-d'Orgrim, dans les hautes-terres d'Arathi, et évoquer avec le docteur Gregory Victor la possibilité de rejoindre les rangs des médecins de la Horde."}},
[6624] = {"Triage",nil, {"Sauver des patients en utilisant des Bandages de Triage, afin de panser leurs blessures. Vous devez sauver 15 patients avant que 6 d'entre eux ne meurent de leurs blessures.nnRappelez-vous l'ordre du triage ! Les Soldats agonisants meurent les premiers, suivis des Soldats grièvement blessés. Les Soldats blessés sont ceux qui meurent les derniers, et ils doivent être soignés APRES ceux qui agonisent et ceux qui sont grièvement blessés ; selon le tri que vous avez fait."}},
[6625] = {"Service de traumatologie de l'Alliance",nil, {"Aller à Theramore, dans le marécage d'Âprefange, et parler au docteur Gustaf VanHowzen de la possibilité de rejoindre les rangs des médecins de l'Alliance."}},
[6626] = {"L'hôte du mal",nil, {"Tuer 8 Gardes de guerre de Tranchebauge, 8 Tisseurs d'épines Tranchebauge et 8 Sectateur de la Tête de mort, puis retourner voir Myriam Chantelune, à l'entrée des Souilles de Tranchebauge."}},
[6627] = {"L'épreuve de la Connaissance",nil, {"Réussir à répondre à la question de Braug Esprit-troublé, puis lui reparler. Il se trouve dans les Serres-Rocheuses."}},
[6628] = {"L'épreuve de la Connaissance",nil, {"Réussir à répondre à la question de Parqual Fintallas, puis lui reparler. Il se trouve à Fossoyeuse."}},
[6629] = {"Tuer Grundig Noir-nuage",nil, {"Tuer Grundig Noir-nuage et 6 Brutes du Totem-sinistre, puis retourner voir Makaba Sabot-plat, à côté de la frontière sud-est des Serres Rocheuses."}},
[6641] = {"Vorsha la Flagellante",nil, {"Garantir la sécurité de Muglash tandis qu'il voyage vers le brasero. L'aider à éteindre le brasero, puis le protéger, en combattant les nagas.nnEn cas de réussite, retourner à l'avant-poste de Zoram'gar et informer le messager Chanteguerre de la mort de Vorsha la Flagellante."}},
[6642] = {"Faveur auprès de la Confrérie, Minerai de sombrefer",nil, {"Le sombre fer, bien qu'il soit répandu dans les Profondeurs, est toujours considéré comme un produit de valeur pour la Confrérie. Les armes sacrées de Ragnaros nécessitent du sombre fer.nnPour chaque 10 morceaux de Minerai de sombre fer que vous offrirez, une faveur vous gagnerez."}},
[6643] = {"Faveur auprès de la Confrérie, Noyau de feu",nil, {"Malheur à ceux qui défient le destin au Cœur du Magma ! La mort frappe rapidement ! Le châtiment est sans pitié.nnDe nombreux membres de la Confrérie ont disparu en recherchant des Noyaux de feu.nnDonnez 1 Noyau de feu et vous obtiendrez une faveur de la part de la Confrérie."}},
[6644] = {"Faveur auprès de la Confrérie, Noyau de lave",nil, {"La Confrérie du Thorium comprend vos difficultés, <nom>. C'est pour cela que Lokthos a reçu les pleins pouvoirs pour négocier.nnDonnez 1 Noyau de lave et vous recevrez une faveur de la part de la Confrérie."}},
[6645] = {"Faveur auprès de la Confrérie, Cuir du Magma",nil, {"Beaucoup de recettes sacrées exigent du cuir des familiers personnels de Ragnaros.nnDonnez 2 Cuirs du Magma, provenant du corps des Chiens du Magma, et vous recevrez une faveur de la Confrérie."}},
[6646] = {"Faveur auprès de la Confrérie, Sang de la montagne",nil, {"Les larmes du Seigneur du feu sont vraiment très rares.nnOffrez 1 Sang de la montagne et une faveur vous sera accordée par la Confrérie."}},
[6661] = {"Chasse au rat dans les profondeurs",nil, {"Capturer 5 Rats des profondeurs en utilisant le Pipeau du dératiseur. Conduire les Rats vers Monty. N'oubliez pas de rendre le Pipeau quand vous aurez terminé."}},
[6662] = {"Mon frère, Nipsy",nil, {"Apporter le Carton de viande mystère à Nipsy, au Dépôt du Tram des profondeurs de Hurlevent."}},
[6681] = {"Ravenholdt, le manoir",nil, {"Apporter le Sceau de Ravenholdt à Fahrad, au Manoir de Ravenholdt, dans les collines de Hautebrande.nnVotre sagacité sera mise à l'épreuve tout au long de la route. Préparez-vous."}},
[6701] = {"Emblèmes du Syndicat",nil, {"Nous sommes en guerre avec le Syndicat depuis des années. Ils compensent par leur nombre leur manque de compétences. Nous n'arrivons pas à les tuer assez rapidement pour que cela ait un impact notable sur leur population !nnNous avons découvert que le meilleur moyen de gérer leur nombre est de voler leurs Emblèmes. Chaque membre qui la perd est 'puni' en interne.nnDétroussez-les et rapportez les Emblèmes à moi ou aux autres Gardes.nnCommencez à Durnholde, au sud d'ici."}},
[6721] = {"La voie du chasseur",nil, {"Parler à Dazalar, à Dolanaar."}},
[6722] = {"La voie du chasseur",nil, {"Parler à Dazalar, à Dolanaar."}},
[6741] = {"Plus de butin !",nil, {"J'ai besoin de plus de Morceaux d'armure, <nom> ! Vous en avez ?"}},
[6761] = {"La nouvelle frontière",nil, {"Parler à Mathrengyl Pasdelours, dans l'Enclave cénarienne de Darnassus."}},
[6762] = {"Rabine Saturna",nil, {"Parler à Rabine Saturna, au village de Havrenuit, à Reflet-de-Lune. Reflet-de-Lune se trouve entre Gangrebois et le Berceau-de-l'Hiver. Le chemin qui y mène traverse le Repaire des Grumegueules."}},
[6781] = {"Plus de morceaux d'armure !",nil, {"<nom>, nous avons besoin de davantage de Morceaux d’armure pour équiper nos troupes !"}},
[6801] = {"Lokholar le Seigneur des Glaces",nil, {"Je viens de Thunder Bluff, <nom>. Cairne en personne m’a diligenté afin que j’assiste le clan Frostwolf en ces temps difficiles.nnMais ne perdons pas plus de temps. Vous devez abattre nos ennemis et me rapporter un peu de leur sang. Une fois que le sang aura été collecté, le rituel d’invocation pourra commencer.nnLa victoire sera certaine lorsque le Seigneur élémentaire sera lâché sur l’armée des Stormpike."}},
[6804] = {"Eau empoisonnée",nil, {"Utiliser l'Aspect de Neptulon sur les élémentaires empoisonnés des Maleterres de l'est. Apporter 12 Bracelets discordants et l'Aspect de Neptulon au duc Hydraxis, en Azshara."}},
[6805] = {"Tempêtes",nil, {"Tuer 15 Tempétueux de poussière et 15 Grondeurs du désert, puis retourner voir le duc Hydraxis, en Azshara."}},
[6821] = {"Œil du Prophète ardent",nil, {"Apporter l'Oeil du Prophète ardent au duc Hydraxis, en Azshara."}},
[6822] = {"Le Cœur du Magma",nil, {"Tuer 1 Seigneur du feu, 1 Géant de lave, 1 Chien antique du Magma et 1 Surgisseur de lave, puis retourner voir le duc Hydraxis, en Azshara."}},
[6823] = {"Agent de Hydraxis",nil, {"Parvenir au niveau de réputation Honoré auprès des Hydraxiens, puis parler au duc Hydraxis, en Azshara."}},
[6824] = {"Les mains de l'ennemi",nil, {"Apporter les Mains de Lucifron, de Sulfuron, de Gehennas et de Shazzrah au duc Hydraxis, en Azshara."}},
[6825] = {"L'appel des airs - l'escadrille de Guse",nil, {"Nous devons préparer la nouvelle flotte des Chevaucheurs de guerre, <nom> ! Mes Chevaucheurs sont prêts pour combattre au centre du champ de bataille, mais d’abord nous devons les mettre en appétit et les préparer à l’assaut.nnIl me faut suffisamment de chair de soldats Stormpike pour nourrir la flotte ! Des centaines de kilos de viande ! Je suis sûr que vous pouvez vous occuper de ça, n’est-ce pas ?"}},
[6826] = {"L'appel des airs - l'escadrille de Jeztor",nil, {"Vous avez bien travaillé, <nom>, mais nous ne faisons que commencer !nnMes Chevaucheurs de guerre doivent goûter à la chair de leurs cibles. Cela permettra d’obtenir comme qui dirait une frappe chirurgicale contre nos ennemis !nnMa flotte est la seconde plus puissante de toute notre force aérienne. Ce sont eux qui frapperont les plus puissants de nos adversaires. Pour cela, je le répète, il nous faut de la chair des Lieutenants de l'Alliances.nnFaites vite !"}},
[6827] = {"L'appel des airs - l'escadrille de Mulverick",nil, {"J’ai subi un emprisonnement inhumain dans ce trou à rats. Vous pouvez me croire quand je vous dis que je n’aspire qu’à la vengeance !nnNous devons agir avec prudence, néanmoins.nnD’abord, mes Chevaucheurs de guerre ont besoin de cibles, de cibles prioritaires. Je me dois de les nourrir avec la chair des commandants de l'Alliance. Malheureusement, ces petits couards sont retranchés loin derrière leurs lignes. Vous avez du pain sur la planche, on peut le dire !"}},
[6843] = {"Da Foo",nil, nil},
[6844] = {"Umber, Archiviste",nil, {"Apporter l'Objet silithide incrusté à Umber, l'archiviste du Cercle cénarien, au village de Havrenuit, à Reflet-de-Lune."}},
[6845] = {"Les secrets du passé",nil, {"Parler à Rabine Saturna, au village de Havrenuit, à Reflet-de-Lune."}},
[6846] = {"Que l'attaque commence !",nil, nil},
[6847] = {"L'Œil qui voit tout de maître Ryson",nil, {"Récupérer l'Oeil-qui-voit-tout de Maître Ryson dans les Grottes Hache-d'hiver, puis le rapporter à Maître Ryson.nnSachez que l'on dit de l'Oeil-qui-voit-tout de Maître Ryson qu'il contient une terrible malédiction !"}},
[6848] = {"L'Œil-qui-voit-tout de maître Ryson",nil, {"Récupérez l'Oeil-qui-voit-tout de Maître Ryson dans les Grottes Hache-d'hiver, puis rapportez-le à Maître Ryson.nnSachez que l'on dit de l'Oeil-qui-voit-tout de Maître Ryson qu'il contient une terrible malédiction !"}},
[6861] = {"Le Déchiqueteur portatif de Zinfoirux",nil, {"Le Maître Ingénieur Zinfoirux veut que vous lui apportiez les matériaux suivants :nn*30 Barres de thorium.nn*50 Barres de Mithril.nn*75 Barres de fer.nn*1 Scie à vapeur."}},
[6862] = {"Le Déchiqueteur portatif de Zinfoirux",nil, {"Le Maître Ingénieur Zinfoirux veut que vous lui apportiez les matériaux suivants :nn*30 Barres de thorium.nn*50 Barres de Mithril.nn*75 Barres de fer.nn*1 Scie à vapeur."}},
[6881] = {"Ivus le Seigneur des forêts",nil, {"Le clan Frostwolf est protégé par un poison constitué d'énergie élémentaire. Leurs chamans ont développé des pouvoirs qui nous détruiront jusqu'au dernier, si on laisse les choses suivre leur cours.nnLa menace est maintenant trop importante pour le Cercle. Ivus doit être appelé à l'aide.nnLes soldats Frostwolf portent des charmes élémentaires, appelés Cristaux de tempête. Nous pouvons utiliser ces charmes pour invoquer Ivus. Partez maintenant et récupérez ces cristaux, <nom> !"}},
[6901] = {"Lancez l'attaque !",nil, nil},
[6921] = {"Parmi les ruines",nil, {"Apporter le Noyau de la brasse à Je'neu Sancrea, à l'Avant-poste de Zoram'gar, dans Orneval."}},
[6922] = {"Baron Aquanis",nil, {"Apporter le Globe d'eau étrange à Je'neu Sancrea, à l'Avant-poste de Zoram'gar, dans Orneval."}},
[6941] = {"L'appel des airs - l'escadrille de Vipore",nil, {"Les troupes d'élite de la Horde en faction sur la ligne de front doivent être engagées ! Je vous demande de tailler dans cette bande de sauvages et d'en faire chuter les effectifs. Revenez me voir avec les Médailles de leurs Lieutenants et de leurs Légionnaires. Lorsque un nombre suffisant de cette racaille aura étouffé dans son propre sang, je ferai déployer la force aérienne."}},
[6942] = {"L'appel des airs - l'escadrille de Slidore",nil, {"Mes gryphons sont en place pour donner l'assaut frontal, mais ils ne peuvent pas le faire tant que les rangs ennemis n'auront pas été réduits préalablement.nnLes guerriers de la Horde chargés de tenir la ligne de front portent des Médailles qu'ils arborent fièrement sur leurs poitrines. Arrachez ces Médailles à leurs cadavres et rapportez-les-moi.nnUne fois que les effectifs ennemis de la ligne de front auront été suffisamment réduits, je ferai donner la force aérienne !"}},
[6943] = {"L'appel des airs - l'escadrille d'Ichman",nil, {"Leur moral est au plus bas. Depuis l'échec cuisant de notre dernière attaque aérienne contre la Horde, ils refusent de voler ! C'est à vous maintenant de remonter leur moral.nnRetournez sur le champ de bataille et frappez le coeur du commandement de la Horde. Abattez leurs commandants et leurs gardiens. Revenez me voir quand vous aurez récupéré autant de Médailles que peut en contenir votre sac ! Je vous promets que lorsque mes gryphons verront ce butin et qu'ils sentiront l'odeur de leur sang sur les Médailles, ils seront prêts à reprendre le combat. Partez maintenant !"}},
[6961] = {"Grand-père Hiver est ici !",nil, {"Parler à Grand-père Hiver. Il se trouve près des Gourmandises Fumebois à Orgrimmar."}},
[6962] = {"Des friandises pour Grand-père Hiver",nil, {"Apporter 5 Biscuits en pain d'épice et un Verre de lait glacé au Grand-père Hiver, à Orgrimmar."}},
[6963] = {"Friandises volées de la fête du Voile d'hiver",nil, {"Trouver l'enquêteur des Gourmandises Fumebois qui est porté disparu dans les montagnes d'Alterac."}},
[6964] = {"L'esprit de cette fête",nil, {"Parler à Sagorne Rôdeur-des-crêtes, dans la Vallée de la Sagesse d'Orgrimmar, au sujet de la fête du Voile d'hiver."}},
[6981] = {"L'Éclat luminescent",nil, {"Allez à Cabestan pour trouver quelqu'un qui puisse vous en dire plus sur l'Éclat luminescent.nnPuis livrez l'Éclat, selon les instructions."}},
[6982] = {"Fournitures de Froide-dent",nil, {"Apporter 10 Fournitures de Froide-dent à l'intendant de l'Alliance, à Dun Baldar."}},
[6983] = {"La méchanceté vous habite...",nil, {"Localiser les friandises, et les ramener à Béachevé Chipsou, à Orgrimmar. C'est l'abominable Grinche, vivant quelque part dans les régions enneigées des montagnes d'Alterac, qui les détiendrait."}},
[6984] = {"Un grand merci des Gourmandises Fumebois !",nil, {"Parler à Grand-père Hiver à Orgrimmar."}},
[6985] = {"Fournitures de Gouffrefer",nil, {"Apporter 10 Fournitures de Gouffrefer à l'Intendant de la Horde, au donjon Loup-de-givre."}},
[7001] = {"Ecuries vides",nil, {"Trouvez un Loup des glaces dans la vallée d'Alterac. Servez-vous de la Muselière de loup des glaces pour « dompter » la bête. Lorsque ce sera fait, le loup des glaces vous suivra jusqu'au Maître des écuries Loup-de-givre. Parlez au Maître des écuries Loup-de-givre pour être crédité de cette capture."}},
[7002] = {"Harnais en cuir de bélier",nil, {"Certains de nos soldats sont occupés à capturer des loups pour le maître des écuries. D’autres doivent nous fournir les accessoires nécessaires à la monte. Je parle des harnais, bien sûr.nnVous devez vous procurer les matières premières sur les béliers de la région. Oui, ceux-là même que les Stormpike utilisent comme montures !nnTuez-les et ramenez-moi leurs peaux. Lorsque vous en aurez assez, nous fabriquerons des harnais, et les Chevaucheurs de loups Frostwolf entreront dans la bataille !"}},
[7003] = {"Géants rétrécis",nil, {"Utilisez l'Ultra-réducteur de Zorbin sur des géants de n'importe quel type en Féralas afin les rendre plus faciles à gérer. Apportez 15 Résidus de miniaturisation que vous trouverez sur les versions rétrécies des géants à Zorbin Poudrozieu aux docks de la Côte oubliée en Féralas."}},
[7021] = {"Grand-père Hiver est ici !",nil, {"Parler à Grand-père Hiver ; il se trouve près des Gourmandises Fumebois, à Orgrimmar."}},
[7022] = {"Grand-père Hiver est ici !",nil, {"Parler à Grand-père Hiver ; il se trouve près des Gourmandises Fumebois, à Forgefer."}},
[7023] = {"Grand-père Hiver est ici !",nil, {"Parler à Grand-père Hiver ; il se trouve près des Gourmandises Fumebois, à Forgefer."}},
[7024] = {"Grand-père Hiver est ici !",nil, {"Parler à Grand-père Hiver. Il se trouve près des Gourmandises Fumebois à Orgrimmar."}},
[7025] = {"Des friandises pour Grand-père Hiver",nil, {"Apporter 5 Biscuits en pain d'épice et un verre de Lait glacé au Grand-père Hiver à Forgefer."}},
[7026] = {"Harnais pour béliers",nil, {"La brigade Stormpike ne peut pas charger à cru ! Il nous faut des harnais pour nos montures. Après tout, nous ne sommes pas des sauvages.nnNous pourrions tuer les béliers qui s'aventurent près de notre base pour fabriquer ces harnais, mais ce serait idiot : nous en avons besoin comme montures.nnVous devez attaquer la base ennemie, tuer leurs loups de monte et prendre leur peaux. Ramenez-les-moi, et nous en ferons des harnais pour la cavalerie. Allez !"}},
[7027] = {"Écuries vides",nil, {"Trouvez un Bélier d'Alterac dans la vallée d'Alterac. Lorsque vous serez à proximité, servez-vous du Collier d'entraînement Foudrepique pour le \"dompter\". Une fois dompté, le Bélier d'Alterac vous suivra jusqu'au Maître des écuries. Parlez au Maître des écuries pour être crédité de cette capture."}},
[7028] = {"Forces maléfiques retorses",nil, {"Collecter 15 Ciselures de Cristaux Théradriques pour Saule, en Désolace."}},
[7029] = {"La corruption de Vylelangue",nil, {"Remplir la Fiole céruléenne renforcée, au Bassin de cristal orange, dans Maraudon.nnEn faire usage sur les Vignes de Vylevrille pour forcer les Engeances Noxcives corrompues à en sortir.nnSoigner 8 plantes en tuant les Engeances Noxcives, puis retourner voir Vark Balafre-glorieuse, à Proie-de-l'Ombre."}},
[7041] = {"La corruption de Vylelangue",nil, {"Remplir la Fiole céruléenne renforcée, au Bassin de cristal orange dans Maraudon.nnEn faire usage sur les vignes de Vylevrille pour forcer les Engeances de Noxxious corrompues à en sortir.nnSoigner 8 plantes en tuant les Engeances, puis retourner voir Taliendra, à la Combe de Nijel."}},
[7042] = {"Friandises volées de la fête du Voile d'hiver",nil, {"Localiser l'enquêteur des Gourmandises Fumebois qui a été porté disparu dans les montagnes d'Alterac."}},
[7043] = {"La méchanceté vous habite...",nil, {"Trouver les Friandises volées, et les ramener à Karfour Tintinnabourse, à Forgefer. C'est l'Abominable Grinche qui aurait le chargement en sa possession, il résiderait dans les régions neigeuses des montagnes d'Alterac."}},
[7044] = {"Légendes de Maraudon",nil, {"Retrouver les deux parties du Sceptre de Celebras : le Bâtonnet de Celebras et le Diamant de Celebras.nnTrouvez un moyen de parler à Celebras."}},
[7045] = {"Un grand merci des Gourmandises Fumebois !",nil, {"Parler à Grandpère Hiver à Ironforge."}},
[7046] = {"Le sceptre de Celebras",nil, {"Aidez Celebras le Racheté pendant qu'il recrée le Sceptre de Celebras.nnParlez-lui après la réussite du rituel."}},
[7061] = {"La fête du Voile d'hiver",nil, {"Prenez le temps de lire ce livre, \"La fête du Voile d'hiver\", pour en apprendre plus sur cette période de fête. Quand vous en aurez fini avec le livre, apportez-le à Cairne Sabot-de-sang, dans la ville des Pitons du Tonnerre."}},
[7062] = {"L'esprit de cette fête",nil, {"Parlez à l'historien Karnik de la fête du Voile d'hiver, en vous rendant à la Ligue des Explorateurs de Forgefer."}},
[7063] = {"La fête du Voile d'hiver",nil, {"Prenez le temps de lire le livre \"La fête du Voile d'hiver\" pour en apprendre plus sur cette période sainte. Quand vous en aurez fini avec le livre, apportez-le au roi Magni Barbe-de-bronze, à Forgefer."}},
[7064] = {"Corruption de la terre et de la graine",nil, {"Anéantir la princesse Theradras et retourner voir Selendra près de Proie-de-l'Ombre, en Désolace."}},
[7065] = {"Corruption de la terre et de la Graine",nil, {"Tuer la princesse Theradras, et retourner voir le Gardien Marandis, à la Combe de Nijel en Désolace."}},
[7066] = {"La graine de la vie",nil, {"Chercher Remulos à Reflet-de-Lune, et lui donner la Graine de la vie."}},
[7067] = {"Les instructions du paria",nil, {"Lire les instructions du paria. Après cela, obtenir l'Amulette d'union de Maraudon et la ramener au paria centaure, dans le sud de Désolace."}},
[7068] = {"Fragments d'Ombréclat",nil, {"Collecter 10 Fragments d'Ombréclats dans Maraudon, et les ramener à Uthel'nay, à Orgrimmar."}},
[7070] = {"Fragments d'Ombréclat",nil, {"Collecter 10 Fragments d'Ombréclats, et les porter à l'archimage Tervosh, dans le marécage d'Âprefange."}},
[7081] = {"Les cimetières de la vallée d'Alterac",nil, {"Attaquez un cimetière, puis présentez-vous au sergent Durgen Foudrepique dans les montagnes d'Alterac."}},
[7082] = {"Les cimetières d'Alterac",nil, {"Attaquez un cimetière, puis présentez-vous au caporal Teeka Gronde-sang dans les montagnes d'Alterac."}},
[7101] = {"Tours et fortins",nil, {"Prenez une tour ennemie, puis présentez-vous au caporal Teeka Gronde-sang dans les montagnes d'Alterac."}},
[7102] = {"Tours et fortins",nil, {"Détruisez la bannière d'une tour ou d'un fortin ennemi, puis présentez-vous au sergent Durgen Foudrepique à Dun Baldar."}},
[7121] = {"L'intendant",nil, {"Parlez à l'Intendant Foudrepique."}},
[7122] = {"Prendre une mine",nil, {"Prenez une mine que les Foudrepiques ne contrôlent pas, puis présentez-vous au sergent Durgen Foudrepique dans les montagnes d'Alterac."}},
[7123] = {"Parlez à notre Intendant",nil, {"Parlez à l'Intendant Loup-de-givre."}},
[7124] = {"Prendre une mine",nil, {"Prenez une mine, puis présentez-vous au caporal Teeka Gronde-sang dans les montagnes d'Alterac."}},
[7141] = {"La bataille d'Alterac",nil, {"Entrez dans la vallée d'Alterac, tuez Drek'Thar, le général de la Horde, puis retournez voir la Prospectrice Taillepierre dans les montagnes d'Alterac."}},
[7142] = {"La bataille pour Alterac",nil, {"Entrez dans la vallée d'Alterac, tuez Vanndar Foudrepique, le général nain, puis retournez voir Voggah Mortepoigne dans les montagnes d'Alterac"}},
[7161] = {"Montrer sa valeur",nil, {"Rendez-vous dans les cavernes des Follepattes, au sud-est de la base principale, et récupérez la Bannière Loup-de-givre qui s'y trouve. Ramenez-la au Maître de guerre Laggrond."}},
[7162] = {"Montrer sa valeur",nil, {"Rendez-vous dans les cavernes de l'Aile de glace, au sud-ouest de Dun Baldar dans la vallée d'Alterac, et récupérez la Bannière Foudrepique. Ramenez-la au lieutenant Haggerdin dans les montagnes d'Alterac."}},
[7163] = {"Ascension et reconnaissance",nil, nil},
[7164] = {"Honoré au sein du clan",nil, nil},
[7165] = {"Montée en grade",nil, {"Tant de morts. Un champ saturé de sang. Mais vous êtes en vie. Vous avez persévéré. Vous avez mené nos armées à la victoire. Et pour cela, soldat, vous avez mérité de monter en grade.nnPrésentez votre insigne."}},
[7166] = {"Héros de légende",nil, {"Puissance et autorité émanent de vous, <classe>. Vous êtes exalté aux yeux des Frostwolf – l’ennemi se cache à la seule mention de votre nom.nnDebout, Héros des Frostwolf. Debout et soyez honoré !nnPrésentez votre insigne."}},
[7167] = {"L'Œil du commandement",nil, {"Drek’Thar chante vos louanges. Kalimdor résonne du bruit de vos exploits. Le chef de guerre sourit – votre héroïque défense de notre clan l’a grandement satisfait.nnVous avez mérité l’Œil du Commandement.nnPrésentez votre insigne, commandant <nom>."}},
[7168] = {"Ascension et reconnaissance",nil, {"La plupart des gens ne vivent pas assez longtemps pour s’élever au-dessus de leur propre médiocrité. Vous avez montré que vous êtes un soldat exemplaire, <classe>. L’heure est venue.nnPrésentez votre insigne."}},
[7169] = {"Honoré au sein de la Garde",nil, {"Toute la base parle de vos exploits sur le Champ sanglant ! Vous avez porté des coups sévères à l’ennemi – et à son moral ! Pour cela, vous avez mérité de progresser au sein des Stormpike.nnPrésentez votre insigne."}},
[7170] = {"Montée en grade",nil, {"Je m’interroge, <nom>. Lorsque vous regardez dans les yeux de l’ennemi, y voyez-vous la peur ? Se cachent-ils plutôt que de vous faire face ? Ils doivent bien se rendre compte qu’ils sont vaincus !nnVous avez mérité que la Garde vous traite avec révérence. Présentez votre insigne !"}},
[7171] = {"Héros de légende",nil, {"Devant moi se tient un héros de l’Alliance.nn<Le Lieutenant Haggerdin salue.>nnRares sont ceux qui ont obtenu un tel grade parmi les Stormpike. J’ai vu l’ennemi tomber devant vous. J’ai vu leur résolution s’effondrer devant votre présence. Lorsque vous entrez dans la mêlée, vous devenez un point de ralliement pour nos forces !nnPrésentez votre insigne."}},
[7172] = {"L'Œil du commandement",nil, nil},
[7181] = {"La légende de Korrak",nil, {"D'après la légende, le chef des puissants trolls Haches-d'hiver se montre quand il le décide pour semer la destruction chez les habitants de la vallée d'Alterac.nnSi Korrak apparaît, détruisez-le et présentez-vous au Maître de guerre Laggrond."}},
[7201] = {"Le dernier élément",nil, {"Partir pour les Profondeurs de Rochenoire et trouver 10 Essences des éléments. Commencer vos recherches par les golems et par les créateurs de golems. Vivian Lagrave a aussi marmonné quelque chose à propos d'élémentaires."}},
[7202] = {"Korrak le Ragesang",nil, {"D'après la légende, le chef des puissants trolls Haches-d'hiver se montre quand il le décide pour semer la destruction chez les habitants de la vallée d'Alterac.nnSi Korrak apparaît, détruisez-le et présentez-vous au Lieutenant Haggerdin dans les montagnes d'Alterac."}},
[7221] = {"Discuter avec la prospectrice Stonehewer",nil, {"Discutez avec la Prospectrice Taillepierre à la Garnison de Dun Baldar."}},
[7222] = {"Discuter avec Voggah Deathgrip",nil, {"Parlez à Voggah au donjon Loup-de-givre."}},
[7223] = {"Morceaux d'armures",nil, {"Apportez 20 Morceaux d'armure à Murgot Forge-profonde à Dun Baldar."}},
[7224] = {"Pris à l'ennemi",nil, {"Apportez 20 Morceaux d'armure au forgeron Regzar au village Loup-de-givre."}},
[7241] = {"La défense des Loups-de-givre",nil, {"Rendez-vous dans la vallée d'Alterac, à l'intérieur des montagnes d'Alterac. Trouvez le maître de guerre Laggrond à l'extérieur du tunnel d'entrée, parlez-lui et commencez votre carrière comme soldat Loup-de-givre. Vous trouverez la vallée d'Alterac au nord de Moulin-de-Tarren à la base des montagnes d'Alterac."}},
[7261] = {"L'impératif souverain",nil, {"Voyagez jusqu'à la vallée d'Alterac, dans les contreforts de Hautebrande. Présentez-vous au lieutenant Haggerdin à l'extérieur du tunnel d'entrée.nnPour la gloire de Barbe-de-bronze !"}},
[7281] = {"Amour fraternel",nil, {"Pénétrez en territoire Foudrepique et tuez le commandant Karl Philips. Lorsque ce sera fait, présentez-vous au commandant Louis Philips."}},
[7282] = {"Amour fraternel",nil, {"Pénétrez en territoire Loup-de-givre et tuez le commandant Louis Philips. Lorsque ce sera fait, présentez-vous au commandant Karl Philips."}},
[7301] = {"Les ailes brisées",nil, {"Enfoncez-vous en territoire Loup-de-givre à la recherche des chefs d'escadrille Slidore, Vipore et Ichman. Si vous les retrouvez, présentez-vous au commandant Duffy."}},
[7302] = {"Les ailes brisées",nil, {"Découvrez ce qui est arrivé aux chefs d'escadrille Guse, Jetzor et Mulverick. Lorsque vous les aurez retrouvés, présentez-vous au commandant Mulfort."}},
[7321] = {"Bisque de tortue apaisante",nil, {"Apporter 10 morceaux de Viande de tortue et des Epices apaisantes à Christoph Jeffcoat au Moulin-de-Tarren."}},
[7341] = {"Un marché honnête",nil, {"Bonjour, <race>. Je suis Kolark, <classe> de primes de Thunderbluff. J’ai beaucoup voyagé de par le monde et j’ai beaucoup vu.nnAu cours de mes voyages dans les mers du Sud, j’ai découvert ces merveilleuses flèches à pointes de thorium. Hélas, j’ai des flèches à ne plus savoir quoi en faire, mais j’ai épuisé ma provision d’obus. Aucun tauren qui se respecte ne se ferait prendre à se servir d’un arc – trop fragile. Ces flèches ne me servent à rien !nnJe vous propose un marché : vous m’apportez des obus en thorium, et je vous donne un nombre égal de flèches à pointes de thorium. Entendu ?"}},
[7342] = {"Les flèches, c'est pour les mauviettes",nil, {"Vous y croyez, vous ? Les gars qui ont fait le dernier chargement de munitions de Kharanos ont cochonné le boulot ! Tout ce que nous avons comme munitions, ce sont ces saletés de flèches bonnes à rien ! Qu’est-ce que je suis censé faire avec 500 000 flèches à pointes de thorium, vous pouvez me le dire ?nnJe vais vous dire. Si vous pouvez m’amener des obus en thorium, je vous file ces flèches à pointe de thorium, et tout de suite ! Entendu ?"}},
[7361] = {"En faveur auprès des Sombrelances",nil, {"Vous avez été <chargé/chargée> de tuer les personnages Elfes de la nuit présents dans la vallée d’Alterac.nnTuez un Elfe de la nuit et présentez-vous à Najak Maléficas avec une Tête coupée d’Elfe de la nuit."}},
[7362] = {"Allié des Taurens",nil, {"Vous avez été <chargé/chargée> de tuer les personnages gnomes présents dans la vallée d’Alterac.nnTuez un Gnome et présentez-vous à Ravak Totem-Sinistre au donjon Loup-de-givre avec une Touffe de cheveux de Gnome."}},
[7363] = {"La condition humaine",nil, {"Vous avez été <chargé/chargée> de tuer les personnages humains présents dans la vallée d’Alterac.nnTuez un humain et présentez-vous au commandant Louis Philips (qui fait la navette entre le front et le donjon Loup-de-givre) avec un Bout d’os humain.nnUn remède à la condition humaine est à portée de main !"}},
[7364] = {"Au service de Gnomeregan",nil, {"Vous avez été <chargé/chargée> de tuer les personnages taurens présents dans la vallée d’Alterac.nnTuez un Tauren et présentez-vous à Dirk Lembrouille à Dun Baldar avec un Sabot de Tauren."}},
[7365] = {"Le requiem de Forteramure",nil, {"Vous avez été <chargé/chargée> de tuer les personnages trolls présents dans la vallée d’Alterac.nnTuez un Troll et présentez-vous à Athramanis à Dun Baldar avec un Mojo de Troll sombrelance."}},
[7366] = {"La pitié de l'archevêque",nil, {"Vous avez été <chargé/chargée> de tuer les personnages Réprouvés présents dans la vallée d’Alterac.nnTuez un Réprouvé et présentez-vous au commandant Karl Philips (qui fait la navette entre le scierie et la mine) avec un Cœur de Réprouvé."}},
[7367] = {"Désarmer les mines",nil, {"Trouvez l'Expert en explosifs Loup-de-givre et tuez-le. Lorsque ce sera fait, présentez-vous à l'Expert en explosifs Foudrepique."}},
[7368] = {"Désarmer les mines",nil, {"Trouvez l'Expert en explosifs Foudrepique et tuez-le. Lorsque ce sera fait, présentez-vous à l'Expert en explosifs Loup-de-givre."}},
[7381] = {"Le retour de Korrak",nil, {"Si Korrak le Ragesang devait réapparaître dans le Champ sanglant, traquez-le et tuez-le.nnApportez le Crâne de Korrak au Maître de guerre Laggrond dans les contreforts de Hautebrande."}},
[7382] = {"Korrak l'Indestructible",nil, {"Si Korrak le Ragesang réapparaît dans le Champ sanglant, traquez-le et tuez-le.nnApportez le Crâne de Korrak au lieutenant Haggerdin dans les Contreforts de Hautebrande."}},
[7383] = {"La couronne de la Terre",nil, {"Remplir la Fiole d'améthyste et la rapporter à Corithras Ragelune à Dolanaar."}},
[7385] = {"Quelques litres de sang",nil, {"Vous avez la possibilité d’offrir des quantités de sang prises à l’ennemi plus importantes. Je serais content d’accepter des offrandes de quelques litres, <nom>."}},
[7386] = {"Grappes de cristaux",nil, {"Parfois, vous serez bloqué dans la bataille pendant des jours ou des semaines. Profitez de ces longues périodes d’activité pour ramasser des grappes de cristaux de tempête Frostwolf.nnLe Cercle accepte de telles offrandes, <nom>."}},
[7401] = {"ON RECHERCHE : des Nains !",nil, {"Vous avez été <chargé/chargée> de tuer les personnages nains présents dans la vallée d’Alterac.nnTuez des Nains et arrachez-leur la colonne vertébrale. Apportez les Colonnes vertébrales de Nains au sergent Yazra Gronde-Sang au donjon Loup-de-givre."}},
[7402] = {"On recherche : ORCS !",nil, {"Vous avez été chargé de tuer les personnages orcs présents dans la Vallée d’Alterac.nnTuez un orc et remettez les Dents d’orc au caporal Noreg Foudrepique à Dun Baldar."}},
[7421] = {"La défense des Sombrelances",nil, nil},
[7422] = {"Pour une poignée de cheveux",nil, nil},
[7423] = {"Toujours plus de bouts d'os !",nil, nil},
[7424] = {"Chasse aux sabots !",nil, nil},
[7425] = {"La collection de mojos de Forteramure",nil, nil},
[7426] = {"L'amour d'un homme",nil, nil},
[7427] = {"On recherche : PLUS DE NAINS !",nil, nil},
[7428] = {"On recherche : PLUS D'ORCS !",nil, nil},
[7429] = {"Libérez Noué !",nil, {"C’est la Clé des menottes gordok – en tous cas, c’est comme ça que l’appellent ces patapoufs. Je ne sais pas qui l’a. Ils le peuvent tous, j’imagine. Les gardes, sans doute…nnMais qu’est-ce que je raconte ? Vous l’avez déjà, n’est-ce pas ? Vous l’avez sur vous ?"}},
[7441] = {"Pusillin et l'Ancien Azj'Tordin",nil, {"Rendez-vous à Hache-tripes et trouvez le diablotin Pusillin. Persuadez-le par tous les moyens de vous rendre le Livre d'Incantations d'Azj'Tordin.nnSi vous parvenez à le récupérer, retournez voir Azj'Tordin au Pavillon de Lariss, en Féralas."}},
[7461] = {"Folie intérieure",nil, {"Vous devez détruire les gardiens qui protègent les 5 Pylônes qui alimentent la Prison d'Immol'thar. Une fois que les Pylônes seront coupés, le champ de force qui entoure Immol'thar se dissipera.nnEntrez dans la Prison d'Immol'thar et éradiquez l'infâme démon qui se trouve en son cœur. Enfin, affrontez le prince Tortheldrin à l'Athenaeum.nnLorsque vous aurez terminé, retournez voir l'Ancienne des Shen'dralar dans la cour."}},
[7462] = {"Le trésor des Shen'dralar",nil, {"Retournez à l'Athenaeum et trouvez le Trésor des Shen'dralar. Prenez votre récompense !"}},
[7463] = {"Rafraîchissement arcanique",nil, {"Rendez-vous dans le quartier de Crochebois de Hache-tripes et tuez Hydrogénos, l'élémentaire de l'eau. Retournez voir le Gardien du savoir Lydros à l'Athenaeum avec l'Essence d'Hydrogénos."}},
[7481] = {"Légendes elfiques",nil, {"Fouillez Hache-tripes à la recherche de Kariel Winthalus. Lorsque vous aurez des informations, donnez-les au Sage Korolusk de Camp Mojache."}},
[7482] = {"Légendes elfiques",nil, {"Fouillez Hache-tripes à la recherche de Kariel Winthalus. Lorsque vous aurez des informations, donnez-les à l'Erudite Roncerune, à Pennelune."}},
[7483] = {"Libram de Rapidité",nil, {"Apportez un Libram de rapidité, 1 Diamant noir parfait, 2 Grands éclats brillants et 2 doses de Sang des héros au Gardien du savoir Lydros à Hache-tripes pour recevoir un Arcanum de rapidité."}},
[7484] = {"Libram de focalisation",nil, {"Apportez un Libram de focalisation, 1 Diamant noir en parfait état, 4 Grands éclats brillants et 2 Peaux des ombres au Gardien du savoir Lydros à Hache-tripes pour recevoir un arcanum de focalisation."}},
[7485] = {"Libram de Protection",nil, {"Apportez un Libram de protection, 1 Diamant noir en parfait état, 2 Grands éclats brillants et 1 Couture d'abomination effilochée au Gardien du savoir Lydros à Hache-tripes pour recevoir un Arcanum de protection."}},
[7486] = {"La récompense du héros",nil, {"Allez chercher votre récompense dans le Coffre d'Hydraxis."}},
[7487] = {"Harmonisation avec le Cœur du Magma",nil, {"Aventurez-vous jusqu'au portail d'entrée du Cœur du Magma dans les profondeurs de Rochenoire et récupérez un Fragment du Magma. Lorsque ce sera fait, retournez voir Lothos Ouvrefaille au mont Rochenoire."}},
[7488] = {"Le filet de Lethtendris",nil, {"Ramenez le Filet de Lethtendris à Latronicus Lancelune au bastion de Pennelune en Féralas."}},
[7489] = {"Le filet de Lethtendris",nil, {"Ramenez le Filet de Lethtendris à Talo Sabot-de-ronce au Camp Mojache, en Féralas."}},
[7490] = {"Victoire pour la Horde !",nil, {"Apportez la Tête d'Onyxia à Thrall à Orgrimmar."}},
[7491] = {"Pour que tous voient",nil, {"Allez voir le seigneur Runthak dans la vallée de la Force."}},
[7492] = {"Camp Mojache",nil, {"Parlez à Talo Sabot-de-ronce au Camp Mojache, en Féralas."}},
[7493] = {"Le voyage commence à peine",nil, {"Tueur de dragons et artisan du cuir expérimenté ? Vous ne cessez pas de m'émerveiller, héros. Je m'incline devant votre supériorité.nnJe vous prie d'accepter mon enseignement. Ce savoir pourrait vous rendre de grands services si vous entreprenez de détruire ce qui reste du Vol noir.nnServez-vous des écailles de leur mère pou créer une cape. Elle vous protègera, vous et vos alliés, du souffle incendiaire du seigneur de Blackrock… Nefarian."}},
[7494] = {"Le bastion de Pennelune",nil, {"Parlez à Latronicus Lancelune au bastion de Pennelune en Féralas."}},
[7495] = {"Victoire pour l'Alliance !",nil, {"Apportez la Tête d'Onyxia au généralissime Bolvar Fordragon à Hurlevent."}},
[7496] = {"Célébrer le triomphe",nil, {"Allez voir le major Mattingly dans la vallée des Héros."}},
[7497] = {"Le voyage commence à peine",nil, {"Tueur de dragons et artisan du cuir expérimenté ? Vous ne cessez pas de m'émerveiller, héros. Je m'incline devant votre supériorité.nnJe vous prie d'accepter mon enseignement. Ce savoir pourrait vous rendre de grands services si vous entreprenez de détruire ce qui reste du Vol noir.nnServez-vous des écailles de leur mère pour créer une cape. Elle vous protègera, vous et vos alliés, du souffle incendiaire du seigneur de Blackrock… Nefarian."}},
[7498] = {"Garona : une étude en discrétion et en trahison",nil, {"Rendez ce livre à ses légitimes propriétaires."}},
[7499] = {"Le codex de défense",nil, {"Rendez ce livre à ses légitimes propriétaires."}},
[7500] = {"Les recettes de l'arcaniste",nil, {"Rendez ce livre à ses légitimes propriétaires."}},
[7501] = {"La lumière et comment l'altérer",nil, {"Rendez ce livre à ses légitimes propriétaires."}},
[7502] = {"Contrôler les ombres",nil, {"Rendez ce livre à ses légitimes propriétaires."}},
[7503] = {"La plus grande race de chasseurs",nil, {"Rendez ce livre à ses légitimes propriétaires."}},
[7504] = {"Sainte Bolognaise : Ce que la lumière ne vous dit pas",nil, {"Rendez ce livre à ses légitimes propriétaires."}},
[7505] = {"Le Horion de givre et vous",nil, {"Rendez ce livre à ses légitimes propriétaires."}},
[7506] = {"Le Rêve d'Emeraude…",nil, {"Rendez ce livre à ses légitimes propriétaires."}},
[7507] = {"Le guide de Dagharn",nil, {"Rendez \"Le guide de Dagharn du tueur de dragons\" à l'Athenaeum."}},
[7508] = {"Forger Quel'Serrar",nil, {"Donnez la Lame elfique inachevée au Gardien du savoir Lydros."}},
[7509] = {"Forger Quel'Serrar",nil, {"Vous devez amener Onyxia à cracher le feu sur la Lame ancienne brute. Lorsque ce sera fait, ramassez la Lame ancienne chauffée. Attention, elle ne restera pas chaude éternellement. Le temps est très important.nnAvant de revenir me voir, il vous faudra tuer la bête et plonger la Lame ancienne chauffée dans son cadavre.nnSi vous y parvenez, Quel'Serrar sera à vous."}},
[7521] = {"Tonneraan le Cherchevent",nil, {"Pour libérer Tonneraan le Cherchevent de sa prison, vous devez présenter au généralissime Demitrian les moitiés droite et gauche des Liens du Cherchevent, 10 barres d'élémentium, et l'Essence du seigneur du Feu."}},
[7522] = {"Examiner le Calice",nil, {"Examiner le Calice de la renaissance et le rapporter au généralissime Demitrian."}},
[7541] = {"Un service pour la Horde",nil, {"Il faudra du temps pour que nos savants étudient le pendentif que vous nous avez rapporté. J'aimerais vous offrir un petit témoignage de notre reconnaissance, pour le service que vous avez rendu à la Horde.nnMerci, <nom>. Vos actions vous font honneur."}},
[7562] = {"Mor'zul Porte-sang",nil, {"Parler à Mor'zul Porte-sang dans les Steppes ardentes."}},
[7563] = {"Rage de sang",nil, {"Apporter 30 bouteilles de Sang de bêtes déchaînées à Mor'zul Porte-sang dans les Steppes ardentes."}},
[7564] = {"Gorzeeki Wildeyes",nil, {"Apporter la Caisse de sang à Gorzeeki Fouregard dans les Steppes ardentes."}},
[7581] = {"Les ancrages de la prison",nil, {"Rendez-vous à Hache-tripes en Féralas et récupérez 15 doses de Sang de satyre sur les satyres Follengeance qui habitent le quartier de Crochebois. Lorsque ce sera fait, retournez voir Levandre à la Balafre impure."}},
[7582] = {"Les parois de la prison",nil, {"Rendez-vous à la gorge du Sombre murmure, au Berceau-de-l'Hiver, et récupérez 5 Larmes de l'Hederine sur les démons Hederine qui occupent la gorge. Lorsque ce sera fait, retournez voir Levandre à la Balafre impure."}},
[7583] = {"Suppression",nil, {"Pénétrez dans la Balafre impure et localisez un Commandant de la garde funeste.nnServez-vous de la Prison de cristal luminescente sur lui. Attendez-vous à être sauvagement <attaqué/attaquée>, car le démon fera l'impossible pour éviter d'être capturé.nnSi vous réussissez, amenez le garde funeste emprisonné à Daio à la Balafre impure."}},
[7601] = {"Les Commandements de Niby",nil, {"Parlez à Démonet à Gangrebois."}},
[7602] = {"La gangressence parfaite",nil, {"Démonet vous a demandé de lui apporter trois Gangressences parfaites, venant de trois régions distinctes.nnLes satyres Legashi d'Azshara, les légionnaires de Jaedenar et les gangregardes sentinelles des Terres foudroyées détiennent la Gangressence parfaite de leur région.nnRécupérez les Gangressences parfaites et rapportez-les à Démonet, à Gangrebois."}},
[7603] = {"Le noyau infernal de Kroshius",nil, {"Fouillez le Val Grêlé pour trouver les restes de Kroshius. Quand vous les aurez localisés, utilisez le Gangrefeu à proximité et attendez le retour de Kroshius. Quand l'Infernal se sera réveillé, tuez-le et prenez le Noyau infernal sur son cadavre.nnRapportez le Noyau infernal de Kroshius à Niby le Tout-Puissant, à Gangrebois."}},
[7604] = {"Un contrat contraignant",nil, {"Apportez le Contrat de la Confrérie du Thorium à Lokhtos Sombrescompte, si vous voulez recevoir les plans du marteau de sulfuron."}},
[7621] = {"Un avertissement",nil, {"<classe> vous allez subir une série d’épreuves au cours desquelles tous vos talents seront poussés à leurs limites.nnÉcoutez attentivement mon histoire.nnAttention, vous devrez achever cette tâche seul. Je conseille à vos alliés de rester à distance. Toute intervention extérieure entraînera un châtiment sévère.nnLe Destin vous a conduit ici, vous et vous seul. Battez-vous avec honneur. Que la Lumière vous protège !"}},
[7622] = {"L’équilibre de la Lumière et de l’Ombre",nil, {"Sauvez 50 Paysans avant que 15 d'entre eux aient été tués. Si vous y parvenez, allez parler à Eris Havrefeu.nnConsultez le Panneau des morts pour voir combien de paysans ont été tués."}},
[7623] = {"Le seigneur Creuseplaie",nil, {"Achetez des Potions des ténèbres à Gorzeeki dans les Steppes ardentes.nnServez-vous-en pour vous rendre à Jaedenar, et parlez au seigneur Creuseplaie."}},
[7624] = {"Ulathek le traître",nil, {"Affrontez Ulathek, puis apportez le Cœur du traître au seigneur Creuseplaie à Jaedenar."}},
[7625] = {"Poussière d'étoile xorothienne",nil, {"Acheter de la Poussière d'étoile xorothienne à Ur'dan. L'apporter à Gorzeeki Fouregard dans les Steppes ardentes."}},
[7626] = {"La Cloche de Dethmoora",nil, {"Apportez 2 Elixirs de puissance de l'ombre à Gorzeeki Fouregard dans les Steppes ardentes."}},
[7627] = {"La Roue de la Marche noire",nil, {"Apportez 6 Grands éclats brillants et 25 charges de Minerai de sombrefer à Gorzeeki dans les Steppes ardentes."}},
[7628] = {"La Chandelle de l'apocalypse",nil, {"Apportez 3 Ecailles de dragon noir à Gorzeeki Fouregard dans les Steppes ardentes."}},
[7629] = {"Livraison de diablotin",nil, {"Apporter le Diablotin en bouteille au laboratoire d'alchimie de la Scholomance. Une fois le parchemin créé, rapportez la bouteille à Gorzeeki Fouregard."}},
[7630] = {"L'arcanite",nil, {"Apporter 3 Barres d’arcanite à Gorzeeki dans les Steppes ardentes."}},
[7631] = {"Le destrier de l'effroi de Xoroth",nil, {"Lisez les Instructions de Mor'zul. Invoquez un Destrier de l'effroi xorothien, vainquez-le, et liez son esprit."}},
[7632] = {"La feuille ancienne",nil, {"Trouvez le propriétaire de la Feuille ancienne pétrifiée. Bonne chance, <nom>, le monde est vaste."}},
[7633] = {"Une rencontre",nil, {"Votre dilligence et votre intégrité ont été notées, <classe>.nnNous sommes les Anciens, les protecteurs de cette forêt qui fut grande, autrefois. Nos formes physiques ont été détruites il y a dix mille ans lorsqu’Archimonde et la Légion ardente ont marché sur le mont Hyjal. Malgré tout, nos esprits demeurent intacts.nnNous avons des tâches à vous proposer. Des tâches qui vous donneront accès à nos secrets, si vous les accomplissez.nnSi vous y parvenez, revenez à ce rocher et nous répondrons à votre appel."}},
[7634] = {"Feuille d'Ancien cousue de tendons",nil, {"L'Ancien Hastat vous demande de lui ramener un Tendon de dragon bleu adulte. Si vous en trouvez un, ramenez-le-lui à Gangrebois."}},
[7635] = {"La bonne corde",nil, {"L'Ancien Stoma vous demande de lui ramener un Tendon de dragon noir adulte. Si vous en trouvez un, ramenez-le à Stoma à Gangrebois."}},
[7636] = {"Le bâton des Anciens",nil, {"Vous devez trouver et tuer quatre corrupteurs démoniaques :nnSimona la Séductrice.nnKlinfran l'Enragé.nnSolenor le Tueur.nnArtorius le Néfaste.nnDétruisez ces créatures et retournez voir l'Ancien Vartus à Gangrebois.nnVous trouverez des indices sur leurs faits et gestes sur l'Ecorce pétrifiée qui se trouve dans votre inventaire.nnVous DEVEZ agir seul."}},
[7637] = {"L'accent sur le sacrifice",nil, {"Rendez-vous à Forgefer et procurez-vous l'Encensoir d'exorcisme du grand prêtre Rohan. Pour vous le procurer, il vous faudra verser une donation de 150 pièces d'or."}},
[7638] = {"Le seigneur Grayson Shadowbreaker",nil, {"Parler au seigneur Grayson Brisombre dans le Quartier de la cathédrale de Hurlevent."}},
[7639] = {"Des preuves de jugement",nil, {"Ramener l'Encensoir d'exorcisme au seigneur Grayson Brisombre, dans le Quartier de cathédrale de Hurlevent."}},
[7640] = {"Exorciser Val-Terreur",nil, {"Servez-vous de l'Encensoir d'exorcisme pour chasser les esprits qui tourmentent Val-Terreur. Lorsque vous aurez tué 25 Esprits de Val-Terreur, retournez voir le seigneur Grayson Brisombre dans le Quartier de la cathédrale de Hurlevent."}},
[7641] = {"L'œuvre de Grimand Elmore",nil, {"Parlez à Grimand Elmore dans le Quartier des nains de Hurlevent."}},
[7642] = {"Une ribambelle d'objets",nil, {"Apporter 40 Etoffes runiques, 6 Barres d'arcanite, 10 Larmes d'Arthas, 5 Fioles d'eau sacrée de Stratholme, et 150 pièces d'or à Grimand Elmore, au Quartier des nains de Hurlevent."}},
[7643] = {"L'ancien esprit équin",nil, {"Procurez-vous un aliment pour chevaux capable de nourrir un esprit équin. Il semble que Merideth Carlson, à Austrivage, sache en fabriquer.nnEnsuite, rendez-vous au donjon de Hache-tripes en Féralas, et tuez Tendris Crochebois. Sa mort libérera l'Ancien esprit équin. Nourrissez-le avec l'aliment spécial afin de l'apaiser. Enfin, donnez-lui le Caparaçon d'arcanite pour qu'il le bénisse."}},
[7644] = {"Le caparaçon en arcanite béni",nil, {"Retourner dans le quartier de la cathédrale de Hurlevent avec le Caparaçon en arcanite béni pour voir le seigneur Grayson Brisombre."}},
[7645] = {"Fourrage enrichi en manne",nil, {"Récupérer 20 Biscuits de manne enrichis, l'ingrédient vital qui permet de préparer du Fourrage enrichi en manne, pour le compte de Merideth Carlson de Southore dans les contreforts de Hautebrande. L'Aube d'argent est la seule à fabriquer ces biscuits.nnVous devrez également lui donner 50 pièces d'or pour apaiser sa susceptibilité."}},
[7646] = {"Le Clairvoyant",nil, {"Apportez un Diamant d’Azeroth et un Diamant noir en parfait état au seigneur Grayson Shadowbreaker dans le Quartier de la cathédrale de Stormwind."}},
[7647] = {"Jugement et rédemption",nil, {"Utilisez le Clairvoyant au coeur du sous-sol du Grand ossuaire, dans la Scholomance. Cela attirera des esprits que vous devrez juger. Lorsqu'ils seront vaincus, le Chevalier de la mort Ravassombre apparaîtra. Vainquez-le et purifiez l'âme du destrier déchu.nnEnsuite, donnez l'Ame rachetée du destrier et le Caparaçon enchanté et béni au Destrier déchu de Ravassombre."}},
[7648] = {"Le chef d'œuvre de Grimand",nil, {"Présentez le Caparaçon d'arcanite au seigneur Grayson Brisombre dans le Quartier de la cathédrale de Hurlevent, pour inspection."}},
[7649] = {"Les plates en thorium enchanté - Volume I",nil, {"Rendez ce livre à ses légitimes propriétaires."}},
[7650] = {"Les plates en thorium enchanté - Volume II",nil, {"Rendez ce livre à ses légitimes propriétaires."}},
[7651] = {"Les plates en thorium enchanté - Volume III",nil, {"Rendez ce livre à ses légitimes propriétaires."}},
[7652] = {"Une affaire en thorium",nil, {"Voilà, messire, j’ai une superbe affaire à vous proposer.nnJe veux me débarrasser d’un certain nombre de plans pour des pièces d’armure impériales en plaques. Je cherche quelqu’un qui voudrait les prendre. En échange, tout ce qu’il me faut, ce sont quelques barres de thorium. Cela vous tente ?"}},
[7653] = {"La ceinture impériale en plaques",nil, {"Les plans de la ceinture vont vous coûter 20 barres de thorium."}},
[7654] = {"Les bottes impériales en plaques",nil, {"Pour le plan des bottes, mon prix est de 40 barres de thorium. Ouais, 40. Vous n’allez pas pleurer, quand même ? Vous voulez un mouchoir ?nn<Derotain ricane.>"}},
[7655] = {"Les bracelets impériaux en plaques",nil, {"Pour les plans des bracelets, il me faudra 20 Barres de thorium..nnVous vous sentez bien, mon gars? Vous êtes tout rouge."}},
[7656] = {"La pansière impériale",nil, {"Pour les plans de la pansière, il va me falloir 60 barres de thorium.nnOh bon, c’est reparti. Vous allez courir voir vos vieux dieux bleus et leur demander pourquoi ils vous ont rejeté ? Allez, haut les cœurs ! Personne n’a dit que la vie était juste !"}},
[7657] = {"Le heaume impérial en plaques",nil, {"Vous n'avez qu'à aligner 50 barres de thorium et les plans du heaume sont à vous."}},
[7658] = {"Les jambières impériales en plaques",nil, {"Pour les plans des protège-pattes, c’est 60 barres de thorium.nnOui, je sais, c’est horrible, je vous ruine et tout et tout. On me l’a déjà faite, alors gardez vos histoires pour vous, d’accord ?"}},
[7659] = {"Les épaulières impériales en plaques",nil, {"Pour le plan des épaulières, ça ira chercher dans les 20 barres de thorium."}},
[7660] = {"Echange de loup arctique",nil, nil},
[7661] = {"Echange de loup - Loup rouge",nil, nil},
[7662] = {"Un nouveau kodo bleu",nil, nil},
[7663] = {"Un nouveau kodo vert",nil, nil},
[7664] = {"Remplacement de raptor ivoire",nil, nil},
[7665] = {"Remplacement de raptor rouge",nil, nil},
[7666] = {"Retour au Grand ossuaire",nil, nil},
[7667] = {"Assistance matérielle",nil, {"Si vous voulez aider Sagorne Rôdeur-des-crêtes de la vallée de la Sagesse d'Orgrimmar, apportez-lui un Diamant d'Azeroth et un Diamant noir en parfait état."}},
[7668] = {"La menace de Ravassombre",nil, {"Servez-vous du Clairvoyant au centre du sous-sol du Grand ossuaire de la Scholomance. Cela attirera des esprits que vous devrez combattre. Les vaincre fera apparaître le Chevalier de la mort Ravassombre. Tuez-le.nnRamenez la Tête de Ravassombre à Sagorne Rôdeur-des-crêtes de la vallée de la Sagesse, à Orgrimmar."}},
[7669] = {"Retour au Grand ossuaire",nil, nil},
[7670] = {"Le seigneur Grayson Brisombre",nil, {"Parler au seigneur Grayson Brisombre dans le Quartier de la cathédrale de Hurlevent."}},
[7671] = {"Un nouveau sabre-de-givre",nil, nil},
[7672] = {"Remplacement de sabre-de-nuit",nil, nil},
[7673] = {"Echange de bélier de givre",nil, nil},
[7674] = {"Echange de bélier noir",nil, nil},
[7675] = {"Remplacement de mécanotrotteur bleu clair",nil, nil},
[7676] = {"Remplacement de mécanotrotteur blanc",nil, nil},
[7677] = {"Echange d'étalon blanc",nil, nil},
[7678] = {"Echange de palomino",nil, nil},
[7681] = {"Hunter test quest",nil, {"Nourrissez votre familier, puis parlez à votre donneur de quête."}},
[7682] = {"Hunter test quest2",nil, {"Nourrissez votre familier, puis parlez à votre donneur de quête."}},
[7701] = {"ON RECHERCHE : Surveillant Maltorius",nil, {"Loin dans les profondeurs de la Fosse aux scories dans le Chaudron, dans la Gorge des Vents brûlants, vous trouverez le nain traître du clan Sombrefer répondant au nom de surveillant Maltorius.nnTuez le surveillant Maltorius et prenez sa tête. Apportez-la au capitaine de guet Lolo Heurteloin dans la Gorge des Vents brûlants pour la récompense."}},
[7703] = {"Affaires inachevées chez les Gordok",nil, {"Trouvez le Gantelet de puissance gordok et rendez-le au Capitaine Kromcrush à Hache-tripes.nnSelon Kromcrush, les histoires des « vieux » racontent que Tortheldrin - un « vilain » elfe qui se fait appeler prince - l'a volé à l'un des rois gordok."}},
[7721] = {"Du carburant pour la zappette",nil, {"Récupérez 10 Noyaux élémentaires d'eau sur les Elémentaires marins et les Embruns le long de la Côte oubliée de Féralas, et rapportez-les à Zorbin Poudrozieu, près du quai là-bas."}},
[7722] = {"Le Flux embrasé",nil, {"Quelque part dans la Fosse aux scories du Chaudron, vous trouverez les Plans secrets : Flux embrasé dérobés par le surveillant Maltorius. Trouvez ces plans et rapportez-les au Maître forgeron Brûlotin dans la gorge des Vents brûlants."}},
[7723] = {"Maudits gros doigts",nil, {"Hansel Lourdemains veut que vous tuiez 20 Golems de guerre lourds.nnLes golems de guerre lourds errent dans les régions qui entourent le Chaudron dans la Gorge des Vents brûlants. Quand vous en aurez tué assez, revenez voir Hansel dans la Gorge des Vents brûlants."}},
[7724] = {"Une menace incandescente",nil, {"Hansel Lourdemains veut que vous tuiez 20 Grandes araignées de lave dans la gorge des Vents brûlants. Vous pouvez trouver les Grandes araignées de lave dans les plateaux et les mares de lave de la gorge des Vents brûlants.nnRevenez voir Hansel dans la gorge des Vents brûlants quand vous aurez tué assez de Grandes araignées de lave."}},
[7725] = {"Le retour des géants rétrécis",nil, {"Utilisez l'Ultra-réducteur de Zorbin sur des géants de n'importe quel type en Féralas afin les rendre plus faciles à gérer. Apportez 10 Résidus de miniaturisation que vous trouverez sur les versions rétrécies des géants à Zorbin Poudrozieu aux docks de la Côte oubliée en Féralas."}},
[7726] = {"Encore du carburant pour la zappette",nil, {"Récupérez 6 Noyaux élémentaires d'eau sur les Elémentaires marins et les Embruns le long de la Côte oubliée de Féralas, et rapportez-les à Zorbin Poudrozieu, près du quai."}},
[7727] = {"Incendosaures ? N'importosaures, plutôt",nil, {"Hansel Lourdemains veut que vous tuiez 20 Incendosaures.nnOn peut les trouver dans les bassins de lave de la Fosse aux scories, qui occupe une partie du Chaudron de la gorge des Vents brûlants.nnRetournez voir Hansel Lourdemains dans la gorge des Vents brûlants lorsque vous aurez tué suffisamment d'Incendosaures."}},
[7728] = {"OBJETS VOLES : Tuyère et longue-vue",nil, {"Trouvez la Tuyère de forge et la Longue-vue du guetteur et rapportez-les au sous-chef Scrange dans la gorge des Vents brûlants.nnLes seules informations dont vous disposez sur ces objets sont les suivantes : ils ont été dérobés par les nains Sombrefer, la Tuyère de forge est un outil utilisé par les forgerons et la Longue-vue du guetteur est un outil de surveillance inestimable pour les guetteurs."}},
[7729] = {"OFFRE D'EMPLOI : Réduire la concurrence",nil, {"Tuez 15 Sous-chefs Sombrefer et 15 Esclavagistes Sombrefer. Revenez voir le sous-chef Scrange dans la gorge des Vents brûlants quand vous aurez accompli cette tâche."}},
[7730] = {"Infestation de Zukk'ash",nil, {"Rapportez 20 Carapaces de Zukk'ash à Hadoken Rôdeur-agile au Camp Mojache en Féralas."}},
[7731] = {"Fouet-cuisant",nil, {"Battez Fouet-cuisant dans le Gouffre grouillant et rapportez ses glandes à Hadoken Swiftstrider au Camp Mojache en Feralas."}},
[7732] = {"Le rapport sur les Zukk'ash",nil, {"Apportez le rapport sur les Zukk'ash du Camp Mojache à Zilzibin Kongadok. Il réside à la Herse d'Orgrimmar."}},
[7733] = {"Qualité améliorée",nil, {"Fournir 10 Peaux de yéti Griffe Féroce à Pratt McGrubben au bastion de Pennelune, en Féralas."}},
[7734] = {"Qualité améliorée",nil, {"Fournir 10 Peaux de yéti Griffe Féroce à Jangdor Rôdeur-agile au Camp Mojache, en Féralas."}},
[7735] = {"Une peau de yéti en parfait état",nil, {"Apporter la Peau de yéti en parfait état à Pratt McGrubben au bastion de Pennelune, en Féralas."}},
[7736] = {"Réapprovisionnement en flux embrasé (Sang-royal)",nil, {"Assez perdu de temps à bavarder, <race>. Il est temps de nous concentrer sur le réapprovisionnement de nos réserves de flux embrasé qui s'amenuisent.nnVoici ce que je veux que vous me rapportiez :nn*Ecailles d'incendosaure.nn*Sang-royal.nn*Charbon.nnJe prendrai tout ce que vous me donnerez !nnEt vous le ferez vite si vous voulez bien vous entendre avec la Confrérie."}},
[7737] = {"Se faire accepter",nil, {"Vous avez monté les barreaux de l'échelle de la Confrérie, <race>. Nous commençons à avoir de vous une opinion favorable. Voilà un bel exploit !nnSi vous souhaitez gagner davantage encore de notre respect, écoutez bien.nnNous pouvons raffiner le flux embrasé en ajoutant simplement un résidu de sombre fer au mélange. On peut trouver les résidus de sombre fer dans les Profondeurs de Blackrock.nnApportez-moi tous les résidus de sombre fer que vous trouverez !"}},
[7738] = {"Une peau de yéti parfaite",nil, {"Apporter la Peau de yéti parfaite à Jangdor Rôdeur-agile au Camp Mojache, en Féralas."}},
[7741] = {"Praise from the Emerald Circle <NYI> <TXT>",nil, nil},
[7761] = {"Les ordres de Main-noire",nil, {"Cet orc est stupide. Il semble que vous deviez trouver un moyen d'obtenir la Marque de Drakkisath pour accéder à l'Orbe de commandement.nnD'après la lettre, la Marque est gardée par le général Drakkisath. Vous devriez peut-être enquêter."}},
[7781] = {"Le seigneur de Rochenoire",nil, {"Apporter la Tête de Nefarian au généralissime Bolvar Fordragon à Hurlevent."}},
[7782] = {"Le seigneur de Rochenoire",nil, {"Allez à la vallée des Héros de Hurlevent et parlez au grand maréchal Afrasiabi."}},
[7783] = {"Le seigneur de Rochenoire",nil, {"Apportez la Tête de Nefarian à Thrall à Orgrimmar."}},
[7784] = {"Le seigneur de Blackrock",nil, {"Parlez au Haut seigneur Saurcroc à Orgrimmar. Il vous attend dans la vallée de la Force."}},
[7785] = {"Examiner le Calice",nil, {"Examiner le Calice de la renaissance et le rapporter au généralissime Demitrian."}},
[7786] = {"Tonneraan le Cherchevent",nil, {"Pour libérer Tonneraan le Cherchevent de sa prison, vous devez présenter au généralissime Demitrian les moitiés droite et gauche des Liens du Cherchevent, 10 barres d'élémentium, et l'Essence du Seigneur du feu."}},
[7787] = {"Éveille-toi, Lame-tonnerre !",nil, {"Présenter la Lame du baiser du vent dormante au généralissime Demitrian."}},
[7788] = {"Triomphez des envahisseurs !",nil, {"Entrez dans le goulet des Chanteguerres, triomphez de la Horde et gagnez une Marque d'honneur du goulet des Chanteguerres. Puis revenez voir la sentinelle Chanteloin au bosquet d'Aile-argent."}},
[7789] = {"Écrasez les usurpateurs d'Aile-argent",nil, {"Entrez dans le goulet des Warsong, triomphez de l'Alliance et obtenez une Marque d'honneur du goulet des Warsong. Puis revenez voir le capitaine Shatterskull au campement de Mor'shan."}},
