[8383] = {"Souvenez-vous de la vallée d'Alterac !",nil, {"La bataille fait rage dans la vallée d'Alterac, <nom> ! Vous avez fait votre part, ces temps-ci, <nom> ?"}},
[8384] = {"La revendication du bassin d'Arathi",nil, {"La guerre contre la Horde diminue rapidement nos réserves, <nom>. Il est vital que nous conservions le contrôle du bassin d'Arathi pour que les ressources continuent à nous arriver ! Êtes-vous déjà venue nous donner un coup de main en Arathi ?"}},
[8385] = {"Des efforts concertés",nil, {"Ah, <nom>. J'espère que vous continuez à faire profiter les différents champs de bataille de votre expérience. Au front, un grand nombre de soldats sont inspirés par des vétérans endurcis comme vous."}},
[8386] = {"La lutte pour le goulet des Chanteguerres",nil, {"N'oubliez pas que la bataille pour le goulet des Warsong représente bien plus que la protection de quelques arbres. Il s'agit de conserver une frontière naturelle entre Ashenvale et les Tarrides. Vous avez des nouvelles du champ de bataille, <nom> ?"}},
[8387] = {"Les envahisseurs de la vallée d'Alterac",nil, {"La bataille pour Alterac fait rage ! Vous devez retourner à la vallée d'Alterac et repousser une fois encore les envahisseurs du territoire des Frostwolf, <nom> !"}},
[8388] = {"Pour un grand honneur",nil, {"Vous inspirez de nombreux et braves soldats, <nom>. Vous continuez à être un exemple pour tous dans la lutte contre l'Alliance. Quelles nouvelles m'apportez-vous de vos voyages ?"}},
[8389] = {"La bataille du goulet des Warsong",nil, {"La bataille continue de faire rage dans le goulet des Warsong, <nom>. Faites votre part pour chasser les sentinelles d'Aile-argent de nos terres !"}},
[8390] = {"La conquête du bassin d'Arathi",nil, {"La guerre contre la racaille de l'Alliance exige des ressources, <nom> ! Nous ne pouvons pas nous permettre de laisser tarir nos réserves au beau milieu de la lutte. Continuez à faire en sorte que le bassin d'Arathi reste entre nos mains !"}},
