[8898] = {"Très chère Colara,",nil, {"Apporter la Lettre froissée à Colara Dean, devant la banque de Hurlevent."}},
[8899] = {"Très chère Colara,",nil, {"Apporter la Lettre immaculée à Colara Dean, devant la banque à Hurlevent."}},
